(window.webpackJsonp=window.webpackJsonp||[]).push([[430],{785:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1364-tai-sao-lai-la-nguoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1364-tai-sao-lai-la-nguoi"}},[n._v("#")]),n._v(" Chương 1364: Tại sao lại là ngươi?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1364: Tại sao lại là ngươi?"),t("br"),t("br"),n._v("Tại Thanh Nguyên đế quốc, Thanh Điền Hoàng từng mang theo mặt nạ đồng xanh, đi thư họa sư công hội tìm xin giúp đỡ, hắn thuận tiện giúp vội vàng giải quyết, phát hiện bên trong ẩn giấu đi Khổng sư hư tượng."),t("br"),t("br"),n._v("Giờ phút này lại nhìn thấy như vậy một bức họa, hơn nữa cảnh giới cao hơn, sơn mạch cũng hoàn toàn giống nhau... Có thể hay không vẽ cùng một nơi?"),t("br"),t("br"),n._v("“Nơi này không có Khổng sư hư tượng, cũng không biết là cố ý không có vẽ, còn là căn bản không có phát hiện!”"),t("br"),t("br"),n._v("Nhíu nhíu mày."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, đem bức tranh đó lấy ra ngoài, cùng trước mắt sơn mạch so sánh, giống nhau như đúc, không có chút nào chênh lệch, chỉ là không có mỹ nữ đi tắm hình vẽ."),t("br"),t("br"),n._v("“Đây là liên minh đế quốc , cùng Thánh tử điện là hai cái phương hướng, khoảng cách cái này rất xa, muốn qua, Thánh vực thất trọng Thánh thú, đều cần phải bay trọn ba tháng!”"),t("br"),t("br"),n._v("Nhìn thấy trong tay hắn lời nói, cùng phía ngoài trùng điệp, hầu như giống nhau như đúc, Lạc Huyền Thanh giải thích nói."),t("br"),t("br"),n._v("“Bạch Khê sơn?”"),t("br"),t("br"),n._v("Trương Huyền nắm đấm căng thẳng."),t("br"),t("br"),n._v("“Ngươi bản vẽ này thư họa sư, trình độ cũng không tệ, giống như đúc, đem ngọn núi này thần vận toàn bộ vẽ ra!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh khen ngợi."),t("br"),t("br"),n._v("Bức họa này, tại Thanh Nguyên đế quốc kinh động như gặp thiên nhân, nhưng ở hắn loại cao thủ này trong mắt, chỉ được cho không sai."),t("br"),t("br"),n._v("“Đúng vậy a!”"),t("br"),t("br"),n._v("Đem bức tranh thu hồi, Trương Huyền tràn đầy kích động."),t("br"),t("br"),n._v("Biết đây là Bạch Khê sơn, có cơ hội, nhất định phải đi qua một chuyến, đã có Khổng sư hư ảnh, có lẽ mỹ nhân đi tắm phương vị, liền có thể tìm tới Khổng sư lưu lại di tích."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Hai người nói chuyện ở giữa, bọt khí theo linh khí dòng sông bên trong bay ra, đi tới to lớn tác phẩm hội họa trước mặt."),t("br"),t("br"),n._v("“Bức họa này, có thể vào không?”"),t("br"),t("br"),n._v("Nhìn trước mắt như là chân thực đồng dạng tác phẩm, Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("Lưu lại Động Hư hồ lô tiền bối, lưu lại bức họa này, tất nhiên có mục đích của mình, nếu là có thể đi vào xem một chút, có lẽ có thể biết càng thêm kỹ càng."),t("br"),t("br"),n._v("“Hẳn là có thể...”"),t("br"),t("br"),n._v("Lạc Huyền Thanh gật đầu."),t("br"),t("br"),n._v("“Cái kia ta xem một chút!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền thân thể nhoáng một cái bay tới đằng trước."),t("br"),t("br"),n._v("Bức họa này, không có giấy, mà là vẽ ở không gian gấp bên trên, từng tầng từng tầng nối liền cùng một chỗ, phi hành rất lâu, mới đi đến núi lớn trước mặt."),t("br"),t("br"),n._v("Sơn mạch âm u tĩnh mịch, cùng người một loại uy nghiêm cảm giác."),t("br"),t("br"),n._v("Bay lên trên đi, rất mau tới đến mỹ nữ đi tắm vị trí, quét mắt một vòng, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Cùng địa phương khác không có cái gì khác biệt, đồng thời không có Khổng sư hư tượng, cũng không có phát hiện có cái gì không gian gấp loại hình che giấu."),t("br"),t("br"),n._v("“Mặc dù không có vẽ ra Khổng sư, nhưng lưu lại bức họa này tiền bối, tất nhiên có khắc sâu dụng ý!”"),t("br"),t("br"),n._v("Nếu như cái này di tích, thật sự là Khưu Ngô cổ thánh lưu lại, tất cả liền nói thông."),t("br"),t("br"),n._v("Hắn là Khổng sư tôi tớ, lưu lại bức họa này, có phải là vì nhắc nhở hậu bối, cái này Bạch Khê sơn... Có gì đó quái lạ!"),t("br"),t("br"),n._v("Nghiên cứu một hồi, biết chỉ là bức tranh, không có khả năng tìm tới cụ thể đồ vật, thân thể nhoáng một cái, lui đi ra."),t("br"),t("br"),n._v("Mới trở lại Lạc Huyền Thanh đám người trước mặt, chỉ thấy núi lớn lắc lư một cái, hóa thành một đám vết mực."),t("br"),t("br"),n._v("“Bức họa này thời gian quá lâu, có thể duy trì nguyên trạng là bởi vì không có người đụng chạm, ngươi vừa rồi tiến vào bên trong, tương đương với chạm phải khí tức người sống, tự nhiên lại không chịu nổi!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh giải thích."),t("br"),t("br"),n._v("Trương Huyền hiểu được."),t("br"),t("br"),n._v("Thật giống như không ít theo trong hầm mộ đồ vật, hào quang diễm lệ, chỉ khi nào lấy ra, liền sẽ lập tức biến thành bùn đất chi sắc."),t("br"),t("br"),n._v("Lại không rầu rĩ, tiếp tục hướng phía trước nhìn, một cái rộng lớn thông đạo xuất hiện tại tầm mắt."),t("br"),t("br"),n._v("Đi theo Lạc Huyền Thanh đám người sau lưng, rơi ở phía trên."),t("br"),t("br"),n._v("Đã trải qua đủ loại không gian vặn vẹo, cước đạp thực địa rơi trên mặt đất, nhất thời buông lỏng không ít."),t("br"),t("br"),n._v("“Ta cảm ứng bảo vật, liền tại bên trong!”"),t("br"),t("br"),n._v("Hướng phía trong một chỉ, Vân Liên Hải hai mắt sáng lên."),t("br"),t("br"),n._v("Trước đó hắn còn có chút cảm ứng không rõ ràng lắm, đến nơi này, đã mười phân rõ ràng, chỉ cần dọc theo thông đạo đi về phía trước, hẳn là có thể thuận lợi tìm tới không ít bảo bối."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nghe được bảo vật ngay tại không xa, đám người liền lập tức tinh thần tỉnh táo, đồng loạt đi thẳng về phía trước."),t("br"),t("br"),n._v("Thời gian không dài, một tòa cung điện liền xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Cung điện không tính quá lớn, cũng không có nhiều xa hoa, đứng sừng sững không biết bao nhiêu năm, cho người ta một loại cũ kỹ cảm giác."),t("br"),t("br"),n._v("“Nơi này đoán chừng gặp nguy hiểm, ta vào xem, các ngươi đằng sau lại cùng lên đến!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh đi đến phía trước nhất, mấy bước đi tới trước cửa."),t("br"),t("br"),n._v("Cung điện cửa chính, toàn bộ từ đá điêu khắc mà thành, phía trên viết lít nha lít nhít phù văn."),t("br"),t("br"),n._v("“Là không gian phù văn, Khưu Ngô cổ thánh sáng tạo ra đặc thù văn tự, thường xuyên quan sát, có khả năng lĩnh ngộ không gian pháp tắc, gia tộc bọn ta, có những văn tự này kỹ càng chú giải!”"),t("br"),t("br"),n._v("Nhìn một cái, Lạc Huyền Thanh lông mày nâng lên."),t("br"),t("br"),n._v("“Không gian phù văn?”"),t("br"),t("br"),n._v("Trương Huyền bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Hắn tại Khưu Ngô cung gặp qua."),t("br"),t("br"),n._v("Đại Bi Thiên Ma chưởng, liền là dùng loại này kiểu chữ viết, lúc ấy không biết đến cùng là cái gì, cuối cùng căn cứ văn tự hình dạng, học được pháp quyết."),t("br"),t("br"),n._v("“Nói như vậy lên, chỗ này di tích, thật sự là cổ thánh lưu lại đúng không?”"),t("br"),t("br"),n._v("Bích Hồng Âm tràn đầy kích động."),t("br"),t("br"),n._v("Khưu Ngô cổ thánh, mặc dù là Khổng sư tôi tớ, nhưng thực lực chân chính, không thể so với Thánh tử điện khai phái tổ sư Bốc Thương cổ thánh yếu hơn bao nhiêu, trọng yếu nhất chính là, đối không gian lĩnh ngộ cực cao, nếu như có thể được hắn truyền thừa, đột phá Thánh vực bát trọng, thậm chí xung kích cửu trọng, đều chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("“Tám chín phần mười, chỉ là... Gia tộc bọn ta, ghi chép cổ thánh sự tích thư tịch không ít, chưa từng nghe nói qua, nơi này còn để lại một chỗ di tích!”"),t("br"),n._v(" Lạc Huyền Thanh kỳ lạ."),t("br"),t("br"),n._v("“Cái này di tích, thoạt nhìn hết sức phức tạp, nhưng đối với cổ thánh loại cường giả cấp bậc này, sợ chỉ là tiện tay mà làm, thậm chí chính mình cũng chưa hẳn nhớ...”"),t("br"),t("br"),n._v("Vân Liên Hải nói."),t("br"),t("br"),n._v("Thân là có khả năng tầm bảo gia tộc, đủ loại bảo tàng đã gặp thực sự quá nhiều."),t("br"),t("br"),n._v("Không ít di tích, đối sau người mà nói, huyền diệu vô cùng, bảo tàng cũng trân quý dị thường, nhưng đối với lưu lại người mà nói, không đáng kể chút nào."),t("br"),t("br"),n._v("Cảnh giới khác biệt, ánh mắt tự nhiên không giống."),t("br"),t("br"),n._v("“Cũng phải!”"),t("br"),t("br"),n._v("Trương Huyền thâm dĩ vi nhiên gật đầu."),t("br"),t("br"),n._v("Thật giống như hắn hiện tại, linh thạch thượng phẩm, đã tác dụng không lớn, tùy tiện ném lên mấy trăm, hơn ngàn đều sẽ không cảm thấy đau lòng, nhưng đặt ở Vạn Quốc liên minh, dù là một viên, đều phải bị vô số người vây đỡ."),t("br"),t("br"),n._v("Động Hư hồ lô, bọn họ thoạt nhìn vô cùng trân quý, đối với cổ thánh tới nói, tiện tay ném đi, đều không tính là gì."),t("br"),t("br"),n._v("“Lạc Huyền Thanh, những phù văn này, ngươi có thể nhận biết, phía trên viết cái gì?”"),t("br"),t("br"),n._v("Vân Liên Hải hỏi."),t("br"),t("br"),n._v("“Là lưu cho hậu nhân, đại ý là, có thể đi đến nơi đây, nói rõ đối không gian lĩnh ngộ rất nhiều, có tư cách được truyền thừa của hắn, còn có ghi chép cái cửa này mở ra phương pháp...”"),t("br"),t("br"),n._v("Nhìn chằm chằm trên cửa văn tự, Lạc Huyền Thanh nói."),t("br"),t("br"),n._v("Những này không gian phù văn, Lạc gia đã nghiên cứu không biết đã bao nhiêu năm, cơ bản đều có thể nhận biết."),t("br"),t("br"),n._v("“Truyền thừa? Ngươi ý tứ, nơi này thật có cổ thánh truyền thừa?”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh nhẹ gật đầu, hít sâu một hơi: “Ta muốn mở cửa, mọi người cẩn thận một chút, đều đi tới đây, tuyệt đối không nên xảy ra chuyện...”"),t("br"),t("br"),n._v("Sau khi nói xong lui một bước, ngón tay một phen, một đạo chân khí bừng lên, trực tiếp hướng cửa nhấn tới."),t("br"),t("br"),n._v("Liên tục ba lần, hiện lên “Phẩm” hình chữ, cửa chính mặt ngoài xuất hiện đạo vệt sóng gợn, “Kẹt kẹt!” Chậm chạp mở ra."),t("br"),t("br"),n._v("Chân khí tuôn ra, tại chung quanh thân thể hình thành vòng bảo hộ."),t("br"),t("br"),n._v("Khưu Ngô cổ thánh mặc dù là danh khí rất lớn Thánh Nhân, nhưng nếu đã lưu lại truyền thừa, tất nhiên sẽ có đủ loại khảo hạch, vô cùng có khả năng liền tồn tại một ít cơ quan bẫy rập loại hình đồ vật, không thể không phòng."),t("br"),t("br"),n._v("Đi đầu đi tiến gian phòng, Lạc Huyền Thanh một lát sau hướng đám người vẫy vẫy tay: “Không có gặp nguy hiểm, tất cả vào đi!”"),t("br"),t("br"),n._v("Đám người nối đuôi nhau mà vào, chỉ có một cái đại sảnh, trụi lủi thứ gì đều không có, một cái pho tượng đứng sừng sững ở cách đó không xa."),t("br"),t("br"),n._v("“Là Khưu Ngô cổ thánh!”"),t("br"),t("br"),n._v("Nhìn rõ ràng pho tượng bộ dáng, Trương Huyền mở lời."),t("br"),t("br"),n._v("Pho tượng này, hết sức quen thuộc, ban đầu ở Khưu Ngô cung thời điểm, chỉ thấy qua!"),t("br"),t("br"),n._v("“Đây là cổ thánh?”"),t("br"),t("br"),n._v("Viên Hiểu đám người tràn đầy khiếp sợ."),t("br"),t("br"),n._v("Bọn họ cứ việc xuất thân danh môn, nhưng cùng cổ thánh chênh lệch niên đại thực sự quá xa xưa, cũng chưa gặp qua."),t("br"),t("br"),n._v("“Ta gặp qua cổ thánh chân dung, cái này đích xác là hắn!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh lên tiếng, mang theo nghi ngờ nhìn qua: “Trương sư như thế nào biết được?”"),t("br"),t("br"),n._v("Lạc gia nắm giữ Khưu Ngô cổ thánh chân dung, vị này chỉ là theo Thanh Nguyên đế quốc mà đến, làm sao có thể một cái nhận ra?"),t("br"),t("br"),n._v("“Mới vừa nói kiểu chữ là cổ thánh lưu lại, ta cảm thấy pho tượng hẳn là hắn, đoán lung tung...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Khưu Ngô cung nắm giữ Diễn Không thiên thư, còn là ít nhất cho thỏa đáng."),t("br"),t("br"),n._v("Thấy hắn không nguyện ý nhiều lời, biết mỗi người đều có cơ duyên, Lạc Huyền Thanh không nói thêm lời, mà là mấy bước đi tới trước mặt, đầu gối mềm nhũn quỳ rạp xuống đất."),t("br"),t("br"),n._v("Rất cung kính dập đầu mấy cái vang tiếng, quay đầu hướng đám người giải thích."),t("br"),t("br"),n._v("“Chúng ta Lạc gia đối không gian hiểu, chính truyền thừa tại cổ thánh, bởi vậy, tiên tổ quy định, chỉ muốn gặp được cổ thánh pho tượng hoặc là lưu lại ý niệm, nhất định phải quỳ lạy, lấy tổ sư lễ tiết đối đãi!”"),t("br"),t("br"),n._v("“Truyền thừa tại cổ thánh?”"),t("br"),t("br"),n._v("Trương Huyền tràn đầy kinh ngạc."),t("br"),t("br"),n._v("Quay đầu nhìn về phía Viên Hiểu đám người, lại thấy bọn họ dường như đã sớm biết."),t("br"),t("br"),n._v("“Không gian dính dáng tiên thiên đại đạo, đại đạo duy nhất, không có khả năng giống kiếm đạo đồng dạng, chia làm rất nhiều loại chân giải, đã như vậy, nhất mạch tương thừa, cũng liền rất đơn giản...”"),t("br"),t("br"),n._v("Suy nghĩ một chút, bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Thật ra thì hẳn là trước thời hạn chỉ biết."),t("br"),t("br"),n._v("Khó trách Lạc Thánh lưu lại giường nói cổ thư có thể làm cho Diễn Không thiên thư trở nên hoàn mỹ, thích hợp tu luyện, nguyên lai cả hai xuất từ đồng nguyên."),t("br"),t("br"),n._v("“Hậu học vãn bối, Lạc Huyền Thanh, gặp qua Khưu Ngô cổ thánh!”"),t("br"),t("br"),n._v("Làm xong những này, bàn tay nhẹ nhàng hướng về phía trước duỗi ra, rơi vào pho tượng cái bệ bên trên."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một tiếng kêu khẽ, cổ thánh trong nháy mắt tản mát ra vạn đạo quang mang, nguyên bản đợi tại nguyên chỗ không nhúc nhích tượng đá, chậm rãi mở mắt."),t("br"),t("br"),n._v("“Cổ thánh ý niệm...”"),t("br"),t("br"),n._v("Bích Hồng Âm, Viên Hiểu đám người tất cả đều tràn đầy kích động, không dám chần chừ, theo sát ở phía sau, đồng loạt quỳ rạp xuống đất."),t("br"),t("br"),n._v("Khưu Ngô cổ thánh, thế nhưng là so với bọn hắn khai phái tổ sư đều muốn cổ xưa Thánh giả, tiên tổ gặp lên, đều muốn quỳ xuống xưng hô một tiếng tiền bối, không dám giả bộ lớn."),t("br"),t("br"),n._v("“Năm đó đi ngang qua nơi này, trong lúc vô tình được một viên Động Hư hồ lô hạt giống, tiện tay trồng xuống đến, hy vọng có thể lưu cho hậu bối, đối hắn có chỗ trợ giúp. Các ngươi có thể đi tới nơi này, nói rõ đối không gian nắm giữ cực cao hiểu, có tư cách đem luyện hóa, hơn nữa được ta truyền thụ cho cơ duyên...”"),t("br"),t("br"),n._v("Tượng đá thanh âm chậm rãi vang lên."),t("br"),t("br"),n._v("“Cảm ơn cổ thánh...”"),t("br"),t("br"),n._v("Đám người từng cái tràn đầy kích động, Lạc Huyền Thanh đang muốn lại nói chút lời cảm kích, đột nhiên nhìn thấy cách đó không xa Trương Huyền vẫn chưa quỳ xuống."),t("br"),t("br"),n._v("Nhướng mày, nhịn không được trách mắng: “Trương sư, nhìn thấy cổ thánh còn không hạ bái...”"),t("br"),t("br"),n._v("Vừa mới mở lời, chỉ thấy cổ thánh pho tượng cũng xoay đầu lại, biến sắc, một bộ vẻ mặt như gặp phải quỷ: “Tại sao lại là ngươi?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1372"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1374"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);