(window.webpackJsonp=window.webpackJsonp||[]).push([[434],{789:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1368-thien-co-đuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1368-thien-co-đuong"}},[n._v("#")]),n._v(" Chương 1368: Thiên Cơ đường")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1368: Thiên Cơ đường"),t("br"),t("br"),n._v("“Vậy làm phiền Lạc huynh!”"),t("br"),t("br"),n._v("Vừa vặn đối Thánh tử điện không quen, đi đâu cũng không tìm tới địa phương, nghe được Lạc Huyền Thanh như vậy lời thề son sắt, Trương Huyền con mắt lập tức sáng lên."),t("br"),t("br"),n._v("Có người dẫn đầu, có thể tiết kiệm đi không ít phiền phức, lại nói, đi đánh Trương Thuần, hắn cũng là vô cùng vui lòng, huống chi người này còn nợ bản thân linh thạch, ngày đã lâu như vậy, cũng nên tính tiền."),t("br"),t("br"),n._v("“Khách khí với ta cái gì...” Cười ha ha một tiếng, Lạc Huyền Thanh đi đầu đi thẳng về phía trước."),t("br"),t("br"),n._v("Trước mắt vị này, mặc dù tu vi không cao, nhưng mà bất kể sắp đặt trận pháp, còn là chỉ điểm người khác đột phá, đều có vượt qua thường nhân năng lực, tới quan hệ thân thiết, đối với hắn mà nói đều có rất lớn bổ ích."),t("br"),t("br"),n._v("Loại cơ hội này, đương nhiên sẽ không buông tha."),t("br"),t("br"),n._v("Một bên tiến lên, một bên dùng ngọc phù truyền âm, một lát sau, mỉm cười, Lạc Huyền Thanh xoay đầu lại: “Người này không ở bên trong viện, mà là tại Thiên Cơ đường, chúng ta đến đó chặn hắn!”"),t("br"),t("br"),n._v("“Thiên Cơ đường?” Trương Huyền cau mày."),t("br"),t("br"),n._v("“Ừm, Thiên Cơ đường cùng trước đó chúng ta đi khảo hạch trận pháp sư công hội đồng dạng, thuộc về một loại đặc thù chức nghiệp tại Thánh tử điện phân bộ.” Lạc Huyền Thanh nói."),t("br"),t("br"),n._v("“Ngươi nói chính là... Thiên Cơ sư?” Trương Huyền mắt sáng rực lên."),t("br"),t("br"),n._v("Trước đó hắn liền nghe qua cái nghề nghiệp này tin đồn, thuộc về thượng cửu lưu chức nghiệp, vô cùng thần bí, có khả năng sử dụng đặc thù kỹ xảo, trước thời hạn dự đoán trong thời gian ngắn sắp chuyện phát sinh, thôi diễn họa phúc, dự báo cát hung."),t("br"),t("br"),n._v("Thánh Nhân gia tộc, bình thường đều sẽ nuôi dưỡng mấy vị, tiến hành đại điển, dự phán đủ loại quyết định."),t("br"),t("br"),n._v("Cùng nhau đi tới, chỉ nghe qua tin đồn, chưa bao giờ thấy qua, không nghĩ tới Thánh tử điện lại có."),t("br"),t("br"),n._v("“Người này mới vừa bị của ngươi thú sủng đánh một trận, đoán chừng cảm thấy xuất sư bất lợi, muốn đi qua xem bói một lần, cũng có thể là muốn tìm người suy tính ra lai lịch của ngươi!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh cười nói."),t("br"),t("br"),n._v("Có thể nhanh như vậy liền biết Trương Thuần tại Thiên Cơ đường, nói rõ Thánh tử điện có không ít ánh mắt, biết mục đích của đối phương, không tính quá khó."),t("br"),t("br"),n._v("“Suy tính lai lịch? Tìm người hỏi thăm một lần không phải tốt?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Lai lịch của hắn còn dùng suy tính ư? Tìm người hỏi thăm một chút, hẳn là càng thêm đơn giản, càng thêm chuẩn xác đi!"),t("br"),t("br"),n._v("“Trương gia người từ trước tới nay lải nhải, sùng bái nhất chính là cái này Thiên Cơ xem bói, không nói cái khác, gia chủ đương thời, Hưng Mộng Kiếm thánh, những năm này đem trọn cái đại lục cửu tinh cấp bậc Thiên Cơ sư, đều mời khắp cả!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh cười nhạo."),t("br"),t("br"),n._v("“Mời Thiên Cơ sư làm gì?” Trương Huyền tò mò."),t("br"),t("br"),n._v("Trương gia thân là đại lục đệ nhất gia tộc, quyền thế củng cố, thiên tài vô số, còn có cái gì không hài lòng, cần Thiên Cơ sư thôi diễn?"),t("br"),t("br"),n._v("“Ai biết được! Dù sao Trương gia người nhất thần kinh, gặp nhất định phải cẩn thận...” Lạc Huyền Thanh khoát tay áo."),t("br"),t("br"),n._v("  Thiên Cơ đường cùng những nghề nghiệp khác khảo hạch điểm đồng dạng, tọa lạc tại trí điện, hai người tốc độ rất nhanh, thời gian không dài liền đến đến trước mặt."),t("br"),t("br"),n._v("Nơi này cứ việc cũng tại trí điện phạm vi bên trong, nhưng cùng những nghề nghiệp khác khảo hạch điểm, hoàn toàn khác biệt, thoạt nhìn mười phần cũ kỹ, để lộ ra viễn cổ khí tức."),t("br"),t("br"),n._v("Toàn bộ kiến trúc, hình như móc ngược mai rùa, mặt ngoài mang theo đủ loại kỳ quái vết rách, dường như ẩn chứa thiên đạo ý niệm, lại hình như là một loại trận pháp đặc biệt, đang dòm ngó giữa thiên địa bí ẩn."),t("br"),t("br"),n._v("Cái khác khảo hạch chỗ cửa chính đều là rộng mở, mà ở trong đó lại là đóng chặt."),t("br"),t("br"),n._v("“Đây là Thiên Cơ đường?”"),t("br"),t("br"),n._v("“Ừm, Thiên Cơ chức nghiệp, ta cũng học qua, nghiên cứu vài ngày, ngay cả nhập môn đều làm không được, ngay sau đó liền từ bỏ! Cái nghề nghiệp này, nói gạt người, một số thời khắc rất chuẩn, phải nói là thật, có thể... Chỉ bằng vào mấy cái mai rùa, tiền đồng, liền suy tính họa phúc, thực sự để cho người ta khó mà tiếp nhận, cụ thể như thế nào, ta cũng không rõ lắm!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh lắc đầu."),t("br"),t("br"),n._v("“Tồn tại liền có đạo lý! Cái nghề nghiệp này có khả năng truyền thừa vài vạn năm mà không bị lịch sử chôn vùi, nói rõ quả thực có hắn giá trị tồn tại.” Trương Huyền cảm khái nói."),t("br"),t("br"),n._v("Lịch sử đều là đi về phía trước, có khả năng truyền thừa lâu như vậy, chịu đến vô số danh sư vây đỡ, tuyệt không phải mặt ngoài nhìn thấy đơn giản như vậy."),t("br"),t("br"),n._v("“Ngươi bây giờ nói chuyện như thế nào cùng muội muội ta đồng dạng, lần trước trò chuyện lên Thiên Cơ sư, nàng cũng là nói như vậy...”"),t("br"),t("br"),n._v("Con mắt liếc tới, Lạc Huyền Thanh lắc đầu."),t("br"),t("br"),n._v("Muội muội của hắn đã từng nói qua lời giống vậy."),t("br"),t("br"),n._v("“Có thể cùng tiểu công chúa ý nghĩ đồng dạng, là vinh hạnh của ta...” Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Không cần vuốt mông ngựa!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh một mặt cảnh giác: “Ta khuyên ngươi không nên đánh muội muội ta chủ ý, đầu tiên như ngươi loại này thực lực nàng căn bản chướng mắt, tiếp theo, Trương gia cùng Lạc gia ý niệm, không phải ngươi một ngoại nhân có thể chống lại! Tốt nhất vẫn là bỏ đi phần này tâm đi!”"),t("br"),t("br"),n._v("Thông qua những ngày này tiếp xúc, hắn cũng phát hiện, vị này Trương Huyền, nơi nào đều tốt, liền là đối với hắn muội muội sự tình, quan tâm có chút quá mức."),t("br"),t("br"),n._v("Đổi lại người khác khẳng định đã sớm đánh một trận tơi bời, nhưng trước mắt vị này cho hắn giúp chiếu cố rất lớn, lại để cho thành công đột phá... Mới nhịn không được khuyên can."),t("br"),t("br"),n._v("Mặc dù cũng rất muốn làm cho đối phương trở thành muội phu của mình, nhưng cũng rõ ràng chuyện này, là tuyệt đối không thể nào."),t("br"),t("br"),n._v("Thân là Lạc gia con cháu, hơn nữa còn là hạch tâm nhất huyết mạch, đều không thể thay đổi muội muội vận mệnh, huống chi một cái chỉ có Xuất Khiếu cảnh đỉnh phong tiểu nhân vật!"),t("br"),t("br"),n._v("Cho nên, còn là đừng suy nghĩ, nghĩ tới càng nhiều, đau lòng càng nhiều, hắn cũng không muốn, thật vất vả công nhận một vị bằng hữu, tại bởi vì hắn muội muội sự tình, rơi vào nguy cơ."),t("br"),t("br"),n._v("“Chướng mắt ta? Cái này có thể chưa hẳn, duyên phận hai chữ khó mà nói...” Trương Huyền nhịn không được mở lời."),t("br"),t("br"),n._v("Hắn cùng Lạc Nhược Hi nhận biết tại mênh mông bên trong dãy núi, nếu như không có những cái kia linh thú, không có tâm huyết của hắn dâng lên nhất định phải giúp một tay, khả năng liền không có hai người quen biết hiểu nhau."),t("br"),n._v("Duyên phận hai chữ, nghe kỳ diệu, thường thường ngay tại đôi kia xem trong nháy mắt."),t("br"),t("br"),n._v("“Trước đó cũng không ít nghĩ như vậy, đều bị ta đánh cho tàn phế, cũng có một chút, bị Trương Thuần đánh phế bỏ...” Hừ một câu, Lạc Huyền Thanh đang muốn nói tiếp, đột nhiên nghe được “Kẹt kẹt!” Một tiếng, trước mắt cửa chính từ từ mở ra."),t("br"),t("br"),n._v("Ngay sau đó từ bên trong đi ra một người mặc trường bào thanh niên."),t("br"),t("br"),n._v("“Hai vị sư huynh, lão sư cho mời!”"),t("br"),t("br"),n._v("Đi tới trước mặt, thanh niên ôm quyền, làm cái cung thỉnh dáng vẻ."),t("br"),t("br"),n._v("“Ngươi lão sư, ngươi nói thế nhưng là... Phong Trọng Thuyên đại sư?” Lạc Huyền Thanh sững sờ, con mắt nhịn không được sáng lên."),t("br"),t("br"),n._v("“Không sai!” Nhẹ gật đầu, thanh niên trên mặt lộ ra vẻ tự hào."),t("br"),t("br"),n._v("“Phong Trọng Thuyên đại sư, từ trước tới nay thần long thấy đầu mà không thấy đuôi, ta nghe nói Trương Thuần, liên tục bái kiến qua bảy lần đều ăn bế môn canh... Lại muốn gặp ta?”"),t("br"),t("br"),n._v("Tràn đầy kích động, Lạc Huyền Thanh suýt chút nữa nhảy dựng lên."),t("br"),t("br"),n._v("Trương gia người, đối thiên cơ sư mười phần tôn trọng, vị này Phong Trọng Thuyên đại sư càng là toàn bộ Thánh tử điện lợi hại nhất một vị, Trương Thuần từng nhiều lần đến đây bái kiến, lại một mực bị cự tuyệt ở ngoài cửa, bản thân liền danh thiếp đều không có phát ra, đối phương liền phái người tới nghênh đón, chẳng phải đại biểu, vượt qua đối phương rất nhiều?"),t("br"),t("br"),n._v("Lời như vậy, sau đó gặp phải Trương Thuần, liền có thể thật tốt chế nhạo hắn một phen."),t("br"),t("br"),n._v("“Ngươi suy nghĩ nhiều, không phải muốn gặp ngươi, lão sư chỉ đích danh muốn gặp là, bên cạnh ngươi vị tiểu hữu này.”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, thanh niên mí mắt vừa nhấc, khoát tay áo."),t("br"),t("br"),n._v("“Ta?” Trương Huyền sửng sốt: “Vì sao?”"),t("br"),t("br"),n._v("Hắn vừa mới tới, liền đối phương là ai, cũng không biết thấy mình làm gì?"),t("br"),t("br"),n._v("“Ta cũng không rõ lắm, đi qua liền biết.”"),t("br"),t("br"),n._v("Thật ra thì, thanh niên cũng cảm thấy có chút khó tin."),t("br"),t("br"),n._v("Lấy thân phận lão sư, liền xem như Thánh tử điện quyền điện chủ cùng trưởng lão, nói không nể mặt mũi, cũng không có biện pháp gì, bây giờ lại muốn gặp một cái, không có bất kỳ cái gì danh khí, thoạt nhìn chỉ có 20 tới tuổi tân sinh, càng làm cho bản thân tự mình ra nghênh tiếp..."),t("br"),t("br"),n._v("Trong lòng tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Nếu là đối phương cùng vị tiểu công chúa kia đồng dạng, nắm giữ siêu tuyệt thực lực, ngược lại cũng thôi, chỉ có Xuất Khiếu cảnh đỉnh phong, tại cái khác địa phương coi như được thiên tài, nhưng ở Thánh tử điện, vừa nắm một bó to, không đáng kể chút nào."),t("br"),t("br"),n._v("“Gặp Trương sư?” Không chỉ có Trương Huyền kỳ lạ, Lạc Huyền Thanh cũng mở to hai mắt nhìn."),t("br"),t("br"),n._v("Còn tưởng rằng rốt cục có cơ hội, tại Trương Thuần trước mặt biểu hiện một chút, không nghĩ tới... Đối phương muốn gặp là bên cạnh vị này."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Mặc dù kỳ lạ, nhưng thấy đối phương cũng không biết, Trương Huyền không nói thêm lời, theo sát tại sau lưng, nhấc chân đi vào cửa chính."),t("br"),t("br"),n._v("Thiên Cơ đường nội bộ thoạt nhìn càng thêm cũ kỹ, từng cái cổ quái cột cờ cắm trên mặt đất, đem đại sảnh một góc, chia cắt thành vô số cái nhỏ hẹp khu vực, mấy chục cái người trẻ tuổi ngồi tại đây chút bên trong, có cầm tiền đồng, có cầm mai rùa, nhắm mắt lại, trầm tư suy nghĩ, tựa hồ tại thôi diễn cái gì."),t("br"),t("br"),n._v("Trước mặt bọn họ, một cái màu đỏ xanh giáp trùng, đang bị gác ở nóng rực lửa than bên trên, xem ra bất cứ lúc nào cũng sẽ tử vong."),t("br"),t("br"),n._v("“Đây là... Có thể sinh tồn ở lôi đình bên trong khinh giáp trùng?”"),t("br"),t("br"),n._v("Trương Huyền tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Khinh giáp trùng mặc dù chưa thấy qua, lại nhìn qua thư tịch ghi chép, nghe nói qua tin đồn, nghe nói loại sinh mạng này, không ăn cơm, không uống nước, chỉ có dông tố đan xen, mới có thể theo trong đất chui ra ngoài, xông lên bầu trời, dựa vào hấp thu lôi điện lực lượng duy trì sinh tồn."),t("br"),t("br"),n._v("Số lượng hiếm thấy, mười phần thưa thớt, một chút thuần thú sư muốn tìm kiếm đều không thể được, ở đây lại gác ở trên lửa nướng... Để cho người ta không hiểu rõ nổi."),t("br"),t("br"),n._v("Chưa từng nghe nói cái đồ chơi này có thể ăn ah, coi như muốn ăn, mười mấy người một cái lớn chừng ngón cái côn trùng cũng không đủ lạnh kẽ răng ah..."),t("br"),t("br"),n._v("“Những này là Thiên Cơ sư bình thường huấn luyện, ngăn cách tại phong kín trong không gian, đóng lại lục thức, dùng ý niệm tìm kiếm Thiên Cơ, suy đoán khinh giáp trùng, là bị lửa than thiêu chết còn là, từ trong đó leo ra?...”"),t("br"),t("br"),n._v("Nhìn ra hắn nghi hoặc, Lạc Huyền Thanh truyền âm giải thích."),t("br"),t("br"),n._v("Hắn mặc dù không phải Thiên Cơ sư, thế nhưng là tới học qua, biết trong đó tình huống."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền kỳ lạ."),t("br"),t("br"),n._v("Khinh giáp trùng đặt ở lửa than bên trên, tùy thời đều có thể sẽ bị thiêu chết, cũng có cơ hội, chạy ra thăng thiên, cụ thể là sống lấy còn là tử vong, không tận mắt quan sát liền không có kết quả, cùng tiết định ngạc mèo đồng dạng, thuộc về nghịch lý, như thế nào thôi diễn Thiên Cơ?"),t("br"),t("br"),n._v("“Khinh giáp trùng, có khả năng không sợ lôi đình, trên thực tế là tại cùng thiên đạo chống lại, bởi vậy cũng được xưng làm Thiên Cơ trùng, phỏng đoán loại sinh mạng này sinh tử, có thể khiến người ta lại càng dễ biết rõ cái kia hư vô mờ mịt cơ duyên, Thiên Đạo quy luật vô thường!”"),t("br"),t("br"),n._v("Tựa hồ nghe đến hai người truyền âm, thanh niên xoay đầu lại, giải thích nói."),t("br"),t("br"),n._v("“Nha...” Trương Huyền cái hiểu cái không, nhìn một cái lửa than bên trên côn trùng, nhíu mày một cái: “Như thế nóng bỏng hỏa diễm, cái này côn trùng hẳn là lập tức liền sẽ chết a?”"),t("br"),t("br"),n._v("“Chết?”"),t("br"),t("br"),n._v("Thanh niên cười cười: “Cái này côn trùng ta thôi diễn qua, có thể sống lâu trăm tuổi, sẽ không chết...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, liền nghe đến một tiếng bóng bay đâm bạo phát thanh âm vang lên, nguyên bản nằm tại lửa than bên trên khinh giáp trùng, đột nhiên bành trướng, trong nháy mắt nổ thành bột phấn."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("“Ây...” Trương Huyền nhìn qua, một mặt kỳ lạ: “Chẳng lẽ... Hôm nay là nó một trăm tuổi sinh nhật?”"),t("br"),t("br"),n._v("“...” Thanh niên."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1376"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1378"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);