(window.webpackJsonp=window.webpackJsonp||[]).push([[435],{791:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1369-nhin-trom-vo-toi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1369-nhin-trom-vo-toi"}},[n._v("#")]),n._v(" Chương 1369: Nhìn trộm vô tội")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1369: Nhìn trộm vô tội"),t("br"),t("br"),n._v("Không có trả lời Trương Huyền lời nói, mà là đi tới khinh giáp trùng trước mặt, nhìn kỹ một hồi, thanh niên có chút không dám tin tưởng."),t("br"),t("br"),n._v("Hắn thôi diễn năng lực, đã đạt đến bát tinh cấp bậc, không ít chuyện đều có thể xem bói đi ra, không sai chút nào, trước mắt cái này côn trùng, trước đó tính qua nhiều lần, đều là thọ chung mà ngủ, nói thế nào nổ liền nổ?"),t("br"),t("br"),n._v("Cái gọi là sống lâu trăm tuổi chỉ là cái hư từ thôi, đương nhiên không phải là đối phương nói tới, ngày hôm nay vừa vặn một trăm!"),t("br"),t("br"),n._v("Hơn nữa liền xem như thật sự là loại tình huống này, cũng không có khả năng tiếng nói mới rơi, liền trực tiếp nổ thành bột phấn đi."),t("br"),t("br"),n._v("Đứng tại chỗ rầu rĩ một hồi, thực sự không hiểu, đến cùng chuyện gì xảy ra, lúc này mới không suy nghĩ nhiều, tiếp tục mang theo hai người đi thẳng về phía trước."),t("br"),t("br"),n._v("Chỉ chốc lát, đi tới một cái phòng, còn chưa kịp gõ cửa, liền nghe đến bên trong một thanh âm vang lên: “Vào đi!”"),t("br"),t("br"),n._v("Ngay sau đó cửa phòng từ từ mở ra."),t("br"),t("br"),n._v("Trương Huyền hướng phía trong nhìn, chỉ thấy một cái lão giả, ngồi ngay ngắn ở chính giữa đại sảnh, một thanh niên thì đứng tại một bên, không phải người khác, đúng là bọn họ muốn tìm vị kia Trương Thuần!"),t("br"),t("br"),n._v("Lúc này thương thế trên người hắn đã tốt đẹp, khí tức so trước đó càng thêm tinh thuần, dường như lần trước mặc dù bị đánh tơi bời một trận, nhưng thực lực nhưng cũng có tăng lên không nhỏ, xem như vì họa được phúc."),t("br"),t("br"),n._v("“Là ngươi?”"),t("br"),t("br"),n._v("Nhìn thấy vào cửa là Trương Huyền, Trương Thuần vẻ mặt lập tức trở nên tái mét."),t("br"),t("br"),n._v("Đường đường Trương gia thiên tài con cháu, bị cái này không biết tên gia hỏa, mượn nhờ thú sủng đánh một trận tơi bời, suýt chút nữa chết tại hạp cốc, mãnh liệt nhục nhã sắp nổ."),t("br"),t("br"),n._v("Bất quá, nhớ tới sự tình gì, vội vàng xoay người, tràn đầy nghi ngờ: “Phong lão, ngươi nói cái kia quý khách... Chẳng lẽ là hắn?”"),t("br"),t("br"),n._v("Vừa rồi Phong lão, để học sinh của hắn đi nghênh đón khách quý, nghe trong tai, còn tưởng rằng là nào đó một vị tu vi hoặc là thực lực đạt tới đỉnh phong cường giả, nằm mơ đều không có nghĩ đến, là người này!"),t("br"),t("br"),n._v("Một cái chỉ có Xuất Khiếu cảnh đỉnh phong tiểu nhân vật, may mắn thuần phục Địa Ngục Thanh Long thú, mới lấy hung hăng, cho dù có chút vốn liếng, cũng không trở thành, để vị này Liên gia chủ đều kính trọng ba phần người, thái độ như thế đi!"),t("br"),t("br"),n._v("“Không sai!” Phong lão đứng dậy, nhìn về phía Trương Huyền, quan sát tỉ mỉ một phen, nhịn không được hai mắt sáng lên: “Quả nhiên là !”"),t("br"),t("br"),n._v("“Diễn Hư thể chất?” Trương Huyền vẻ mặt nghi hoặc."),t("br"),t("br"),n._v("Mặc dù Thiên Đạo thư viện không cách nào nhìn ra hắn tình huống cụ thể, nhưng nhìn qua nhiều như vậy thư tịch, biết rất nhiều thể chất đặc thù, không thuộc về trong đó bất kỳ một cái nào."),t("br"),t("br"),n._v("Nói cách khác, chỉ là bình thường không thể lại bình thường người bình thường!"),t("br"),t("br"),n._v("Không giống Triệu Nhã cùng Ngụy Như Yên đồng dạng, có thể kích hoạt thể chất, nhanh chóng tấn cấp."),t("br"),t("br"),n._v("Diễn Hư thể chất là cái gì? Trước kia chưa từng nghe qua a?"),t("br"),t("br"),n._v("Thật muốn có cái gì thể chất đặc thù, cũng không trở thành tiến bộ chậm như vậy, liền học sinh, bạn gái cũng không sánh bằng."),t("br"),t("br"),n._v("“Đại Diễn số lượng bốn mươi chín, nhiều một là hư, mà ngươi chính là cái kia ‘Hư’ thể chất.”"),t("br"),t("br"),n._v("Sợi sợi chòm râu, Phong lão cười giải thích: “Dùng phổ thông mà nói, liền là không cách nào dùng Thiên Cơ thôi diễn phương pháp, tới xác định vận mệnh của ngươi cùng hướng đi!”"),t("br"),t("br"),n._v("Trương Huyền còn chưa hiểu tới, chuyện gì xảy ra, một bên thanh niên, dường như giật nảy mình, khắp khuôn mặt là khó tin."),t("br"),t("br"),n._v("“Lão sư, loại thể chất này thật tồn tại?”"),t("br"),t("br"),n._v("“Trước kia ta cũng tưởng rằng cái truyền thuyết, thẳng đến lần kia nhận ủy thác của người, mới hiểu được, không chỉ tồn tại, còn là có mấy vị...”"),t("br"),t("br"),n._v("Phùng lão lắc đầu, dường như không muốn trong vấn đề này tiếp tục vướng víu, nói: “Không tin, ngươi có thể suy tính một lần thử một chút.”"),t("br"),t("br"),n._v("“Cái này...” Thanh niên sửng sốt một chút, cổ tay khẽ đảo, lấy ra ba cái tiền đồng, trầm tư một lát, nhẹ nhàng một quăng."),t("br"),t("br"),n._v("Tiền đồng rơi trên mặt đất, không ngừng xoay tròn, có điều, còn không có triệt để hiển lộ ra quẻ tượng, liền tất cả đều phát ra “Tạch tạch! Tạch tạch!” Phát ra giòn vang, xuất hiện vết rách."),t("br"),t("br"),n._v("Ngay sau đó cùng trước đó khinh giáp trùng đồng dạng, “Bành!” Nổ tung."),t("br"),t("br"),n._v("Sắc mặt trắng nhợt, như gặp phải trọng kích, thanh niên liên tục lui về phía sau mấy bước, một ngụm máu tươi theo khóe miệng tràn ra ngoài, trong mắt tràn đầy hoảng sợ cùng khó tin."),t("br"),t("br"),n._v("Trước mắt vị này, rõ ràng đang ở trước mắt, thế nhưng là sử dụng Thiên Cơ thôi toán chi thuật, lại phát hiện tựa như hư vô đồng dạng."),t("br"),t("br"),n._v("Căn bản không tính được tới nhân quả cùng lai lịch."),t("br"),t("br"),n._v("“Như thế nào?”"),t("br"),t("br"),n._v("Dường như đã sớm biết kết quả này, Phong lão nhìn lại."),t("br"),t("br"),n._v("“Suy tính không ra...”"),t("br"),t("br"),n._v("Hít sâu vài khẩu khí, hóa giải một lần thương thế bên trong cơ thể, thanh niên cắn răng nói."),t("br"),t("br"),n._v("“Không sai, quả thực suy tính không ra! Bởi vì, hắn là cái kia hư vô, không chỉ vận mệnh, liền trước đó đã làm sự tình, thậm chí về sau muốn tiến lên phương hướng, cũng không cách nào tính toán!”"),t("br"),t("br"),n._v("Phong lão nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Một mặt chấn kinh, thanh niên mới chợt hiểu ra, rõ ràng vì sao lão sư muốn đích thân tiếp kiến vị này."),t("br"),t("br"),n._v("Diễn Hư thể chất, là thích hợp nhất trở thành Thiên Cơ sư thể chất đặc thù, trong lịch sử cho dù có, cũng chỉ có rải rác hơn mười người mà thôi, nằm mơ đều không có nghĩ đến, trước mắt vị này thế mà chính là."),t("br"),t("br"),n._v("“Tính toán không ra lai lịch?”"),t("br"),t("br"),n._v("Nghe được song phương trò chuyện, Trương Huyền nhíu nhíu mày, có điều, thời gian không dài, liền hiểu được."),t("br"),t("br"),n._v("Hắn sở dĩ không cách nào bị thôi diễn, đoán không sai, hẳn là Thiên Đạo thư viện nguyên nhân."),t("br"),t("br"),n._v("Thứ này, bản thân liền là Thiên Đạo, che đậy Thiên Cơ, làm cho không người nào có thể phát giác, hẳn là mười phần chuyện đơn giản."),t("br"),t("br"),n._v("Nói cách khác, căn bản không phải cái gì cái gọi là Diễn Hư thể chất."),t("br"),t("br"),n._v("“Không sai, không cách nào suy tính lai lịch, chẳng khác nào thân ở thiên cơ sơ hở bên trong, loại người này một khi trở thành Thiên Cơ sư, thôi diễn tương lai, tính toán thiên hạ, liền sẽ không gặp phải phản phệ, thành tựu sẽ vô tận!”"),t("br"),t("br"),n._v("Phong lão con mắt sáng lên."),t("br"),t("br"),n._v("Thiên Cơ sư thôi diễn tương lai, tương đương với tiết lộ thiên đạo cơ mật, tất nhiên cũng muốn chịu đến thiên đạo trừng phạt, nguyên nhân chính là như vậy, lợi hại Thiên Cơ sư, thường thường đều không được kết thúc yên lành."),t("br"),t("br"),n._v("Suy tính việc nhỏ, bằng vào thực lực bản thân cùng tầm mắt, có lẽ còn có thể lẩn tránh, không bị ảnh hưởng, nhưng việc lớn... Thường thường chịu đến xâm hại, cuối cùng lúc độ kiếp, bị oanh kích thành cặn bã, hài cốt không còn."),t("br"),t("br"),n._v("Bởi vậy, Thiên Cơ sư, thoạt nhìn nở mày nở mặt, thần bí, trên thực tế tương đương đang rình coi Thiên Đạo, so những nghề nghiệp khác càng thêm nguy hiểm."),t("br"),n._v("Diễn Hư thể chất, tương đương với sơ hở chi thể, coi như Thiên Đạo đều không thể tính toán tồn tại, nhìn trộm mà không bị trừng phạt..."),t("br"),t("br"),n._v("Nhìn trộm vô tội, chính là làm Thiên Cơ sư thích hợp nhất thể chất."),t("br"),t("br"),n._v("“Trước đó không có chú ý, Trương Thuần để cho ta tới thôi diễn một lần lão sư của ngươi cùng bối cảnh, ta mới phát hiện đặc thù...” Phong lão tiếp tục nói."),t("br"),t("br"),n._v("Trương Thuần vẻ mặt nghẹn đỏ lên, buồn bực sắp bùng nổ."),t("br"),t("br"),n._v("Hắn bị đánh một trận, muốn trả thù lại, cho nên, mới tiêu tốn to lớn đánh đổi, tìm kiếm vị này Phong lão, muốn cho hắn giúp một tay thôi diễn một lần người này lai lịch, nằm mơ đều không có nghĩ đến... Đẩy là đẩy, không những không cùng hắn nói kết quả, đối phương còn thành khách quý!"),t("br"),t("br"),n._v("Cái này gọi chuyện gì!"),t("br"),t("br"),n._v("“Ngươi thân là Diễn Hư thể chất, thích hợp nhất trở thành Thiên Cơ sư...”"),t("br"),t("br"),n._v("Giải thích xong, Phong lão lần nữa nhìn về phía Trương Huyền, trong mắt mang theo hưng phấn: “Có bằng lòng hay không làm của ta thân truyền? Bái ta làm thầy?”"),t("br"),t("br"),n._v("“Phong lão thu học sinh vô số, nhưng thân truyền, nhưng vẫn không có...” Trương Thuần da mặt nhảy một cái."),t("br"),t("br"),n._v("Thiên Cơ sư cứ việc sức chiến đấu không mạnh, nhưng thân là thượng cửu lưu chức nghiệp một trong, địa vị vô cùng kính trọng, liền xem như gia chủ đều lễ ngộ ba phần, nhất là vị này Phong lão, càng là trong đó cường đại nhất một trong..."),t("br"),t("br"),n._v("Cái này Trương Huyền thật muốn làm đối phương thân truyền đệ tử, tương đương với thân phận không kém gì hắn, lại nghĩ báo thù, liền khó khăn."),t("br"),t("br"),n._v("“Thân truyền?”"),t("br"),t("br"),n._v("Một bên thanh niên cũng tràn đầy hâm mộ."),t("br"),t("br"),n._v("Hắn mặc dù là lão sư học sinh, lại chỉ là giảng bài, cũng không phải là thân truyền, không đột phá tầng này thân phận, chỉ sợ đời này, bát tinh Thiên Cơ sư, liền đi tới đỉnh phong, lại không cách nào tiến bộ."),t("br"),t("br"),n._v("Bất quá, loại thể chất này, hâm mộ cũng vô dụng, không học được."),t("br"),t("br"),n._v("Không nghĩ tới đối phương vừa thấy mặt liền muốn thu đồ, Trương Huyền nhíu nhíu mày, vẫn chưa cuống cuồng cự tuyệt, cũng không có cho thấy thái độ của mình, mà là mang theo nghi ngờ nhìn qua: “Diễn Hư thể chất rất ít?”"),t("br"),t("br"),n._v("“Mười phần thưa thớt, toàn bộ đại lục, ta biết, cộng thêm ngươi hết thảy cũng liền bốn người mà thôi!”"),t("br"),t("br"),n._v("Phong lão giải thích nói."),t("br"),t("br"),n._v("“Bốn người? Không biết bốn người nào?”"),t("br"),t("br"),n._v("Không nghĩ tới, loại trừ hắn, còn có ba người, Trương Huyền nhịn không được có chút hiếu kỳ."),t("br"),t("br"),n._v("Trương Thuần cũng dừng lại suy nghĩ lung tung, đồng loạt nhìn qua."),t("br"),t("br"),n._v("“Vị này là Lạc gia Lạc Huyền Thanh đi!”"),t("br"),t("br"),n._v("Không có trả lời Trương Huyền lời nói, Phong lão đột nhiên nhìn về phía cách đó không xa thanh niên."),t("br"),t("br"),n._v("“Huyền Thanh gặp qua Phong lão!”"),t("br"),t("br"),n._v("Lạc Huyền Thanh ôm quyền."),t("br"),t("br"),n._v("“Ừm, không cần khách khí, lần trước, tiểu công chúa bị phong ấn tu vi rời đi, quý gia chủ từng để cho ta giúp một tay suy tính qua một lần... Ta không thể nhận ra cảm giác nàng xác thực hành tung, đoán không sai... Nàng cũng là Diễn Hư thể chất!”"),t("br"),t("br"),n._v("Phong lão nói."),t("br"),t("br"),n._v("“Muội muội ta cũng là?”"),t("br"),t("br"),n._v("Lạc Huyền Thanh sững sờ."),t("br"),t("br"),n._v("Trước đó liền kỳ lạ, vì sao muội muội rời khỏi về sau, gia tộc tìm không ít Thiên Cơ sư, vì sao đều thẩm tra không đến... Nguyên nhân chính là việc này, mới đối Thiên Cơ sư có thành kiến, tưởng rằng gạt người trò xiếc, không nghĩ tới muội muội lại là loại này thể chất đặc biệt."),t("br"),t("br"),n._v("Phong lão gật đầu."),t("br"),t("br"),n._v("“Cái kia hai vị khác đâu?”"),t("br"),t("br"),n._v("Trương Thuần hỏi."),t("br"),t("br"),n._v("“Một cái khác, là ta đã thấy vị thứ nhất Diễn Hư thể chất người, cũng chính là các ngươi Trương gia vị kia tiểu thiên tài... Ta vô luận như thế nào suy tính, cũng không biết hắn bất kỳ tung tích nào, thật giống như, chưa hề xuất hiện qua!”"),t("br"),t("br"),n._v("Phong lão nói."),t("br"),t("br"),n._v("“Hắn?” Trương Thuần nhẹ gật đầu, nhất thời thở phào nhẹ nhõm."),t("br"),t("br"),n._v("May mắn tiểu thiên tài là loại thể chất này, nếu không, Lạc Huyền Thanh khẳng định muốn ở trước mặt hắn không ngừng đắc ý."),t("br"),t("br"),n._v("“Vị cuối cùng, là nữ tử, cùng Trương sư tuổi tác tương đương, thần bí, mạnh mẽ, mấy tháng trước, gặp qua một lần... Đáng tiếc, còn không có đợi tới nói chuyện, cũng đã biến mất không thấy!”"),t("br"),t("br"),n._v("Lắc đầu, Phong lão cười khổ một tiếng."),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Lên tiếng, Trương Huyền lông mày nhăn lại."),t("br"),t("br"),n._v("Một mực nghe nói tiểu thiên tài như thế nào như thế nào lợi hại, vừa nói như vậy, mới hiểu được, quả thực thật lợi hại."),t("br"),t("br"),n._v("Cũng giống như mình, không ngớt phi công đều không thể thôi diễn..."),t("br"),t("br"),n._v("Khó trách có thể trang bức một mực không xuất hiện, ngay cả Lạc Huyền Thanh vị này anh vợ cũng không biết người ở nơi nào!"),t("br"),t("br"),n._v("“Không đúng... Ngươi nói ta là Diễn Hư thể chất, không cách nào thôi diễn lai lịch cùng hành động! Cái kia... Vừa rồi ta cùng Lạc huynh đi tới cửa, ngươi là như thế nào biết được, hơn nữa để cho người ta đi nghênh đón?”"),t("br"),t("br"),n._v("Đột nhiên, nghĩ đến cái gì, Trương Huyền nhịn không được nhìn lại."),t("br"),t("br"),n._v("Đã bản thân hắn không cách nào suy tính, đối phương lại thế nào biết, lúc này sẽ tới?"),t("br"),t("br"),n._v("Hơn nữa, lại như thế nào biết đến đại sảnh bên ngoài, trực tiếp mở cửa ra?"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn biết hỏi thăm vấn đề này, Phong lão dừng lại một chút, một mặt cổ quái nhìn qua: “Ngươi... Không biết sử dụng thần thức ư? Nơi này cùng cửa chính, khoảng cách gần như vậy, thần thức có thể tuỳ tiện lan tràn đi qua ah!”"),t("br"),t("br"),n._v("“...” Trương Huyền."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1377"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1379"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);