(window.webpackJsonp=window.webpackJsonp||[]).push([[437],{793:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-129-đe-nam-thanh-ngoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-129-đe-nam-thanh-ngoc"}},[n._v("#")]),n._v(" Chương 129: Đề Nam Thanh Ngọc")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Thiên Vũ cửa hàng giống như trước đây náo nhiệt, dòng người cuồn cuộn, khắp nơi đều là đoàn người, Trương Huyền hai tay chắp ở sau lưng, chậm rãi đi vào."),t("br"),t("br"),n._v(" “Lão gia, Lăng đại nhân chỗ làm việc liền ở mặt trước...” "),t("br"),t("br"),n._v(" Đi rồi một hồi, Tôn Cường chỉ về phía trước."),t("br"),t("br"),n._v(" Trương Huyền ngẩng đầu, quả nhiên thấy một cái trang trí tráng lệ đại sảnh xuất hiện ở trước mắt, trước cửa hai con thanh ngọc thạch sư, uy vũ cao to, để lộ ra nồng nặc uy nghiêm."),t("br"),t("br"),n._v(" Đặc biệt là một đôi mắt, đen nhánh có Thần, khác nào sống bình thường, để người nhìn một chút, liền cảm thấy khiếp đảm không ngớt."),t("br"),t("br"),n._v(" “Nghe nói này một đôi sư tử đá là Lăng đại nhân chuyên môn mời Lục Mạt thợ thủ công luyện chế, nguyên liệu là tại , Thiên Huyền Vương quốc tốt nhất thanh ngọc! Lại thêm lên Lục Mạt thợ thủ công chạm trổ, riêng là một con, liền giá trị mấy triệu kim tệ.” "),t("br"),t("br"),n._v(" Tôn Cường trong mắt mang theo kịch liệt."),t("br"),t("br"),n._v(" Rất nhiều người có tiền trước cửa, đều bày ra một ít vật đáng tiền, biểu lộ ra bản thân tài lực cùng khí độ, cũng có chiêu của cải nhận bảo ý tứ, xem ra vị này Lăng Thiên Vũ cũng không thể ngoại lệ."),t("br"),t("br"),n._v(" “Mấy trăm vạn?” Trương Huyền âm thầm tặc lưỡi."),t("br"),t("br"),n._v(" Trước có trăm vạn kim tệ, liền cảm thấy không ít, trở thành một phương phú hào, nghe nói như thế mới biết, cùng chân chính người giàu có so sánh, chút tiền này cùng ăn mày không khác nhau chút nào."),t("br"),t("br"),n._v(" “Lão gia, có muốn hay không ta đi tìm một hồi Lăng đại nhân, nói ngươi đến rồi, để hắn nghênh tiếp?” Tôn Cường nhìn sang."),t("br"),t("br"),n._v(" Ở trong mắt hắn, Lăng Thiên Vũ đại nhân tuy rằng phú khả địch quốc, nhưng chân chính bàn về địa vị so với danh sư hay là kém rất nhiều."),t("br"),t("br"),n._v(" Danh sư thế nhưng liền trầm đuổi bệ hạ cũng không dám đắc tội nhân vật cường hãn, mà Lăng Thiên Vũ, có tiền nữa cũng chỉ là một thương nhân thôi."),t("br"),t("br"),n._v(" “Không cần!” Trương Huyền vung vung tay: “Ta tùy tiện xem một cái.” "),t("br"),t("br"),n._v(" Chậm rãi về phía trước, đi tới hai cái cao to sư tử đá trước mặt, đưa tay sờ lên."),t("br"),t("br"),n._v(" Vào tay băng trơn, làm cho người ta một loại cực kỳ cảm giác thoải mái, nhìn chút liền biết tuyệt đối là tốt nhất thanh ngọc."),t("br"),t("br"),n._v(" Ngón tay đụng vào đã thu, Trương Huyền hướng về hai bên phải trái nhìn một vòng, chung quanh chạm đến, qua thật lâu, này chắp hai tay sau lưng, chầm chậm đi về tới."),t("br"),t("br"),n._v(" Thấy mình vị này lão gia, đông nhìn một cái tây xem một cái, như là không từng va chạm xã hội người nhà quê như thế, Tôn Cường tràn đầy nghi hoặc."),t("br"),t("br"),n._v(" “Qua bên kia ngồi đi!” "),t("br"),t("br"),n._v(" Không để ý tới đối phương ánh mắt kỳ quái, Trương Huyền cười nhạt."),t("br"),t("br"),n._v(" Thiên Vũ thương trường có không ít có thể cung cấp người nghỉ ngơi địa phương, cách nơi này không xa thì có một cái, Trương Huyền ngồi xuống, chút ấm nước trà."),t("br"),t("br"),n._v(" Tôn Cường đứng ở một bên, như hòa thượng sờ mãi không thấy tóc."),t("br"),t("br"),n._v(" Vị này lão gia nói muốn tới tìm Lăng đại nhân, kết quả đến địa phương, lại không đi vào, cũng không để cho mình đi thông bẩm, ngồi ở chỗ này uống trà? Muốn làm gì?"),t("br"),t("br"),n._v(" “Có phải là kỳ quái hay không ta tại sao không tiến vào phòng đi tìm ngươi nói vị kia Lăng đại nhân?” Nhìn ra ý nghĩ của hắn, Trương Huyền nụ cười nhạt nhòa nói."),t("br"),t("br"),n._v(" “Thuộc hạ không dám phỏng đoán lão gia tâm tư!” "),t("br"),t("br"),n._v(" Tuy rằng như thế muốn, Tôn Cường nhưng cũng không dám nói ra."),t("br"),t("br"),n._v(" “Không cần sốt sắng, nói là được!” Biết đối phương bị quản chế tại danh sư áp lực, không dám phí lời, Trương Huyền nói."),t("br"),t("br"),n._v(" “Ta... Chẳng qua là cảm thấy kỳ quái... Lão gia là danh sư, muốn gặp Lăng đại nhân, hắn chắc chắn sẽ không từ chối...” Tôn Cường không nhịn được nói."),t("br"),t("br"),n._v(" “Nói ra thân phận, hắn đương nhiên sẽ không từ chối, cũng không dám từ chối, bất quá ngươi có tin hay không... Ta không nói ra thân phận, cũng có thể làm cho chính hắn đi ra thấy ta!” Trương Huyền nhìn sang."),t("br"),t("br"),n._v(" Đùa giỡn, làm danh sư, chủ động cho thấy thân phận đi gặp một cái thương nhân, chẳng phải quá hạ giá?"),t("br"),t("br"),n._v(" Chỉ có làm cho đối phương đuổi tới lại đây, đồng thời thông qua suy đoán xác nhận thân phận của chính mình, mới có thể tạo được chấn động hiệu quả."),t("br"),t("br"),n._v(" “Không nói ra thân phận?” Tôn Cường tràn đầy không thể tin được: “Cái này không thể nào đi, Lăng đại nhân tuy chỉ là cái thương nhân, nhưng có thể ở đây mở lên lớn như vậy thương trường, phía sau cũng là có bối cảnh, một ít vương công đại thần, muốn gặp hắn, cũng chưa chắc có thể thấy, không nói ra thân phận, làm sao sẽ tới?” "),t("br"),n._v(" Lăng Thiên Vũ có thể tại tấc đất tấc vàng Vương thành mở lớn như vậy cửa hàng, sau lưng tất nhiên có vương thất đại lực phù trợ, người như thế coi như chỉ là cái thương nhân, cũng không thể khinh thường."),t("br"),t("br"),n._v(" Nếu như nói nổi danh sư thân phận, nhất định sẽ chạy chạy tới, không nói... Làm sao có khả năng đi ra? Đường đường Lăng đại nhân nếu như như thế nhàn, phỏng chừng cũng không thể đem chuyện làm ăn làm lớn như vậy."),t("br"),t("br"),n._v(" Tôn Cường cứ việc cũng tại thương thành lái qua cửa hàng, nhưng lại biết, dựa vào mặt mũi của hắn, là bất luận làm sao đều không thể để vị đại nhân này nhìn nhiều."),t("br"),t("br"),n._v(" “Ha ha!” "),t("br"),t("br"),n._v(" Nhẹ nhàng nở nụ cười, Trương Huyền nâng chung trà lên, cử chỉ tao nhã: “Không tin mà nói, lão gia ngày hôm nay liền để hắn ngoan ngoãn lại đây, bất quá, trước đó, ta dặn dò ngươi làm cái gì, ngươi thì làm cái đó, không thể mảy may làm trái!” "),t("br"),t("br"),n._v(" “Rõ!” Thấy lão gia tự tin như thế, Tôn Cường tuy rằng lòng tràn đầy nghi hoặc, hay là không nhịn được gật đầu đáp ứng."),t("br"),t("br"),n._v(" “Tốt lắm, đi đem bên trái cái kia sư tử đá đầu cho ta gõ xuống đến.” Trương Huyền nói."),t("br"),t("br"),n._v(" “Cái gì?” "),t("br"),t("br"),n._v(" Tôn Cường một cái lảo đảo, suýt chút nữa không ngất đi."),t("br"),t("br"),n._v(" Vốn tưởng rằng lão gia sẽ nghĩ ra cái gì lợi hại biện pháp, kết quả... Tựu cái này cái?"),t("br"),t("br"),n._v(" Giá trị mấy triệu sư tử đá, Lăng đại nhân coi như trân bảo, thật muốn chạy tới đem đầu nện xuống đến, phỏng chừng đầu của mình cũng gần như sắp dọn nhà!"),t("br"),t("br"),n._v(" “Lão gia, ta còn không muốn chết...” "),t("br"),t("br"),n._v(" Tôn Cường đều sắp khóc."),t("br"),t("br"),n._v(" “Có ta tại, muốn chết nào có dễ dàng như vậy, yên tâm đi!” Trương Huyền xua tay."),t("br"),t("br"),n._v(" “...” Tôn Cường môi co giật."),t("br"),t("br"),n._v(" Muốn chết xác thực không dễ dàng, còn muốn cho ngươi bồi thường tiền đây, làm sao có khả năng trực tiếp giết, có thể... Coi như không giết, cũng muốn chơi tàn phế có được hay không?"),t("br"),t("br"),n._v(" “Làm sao? Lão gia mà nói, đều dám không nghe?” "),t("br"),t("br"),n._v(" Trương Huyền hơi nhướng mày, lộ ra nồng đậm uy nghiêm."),t("br"),t("br"),n._v(" Làm lão sư làm thói quen, giờ khắc này lại phối hợp bộ này khô vàng dung mạo, ngược lại có một phen khí độ."),t("br"),t("br"),n._v(" “Ta...” "),t("br"),t("br"),n._v(" “Yên tâm đi, ngươi đi đập phá cái này sư tử, trở lại ta chỉ điểm ngươi đột phá võ giả tam trọng. Có ta tại, không biết có vấn đề, ngươi nói vị kia Lăng Thiên Vũ, không những không biết làm khó ngươi, còn có thể hướng ngươi cảm kích, chủ động tiễn đưa ngươi thứ tốt.” Biết vị này Lăng đại nhân tích uy vị trí, muốn cho Tôn Cường làm chuyện như vậy, thật có chút làm người khác khó chịu, Trương Huyền vung tay lên, trước ưng thuận chỗ tốt."),t("br"),t("br"),n._v(" “Ây... Tốt!” "),t("br"),t("br"),n._v(" Nghe được hứa hẹn, nghĩ đến đối phương là danh sư, coi như Lăng đại nhân lợi hại đến đâu, cũng có thể không dám quá mức làm càn, Tôn Cường chần chờ một chút, cắn răng một cái."),t("br"),t("br"),n._v(" Hắn tại võ giả tam trọng buồn ngủ thực sự quá lâu, rất nhiều phương pháp đều muốn qua, vốn tưởng rằng đời này cũng không thể lại đạp một bước, có cơ hội này, làm bất cứ chuyện gì đều sẽ không tiếc."),t("br"),t("br"),n._v(" Chính vì như thế, đối phương mời hắn làm quản gia, không có quá nhiều chần chờ liền đáp ứng rồi."),t("br"),t("br"),n._v(" Có liều mạng quyết tâm, quay người lại vài bước đi tới bên trái sư tử bằng đá trước mặt, cháu chân khí trong cơ thể phun trào, cường cả người khí tức nhấc lên tới."),t("br"),t("br"),n._v(" Xem ra cái tên này thân thể mập mạp, không làm sao bắt mắt, thực lực xác thực không nhỏ, quyền phong gào thét, mang theo sức mạnh to lớn, thẳng tắp hạ xuống!"),t("br"),t("br"),n._v(" Chân Khí cảnh tuy rằng không tính là cái gì cao thủ lợi hại, nhưng đạt đến đỉnh phong cũng có 450 kg sức mạnh, con này thanh ngọc sư tử, phẩm chất cứng rắn, lại giống như thủy tinh dễ dàng nát, nắm đấm hạ xuống, một tiếng vang giòn."),t("br"),t("br"),n._v(" Răng rắc!"),t("br"),t("br"),n._v(" Giá trị trăm vạn sư tử bằng đá đầu lâu liền rớt xuống, trên đất ngã thành bụi phấn."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-137"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-139"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);