(window.webpackJsonp=window.webpackJsonp||[]).push([[438],{792:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1371-bi-tham-phong-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1371-bi-tham-phong-lao"}},[n._v("#")]),n._v(" Chương 1371: Bi thảm Phong lão")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1371: Bi thảm Phong lão"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Da mặt run lên, Trương Huyền bối rối, đợi tại nguyên chỗ, thu tay lại cũng không phải, không thu cũng không phải, không biết như thế nào cho phải."),t("br"),t("br"),n._v("Có thiên phú liền có thiên phú, không có thiên phú liền không có thiên phú... Nổ làm gì?"),t("br"),t("br"),n._v("Ta chỉ vận dụng chút Thiên Đạo chân khí, tiến hành ngụy trang, lại mượn chút thư viện lực lượng, cái khác cái gì đều không có thi triển..."),t("br"),t("br"),n._v("Nhìn vừa rồi Phong lão lấy ra thứ này bộ dạng, cho rằng là gia truyền bảo bối, liền như thế liền nổ thành bột phấn... Còn không tìm bản thân liều mạng?"),t("br"),t("br"),n._v("Chính không biết như thế nào cho phải, liền thấy mới vừa rồi còn một mặt ý cười lão giả, chẳng biết lúc nào đã đi tới trước mặt, nhìn lấy vỡ vụn trên mặt đất mai rùa, da mặt không ngừng run rẩy, dường như đã phát điên."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Cũng không ngờ tới sẽ xuất hiện kết quả này, Ngọc Thần dọa đến không dám nói lời nào."),t("br"),t("br"),n._v("Thứ này đối lão sư tầm quan trọng, người khác lý giải không sâu, hắn nhưng là khắc sâu rõ ràng, một chút vỡ thành bột phấn, tương đương với trực tiếp muốn mạng già."),t("br"),t("br"),n._v("“Của ta mai rùa...”"),t("br"),t("br"),n._v("Quả nhiên, không ngừng run rẩy, Phong lão bàn tay đưa tới, vuốt ve đầy đất bã vụn, tựa như trong nháy mắt già không biết bao nhiêu tuổi."),t("br"),t("br"),n._v("“Phong lão, hắn không phải cố ý, nếu không... Ta nghĩ phương pháp tìm người giúp ngươi dính một chút? Ngươi nhìn ngươi cái này, vốn là rất nhiều vết rách, sắp hỏng, năm ngoái ta tại Trần gia thôn nhận biết một vị thợ thủ công, hắn thủ công tinh xảo, giá tiền lại phải chăng, già trẻ không gạt, thành thật ta giới thiệu cho ngươi...”"),t("br"),t("br"),n._v("Lạc Huyền Thanh an ủi."),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Phong lão cũng nhịn không được nữa, máu tươi phun ra."),t("br"),t("br"),n._v("“Khụ khụ...”"),t("br"),t("br"),n._v("Liếc anh vợ một cái, Trương Huyền không còn gì để nói."),t("br"),t("br"),n._v("Không biết an ủi người, cũng đừng an ủi người được không?"),t("br"),t("br"),n._v("Loại cấp bậc này mai rùa, vô cùng trân quý, dính lại... Có thể hữu dụng sao?"),t("br"),t("br"),n._v("“Phong lão, đã đều bể nát, cũng liền đừng thương tâm, mai rùa mà thôi, ngày mai ta cho ngươi nhiều bắt mấy con rùa đen tới, muốn làm mấy cái, liền làm mấy cái, thịt còn có thể nấu canh...”"),t("br"),t("br"),n._v("Suy tư một chút, Trương Huyền mặt mũi thành khẩn."),t("br"),t("br"),n._v("“Phốc! Phốc!”"),t("br"),t("br"),n._v("Phong lão nhổ máu càng nhiều."),t("br"),t("br"),n._v("“Nếu không, ta giúp ngươi lại làm một cái...”"),t("br"),t("br"),n._v("Thấy càng an ủi, đối phương càng thổ huyết, Trương Huyền cũng không biết nói thế nào."),t("br"),t("br"),n._v("Vừa rồi chạm đến một chút, Thiên Đạo thư viện thuận tiện sinh thành thư tịch, mặc dù cái này mai rùa, truyền thừa lâu đời, cấp bậc cũng rất cao, nhưng mà thiếu hụt cũng không ít, chỉ cần bằng lòng đi làm, thu thập đầy đủ thư tịch, hẳn là có khả năng lần nữa làm ra một cái tương đồng, thậm chí cấp bậc cao hơn!"),t("br"),t("br"),n._v("Nghĩ đến cái này, ý thức lần nữa đi vào thư viện, ý định tiếp tục nghiên cứu một chút, phục chế cái này mai rùa khả năng có bao lớn, liền nghe đến một bên Ngọc Thần tràn đầy khẩn trương thanh âm vang lên."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Thanh âm phát run, dường như mang theo cực lớn khủng hoảng."),t("br"),t("br"),n._v("Nghe hắn kêu cuống cuồng, tràn đầy đau thương Phong lão xoay đầu lại."),t("br"),t("br"),n._v("“Ngươi nhìn...”"),t("br"),t("br"),n._v("Ngọc Thần chỉ vào đỉnh đầu."),t("br"),t("br"),n._v("Vội vàng ngẩng đầu, Phong lão ngay sau đó con ngươi thu hẹp."),t("br"),t("br"),n._v("Thấy hai người biểu lộ kỳ lạ, Trương Huyền mấy người cũng ngẩng đầu lên, vừa nhìn phía dưới, tất cả đều kìm lòng không được ngẩn người."),t("br"),t("br"),n._v("Chỉ thấy trên đầu trạng thái như mai rùa nóc nhà, giờ phút này cũng xuất hiện vết rách to lớn, chậm rãi lan tràn lên phía trên, bốn phía dùng để củng cố phù văn cùng trận pháp, một chút tác dụng đều không có, hình như gặp một loại không thể nghịch chuyển lực lượng."),t("br"),t("br"),n._v("“Tại sao có thể như vậy?”"),t("br"),t("br"),n._v("đọc trＵyện với http://ngantruyen.com/"),t("br"),n._v("Lại không để ý tới thổ huyết, Phong lão cào đầu."),t("br"),t("br"),n._v("Hi Thánh mai rùa, nói nổ liền nổ, có thể nói niên đại xa xưa, xem bói nhiều người, nhận lấy thiên đạo trừng phạt, có thể cái này... Hao tốn không biết bao nhiêu nhân lực vật lực, mới xây dựng mà thành, chắc chắn dị thường, liền xem như Thánh vực bát trọng cường giả đỉnh phong, toàn lực công kích, cũng sẽ không có chuyện xảy ra, thế nào... Cũng bắt đầu sụp đổ?"),t("br"),t("br"),n._v("“Dừng lại!”"),t("br"),t("br"),n._v("Gào lên một tiếng, bàn tay bỗng nhiên hướng lên chộp tới."),t("br"),t("br"),n._v("Muốn ngừng lại trận pháp sụp đổ, bảo vệ phần cơ nghiệp này."),t("br"),t("br"),n._v("Bất quá, chân khí xông lên, vẫn như cũ làm dịu không được trận pháp cùng phòng ốc sụp đổ, hình như có một cỗ không biết tên lực lượng xâm nhập, không phải sức người có thể chống lại."),t("br"),t("br"),n._v("Loại lực lượng này trước mặt, Thánh vực cửu trọng, cũng chỉ là sâu kiến, cái gì cũng không bằng."),t("br"),t("br"),n._v("Tạch tạch! Tạch tạch!"),t("br"),t("br"),n._v("Cũng lại không chịu nổi, liên tiếp nổ vang, vô số nham thạch từ bên trên trượt xuống, trước đó đang tại minh tưởng rất nhiều Thiên Cơ sư, tất cả đều giật nảy mình, đồng loạt mở mắt ra, nhao nhao trốn ra phía ngoài đi."),t("br"),t("br"),n._v("Trương Huyền đám người trốn cũng không phải, không trốn cũng không phải, đành phải đem chân khí tế ra, hình thành chân khí che đậy, chặn lại phía trên rơi xuống đá vụn."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Khói bụi dâng lên, nguyên bản cũng có chút cổ xưa mai rùa kiến trúc, không đến một phút đồng hồ liền biến thành một vùng phế tích, ẩn chứa trong đó đủ loại trận pháp, cũng hủy một trong bó đuốc."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền nháy con mắt: “Cũng quá không bền chắc đi, muốn đổi làm người bình thường, còn không tại chỗ đập chết?”"),t("br"),t("br"),n._v("Bên ngoài cái này mai rùa kiến trúc, còn rất đẹp mắt, không nghĩ tới như vậy trông thì ngon mà không dùng được, may mắn thực lực mạnh, nếu không, cứng rắn đập ở trên đầu, người bình thường có bao nhiêu mạng cũng gánh không được ah."),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Lần nữa run rẩy, Phong lão lại nôn một ngụm máu."),t("br"),n._v(" “Lão sư, đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Vội vàng đi tới trước mặt, Ngọc Thần tràn đầy lo lắng nhìn qua, sợ lão sư thái qua đau lòng, tức chết tại chỗ."),t("br"),t("br"),n._v("“Hẳn là Thiên Đạo phản phệ...”"),t("br"),t("br"),n._v("Ổn một hồi, Phong lão lúc này mới chậm tới, thở ra một hơi, nhìn lấy đầy đất phế tích, tràn đầy đau thương."),t("br"),t("br"),n._v("“Phản phệ?”"),t("br"),t("br"),n._v("Ngọc Thần không hiểu."),t("br"),t("br"),n._v("“Thiên Cơ sư, tìm kiếm cơ duyên, đánh cắp thiên địa bí ẩn, tự nhiên bị Thiên Đạo căm thù đến tận xương tuỷ. Chỉ bất quá, chúng ta sử dụng thủ đoạn đặc thù che giấu loại này khí thế cảm ứng, đối phương không cách nào phát hiện... Vừa rồi loại thủ đoạn này hình như đột nhiên mất đi tác dụng, thiên đạo uy nghiêm từ nội bộ bắn ra, lúc này mới biến thành như vậy...”"),t("br"),t("br"),n._v("Chần chờ một chút, Phong lão chậm rãi nói."),t("br"),t("br"),n._v("Thân là cửu tinh Thiên Cơ sư, mặc dù mai rùa vỡ có chút kỳ quái, phòng ốc sập cũng có chút không đúng, cẩn thận thôi diễn, vẫn có thể biết một chút."),t("br"),t("br"),n._v("Bất kỳ nghề nghiệp nào, tu luyện tới đỉnh phong đều sẽ gặp phải thiên địa ghen ghét, thật giống như Xuất Khiếu kiếp, Động Hư kiếp, đều là thiên địa đối cường giả trừng phạt."),t("br"),t("br"),n._v("Thiên Cơ sư, nhìn trộm Thiên Đạo, lại càng dễ gặp phải trừng phạt, chỉ bất quá trước thời hạn đề phòng biện pháp làm tốt thôi, thật giống như một chút đặc thù nơi, một khi cảnh sát tới, sẽ trước thời hạn đem tiền, người giấu kỹ, giải trí tính chất, không dính dáng đánh bạc, bắt không được người, coi như vi phạm cũng không trở thành quá mức nghiêm trọng."),t("br"),t("br"),n._v("Loại nghề nghiệp này, có thể tiến hành nhìn trộm, tự nhiên cũng làm xong đề phòng, nằm mơ đều không có nghĩ đến, tầng tầng đề phòng phía dưới, còn là bị phát hiện, không chỉ mai rùa bị hủy, toàn bộ Thánh tử điện Thiên Cơ đường, vài vạn năm cơ nghiệp, cũng hủy hoại chỉ trong chốc lát..."),t("br"),t("br"),n._v("Nắm ngực, Phong lão tràn đầy đau lòng."),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy một cái."),t("br"),t("br"),n._v("Còn tưởng rằng là mai rùa cùng kiến trúc không chắc, nằm mơ đều không có nghĩ đến cùng Thiên Đạo liên quan đến."),t("br"),t("br"),n._v("Vừa rồi hắn mượn Thiên Đạo thư viện, tới dò xét mai rùa, mai rùa bể nát... Tới tính toán có thể hay không có khả năng điêu khắc, điện đường liền sập..."),t("br"),t("br"),n._v("Chẳng lẽ trước mắt cục diện này, thật tới liên quan đến?"),t("br"),t("br"),n._v("Nếu thực như thế, hắn liền có rất lớn trách nhiệm."),t("br"),t("br"),n._v("Trên mặt lúc đỏ lúc trắng, chính không biết giải thích như thế nào, chỉ thấy Phong lão cười khổ một tiếng, nhìn lại."),t("br"),t("br"),n._v("“Trương sư, ngươi không cần tự trách mình! Chuyện này không liên quan gì đến ngươi, có thể là chúng ta Thiên Cơ đường gần nhất buông lỏng, có thứ gì sự tình không có thôi diễn đến, mới xuất hiện như vậy biến cố, dưới tình huống bình thường, chúng ta cái nghề nghiệp này, mặc dù không ít chuyện, muốn nói cẩn thận làm cẩn thận, chú ý thiên đạo hài hòa, nhưng chức nghiệp bản thân, còn là hết sức an toàn.”"),t("br"),t("br"),n._v("“An toàn?” Thấy đối phương vẫn chưa trách tội bản thân, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Thiên Đạo thư viện sự tình, khẳng định là không thể nói, đã đối phương không có phát hiện, còn là điệu thấp chứa mơ hồ cho thỏa đáng."),t("br"),t("br"),n._v("Còn đối phương thiệt hại, tìm cái khác lấy cớ nghĩ biện pháp đền bù liền có thể."),t("br"),t("br"),n._v("“Đúng vậy a, ngươi thấy chỉ là ngoài ý muốn, loại tình huống này vạn năm cũng sẽ không xuất hiện một lần...”"),t("br"),t("br"),n._v("Sợ thật vất vả gặp phải thiên tài, ra lùi bước chi tâm, Phong lão vội vàng giải thích: “Không tin, ngươi nhìn bên kia Tàng Thư các, liền không có vấn đề gì.”"),t("br"),t("br"),n._v("Nói xong hướng một bên chỉ đi."),t("br"),t("br"),n._v("Theo ngón tay hắn phương hướng, Trương Huyền nhìn một cái, quả nhiên thấy một cái kiến trúc, đứng sừng sững ở cách đó không xa, cứ việc cùng to lớn mai rùa liên kết, cũng không có chịu ảnh hưởng, từng dãy giá sách song song trong phòng, bốn phía bụi đất tung bay, nơi này lại sạch sẽ như mới."),t("br"),t("br"),n._v("“Đây đều là các ngươi tàng thư?”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Chỉ cần đọc sách, liền có thể ngưng tụ ra Thiên Đạo công pháp, làm cho đối phương chật vật như thế, cũng có thể tiến hành bồi thường."),t("br"),t("br"),n._v("“Đúng vậy a!”"),t("br"),t("br"),n._v("Phong lão gật đầu, sinh ra tự hào chi ý: “Chúng ta Thánh tử điện Thiên Cơ đường, cứ việc nhân số so những nghề nghiệp khác muốn ít hơn nhiều, có thể công pháp bí tịch, không chút nào đều không kém cỏi, hi Thánh mai rùa cứ việc xảy ra chút ngoài ý muốn hỏng, ngươi vẫn là có thể thông qua đọc sách học tập, tới đo lường bản thân năng lực thiên phú...”"),t("br"),t("br"),n._v("Trước đó đối phương muốn nhìn sách, học tập, xác định bản thân có thích hợp hay không cái nghề nghiệp này, hắn không có đồng ý, cảm thấy rất khá là phiền toái."),t("br"),t("br"),n._v("Hiện tại hi Thánh mai rùa vỡ vụn, coi như muốn đo lường cũng không cách nào làm được, chỉ có thể sử dụng loại này nguyên thủy nhất phương pháp."),t("br"),t("br"),n._v("“Vậy thì tốt, ta xem trước một chút...”"),t("br"),t("br"),n._v("Nghe được có thể tùy tiện nhìn, Trương Huyền thở phào nhẹ nhõm, người còn chưa tới đến trước mặt, con mắt liền quét tới, đồng thời, trong lòng âm thầm hô khẽ: “Thiếu hụt!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Lời còn chưa dứt, liền thấy trước mắt thư tịch, lập tức giống như là bị đại hỏa thiêu đốt đồng dạng, trong nháy mắt bùng cháy thành tro tàn, ngay sau đó toàn bộ kiến trúc, cũng cùng trước đó mai rùa đồng dạng, xuất hiện vết rách to lớn."),t("br"),t("br"),n._v("Mấy hơi thở, liền sụp đổ xuống, đồng dạng biến thành một đống phế tích."),t("br"),t("br"),n._v("“Tàng Thư các...” Vẻ mặt lần nữa một đỏ, Phong lão lại là phun ra một ngụm máu tươi, tựa hồ cũng có chút phát điên."),t("br"),t("br"),n._v("Đây rốt cuộc xảy ra chuyện gì? Thế nào trong nháy mắt, Thiên Cơ đường liên tiếp xảy ra chuyện?"),t("br"),t("br"),n._v("Nếu như là một lần, có thể nói là không có giấu diếm tốt khí tức, bị Thiên Đạo phát hiện, có thể liên tục ba lần... Liền xem như hắn cũng không biết nên như thế nào giải thích."),t("br"),t("br"),n._v("Trương Huyền lần nữa khóe miệng giật một cái: “Chẳng lẽ bởi vì... Xem sách?”"),t("br"),t("br"),n._v("Nếu như trước đó hắn còn không dám xác định, lần này, mới nhìn một cái thư tịch, thư viện liền sụp đổ, đã gần như xác nhận."),t("br"),t("br"),n._v("Quay đầu nhìn về phía có chút đờ đẫn lão giả, trong lòng tràn đầy không đành lòng: “Phong lão, Phong lão, Phong lão...”"),t("br"),t("br"),n._v("Liên tục hô ba tiếng, đối phương đều không có trả lời, trong lòng áy náy càng lúc càng nồng nặc, đang muốn cẩn thận quan sát một chút, đối phương có thể hay không nhận đả kích quá lớn, tổn thương nguyên khí, liền thấy một đạo thô to lôi điện không biết từ đâu mà đến, thẳng tắp bổ vào đối phương trên đầu."),t("br"),t("br"),n._v("Tạch tạch!"),t("br"),t("br"),n._v("Phong lão nằm ở trên mặt đất."),t("br"),t("br"),n._v("“Phong lão, ngươi...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Trương Huyền vội vàng tiến lên."),t("br"),t("br"),n._v("Thình thịch thình thịch bành!"),t("br"),t("br"),n._v("Lôi điện đem lão giả nhấn chìm, chỉ để lại một cái chân, ở bên ngoài không ngừng co rúm, giày đều run mất."),t("br"),t("br"),n._v("(Đầu tháng, cầu nguyệt phiếu!)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1379"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1381"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);