(window.webpackJsonp=window.webpackJsonp||[]).push([[440],{795:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1373-thu-vien-thue-bien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1373-thu-vien-thue-bien"}},[n._v("#")]),n._v(" Chương 1373: Thư viện thuế biến")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1373: Thư viện thuế biến"),t("br"),t("br"),n._v("Mặc dù đối Trương gia có chút ý kiến, nhưng cũng không phải cố tình gây sự người, chỉ cần đối phương nhân phẩm còn qua được, không tính quá ti tiện, bản thân cũng sẽ không quá phận."),t("br"),t("br"),n._v("Thật giống cùng Trương Thuần đồng dạng, chết không biết xấu hổ, con cóc muốn ăn thịt thiên nga, vậy liền thật không tiện, cũng đừng trách hắn không khách khí."),t("br"),t("br"),n._v("Không làm hắn nghi ngờ nhân sinh, liền không gọi Trương Huyền."),t("br"),t("br"),n._v("“Ngươi mới vừa nói Thánh tử, là có ý gì?”"),t("br"),t("br"),n._v("Làm ra quyết định kỹ càng, nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Thánh tử điện, ẩn hàm nhiều loại chức nghiệp, chỉ cần tại một cái chức nghiệp bên trong, có khả năng vượt qua cái khác tất cả tương đồng thực lực người, trở thành hoàn toàn xứng đáng đệ nhất, liền có thể thu hoạch được ‘Thánh tử’ vinh dự. Hơn nữa, cái này không riêng gì vinh dự, càng là thân phận và địa vị biểu tượng, chỉ cần trở thành Thánh tử, chẳng khác nào thành Thánh tử điện một cái chức nghiệp bên trong, hoàn toàn xứng đáng học sinh người phát ngôn, tất cả mọi người đại sư huynh!”"),t("br"),t("br"),n._v("Thấy hắn không hiểu, Lạc Huyền Thanh giải thích nói: “Vị này Trương Phong, chính là khải linh đại biểu Thánh tử thân phận trấn thủ tượng đá, cũng để hắn di động trọn bảy bước, phá vỡ công hội 3000 năm qua ghi chép, lúc này mới thành công.”"),t("br"),t("br"),n._v("“Tượng đá?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Khải linh tượng đá hẳn là mười phần đơn giản đi, lúc trước hắn nhưng là liền toàn bộ công hội đều khải linh thành công."),t("br"),t("br"),n._v("“Chờ ngươi tận mắt nhìn đến, liền biết trong đó khó khăn!”"),t("br"),t("br"),n._v("Thấy hắn xem thường, Lạc Huyền Thanh cười khổ lắc đầu."),t("br"),t("br"),n._v("Lợi hại khải linh sư, khải linh tượng đá quả thực không khó, nhưng muốn muốn khải linh trấn thủ tượng đá liền không dễ dàng như vậy, bằng không thì cũng không có khả năng 3000 năm qua chỉ có Trương Phong một người làm được."),t("br"),t("br"),n._v("Khải linh sư công hội cũng tại trí điện, đi thời gian không dài liền đến đến trước mặt."),t("br"),t("br"),n._v("Đứng tại công hội trước cổng chính, nhìn thấy cái gọi là trấn thủ tượng đá, Trương Huyền rốt cuộc minh bạch đối phương vì cái gì đã nói như vậy."),t("br"),t("br"),n._v("Chừng cao mười mấy trượng, độ rộng cũng có sáu, bảy trượng, đứng sừng sững ở tại chỗ, tựa như một tòa cao vút cao ốc, không biết dùng cái gì nham thạch, điêu khắc mà thành, toàn thân đen nhánh, hình như có khả năng hấp thu ý niệm của người khác, để khải linh hỏa diễm khó mà bùng cháy."),t("br"),t("br"),n._v("“Cái này... Quả thực không quá dễ dàng.”"),t("br"),t("br"),n._v("Cảm thụ một chút, Trương Huyền ánh mắt ngưng trọng."),t("br"),t("br"),n._v("Cái này tượng đá liền xem như hắn muốn khải linh, chỉ sợ đều cần tiêu tốn thời gian rất dài, tiêu hao rất nhiều."),t("br"),t("br"),n._v("Hơn nữa mấu chốt nhất là chất liệu đặc thù, không dễ dàng ẩn chứa linh tính, cho dù thành công, hẳn là không bao lâu, cũng biết lần nữa tiêu tán."),t("br"),t("br"),n._v("Cấp cho khải linh, liền như là hướng trong giỏ trúc rót, rót nhanh, dòng nước chảy nhanh hơn."),t("br"),t("br"),n._v("Cái trước tốc độ không đuổi kịp người sau, liền vĩnh viễn không có khả năng thành công, vị này Trương Phong, không chỉ hoàn thành, còn để hắn đi bảy bước, quả thực thủ đoạn mạnh mẽ."),t("br"),t("br"),n._v("Đi tới trước mặt, tay cầm tới tiếp xúc."),t("br"),t("br"),n._v("Nguyên thần khẽ động, một đạo ý niệm tuôn đi vào."),t("br"),t("br"),n._v("Trong chốc lát, Trương Huyền hình như rơi vào một cái đen kịt, không có ánh sáng môi trường, đưa tay không thấy được năm ngón, liền thần thức đều toả ra không đi ra."),t("br"),t("br"),n._v("Nhẹ nhàng điểm một cái, một tia sáng từ đầu ngón tay rọi sáng ra đến, chẳng qua ánh sáng còn không có triệt để lan ra, liền cảm thấy một cỗ to lớn hấp lực không ngừng thôn phệ lấy bốn phía tia sáng."),t("br"),t("br"),n._v("Cùng bốn phía đều là tinh huy thạch đồng dạng, ngọn lửa chỉ có thể ở đầu ngón tay xoay quanh, chiếu không tới cái khác chỗ xa hơn."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Tinh thần khẽ động, nguyên thần quay về thân thể, lần nữa mở mắt, Trương Huyền thở ra một hơi."),t("br"),t("br"),n._v("Quả thực rất khó, so trước đó khải linh công hội đều muốn khó khăn gấp mấy lần, coi như thực lực bây giờ muốn làm được cũng không quá dễ dàng."),t("br"),t("br"),n._v("“Thế nào?” Lạc Huyền Thanh nhìn lại."),t("br"),t("br"),n._v("“Có thể hay không mang ta đi nhìn một chút bát tinh khải linh sư thư tịch? Ta hơi nghi hoặc một chút, tạm thời không nghĩ ra, cần đọc sách, mới có thể giải quyết.”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Hắn hiện tại chỉ nhìn qua thất tinh cấp bậc thư tịch, bát tinh còn không có hệ thống học qua."),t("br"),t("br"),n._v("Chỉ cần có đầy đủ sách nhìn, ngưng tụ thành Thiên Đạo cấp bậc pháp quyết, trở thành cái gọi là Thánh tử, hẳn là có thể tuỳ tiện hoàn thành."),t("br"),t("br"),n._v("“Đọc sách?” Lạc Huyền Thanh mỉm cười: “Chỉ cần thông qua bát tinh khảo hạch, trở thành bát tinh khải linh sư, muốn nhìn sách gì, tùy tiện nhìn, không có hạn chế. Như vậy, ngươi trước đi qua, ta vừa vặn có chút việc muốn làm...”"),t("br"),t("br"),n._v("“Tốt!” Nghe được đơn giản, Trương Huyền không nói thêm lời, đi vào công hội."),t("br"),t("br"),n._v("Nơi này cùng lúc trước trận pháp sư khảo hạch điểm, có chút tương tự, không có mấy người, có vẻ mười phần vắng vẻ, rất mau tới đến trước đài, tìm phục vụ viên lấy ra huy chương của mình, đưa tới."),t("br"),t("br"),n._v("“Trương sư, mời tới bên này!”"),t("br"),t("br"),n._v("Nhân viên phục vụ là cái 20 tới tuổi nữ hài, biết thân phận sau có vẻ tương đối niềm nở, rất nhanh liền đem hắn dẫn tới Tàng Thư các lân cận."),t("br"),t("br"),n._v("“Thất tinh khải linh sư liên quan thư tịch, tất cả đều ở đây... Số lượng rất nhiều, nếu như cần kiểm tra cái gì, nói trước một tiếng, ta có thể giúp ngươi tìm ra...”"),t("br"),t("br"),n._v("“Không cần, cám ơn!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền không tiếp tục để ý cô gái này, đi thẳng vào."),t("br"),t("br"),n._v("Thanh Nguyên thành công hội, tương đối thấp cấp, thư tịch tương đối hơi ít, khảo hạch bát tinh trước đó, nhiều hơn nữa nhìn một chút thất tinh, để cho mình kiến thức lượng, càng thêm củng cố."),t("br"),t("br"),n._v("Đi vào phòng, quả nhiên thấy được vô số thư tịch, đứng sừng sững ở trước mắt."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Trong lòng hơi động."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Thư tịch nhao nhao xuất hiện trong óc."),t("br"),t("br"),n._v("“Ừm? Thế nào tốc độ so trước đó nhanh?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Trước đó ánh mắt đảo qua, đồng dạng có thể thu thập, nhưng mà mỗi lần đều có chút trệ về sau, nói cách khác ánh mắt đảo qua ba hàng giá sách, thư viện nhiều nhất tạo ra hai hàng."),t("br"),t("br"),n._v("Mà bây giờ nhìn ba hàng, tạo ra ba hàng, tốc độ nhanh một phần ba không thôi."),t("br"),t("br"),n._v("“Thư viện chưa từng xuất hiện biến động, ta cũng không có hôn mê, hẳn không phải là phát sinh thuế biến!”"),t("br"),t("br"),n._v("Suy nghĩ trong đầu không ngừng lóe lên."),t("br"),n._v(" Tại danh sư học viện nhìn thấy Lạc Nhược Hi về sau, thư viện từng phát sinh qua một lần thuế biến, trước đó cần dùng tay chạm đến, mới có thể đem thư tịch thu vào trong đó, thuế biến về sau, chỉ cần con mắt quan sát là được."),t("br"),t("br"),n._v("Loại này trên phạm vi lớn biến hóa, để hắn rơi vào hôn mê trọn ba ngày, hiện tại không có xuất hiện loại tình huống này, thu vào thư tịch tốc độ lại biến nhanh.. Chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Chẳng lẽ...”"),t("br"),t("br"),n._v("Mắt sáng lên."),t("br"),t("br"),n._v("Nghĩ tới nghĩ lui, chỉ có một nguyên nhân..."),t("br"),t("br"),n._v("Cái kia chính là... Hủy đi Thiên Cơ sư công hội!"),t("br"),t("br"),n._v("Hủy đi một cái nhìn trộm Thiên Đạo ổ điểm, tương đương hủy đi một cái phạm tội tập thể, một khối u ác tính... Tốc độ biến nhanh, có thể hay không cùng hắn liên quan đến?"),t("br"),t("br"),n._v("“Chẳng lẽ chơi đùa Thiên Cơ sư, còn có ban thưởng?”"),t("br"),t("br"),n._v("Trương Huyền mắt sáng rực lên."),t("br"),t("br"),n._v("Thiên Đạo thư viện mặc dù rất nghịch thiên, nhưng mà nếu như có thể lần nữa tấn cấp, đối với hắn thực lực tăng lên, nhất định có chỗ tốt rất lớn."),t("br"),t("br"),n._v("“Xem ra sau này muốn bao nhiêu tìm cơ hội, diệt mấy cái Thiên Cơ sư công hội...”"),t("br"),t("br"),n._v("Âm thầm gật đầu."),t("br"),t("br"),n._v("Đương nhiên, đây chỉ là suy đoán, cụ thể có phải hay không nguyên nhân này, còn cần khảo chứng."),t("br"),t("br"),n._v("Đọc sách tốc độ biến nhanh, lúc đầu cần ba canh giờ mới có thể xem xong thư tịch, hai canh giờ liền hoàn toàn thu vào trong óc, biến thành kiến thức của mình."),t("br"),t("br"),n._v("Lần nữa trở lại đại sảnh, tìm được nhân viên phục vụ."),t("br"),t("br"),n._v("“Ta muốn khảo hạch bát tinh khải linh sư, không biết cần cái nào trình tự?”"),t("br"),t("br"),n._v("Muốn quan sát bát tinh cấp bậc thư tịch, đầu tiên muốn khảo hạch đem đối ứng cấp bậc."),t("br"),t("br"),n._v("“Bát tinh khải linh sư?”"),t("br"),t("br"),n._v("Nữ hài ánh mắt lộ ra vẻ kinh ngạc, ngay sau đó nhẹ gật đầu: “Khải linh sư khảo hạch mười phần đơn giản, không giống luyện đan sư, luyện khí sư loại hình thượng cửu lưu chức nghiệp phiền toái như vậy, chỉ cần ngay trước trưởng lão mặt, khải linh cấp tám sơ cấp khôi lỗi, là có thể.”"),t("br"),t("br"),n._v("“Làm phiền!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Nữ hài dẫn đường, rất mau tới đến một cái đại điện."),t("br"),t("br"),n._v("Phụ trách khảo hạch trưởng lão, là cái hơn sáu mươi tuổi lão giả, đã đạt đến bát tinh cấp bậc."),t("br"),t("br"),n._v("Khôi lỗi cùng trước đó tại Thanh Nguyên thành nhìn thấy có chút tương tự, kết cấu đơn giản, khảo nghiệm là đối khải linh nghề nghiệp lĩnh ngộ, đi tới trước mặt, Trương Huyền tay cầm ấn đi lên."),t("br"),t("br"),n._v("Nguyên thần lóe lên, một đạo ý niệm tiến vào bên trong, thời gian không dài, triệt để nhen lửa."),t("br"),t("br"),n._v("Cái này cấp tám khôi lỗi, thuộc về cấp thấp nhất, cùng khảo hạch bát tinh trận pháp sư lúc bố trí trận pháp đồng dạng, chỉ cần nắm giữ đột phá năng lực, đều có thể thuận lợi thông qua."),t("br"),t("br"),n._v("“Đây là của ngươi bát tinh huy chương!”"),t("br"),t("br"),n._v("Không nghĩ tới vị này, một lần liền thông qua khảo hạch, lão giả cũng mười phần giật mình, rất nhanh liền đem huy chương xin xuống, đưa tới."),t("br"),t("br"),n._v("Trương Huyền thở phào nhẹ nhõm, đi tới bát tinh khải linh sư tàng thư vị trí, tiếp tục quan sát."),t("br"),t("br"),n._v("Sau ba canh giờ, ra khỏi phòng."),t("br"),t("br"),n._v("Mặc dù mang theo mỏi mệt, dĩ nhiên đã đem bát tinh cấp bậc khải linh công pháp toàn bộ nắm giữ, đối cái nghề nghiệp này lĩnh ngộ, thuận lợi đạt đến bát tinh đỉnh phong cấp bậc."),t("br"),t("br"),n._v("“Trương sư, thế nào?”"),t("br"),t("br"),n._v("Vừa rời đi Tàng Thư khố, chỉ thấy Lạc Huyền Thanh tiến lên đón."),t("br"),t("br"),n._v("“Ừm, không có gì bất ngờ xảy ra...”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Trương Huyền trả lời một câu, đột nhiên dừng lại, một mặt trợn mắt hốc mồm nhìn về phía trước: “Cái này... Này làm sao cái tình huống?”"),t("br"),t("br"),n._v("Chỉ thấy trước mắt, ba canh giờ phía trước, còn huy hoàng cao vút đại điện, giờ phút này, đã đổ sụp xuống, bốn phía bốc lên khói trắng."),t("br"),t("br"),n._v("“Nha... Ta tìm công hội Triệu trưởng lão, giúp một tay củng cố nguyên thần... Ai biết người này, thủ đoạn quá yếu, dẫn đến nguyên thần lao ra, không có khống chế lại... Liền biến thành như vậy!”"),t("br"),t("br"),n._v("Gãi đầu một cái, Lạc Huyền Thanh mang theo lúng túng nói."),t("br"),t("br"),n._v("“Củng cố nguyên thần... Có thể biến thành như vậy?”"),t("br"),t("br"),n._v("Trương Huyền ngẩn người."),t("br"),t("br"),n._v("Vừa rồi người này nói, muốn tới khải linh sư công hội có một số việc, hắn liền đoán được, có thể muốn tìm người giúp một tay củng cố nguyên thần, để xung kích Động Hư kiếp... Vốn cho rằng, cái này rất đơn giản, nằm mơ đều không có nghĩ đến, còn là đem người ta điện đường phá hủy..."),t("br"),t("br"),n._v("Có thể đi hay không đến chỗ nào đều làm phá hư? Làm như vậy, rất mất mặt được không?"),t("br"),t("br"),n._v("“Đều là cái này điện đường thiết kế không tốt lắm, kiến trúc không chắc...”"),t("br"),t("br"),n._v("Khoát tay áo, Lạc Huyền Thanh khẽ nói, một mặt không liên quan chuyện ta bộ dáng."),t("br"),t("br"),n._v("“Lạc Huyền Thanh, ngươi có xấu hổ hay không?”"),t("br"),t("br"),n._v("Lời còn chưa dứt, liền nghe đến một cái gầm thét vang lên, ngay sau đó liền thấy mấy người, nổi giận đùng đùng đi tới."),t("br"),t("br"),n._v("Đi đầu một vị, là cái thanh niên, hai mươi tám, chín tuổi bộ dạng, bộ dáng cùng Trương Thuần có chút tương tự."),t("br"),t("br"),n._v("“Trương Phong, ngươi có ý tứ gì?”"),t("br"),t("br"),n._v("“Ngươi hết thảy tới hai lần, đều biến thành như vậy, từ hôm nay trở đi, ta khải linh sư công hội, lại không hoan nghênh ngươi, nếu như còn dám bước vào nửa bước, cũng đừng trách chúng ta không khách khí...”"),t("br"),t("br"),n._v("Trương Phong cắn răng nói."),t("br"),t("br"),n._v("“Không khách khí? Các ngươi công hội kiến trúc chất lượng quá kém, liên quan quái gì đến ta?” Lạc Huyền Thanh khẽ nói."),t("br"),t("br"),n._v("“Ngươi còn có mặt mũi nói... Nếu không phải nguyên thần của ngươi không khống chế được, sẽ biến thành như vậy? Ngươi chính là cái mười phần phá hư vương!”"),t("br"),t("br"),n._v("Bàn tay lớn hất lên, Trương Phong tiến về phía trước một bước: “Ta hiện tại liền ban bố khải linh sư công hội... Chỉ cần ngươi còn dám bước vào nơi này, tất cả khải linh sư, có thể trực tiếp tiến công, lại không lưu tình...”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1381"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1383"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);