(window.webpackJsonp=window.webpackJsonp||[]).push([[445],{800:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1378-vo-si-ho-lo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1378-vo-si-ho-lo"}},[n._v("#")]),n._v(" Chương 1378: Vô sỉ hồ lô")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1378: Vô sỉ hồ lô"),t("br"),t("br"),n._v("Động Hư kiếp, nguy hiểm vô cùng, dựa theo bình thường kế hoạch, Lạc Huyền Thanh sẽ ở Thánh tử điện chuyên môn độ kiếp nơi dẫn động lôi kiếp, như vậy mới có thể thu đến Khổng sư ý niệm che chở, để tâm ma giảm bớt."),t("br"),t("br"),n._v("Mà bây giờ, thế mà ở đây, đưa tới lôi đình, nhất định là xuất hiện cái gì không thể khống chế biến cố, dẫn tới lôi kiếp, hoặc là lui địch, hoặc là muốn cùng chi đồng hướng về tận."),t("br"),t("br"),n._v("Biết những này, Trương Huyền vẫn chưa cuống cuồng hướng về phía trước, mà là Minh Lý Chi Nhãn chớp động, hướng bốn phía nhìn, một lát sau, mày nhăn lại."),t("br"),t("br"),n._v("Bốn phía một bóng người đều không có."),t("br"),t("br"),n._v("Chẳng lẽ nghĩ sai, lại hoặc là, vừa rồi cùng hắn chiến đấu người, nhìn thấy lôi kiếp thế tới hung hăng, tất cả đều dọa đến rút lui?"),t("br"),t("br"),n._v("“Trước mặc kệ, Lạc huynh cảm xúc vội vàng xao động, tùy tiện độ kiếp, rất dễ dàng xuất hiện nguy hiểm...”"),t("br"),t("br"),n._v("Thấy bốn phía không có người, Trương Huyền không tiếp tục để ý, mà là nhìn về phía lôi đình phía dưới thanh niên."),t("br"),t("br"),n._v("Động Hư kiếp, dính dáng không gian biến hóa, liền xem như hắn, muốn vượt qua đều không có dễ dàng như vậy, lúc này Lạc Huyền Thanh, hai mắt đỏ thẫm, vừa nhìn liền biết tinh thần không đúng, cưỡng ép xung kích lời nói, làm không cẩn thận không những không cách nào thành công, còn có thể chết ở trong đó."),t("br"),t("br"),n._v("“Đi qua cứu người...”"),t("br"),t("br"),n._v("Cũng lại không để ý tới cái khác, thân thể nhoáng một cái, Trương Huyền thẳng tắp hướng lôi đình vọt tới."),t("br"),t("br"),n._v("Mới vừa bay vào đi, khoảng cách trước đó đứng thẳng chỗ, không đủ ba trăm mét địa phương, hai bóng người chậm rãi hiện lên."),t("br"),t("br"),n._v("Bọn họ hình như từ không gian bên trong gạt ra đồng dạng, lại như đồng căn vốn không ở cái thế giới này, coi như thần thức đảo qua đi, đều không thể phát hiện chút nào."),t("br"),t("br"),n._v("“Hắn là ai?”"),t("br"),t("br"),n._v("Đi đầu một cái lão giả, đang mặc kim bào, nhìn về phía Trương Huyền bóng lưng biến mất, mày nhăn lại."),t("br"),t("br"),n._v("“Bẩm báo trưởng lão, vị này gọi là Trương Huyền, là Huyền Thanh thiếu gia, tại Thánh tử điện mới vừa quen bằng hữu, hai người cùng đi tìm kiếm Động Hư hồ lô, mới để cho thiếu gia, thuận lợi đột phá đến nửa bước Động Hư!”"),t("br"),t("br"),n._v("Sau lưng một cái khác lão giả, ôm quyền giải thích."),t("br"),t("br"),n._v("“Trương Huyền? Trương gia?” Kim bào lão giả sững sờ."),t("br"),t("br"),n._v("“Căn cứ điều tra, cũng không phải là Trương gia người, mà là từ một cái tiểu vương quốc từng bước một đi tới, nghe nói là Dương sư Dương Huyền thân truyền!”"),t("br"),t("br"),n._v("Cái thứ hai lão giả tiếp tục nói."),t("br"),t("br"),n._v("“Dương Huyền? Người này, cũng bắt đầu thu học sinh ư?”"),t("br"),t("br"),n._v("Lắc đầu, kim bào lão giả có chút không hiểu: “Nếu là học sinh của hắn, hẳn là kiến thức bất phàm, làm sao lại vọt thẳng đi qua? Chẳng lẽ cảm thấy bằng vào hắn, Xuất Khiếu cảnh đỉnh phong thực lực, có thể cho Huyền Thanh trợ giúp?”"),t("br"),t("br"),n._v("Độ kiếp, cần bản thân cố gắng, kiêng kỵ nhất liền là giúp một tay."),t("br"),t("br"),n._v("Nhúng tay càng nhiều người, lôi đình liền sẽ càng lớn, thậm chí đến cuối cùng xuất hiện khó mà khống chế cục diện."),t("br"),t("br"),n._v("Thực lực của người này cứ việc không mạnh, đối Động Hư kiếp ảnh hưởng không lớn, nhưng... Như vậy tùy tiện tiến lên, không những không thể hỗ trợ, còn tương đương tự tìm đường chết..."),t("br"),t("br"),n._v("Đến cùng nghĩ như thế nào?"),t("br"),t("br"),n._v("“Vị này Trương Huyền, căn cứ của ta điều tra, cũng là mười phần phá hư vương, hẳn là cùng thiếu gia tính tình hợp nhau, thấy hắn gặp lôi kiếp, cũng nhịn không được nữa, đi qua nhìn một chút...”"),t("br"),t("br"),n._v("Cái thứ hai lão giả giải thích."),t("br"),t("br"),n._v("“Nhịn không được?” Kim bào lão giả hừ một tiếng: “Làm việc không có kế hoạch, đợi Huyền Thanh độ kiếp thành công, nói cho hắn biết, đừng cùng loại này người lỗ mãng kết giao...”"),t("br"),t("br"),n._v("Lạc Huyền Thanh bốn phía làm phá hư, bản thân hắn liền không vui, nghe được cùng như vậy người ngưu tầm ngưu, mã tầm mã, nhất thời càng thêm không vui."),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Cái thứ hai lão giả nhẹ gật đầu."),t("br"),t("br"),n._v("Trương Huyền cũng không biết, vừa rồi Minh Lý Chi Nhãn quan sát không có vật gì vị trí, sẽ có bóng người hiện lên, giờ phút này đã đi tới khoảng cách Lạc Huyền Thanh chỗ không xa."),t("br"),t("br"),n._v("“Sao ngươi lại tới đây...”"),t("br"),t("br"),n._v("Nhìn thấy hắn xuất hiện, hơn nữa trực tiếp đi vào lôi kiếp phạm vi, Lạc Huyền Thanh có chút nóng nảy."),t("br"),t("br"),n._v("“Trạng thái này của ngươi, khẳng định không cách nào thành công, ta tới giúp ngươi...”"),t("br"),t("br"),n._v("Trương Huyền giải thích."),t("br"),t("br"),n._v("“Giúp ta? Chính ta lôi kiếp, như thế nào giúp?”"),t("br"),t("br"),n._v("Lạc Huyền Thanh lắc đầu: “Nhanh lên một chút rời đi, nếu không lôi đình một khi hội tụ hoàn thành, ta cũng chưa chắc gánh vác được, căn bản không có cách nào bảo vệ ngươi...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, trên bầu trời lôi đình, rốt cục hội tụ xong lực lượng, rầm rầm rơi xuống."),t("br"),t("br"),n._v("Tạch tạch! Tạch tạch!"),t("br"),t("br"),n._v("Một đạo ngang qua hơn mười mét thô to lôi đình, đem không gian xé rách ra một đạo hắc tuyến, thẳng tắp rơi xuống."),t("br"),t("br"),n._v("“Cho ta chặn lại!”"),t("br"),t("br"),n._v("Lại không để ý tới cùng thanh niên trước mắt nói chuyện, Lạc Huyền Thanh song chưởng bỗng nhiên hướng lên nâng lên, lòng bàn tay lực lượng hùng hồn, nghênh đón tiếp lấy."),t("br"),t("br"),n._v("Nửa bước Động Hư cảnh lực lượng tựa như đại dương, cùng lôi đình đụng vào nhau, không gian bốn phía, từng tầng từng tầng vỡ vụn."),t("br"),t("br"),n._v("Tạch tạch!"),t("br"),t("br"),n._v("Mặc dù ngăn lại phần lớn, vẫn như cũ có một tia chớp, rơi vào trên đầu, Lạc Huyền Thanh tóc dựng lên, toàn thân cháy đen."),t("br"),t("br"),n._v("Động Hư kiếp, so trước đó Trương Huyền dẫn tới Xuất Khiếu kiếp, mạnh mẽ không biết gấp bao nhiêu lần, cho dù tu vi của hắn đột nhiên tăng mạnh, đạt đến nửa bước Động Hư, vẫn như cũ bị oanh kích toàn thân như nhũn ra, chân khí trong cơ thể suýt chút nữa tán loạn."),t("br"),t("br"),n._v("“Ngươi nhanh lên một chút rời đi, cái này lôi đình ta thật chống lại không được...”"),t("br"),t("br"),n._v("Nhanh chóng thở hổn hển vài hớp, cảm thấy toàn thân rã rời, lực lượng toàn thân tiêu hao tiếp cận một nửa, Lạc Huyền Thanh con mắt đỏ thẫm."),t("br"),t("br"),n._v("Động Hư kiếp, nắm giữ sáu đạo lôi đình, một đạo so một đạo lợi hại, đạo thứ nhất hắn cũng có chút chống lại không được, thật muốn toàn bộ lạc xuống, thật không biết có thể hay không kiên trì xong."),t("br"),t("br"),n._v("“Ngươi chớ để ý, ta đi lên xem một chút...”"),t("br"),t("br"),n._v("Không để ý tới đối phương, Trương Huyền đột nhiên động một cái, bỗng nhiên hướng lên bầu trời nhảy lên."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương vì cứu mình, vọt thẳng vào lôi đình, đi vào chỗ nguy hiểm nhất, Lạc Huyền Thanh tràn đầy cảm động."),t("br"),n._v(" Thời khắc sống còn thấy giao tình."),t("br"),t("br"),n._v("Đừng nói bằng hữu, rất nhiều đạo lữ trong lúc đó, đều sẽ đại nạn lúc đến đều tự bay, năng lực cứu hắn, không quan tâm, trực tiếp đi vào nguy hiểm nhất chỗ... Vị này Trương sư, quả nhiên tính tình thật!"),t("br"),t("br"),n._v("“Sẽ không phải hắn... Ưa thích nam nhân a?”"),t("br"),t("br"),n._v("Đột nhiên, nhớ tới một việc, Lạc Huyền Thanh run rụt lại."),t("br"),t("br"),n._v("Từ khi biết, vị này Trương sư dường như đối với hắn liền cực kỳ tốt, vượt xa quá bình thường bằng hữu... Chẳng lẽ, thật đối với mình có cái gì ý nghĩ xấu?"),t("br"),t("br"),n._v("“Dù sao ta cận kề cái chết cũng sẽ không từ...”"),t("br"),t("br"),n._v("Lắc đầu, không nghĩ nhiều nữa, nhanh chóng khôi phục trong cơ thể nguyên khí, tranh thủ đối kháng lần thứ hai rơi xuống lôi đình."),t("br"),t("br"),n._v("“Người này... Điên rồi đi?”"),t("br"),t("br"),n._v("Không chỉ hắn chấn kinh, phía ngoài hai cái lão giả cũng trợn mắt hốc mồm."),t("br"),t("br"),n._v("Gặp qua cứu người, cùng một chỗ đối kháng lôi kiếp, cũng nhìn qua liều chết bảo vệ... Có thể trực tiếp xông vào lôi đình tự tìm cái chết, nhưng chưa từng thấy qua..."),t("br"),t("br"),n._v("Dương Huyền, lợi hại như thế danh sư, dạy thế nào đi ra như vậy một cái ngốc thiếu đồ đệ?"),t("br"),t("br"),n._v("“Động Hư cảnh lôi đình, Thánh vực bát trọng cường giả, cũng không dám tuỳ tiện tiến vào bên trong, hắn một cái Thánh vực ngũ trọng Xuất Khiếu cảnh đỉnh phong chui vào, hẳn phải chết không nghi ngờ... Một lát nữa thay hắn nhặt xác đi!”"),t("br"),t("br"),n._v("Kim bào lão giả lắc đầu."),t("br"),t("br"),n._v("Gặp qua điên cuồng, chưa thấy qua như thế bị điên, đây cũng không phải là cứu người, mà là tự tìm đường chết."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Không để ý tới đám người chấn kinh, Trương Huyền vừa tiến vào lôi đình nội bộ, con mắt lập tức thả ra hưng phấn mà ánh sáng."),t("br"),t("br"),n._v("“Tiểu kiếp kiếp, ngươi Trương Huyền ca ca tới...”"),t("br"),t("br"),n._v("Một tiếng thở phào, cũng không tiếp tục che giấu khí tức trong người, toàn thân huyệt đạo mở ra, liều mạng hấp thu."),t("br"),t("br"),n._v("Đi tìm Động Hư hồ lô, chỉ trải qua năm sáu ngày, mà trở về lại hao tốn hơn mười ngày thời gian."),t("br"),t("br"),n._v("Giúp Bích Hồng Âm đám người đột phá, cộng lại cũng chỉ mấy canh giờ, thời gian còn lại, Trương Huyền cũng không có lãng phí, một mực mượn nhờ Động Hư hồ lô phát triển đan điền, để hắn tạo thành hình vòng không gian."),t("br"),t("br"),n._v("Thời gian không phụ người hữu tâm, trở lại Thánh tử trước điện, quả thực thành công, nhưng... Vấn đề cũng theo đó hiện lên."),t("br"),t("br"),n._v("Hình vòng không gian hình thành về sau, trong đan điền dung tích, chợt tăng không chỉ gấp mười lần, chân khí trong cơ thể hắn, tính toán đâu ra đấy chỉ có một phần mười, đang rầu không biết như thế nào đem chứa đầy, liền thấy lôi đình, làm sao không hưng phấn?"),t("br"),t("br"),n._v("Có thứ này, tuyệt đối có thể lần nữa đem chân khí, tích tụ đến đỉnh phong, khỏi cần lại tội nghiệp, chỉ trốn ở một cái góc."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Lôi điện đi vào thân thể, nhất thời thay đổi thành lôi đình chân khí, chảy vào đan điền, bên trong đan điền, lúc đầu lười biếng không nhúc nhích hồ lô, cảm nhận được cỗ lực lượng này, đột nhiên thân thể chấn động, bỗng nhiên nhảy ra ngoài."),t("br"),t("br"),n._v("“Ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Không thèm để ý câu hỏi của hắn, hồ lô vừa xuất hiện, đột nhiên hưng phấn mà một tiếng thở phào, thẳng tắp bay về phía một bên, ngay sau đó quay tròn xoay tròn."),t("br"),t("br"),n._v("Nương theo nó xoay tròn, bốn phía vô số lôi đình lập tức lít nha lít nhít tụ đến, vừa rơi xuống đến trên người của nó, lập tức giống như là rơi vào lỗ đen, cũng lại không nhìn thấy."),t("br"),t("br"),n._v("“Ta đi... Ngươi cho ta chậm một chút! Ngươi chỉ là cái hồ lô, có thể muốn chút mặt ư?”"),t("br"),t("br"),n._v("Không nghĩ tới cái hồ lô này, giống như hắn, nuốt chửng lôi điện, Trương Huyền sửng sốt một chút, ngay sau đó lên cơn giận dữ."),t("br"),t("br"),n._v("Cái gì ngoạn ý nha..."),t("br"),t("br"),n._v("Thật vất vả đuổi kịp Lạc Huyền Thanh dẫn tới lôi kiếp, ta dễ dàng ư?"),t("br"),t("br"),n._v("Trong cơ thể còn một đống lớn trống chỗ không có bổ sung xong đâu, ngươi ngược lại trước đoạt... Mặt đâu?"),t("br"),t("br"),n._v("Làm một cái bảo vật, biết hay không tôn trọng chủ nhân?"),t("br"),t("br"),n._v("Một mặt buồn bực, thấy hô không trở lại, tiếp tục hấp thu, mới hấp thu một hồi, lần nữa cảm thấy lôi điện càng ngày càng ít, ngẩng đầu nhìn lại, chỉ thấy cách đó không xa lôi đình lại tạo thành một cái vòng xoáy, chẳng biết lúc nào, giấu ở trữ vật giới chỉ dây leo cũng bay ra, từng ngụm từng ngụm nuốt chửng lôi đình."),t("br"),t("br"),n._v("“Ta sát... Cái này lôi điện là của ta!”"),t("br"),t("br"),n._v("Trương Huyền sắp điên rồi."),t("br"),t("br"),n._v("Quá không muốn mặt đi, ngang nhiên mở cướp, cái này hồ lô cùng dây leo, coi như cũng được không được?"),t("br"),t("br"),n._v("Da mặt co rúm, lần nữa muốn mắng hơn mấy câu, chỉ thấy phân thân chẳng biết lúc nào cũng xuất hiện ở trước mắt, hừ một tiếng, cũng chạy đến lôi đình ở giữa, nhanh chóng hấp thu."),t("br"),t("br"),n._v("“Các ngươi...”"),t("br"),t("br"),n._v("Thấy những cái thứ này, cả đám đều hấp thu so với chính mình nhanh hơn, biết giờ phút này không phải tức giận thời điểm, Trương Huyền cắn răng một cái, vu hồn rời đi thân thể, hai bút cùng vẽ, không ngừng nuốt chửng."),t("br"),t("br"),n._v("Nương theo cả hai đồng thời tăng tốc, từng đạo chân khí nhanh chóng dọc theo kinh mạch chảy vào đan điền, bổ sung trong cơ thể thiếu hụt."),t("br"),t("br"),n._v("Mới bổ sung trong cơ thể một phần ba chân khí, liền cảm thấy lôi đình càng ngày càng thưa thớt, lần nữa nhìn, chỉ thấy hồ lô, dây leo cùng phân thân ba cái gia hỏa, càng hấp thu càng nhanh, bốn phía vốn là nồng đậm lôi điện, lúc này đã trở nên mỏng manh như là sương mù."),t("br"),t("br"),n._v("“Đáng ghét ah...”"),t("br"),t("br"),n._v("Càng nghĩ càng giận, Trương Huyền mắng một câu, cắn chặt răng răng, tăng thêm tốc độ hấp thu."),t("br"),t("br"),n._v("Hắn bên này tức giận sắp bùng nổ, phía dưới cố gắng khôi phục lực lượng Lạc Huyền Thanh, rốt cục chờ đến lần thứ hai lôi đình đến."),t("br"),t("br"),n._v("Bất!"),t("br"),t("br"),n._v("Nói láo giống như thanh âm vang lên, trước đó hơn mười mét độ lớn lôi đình, giờ phút này chỉ có to bằng ngón tay, hữu khí vô lực đi tới đỉnh đầu của hắn."),t("br"),t("br"),n._v("Phốc xích!"),t("br"),t("br"),n._v("Rơi vào trên đầu, liền cái cây gỗ đều đánh lực lượng đều không có, trực tiếp dập tắt."),t("br"),t("br"),n._v("“...” Lạc Huyền Thanh."),t("br"),t("br"),n._v("“...” Kim bào trưởng lão cùng vị thứ hai lão giả."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1386"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1388"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);