(window.webpackJsonp=window.webpackJsonp||[]).push([[453],{808:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1385-goi-ta-su-huynh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1385-goi-ta-su-huynh"}},[n._v("#")]),n._v(" Chương 1385: Gọi ta sư huynh!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1385: Gọi ta sư huynh!"),t("br"),t("br"),n._v("Vì trở thành Thánh tử điện Thánh tử, tất cả mọi người dùng hết toàn lực biểu hiện, tựa như hắn, khảo hạch ba ngày, một lần đều không có dám chợp mắt, đem bản thân tất cả có thể thi triển thủ đoạn, đều phát huy ra, không dám khinh thường chút nào."),t("br"),t("br"),n._v("Vị này ngược lại tốt, miễn cưỡng đi vào lớp tinh anh là được, còn tên thứ mấy, căn bản không quan tâm!"),t("br"),t("br"),n._v("Thậm chí... Bị nhiều người như vậy nghi ngờ, đều không có mở miệng phản bác qua!"),t("br"),t("br"),n._v("Yên tĩnh như nước, không tranh quyền thế, đây mới thật sự là cao thủ, thiên tài chân chính, chân chính khiêm tốn người."),t("br"),t("br"),n._v("“Đúng vậy a, quả thực rất biết điều, nếu không phải Phùng Tử Dật khiêu chiến, ta cũng không biết, khóa này tân sinh bên trong, nắm giữ nhân vật lợi hại như thế...”"),t("br"),t("br"),n._v("Phí sư cũng cảm khái một câu, đang muốn tiếp tục khen ngợi, chỉ thấy một người học viên vội vã đi tới trước mặt, ghé vào lỗ tai hắn nói một câu, lông mày đột nhiên giương lên, vội vàng xoay người."),t("br"),t("br"),n._v("“Hàn Phù trưởng lão, sao ngươi lại tới đây?”"),t("br"),t("br"),n._v("Một mặt cung kính."),t("br"),t("br"),n._v("Trương Hủ vội vàng xoay người, chỉ thấy một cái râu tóc trắng tinh, tiên phong đạo cốt lão giả đi tới trước mặt."),t("br"),t("br"),n._v("“Hàn Phù trưởng lão?”"),t("br"),t("br"),n._v("Trong đầu vội vàng suy tư, nhớ lại lúc trước tại Trương gia nhìn thấy về Thánh tử điện tin tức, rất nhanh vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("“Thánh tử điện, khải linh sư công hội đại trưởng lão, tu vi đạt đến Thánh vực bát trọng đỉnh phong...”"),t("br"),t("br"),n._v("Âm thầm gật đầu, cũng ôm quyền khom người: “Trương gia Trương Hủ, gặp qua Hàn trưởng lão!”"),t("br"),t("br"),n._v("“Không cần khách khí...” Tùy ý khoát tay áo, Hàn trưởng lão nhìn về phía lôi đài, có chút nóng nảy: “Trương sư... Lúc nào chiến đấu xong?”"),t("br"),t("br"),n._v("“Không biết Hàn trưởng lão tìm Trương sư, vì chuyện gì? Gấp gáp lời nói, ta có thể nghĩ biện pháp để bọn hắn dừng lại...”"),t("br"),t("br"),n._v("Phí sư nhịn không được nhìn qua."),t("br"),t("br"),n._v("Đối phương thế nhưng là Thánh tử điện khải linh cao thủ mạnh nhất, coi như Trương sư trở thành Thánh tử, cũng không trở thành hắn tự mình chạy tới tìm kiếm đi."),t("br"),t("br"),n._v("“Ta tìm hắn...”"),t("br"),t("br"),n._v("Nghe được hỏi thăm, Hàn trưởng lão tăng một mặt thấu hồng, tràn đầy xấu hổ: “Là muốn hỏi một chút, cái kia một mực chạy trấn thủ tượng đá, đến cùng lúc nào có thể dừng lại? Bởi vì chạy càng lúc càng nhanh, đã đụng hủy lân cận bốn cái công hội điện đường, lại không dừng lại, ta sợ... Khải linh sư công hội sẽ bị cái khác công hộ đuổi giết...”"),t("br"),t("br"),n._v("Người khác khải linh trấn thủ tượng đá, đi bảy bước, cũng đã đánh vỡ ba ngàn năm ghi chép, vị này ngược lại tốt, không chỉ làm làm nóng người nắm, còn chạy không dừng được... Đã lâu như vậy, một mực chạy như điên, hắn đi qua ngăn cản, đều bị đạp thổ huyết ba trở về, nhưng vẫn không có biện pháp."),t("br"),t("br"),n._v("Nói thật, ngoan ngoãn chạy, đến không có gì, dựa theo tượng đá đặc thù, sớm tối đều biết dừng lại, có thể... Cái đồ chơi này thực sự quá lớn, càng chạy càng nhanh, thỉnh thoảng nhảy cóc, chống đẩy, sâu ngồi lên, nâng cao chân, nhanh chóng chạy, tăng tốc chạy, trước nhào lộn, hậu chiêu lật, lộn ngược ra sau... Ngược lại cũng thôi, nhưng chạy phạm vi càng lúc càng lớn, đem bốn phía theo sát công hội, toàn bộ phá hủy va sụp, cũng có chút không chân chính..."),t("br"),t("br"),n._v("Lại không dừng lại, thật muốn chọc giận cái khác công hội, sợ sẽ đem tượng đá trực tiếp hủy đi, đến lúc đó... Thật sự phiền toái."),t("br"),t("br"),n._v("“Trấn thủ tượng đá... Không dừng được?”"),t("br"),t("br"),n._v("“Làm sập mấy cái công hội?”"),t("br"),t("br"),n._v("Phí sư cùng Trương Hủ đồng thời lay động một chút."),t("br"),t("br"),n._v("“Còn có, khi ta tới, nhìn thấy thiên cơ sư công hội cũng đổ sụp biến thành phế tích, nghe nói cùng hắn liên quan đến, thuận tiện muốn tới đây nhìn một chút, đối phương không có đến tìm phiền phức đi...”"),t("br"),t("br"),n._v("Hàn trưởng lão nói tiếp."),t("br"),t("br"),n._v("“Thiên cơ sư công hội... Biến phế tích?”"),t("br"),t("br"),n._v("Phí sư cùng Trương Hủ nhìn nhau, khóe miệng co giật..."),t("br"),t("br"),n._v("Không phải rất biết điều ư?"),t("br"),t("br"),n._v("Thế nào cái này mấy món sự tình, đều không giống khiêm tốn người, mới có khả năng đi ra..."),t("br"),t("br"),n._v("Cũng nhịn không được nữa, ngẩng đầu nhìn về phía lôi đài."),t("br"),t("br"),n._v("Ngay sau đó, nhìn thấy Phùng Tử Dật, đang kề sát ở thủy tinh bộ dáng phong ấn phía trên, mặt uốn éo thành bánh quai chèo, sau lưng kiếm khí ngang dọc, không ngừng vọt tới, ánh mắt mập mờ, cả người một bộ sinh không thể yêu biểu lộ, tựa như lúc nào cũng sẽ điên mất."),t("br"),t("br"),n._v("“Thật mạnh...”"),t("br"),t("br"),n._v("Thấy Phùng Tử Dật, sớm chuẩn bị tốt bốn cái phòng ngự, giờ phút này tất cả đều vỡ vụn, toàn thân đều là lỗ kiếm, cả người ăn mày đồng dạng, hai người lần nữa nuốt ngụm nước bọt."),t("br"),t("br"),n._v("Phá vỡ nhiều như vậy công kích, kiếm khí vẫn như cũ uy lực không giảm... Coi như Phí sư, đều làm không được."),t("br"),t("br"),n._v("“Ta hỏi thăm một chút, lúc nào có thể kết thúc...”"),t("br"),t("br"),n._v("Thấy kiếm khí cuồn cuộn, không có đình chỉ ý tứ, Phí sư hít sâu một hơi, đang muốn la lên, liền nghe đến Phùng Tử Dật tràn đầy tức giận tiếng rên rỉ vang lên."),t("br"),t("br"),n._v("“Ngươi gạt người, gạt người... Vừa rồi rõ ràng chỉ dùng một phần mười lực lượng, lại gạt ta nói một phần sáu...”"),t("br"),t("br"),n._v("Vị này Dương sư thân truyền, đều nhanh muốn điên rồi."),t("br"),t("br"),n._v("Trước đó đối phương nói một phần ba lực lượng, hắn liền cảm thấy giả, về sau còn nói một phần sáu, cảm thấy lại thêm giả... Lúc này mới đề nghị ở đây, để hắn dùng hết toàn lực biểu hiện ra thực lực, muốn thăm dò một chút, có hay không như đối phương nói tới..."),t("br"),t("br"),n._v("Kết quả, người này quả thật nói láo..."),t("br"),t("br"),n._v("Căn bản cũng không phải là một phần sáu, mà là chỉ dùng một phần mười!"),t("br"),t("br"),n._v("Một phần mười, liền một phần mười... Làm gì nói một phần sáu, để cho ta phòng ngự không đủ, hiện tại cũng sắp bị đánh chết..."),t("br"),t("br"),n._v("Ngươi biết không? Có đôi khi khiêm tốn, thực sẽ hại chết người..."),t("br"),t("br"),n._v("Ầm ầm! Ầm ầm!"),t("br"),t("br"),n._v("Chân khí trong cơ thể toàn bộ tiêu hao từng li từng tí không dư thừa, Trương Huyền lúc này mới ngừng tay đến, miệng lớn thở hổn hển, một mặt bất đắc dĩ."),t("br"),t("br"),n._v("Cứ việc chân khí trong cơ thể số lượng, chợt tăng gấp năm lần, thực lực cũng từ Xuất Khiếu cảnh sơ kỳ tăng thêm tới được đỉnh phong, có thể Nhất Kiếm Phá Hải, vẫn như cũ không cách nào triệt để khống chế, một khi thi triển, nhất định phải đem chân khí trong cơ thể toàn bộ tiêu hao sạch sẽ mới có thể dừng tay."),t("br"),t("br"),n._v("Trước kia chân khí ít, đánh một hồi liền dừng lại, hiện tại kiếm khí ngang dọc, trọn vẹn duy trì hơn mười phút, mới hoàn toàn tiêu hao sạch sẽ."),t("br"),t("br"),n._v("Thở ra một hơi, đem đầy trời trường kiếm thu hồi, ngẩng đầu nhìn lại, chỉ thấy Phùng Tử Dật xếp thành một cái quỷ dị tạo dáng, kề sát ở phong ấn lên, toàn thân quần áo đã vỡ không sai biệt lắm, mặc dù không có chết, nhưng cũng bị trọng thương, không nói ra được thê thảm."),t("br"),t("br"),n._v("“Thật không có lừa ngươi, vừa rồi thi triển kiếm pháp thời điểm, lại có cảm ngộ mới, uy lực hơi tăng trưởng một chút...”"),t("br"),t("br"),n._v("Đi tới trước mặt, đem hắn từ phong ấn bên trên chụp xuống, Trương Huyền mang theo xấu hổ giải thích."),t("br"),n._v("Thật đúng là không phải cố ý lừa gạt đối phương, mà là trước đến giờ không có như vậy dùng hết toàn lực thi triển qua chiêu này, chân khí dồi dào, có đầy đủ thời gian thí nghiệm, bởi vậy, thi triển đồng thời, có càng nhiều lĩnh ngộ, lực công kích mới càng ngày càng mạnh."),t("br"),t("br"),n._v("Thiên đạo công pháp, cứ việc không có sai lầm, nhưng hắn thể lực cùng tinh thần có hạn, phát huy càng nhiều, phối hợp càng tốt, uy lực tự nhiên sẽ càng ngày càng mạnh mẽ."),t("br"),t("br"),n._v("“Còn có thể tăng thêm...”"),t("br"),t("br"),n._v("Phùng Tử Dật lần nữa thổ huyết."),t("br"),t("br"),n._v("Đều loại uy lực này, còn tăng trưởng, để cho người ta có sống hay không?"),t("br"),t("br"),n._v("“Thực lực của ngươi, quả thực rất lợi hại, nhưng danh sư, mạnh nhất không phải chiến đấu, mà là giảng bài... Có dám so với ta thí nghiệm một phen?”"),t("br"),t("br"),n._v("Qua thật lâu, Phùng Tử Dật lúc này mới khôi phục lại, hung tợn nhìn qua."),t("br"),t("br"),n._v("Lúc này, Trương Huyền cũng giao phó xong Hàn trưởng lão, như thế nào khống chế lại tượng đá, thấy hắn vẫn như cũ muốn tỷ thí, không buông tha, nhịn không được mở miệng: “Thôi được rồi, ta sợ ngươi nhận đả kích càng lớn!”"),t("br"),t("br"),n._v("Mặc dù, không biết đối phương vì sao phải cứ cùng hắn tỷ thí, nhưng tỷ thí giảng bài... Thật sợ đối phương không chịu nổi, sẽ trực tiếp điên mất."),t("br"),t("br"),n._v("“Không có gì có thể đả kích, lão sư từng nói cho ta biết, chỉ lấy ta một cái học sinh, hiện tại ngươi lại xuất hiện, ta muốn thấy nhìn, đến cùng dạng gì tư chất, mới có thể để cho hắn phá lệ!”"),t("br"),t("br"),n._v("Phùng Tử Dật nói."),t("br"),t("br"),n._v("“Phá lệ? Tư chất?”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Nghe đối phương khẩu khí, dường như cũng không biết bản thân vị này là giả, còn tưởng rằng, là Dương sư mặt khác thu học sinh, lúc này mới mang theo ghen hương vị, đến đây khiêu chiến."),t("br"),t("br"),n._v("Kết quả, không nghĩ tới, bại thê thảm như thế."),t("br"),t("br"),n._v("“Một chút lợi hại cao nhân, đều là tùy duyên thu đồ, cụ thể có bao nhiêu đệ tử, ngoại trừ chính hắn, người ngoài cũng không hiểu rõ tình hình, thậm chí đồ đệ trong lúc đó, cũng sẽ không biết!”"),t("br"),t("br"),n._v("Nhìn qua không ít sách vở, biết một chút danh sư thói quen."),t("br"),t("br"),n._v("Tựa như hắn, nếu như bây giờ thu đồ, Triệu Nhã, Vương Dĩnh, Viên Đào đám người, khẳng định cũng hết thảy đều không biết, có cái tiểu sư đệ hoặc là tiểu sư muội tồn tại."),t("br"),t("br"),n._v("“Ta cũng là vận khí tốt, mới đến lão sư ưu ái...”"),t("br"),t("br"),n._v("Thấy đối phương cũng không hiểu rõ tình hình, cũng không phải là vạch trần bản thân giả tạo thân phận, Trương Huyền lúc này mới thở phào nhẹ nhõm, một mặt mong đợi nhìn qua: “Ta rất lâu không gặp lão sư, không biết lão nhân gia ông ta hiện tại nơi nào? Có thể hay không dẫn ta thấy hắn?”"),t("br"),t("br"),n._v("“Lão sư, cùng ta tại hai năm trước tách ra, cụ thể đi nơi nào, cũng không hiểu rõ tình hình, dù sao đưa tin ngọc phù, liên lạc không được...”"),t("br"),t("br"),n._v("Phùng Tử Dật lắc đầu."),t("br"),t("br"),n._v("“Quá tốt rồi...” Trương Huyền hưng phấn mà suýt chút nữa nhảy dựng lên."),t("br"),t("br"),n._v("Vốn đang lo lắng, người này sẽ để lộ bí mật tố cáo, đã liên lạc không được, cũng quá tốt, chí ít hiện nay tránh thoát một kiếp."),t("br"),t("br"),n._v("Chỉ cần về sau trở thành Thánh tử điện điện chủ, coi như đối phương đi tìm đến, vạch trần giả mạo, có loại thân phận này, đoán chừng cũng không có người còn dám đến tìm phiền phức..."),t("br"),t("br"),n._v("Nói cách khác, đối với hắn mà nói, thời gian mười phần quan trọng, có thể rút ngắn một chút, liền là một chút."),t("br"),t("br"),n._v("“Quá tốt rồi?”"),t("br"),t("br"),n._v("Phùng Tử Dật lông mày nhíu một cái."),t("br"),t("br"),n._v("Nghe được liên lạc không được lão sư, không phải rất mất mát ư? Ngươi hưng phấn như vậy làm cái gì?"),t("br"),t("br"),n._v("“A, ta nói chính là, quá làm cho người ta thất lạc...”"),t("br"),t("br"),n._v("Biết vừa rồi có chút thất lễ, Trương Huyền vội vàng mở miệng."),t("br"),t("br"),n._v("“Của ngươi thất lạc là như thế này?”"),t("br"),t("br"),n._v("Tức xạm mặt lại, Phùng Tử Dật lông mày nhảy loạn, cũng nhịn không được nữa: “Thế nào? Có dám hay không so với ta thí nghiệm giảng bài?”"),t("br"),t("br"),n._v("“Tử Dật sư đệ, ngươi không phải là đối thủ của ta, thôi được rồi, đã đều là Dương sư học sinh, ta cũng liền không đả kích ngươi...” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Sư đệ?”"),t("br"),t("br"),n._v("Phùng Tử Dật suýt chút nữa nổ tung: “Ta bảy tuổi đi theo lão sư, nhập môn sớm, hơn nữa tuổi tác cũng lớn hơn ngươi, hẳn là xưng hô ngươi là sư đệ...”"),t("br"),t("br"),n._v("“Đừng khách khí... Đạt giả vi sư, thực lực của ta so với ngươi còn mạnh hơn, đương nhiên là sư huynh! Chờ ngươi lúc nào, có thể vượt qua ta, lại xưng hô sư đệ cũng không muộn...”"),t("br"),t("br"),n._v("Khoát tay áo, Trương Huyền tâm tình thư thái một hồi."),t("br"),t("br"),n._v("Trước mắt vị này đã không biết, về sau liền có thể lấy hắn làm lá chắn, chứng thực Dương sư đệ tử thân phận."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nghẹn sắc mặt đỏ lên, Phùng Tử Dật muốn nói cái gì lại nói không ra miệng."),t("br"),t("br"),n._v("Tu vi của đối phương so với hắn thấp, nhưng sức chiến đấu quả thực mạnh mẽ hơn hắn quá nhiều, thật muốn thi triển chiêu kiếm pháp này, căn bản ngăn cản không nổi."),t("br"),t("br"),n._v("Đồng dạng thân là thân truyền, lão sư không có xác định thân phận phía trước, quả thực đều là thực lực mạnh nắm giữ quyền nói chuyện, bản thân không cách nào vượt qua đối phương, muốn làm sư huynh, quả thực không có sức thuyết phục."),t("br"),t("br"),n._v("“Tỷ thí xong giảng bài lại nói, ngươi thắng, ta tự nhiên nhận ngươi sư huynh...”"),t("br"),t("br"),n._v("Hất lên ống tay áo, Phùng Tử Dật nói."),t("br"),t("br"),n._v("“Giảng bài?”"),t("br"),t("br"),n._v("Thấy hắn chưa từ bỏ ý định, Trương Huyền nhíu nhíu mày, đang muốn nói chuyện, chỉ thấy một thanh niên một mặt cổ quái vội vã đi tới."),t("br"),t("br"),n._v("“Phí sư, khôi lỗi ngõ hẻm Liêu trưởng lão, chờ ở bên ngoài lấy, nhất định phải đòi muốn bái Trương Huyền làm thầy...”"),t("br"),t("br"),n._v("“Liêu trưởng lão... Muốn bái sư?”"),t("br"),t("br"),n._v("Phùng Tử Dật ngây người, tất cả mọi người xôn xao."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1393"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1395"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);