(window.webpackJsonp=window.webpackJsonp||[]).push([[458],{813:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1390-đi-an-com"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1390-đi-an-com"}},[n._v("#")]),n._v(" Chương 1390: Đi ăn cơm")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1390: Đi ăn cơm"),t("br"),t("br"),n._v("Bạch Vũ Thánh y cùng hắn đều là trưởng lão, quan hệ không tệ, thường xuyên cùng một chỗ ra ngoài thí luyện, tìm kiếm bảo vật... Giờ phút này biết rõ có người độ kiếp, chẳng những không giúp đỡ, còn chủ động tấn công... Thật giống như, nhìn thấy bốc cháy, không những không hắt nước, còn tưới dầu đồng dạng..."),t("br"),t("br"),n._v("Ngại độ kiếp người, chết không đủ nhanh, vẫn là lễ điện không có hủy hoại?"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Không để ý tới vị này Hồ Thanh, Bạch Vũ lần nữa cắn răng, bàn tay lăn mình, lần nữa đối lôi đình công kích mà đi."),t("br"),t("br"),n._v("Tạch tạch!"),t("br"),t("br"),n._v("Đối mặt khiêu khích, lôi điện dường như cũng có chút nổi giận, hai đầu thô to lôi điện, bổ tới, rơi vào Thanh Long thú cùng Bạch Vũ trên người, trong khoảnh khắc, một người một rồng, thân thể cứng ngắc, vảy rồng cùng lông dựng lên, từ không trung rớt xuống."),t("br"),t("br"),n._v("“Thiên uy không được khiêu khích...”"),t("br"),t("br"),n._v("Hồ Thanh lắc đầu, lần nữa lông mày nhăn lại, vung tay lên: “Khởi động lễ điện phòng ngự đại trận, chặn lại lôi kiếp, thuận tiện đem vị kia độ kiếp gia hỏa tìm ra, chạy tới tin điện đi!”"),t("br"),t("br"),n._v("“Vâng!” Lại có hai vị trưởng lão đồng thời gật đầu, đang định tiến lên, tìm kiếm người độ kiếp, chỉ thấy nám đen khắp người Bạch Vũ, Thanh Long thú lần nữa bay lên, lực lượng cuồng bạo, lại một lần bắn ra, rơi vào tầng mây."),t("br"),t("br"),n._v("Tạch tạch! Tạch tạch!"),t("br"),t("br"),n._v("Lôi đình trở nên càng lớn, lôi điện lấp lánh, ánh sáng bắn ra bốn phía."),t("br"),t("br"),n._v("Bọn họ đều là Thánh vực bát trọng đỉnh phong cường giả, cố ý trêu chọc, lôi điện đã không phải Động Hư kiếp, e là cho dù là Thánh vực cửu trọng cường giả tới, cũng muốn lột một tầng da."),t("br"),t("br"),n._v("“Cái này... Ta thế nào độ qua?”"),t("br"),t("br"),n._v("Liêu trưởng lão bại liệt."),t("br"),t("br"),n._v("Trêu ai ghẹo ai!"),t("br"),t("br"),n._v("Vốn cho rằng hẳn phải chết không nghi ngờ, đối phương cho hi vọng, mà bây giờ... Lại cho tuyệt vọng!"),t("br"),t("br"),n._v("Hắn mặc dù những năm này tích lũy hùng hậu, nhưng cũng biết, như vậy cuồng bạo lôi đình, tuyệt đối không cách nào vượt qua!"),t("br"),t("br"),n._v("“Bình tĩnh, để bọn hắn hai cái lại chơi đùa lớn hơn một chút...”"),t("br"),t("br"),n._v("Trương Huyền vỗ vỗ bả vai, an ủi một câu."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Liêu trưởng lão cảm thấy càng thêm bại liệt."),t("br"),t("br"),n._v("“Tốt... Chúng ta ra ngoài đi!”"),t("br"),t("br"),n._v("Thấy tại Thanh Long thú cùng Bạch Vũ công kích đến, lôi đình đã tích góp đến điểm cao nhất, bất cứ lúc nào cũng sẽ không nắm được, triệt để sụp đổ, Trương Huyền có chút tiếc hận, chào hỏi một tiếng."),t("br"),t("br"),n._v("Lôi đình để tu vi cao tấn công, sẽ trở nên càng thêm cường đại, nhưng mà bởi vì nội tình ở nơi đó bày biện, quá lớn sẽ không nắm được, ngược lại hiệu quả càng kém."),t("br"),t("br"),n._v("Thật giống như máy kéo động cơ, nhất định phải cải tiến thành xe thể thao, khẳng định không cách nào làm được."),t("br"),t("br"),n._v("“Như vậy ra ngoài, sẽ chết...”"),t("br"),t("br"),n._v("Khuôn mặt bạc màu, Liêu trưởng lão hai chân không khỏi bản thân khống chế."),t("br"),t("br"),n._v("Mặc dù trải qua vô số sinh tử, nhưng ở thiên uy bên dưới, vẫn là kìm lòng không được cảm thấy sợ hãi cùng sợ hãi."),t("br"),t("br"),n._v("“Chết? Ngươi suy nghĩ nhiều...”"),t("br"),t("br"),n._v("Nhìn hắn bộ dáng này, Trương Huyền rõ ràng lo lắng cái gì, nở nụ cười, khắp khuôn mặt là chói lọi: “Yên tâm, ngươi cái này lôi kiếp, ta giúp ngươi độ, một lát nữa, chỉ cần nhìn thấy lôi kiếp nhỏ, liền phóng thích lực lượng, thấy nó muốn chạy trốn, cũng phóng thích lực lượng là được!”"),t("br"),t("br"),n._v("“Lôi kiếp nhỏ? Trốn?”"),t("br"),t("br"),n._v("Liêu trưởng lão mặt mũi choáng váng: “Trương sư, có thể hay không nói rõ ràng một chút... Ta nghe không hiểu... Thật nghe không hiểu ah...”"),t("br"),t("br"),n._v("Lôi đình, bình thường đều là không đem độ kiếp người đánh chết, không chịu bỏ qua, thế nào đến trong miệng ngươi, trở nên như thế manh?"),t("br"),t("br"),n._v("Còn thu nhỏ, còn chạy trốn... Ngươi coi Thiên đạo là nhà ngươi, nói trừng phạt ai, liền trừng phạt ai, nói thế nào trừng phạt, liền thế nào trừng phạt?"),t("br"),t("br"),n._v("“Không cần nghe hiểu, nhớ của ta dặn dò chính là...”"),t("br"),t("br"),n._v("Biết loại chuyện này, coi như giải thích, đối phương cũng rất khó lý giải, Trương Huyền không nói thêm lời, cổ tay khẽ đảo, lấy ra mấy thứ đồ, một bên chuẩn bị, vừa chạy ra ngoài đi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy hắn tự tin như vậy, không biết trả lời như thế nào, Liêu trưởng lão một bộ ung dung hy sinh bộ dáng, theo sát ở phía sau."),t("br"),n._v("“Phí sư, chúng ta cũng qua xem một chút đi...”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, Trương Hủ nói."),t("br"),t("br"),n._v("“Ừm!” Phí sư theo sát tới."),t("br"),t("br"),n._v("Mặc dù thân ở trong điện, nhưng thần thức có thể tuỳ tiện quan sát được trên không mây đen lớn nhỏ, hơn ngàn mẫu phạm vi bao phủ, một khi rơi xuống, bọn họ những người này sợ rằng đều trốn không thoát."),t("br"),t("br"),n._v("Đã vị này Trương sư như vậy có tự tin, vừa vặn nhìn một chút, đến cùng thế nào vượt qua!"),t("br"),t("br"),n._v("Phùng Tử Dật cũng theo sát tới, mới vừa đi mấy bước, liền thấy Trương Hủ đánh cái lảo đảo, suýt chút nữa ngã sấp xuống, nhướng mày: “Làm sao vậy?”"),t("br"),t("br"),n._v("Thân là Trương gia thiên tài, cái gì cảnh tượng hoành tráng chưa thấy qua, không đến mức còn chưa đi ra gian phòng liền sợ đến như vậy đi."),t("br"),t("br"),n._v("“Trương sư đây là... Làm gì?”"),t("br"),t("br"),n._v("Không có trả lời hắn, bờ môi run rẩy hai lần, Trương Hủ nhịn không được hỏi."),t("br"),t("br"),n._v("Nhíu nhíu mày, Phùng Tử Dật nhìn sang, vừa nhìn phía dưới, cũng ngây người tại chỗ, phảng phất ngu."),t("br"),t("br"),n._v("Chỉ thấy lúc này Trương Huyền, chẳng biết lúc nào tìm khối vải trắng, thật chỉnh tề xếp xong, để ở trước ngực, một mặt ưu nhã, đồng thời lấy ra một chén nước, súc súc miệng, hình như muốn đi tiệm cơm nghênh đón mỹ vị tiệc."),t("br"),t("br"),n._v("“Hắn đây là... Muốn đi ăn cơm?”"),t("br"),t("br"),n._v("Khóe miệng run lên hai lần, Phùng Tử Dật dùng sức vò đầu."),t("br"),t("br"),n._v("Đi theo lão sư sau lưng thật nhiều năm, chưa từng nghe nói qua, người khác độ kiếp cũng muốn lẫn vào một chân, càng không nghe qua, nhìn thấy người khác sợ chi rắn rết lôi đình, hưng phấn mà chuẩn bị kỹ càng bữa ăn chuẩn bị trước, tiến lên muốn ăn cơm..."),t("br"),t("br"),n._v("Người này đến cùng là độ kiếp, vẫn là muốn đi hưởng thụ mỹ vị?"),t("br"),t("br"),n._v("Tạch tạch! Tạch tạch!"),t("br"),t("br"),n._v("Chấn kinh âm thanh bên trong, chỉ thấy Trương Huyền cùng Liêu trưởng lão đã đi ra đại điện, nhìn thấy chính chủ xuất hiện, trên không mây đen phát ra kịch liệt nổ vang, bất cứ lúc nào cũng sẽ rơi xuống."),t("br"),t("br"),n._v("“Trương sư, mặc kệ ta có thể hay không vượt qua, lần này đều muốn cảm ơn ngươi xuất thủ cứu giúp...”"),t("br"),t("br"),n._v("Vốn là còn chút sợ hãi, chân chính nhìn thấy lôi đình, Liêu trưởng lão biết cũng lại trốn không thoát, ánh mắt lộ ra coi thường cái chết biểu lộ."),t("br"),t("br"),n._v("Có thể tu luyện tới hắn loại cảnh giới này, hơn nữa vì cứu người thân trúng âm sương hỏa độc, bản thân cũng không phải là cái sợ phiền phức người, nếu không, cũng không có khả năng có loại thực lực này cùng địa vị."),t("br"),t("br"),n._v("“Tốt, tại nguyên chỗ đợi tốt... Ta đi một chút liền đến!”"),t("br"),t("br"),n._v("Đánh gãy hắn, Trương Huyền khoát tay áo, không tiếp tục để ý hắn nói nhảm, thân thể nhảy lên, thẳng tắp hướng lôi đình hải dương vọt tới."),t("br"),t("br"),n._v("“Đây là muốn xông vào lôi hải?”"),t("br"),t("br"),n._v("“Như vậy sẽ chết...”"),t("br"),t("br"),n._v("Tất cả mọi người đều ngây người, ngay cả Hồ Thanh cũng thân thể run lên, suýt chút nữa từ không trung rơi xuống."),t("br"),t("br"),n._v("Gặp qua thấy lôi đình, đi trốn, cũng đã gặp, thấy lôi đình, xoay người bỏ chạy... Có thể chủ động nghênh đón người khác lôi kiếp, còn lần đầu tiên nhìn thấy."),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn lại như vậy làm, biết trách lầm đối phương, nắm đấm xiết chặt, Liêu trưởng lão hốc mắt đỏ lên."),t("br"),t("br"),n._v("Xông vào lôi kiếp, đây đều là vì hắn..."),t("br"),t("br"),n._v("Đang định đồng dạng tiến lên, đối kháng lôi đình, chỉ thấy đầy trời mây đen lắc lư một cái, quả nhiên cùng trước đó đối phương nói đồng dạng, rút nhỏ một vòng."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Đang tại kỳ lạ, cân nhắc có muốn hay không như đối phương nói như vậy, phóng thích lực lượng, chỉ thấy lôi đình gặp quỷ đồng dạng, xoay người bỏ chạy, tốc độ nhanh chóng, thiểm điện đều đuổi không kịp."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Lôi đình chạy trốn, bầu trời khôi phục sáng sủa, Trương Huyền xuất hiện lần nữa trên không trung, nhìn lấy chạy trốn lôi điện, mặt mũi bất đắc dĩ, đang muốn nói chuyện, ngực đột nhiên một phồng."),t("br"),t("br"),n._v("“Nấc...”"),t("br"),t("br"),n._v("Ợ một cái thanh âm, tựa như trống to, to rõ thanh dương."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1398"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1400"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);