(window.webpackJsonp=window.webpackJsonp||[]).push([[46],{400:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1018-cho-khan-truong-cam-thoang-cai-la-tot-roi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1018-cho-khan-truong-cam-thoang-cai-la-tot-roi"}},[n._v("#")]),n._v(" Chương 1018: Chớ khẩn trương, cắm thoáng cái là tốt rồi!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Hàn hội trưởng thật sự có chút phát điên."),t("br"),t("br"),n._v("Hắn làm sao đều không nghĩ ra, những học sinh này tại sao lại như vậy tin tưởng cái này không đáng tin cậy viện trưởng."),t("br"),t("br"),n._v("Bất kể nói thế nào, hắn cùng mọi người đều ở chung hai ngày hai đêm, phối hợp không nói thân mật vô gian, cũng có ăn ý... Dựa theo tình huống bình thường, cho dù người khác muốn bọn họ công kích, nhờ vào tình cảm cũng sẽ do dự một chút..."),t("br"),t("br"),n._v("Kết quả, không có chút nào dừng lại, trực tiếp đánh bay..."),t("br"),t("br"),n._v("Quả thực không phải nghe lời, mà là mù quáng tin phục!"),t("br"),t("br"),n._v("Nhất là vừa rồi, hắn còn đem kết quả nói, một khi cái tên này tìm tiết điểm không đúng, tất cả mọi người khả năng chết theo, vẫn như cũ việc nghĩa chẳng từ nan..."),t("br"),t("br"),n._v("Này bằng với đem tài sản của mình tính mạng toàn bộ đặt ở ở độ tuổi này không lớn thanh niên trên thân, không chần chờ chút nào... Liền xem như viện trưởng cũng không trở thành tín nhiệm không có bất kỳ cái gì phân biệt năng lực đi!"),t("br"),t("br"),n._v("Quả thực chính là cái gì kết quả đều không quan tâm mù quáng tin phục cuồng nhiệt phấn."),t("br"),t("br"),n._v("Giãy dụa lấy theo trong đá vụn đứng lên, lần nữa hướng trên không nhìn lại, chỉ thấy Trương Huyền trong tay trận kỳ, đã thoát ly bàn tay, trực tiếp hướng một cái tiết điểm bay đi."),t("br"),t("br"),n._v("“Xong...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Tiết điểm này hắn từng cân nhắc qua, là lực lượng nhất cuộn trào mãnh liệt, cuồng bạo nhất chỗ, một khi trận kỳ cắm vào, không những không thể phát tiết lực lượng, chỉ sợ còn có thể đem hoàn toàn dẫn nổ."),t("br"),t("br"),n._v("Không nghĩ tới hắn Hàn Tự một đời anh danh, cuối cùng thế mà chết tại nơi này, mà lại là bị một cái không hiểu quy củ gia hỏa, cứ thế mà đùa chơi chết... Suy nghĩ một chút đều cảm thấy tâm tắc, buồn từ đó tới."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Ngay tại lòng tràn đầy đau thương, cảm thấy lại không lưu luyến thời điểm, phía dưới lực lượng mãnh liệt, rốt cục tích góp đầy, cuộn trào mãnh liệt mà ra."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Rung động dữ dội, để mặt đất bắt đầu sôi trào, đứng tại cái hố bên cạnh rất nhiều học sinh, nguyên một đám đứng không vững, đồng loạt ngã xuống đất, ngay cả Ngô sư đám người mắt trên mặt vẻ sợ hãi."),t("br"),t("br"),n._v("“Lần này hình như so trước đó càng thêm hung mãnh...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Hàn hội trưởng vẻ mặt khó coi, ánh mắt bên trong tràn đầy khó tin."),t("br"),t("br"),n._v("Lần này phía dưới vọt tới lực lượng, so trước đó phải cường đại thật nhiều lần, phía trên phong ấn tựa như lúc nào cũng sẽ bị xé rách đến vỡ nát."),t("br"),t("br"),n._v("Nếu như trước đó liền có khổng lồ như vậy lực lượng, đừng nói hắn ngăn cản không nổi, cho dù cộng thêm hai vạn học sinh, cũng không có bất kỳ chỗ dùng nào."),t("br"),t("br"),n._v("Đến cùng xảy ra chuyện gì? Làm sao lực lượng lại đột nhiên bạo tăng nhiều như vậy?"),t("br"),t("br"),n._v("“Xong...” Hàn hội trưởng lộ ra tuyệt vọng."),t("br"),t("br"),n._v("Trước đó loại lực lượng kia ngăn cản lên, liền hao tốn vô số tinh lực, hiện tại như vậy cuồng bạo, không cần nghĩ, phong ấn khẳng định ngăn cản không nổi, lực lượng khổng lồ tất nhiên bộc phát ra đem phạm vi mấy ngàn cây số chỗ, nổ thành bột mịn."),t("br"),t("br"),n._v("Chính cảm thấy không có bất kỳ cái gì hi vọng, tất cả mọi người khả năng chết ở chỗ này thời điểm, chỉ nghe thấy trên không tung bay Trương viện trưởng, lông mày nâng lên."),t("br"),t("br"),n._v("“Ngoan, chớ khẩn trương, cắm thoáng cái là tốt rồi!”"),t("br"),t("br"),n._v("Nương theo lời nói, trước đó liền bay ra ngoài trận kỳ đột nhiên tăng nhanh tốc độ, trực tiếp hướng hắn xác định rõ tiết điểm, đâm tới."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Chân khí cùng tiết điểm tiếp xúc, vốn bất cứ lúc nào cũng sẽ nổ tung phong ấn, như bị đạp đuôi chó săn, tại không trước đó hung mãnh, phong ba một tiếng, bình tĩnh trở lại, dịu dàng giống như một vũng nước sạch."),t("br"),t("br"),n._v("Ngay sau đó, tiết điểm chỗ một tiếng nổ vang, nhất đạo lực lượng hùng hậu, trực tiếp hướng lên bầu trời vọt tới, hình như bóng bay bị đâm hư một cái lỗ thủng, khí lãng khổng lồ, tựa hồ muốn bầu trời đều cắt ra."),t("br"),t("br"),n._v("“Cái này... Giải quyết?”"),t("br"),t("br"),n._v("Toàn thân cứng ngắc, Hàn hội trưởng dùng sức vuốt mắt."),t("br"),t("br"),n._v("Tiết điểm này không phải lực lượng cuồng bạo nhất hung mãnh nhất chỗ ư? Làm sao trận kỳ cắm xuống đi lên về sau, lực lượng liền thuận lợi toả ra, đạt đến trước đó nghĩ cũng không dám nghĩ hiệu quả?"),t("br"),t("br"),n._v("Không chỉ hắn là loại vẻ mặt này, vừa mới bay rất xa, thật vất vả trở về Lưu Mặc cũng là trợn mắt hốc mồm."),t("br"),t("br"),n._v("Không dùng trận bàn, địa hình cũng không có nhìn, lấy được trận kỳ về sau, tiện tay cắm xuống... Cuồng bạo khí lưu liền ngừng, ngươi dùng thật sự là trên trận pháp kỹ xảo? Ta học nhiều năm như vậy trận pháp, thế nào cảm giác chưa từng nghe nói qua còn có loại này kỹ thuật đâu?"),t("br"),t("br"),n._v("Vội vàng quay đầu hướng phía dưới học sinh nhìn lại, lại thấy bọn họ không có chút nào kinh ngạc, hình như đã sớm biết viện trưởng sẽ nhẹ nhõm ngăn lại cuồng bạo lực lượng đồng dạng."),t("br"),t("br"),n._v("Hắn cùng lão sư Hàn hội trưởng, dùng hai ngày hai đêm đều không có giải quyết, người ta tới cũng liền mấy mươi phút, tùy tiện muốn cái trận kỳ cắm xuống... Liền xong công việc..."),t("br"),t("br"),n._v("Chẳng lẽ, cái tên này trước đó nói hai người bọn họ đần... Cũng không phải là châm chọc, mà là lời nói thật?"),t("br"),t("br"),n._v("Tràn đầy buồn bực, liền nhìn Trương Huyền từ không trung từng bước một đi xuống, đi tới rất nhiều học sinh phía trên ngừng lại."),t("br"),t("br"),n._v("“Chư vị hai ngày này vất vả, nơi này tai hoạ ngầm đã giải quyết triệt để, nhanh nghỉ ngơi khôi phục thể lực đi!”"),t("br"),t("br"),n._v("“Đa tạ viện trưởng!”"),t("br"),t("br"),n._v("“Ta liền biết, không có viện trưởng không giải quyết được sự tình!”"),t("br"),t("br"),n._v("“Trước đó cái kia Hàn hội trưởng còn lời thề son sắt nói cái gì bản thân là thất tinh đỉnh phong trận pháp sư, thoạt nhìn cũng chả có gì đặc biệt!”"),t("br"),t("br"),n._v("“Càng là loại này không có thực lực người, càng biết khoác lác, ngươi xem chúng ta viện trưởng, lợi hại như vậy, khoe khoang qua ư?”"),t("br"),t("br"),n._v("“Đúng vậy a, viện trưởng thật sự là quá khiêm tốn...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được viện trưởng chính miệng thừa nhận, vấn đề đã giải quyết, phía dưới tất cả mọi người đồng thời xôn xao, nguyên một đám kích động vẻ mặt đỏ lên, trực tiếp khoanh chân ngồi dưới đất, bắt đầu khôi phục."),t("br"),n._v(" Hai ngày không ngủ không nghỉ, cũng làm cho bọn họ hao tổn rất lớn, thậm chí không ít đều bị trọng thương."),t("br"),t("br"),n._v("Bất quá, cũng không tính là quá nghiêm trọng, chỉ cần cho thời gian, có thể rất nhanh khôi phục."),t("br"),t("br"),n._v("An bài tất cả mọi người tu luyện khôi phục thể lực, gặp được thương thế thực sự quá nghiêm trọng, Trương Huyền liền tự mình ra tay giúp đỡ điều trị, bận rộn tiếp cận một canh giờ, thấy tất cả học sinh, đều hoàn hảo như lúc ban đầu, lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Khó trách... Những học sinh này, đều vô điều kiện tin phục, không cần suy nghĩ... Nguyên lai, hắn cũng không đem bản thân xem như viện trưởng, mà là mỗi người lão sư, bằng hữu, chiến hữu...”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Hàn hội trưởng, Lưu Mặc hai người mặt mũi cười khổ, rốt cuộc minh bạch là gì thời khắc nguy cơ, những học sinh này không tin mình hai người, mà đối vị này Trương Huyền tín nhiệm không nghi ngờ."),t("br"),t("br"),n._v("Thanh Nguyên phong hào đế quốc cấp dưới tứ đại học viện, mỗi một vị viện trưởng, đều địa vị kính trọng, so với bọn hắn đều chỉ cao không thấp, nhân vật như vậy, đối một chút tứ tinh, ngũ tinh danh sư, không có chút nào không kiên nhẫn, ngược lại từng cái dốc lòng chú ý... Chỉ phần này tâm, chính là vô số người làm không được."),t("br"),t("br"),n._v("“Được rồi...”"),t("br"),t("br"),n._v("Xử lý xong những này, Trương Huyền trở lại Ngô sư đám người trước mặt, nhìn về phía Hàn hội trưởng hai người vội vàng ôm quyền: “Thực sự thật không tiện, vừa rồi tình thế nguy cấp, ta sợ trễ xuất hiện biến cố, vô lễ chỗ mong rằng rộng lòng tha thứ!”"),t("br"),t("br"),n._v("“Trương viện trưởng chuyện này, là chúng ta lỗ mãng...”"),t("br"),t("br"),n._v("Hàn Tự, Lưu Mặc hai người vẻ mặt đỏ lên, hận không thể có một cái lỗ chui vào."),t("br"),t("br"),n._v("Vốn bọn họ là hảo tâm, cảm thấy một khi phạm sai lầm, sẽ làm ra không cách nào thu tràng cục diện, kết quả, nhưng bởi vậy suýt chút nữa làm trễ nải đại sự, vừa nghĩ tới liền mặt mũi áy náy."),t("br"),t("br"),n._v("May mắn vị này Trương viện trưởng không câu nệ tiểu tiết, không có quy củ nhiều như vậy, để cho người ta đem bọn hắn đánh xuống, nếu không... Tất nhiên trở thành lịch sử tội nhân."),t("br"),t("br"),n._v("“Lực lượng tán dật còn cần một đoạn thời gian, phong ấn cũng không có hoàn toàn mở ra, không bằng chúng ta về trước Hỏa Nguyên thành, vừa vặn chờ một chút Chiến sư đường người!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Mặc dù hắn giải quyết phong ấn xuất hiện tai hoạ ngầm, nhưng muốn đi vào trong đó, còn cần một đoạn thời gian, nhất là cái này lực lượng cuồng bạo, hoàn toàn phát tiết sạch sẽ, khẳng định còn muốn ít nhất hai ba ngày công phu."),t("br"),t("br"),n._v("Lại thêm Chiến sư đường cùng cái khác ba đại học viện người, còn chưa tới đến, cần chờ bọn họ cùng một chỗ."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, mọi người ngồi phi hành Thánh thú, hướng Hỏa Nguyên thành bay đi."),t("br"),t("br"),n._v("Danh Sư đường có cái độc lập phủ đệ, mọi người lại đi vào, còn rất nhiều học sinh, Trương Huyền an bài Triệu viện trưởng đám người đi trước mang về."),t("br"),t("br"),n._v("Bên trong di tích bộ, nguy hiểm trùng điệp, những học sinh này quá khứ quá mức nguy hiểm, vẫn là về học viện trước lại nói."),t("br"),t("br"),n._v("Tại phủ đệ đợi một ngày, ba đại học viện viện trưởng, trưởng lão cùng Chiến sư đường mọi người, tất cả đều đi tới."),t("br"),t("br"),n._v("Liêu xông tiễn Trịnh Dương đi Chiến sư đường tổng bộ, cũng không đến, có điều, nhưng đổi hai vị khác thiên phu trưởng, thực lực mạnh hơn, đều đạt đến Thánh vực tam trọng đỉnh phong."),t("br"),t("br"),n._v("“Ngươi chính là Trương Huyền Trương viện trưởng?”"),t("br"),t("br"),n._v("Trong đó một vị, nhìn thấy Trương Huyền lần đầu tiên, liền lộ ra nồng đậm địch ý."),t("br"),t("br"),n._v("Chiến sư đường thất bại tan tác mà quay trở về sự tình, hắn nghe nói, biết trước mắt vị viện trưởng này, mặc dù chỉ có Tòng Thánh cấp bậc, nhưng chân chính sức chiến đấu kinh người, so với bọn họ Chiến sư, đều chỉ mạnh không yếu."),t("br"),t("br"),n._v("“Đúng!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Có dám cùng ta so sánh với một hồi?”"),t("br"),t("br"),n._v("Vị Thiên phu trưởng này, chiến ý sôi trào."),t("br"),t("br"),n._v("“Tỷ thí?” Trương Huyền một mặt bất đắc dĩ, thật sự là sợ điều gì sẽ gặp điều đó."),t("br"),t("br"),n._v("Hắn liền muốn an tĩnh làm mỹ nam tử, là khó khăn như thế sao?"),t("br"),t("br"),n._v("Quá ưu tú, cũng rất mệt mỏi ah!"),t("br"),t("br"),n._v("“Không sai, dám vẫn là không dám?” Vị Thiên phu trưởng này hai mắt áp sát."),t("br"),t("br"),n._v("“Mọi người mới lần thứ nhất gặp mặt, liền chém chém giết giết nhiều không tốt, đến, uống rượu, ta mời ngươi uống rượu...” Lấy ra một cái hồ lô rượu, Trương Huyền một mặt chân thành."),t("br"),t("br"),n._v("Thiên phu trưởng lắc đầu: “Ngươi không muốn so sánh với đấu cũng được, ta sẽ nghĩ biện pháp chứng minh, Chiến sư đường không phải có thể dễ dàng vũ nhục!”"),t("br"),t("br"),n._v("Thấy hắn không đánh, ngược lại muốn uống rượu, vị Thiên phu trưởng này hừ lạnh một tiếng, xoay người rời khỏi."),t("br"),t("br"),n._v("Thông qua hỏi thăm, biết vị này Chiến sư kêu Phùng Huân, tại Chiến sư đường đều là phần tử hiếu chiến, nghe được đường đường Chiến sư chạy tới khảo hạch học sinh bình thường, bị đánh hoa rơi nước chảy, trong lòng liền sinh ra cường đại địch ý, muốn cùng vị này Trương Huyền tỷ thí một trận."),t("br"),t("br"),n._v("Chiến sư đường một vị khác dẫn đội thiên phu trưởng, kêu Giang Nguyên, mặt ngoài rất hòa khí, trong mắt nhưng cũng chiến ý dạt dào."),t("br"),t("br"),n._v("Chiến sư luôn luôn là vô địch đại danh từ, lại tại Hồng Viễn học viện liên tục vấp phải trắc trở, thân là thiên phu trưởng, tự nhiên muốn đem tôn nghiêm giành lại tới."),t("br"),t("br"),n._v("“Sư thúc, lần này chúng ta Thanh Nguyên đế quốc tới bên này bốn người, ta, Ngô sư, Hàn hội trưởng cùng Lưu Mặc, ngươi đều thấy qua. Chiến sư đường loại trừ Giang Nguyên, Phùng Huân cùng Trác Thanh Phong, còn có mười hai vị Chiến sư. Còn ba đại học viện, ba vị viện trưởng, cộng thêm trưởng lão, tổng cộng chín người... Nói cách khác, lần này chúng ta muốn đi vào di tích, có chừng ba mươi người.”"),t("br"),t("br"),n._v("Mộc sư đi tới trước mặt, kỹ càng nói."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Vốn Triệu Bính Tuất mấy người cũng muốn cùng đi vào, bất quá hắn không có đồng ý."),t("br"),t("br"),n._v("Chỗ này di tích nguy cơ trùng trùng, ngộ nhỡ bọn họ đều đi vào, học viện liền một trưởng lão đều không có, tất nhiên rơi vào hỗn loạn."),t("br"),t("br"),n._v("“Căn cứ suy tính, ngày mai di tích liền sẽ mở ra, trước sáng sớm liền muốn xuất phát...” Mộc sư nói."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu, cùng Mộc sư cáo từ, trở lại bản thân sân nhỏ, đang định nghỉ ngơi, chỉ thấy Lạc Nhược Hi ngồi tại bản thân trong viện trên băng ghế đá, một tay nâng cái má, không biết nghĩ cái gì."),t("br"),t("br"),n._v("(Cầu chút phiếu đề cử!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1026"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1028"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);