(window.webpackJsonp=window.webpackJsonp||[]).push([[463],{818:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1393-ton-cuong-noi-gian-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1393-ton-cuong-noi-gian-thuong"}},[n._v("#")]),n._v(" Chương 1393: Tôn Cường nổi giận (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1393: Tôn Cường nổi giận (thượng)"),t("br"),t("br"),n._v("“Danh Sư đường tổng bộ, trừ đường chủ, địa vị cao nhất, đương đại người mạnh nhất một trong?”"),t("br"),t("br"),n._v("Thân thể run rẩy, Tôn Cường kích động suýt chút nữa nhảy dựng lên."),t("br"),t("br"),n._v("Trước đó cho rằng lão gia là cái bát tinh danh sư là tốt lắm rồi, loại thực lực này, tại Thánh tử điện tốt nhất cụp lại cái đuôi mà đối nhân xử thế, đừng quá mức khoa trương, dù sao, loại cấp bậc này, một trảo một nắm lớn, đầy đất đều là..."),t("br"),t("br"),n._v("Nằm mơ đều không có nghĩ đến, lại lợi hại như thế!"),t("br"),t("br"),n._v("Danh Sư đường tổng bộ thái thượng trưởng lão, cửu tinh danh sư, đương đại người mạnh nhất... Bất kể cái nào tên tuổi, đều mạnh mẽ tột đỉnh!"),t("br"),t("br"),n._v("Nói ra đủ có thể khiến toàn bộ thế giới cũng vì đó chấn động!"),t("br"),t("br"),n._v("Hắn chỉ là Thiên Huyền vương quốc, một cái bán hai tay phòng, làm phòng ốc môi giới, thế mà đạt được loại người này tán thưởng cùng tán thành... Trong lòng lập tức sinh ra một loại nồng đậm cảm giác tự hào."),t("br"),t("br"),n._v("“Cửu tinh danh sư ánh mắt siêu quần, có thể vừa ý ta, chỉ sợ, cũng có không muốn người biết thiên phú...”"),t("br"),t("br"),n._v("Mắt sáng lên, Tôn Cường kích động."),t("br"),t("br"),n._v("Một mực đến nay, đều cảm thấy hắn là cái tầm thường, cùng những người khác so ra, cái gì cũng không tính, hiện tại biết lão gia là lợi hại như thế cường giả, giờ mới hiểu được, chỉ sợ, cũng là vạn người chưa chắc có được một thiên tài."),t("br"),t("br"),n._v("Nếu không, đường đường cửu tinh danh sư, làm sao có thể đối với hắn tốt như vậy, hơn nữa ủy thác trách nhiệm, để hắn chăm sóc bản thân thân truyền?"),t("br"),t("br"),n._v("“Có thể được đến như vậy người coi trọng, là Trương sư phúc phận, nhưng có thể được đến Trương sư đệ tử như vậy, Dương sư nhất định cũng vô cùng vui vẻ...”"),t("br"),t("br"),n._v("Dường như cảm nhận được trong lòng của hắn tự hào, Trương Cửu Tiêu cảm khái."),t("br"),t("br"),n._v("Người khác đều cảm thấy là Trương Huyền tu tám đời phúc phận mới bái Dương Huyền làm thầy, nhưng hắn biết, thân là trời nhận danh sư, chỉ cần công khai, toàn bộ đại lục tất cả danh sư, đều bằng lòng bắt làm đồ."),t("br"),t("br"),n._v("Cảm khái xong, đột nhiên nhớ tới đối phương tra hỏi, nghi ngờ nhìn qua: “Ngươi thân là Dương sư quản gia, không biết Dương sư chân chính thân phận?”"),t("br"),t("br"),n._v("Hắn không rõ ràng lắm thì cũng thôi đi, đối phương cũng đã gặp qua Dương sư, thế nào cũng không biết?"),t("br"),t("br"),n._v("“Lão gia tính cách khiêm tốn, không nguyện ý hiển lộ, cho nên, ta cũng không hiểu rõ tình hình...” Tôn Cường nói."),t("br"),t("br"),n._v("“Thì ra là thế, đây mới thật sự là danh sư...”"),t("br"),t("br"),n._v("Trương Cửu Tiêu từ đáy lòng cảm khái."),t("br"),t("br"),n._v("Chân chính danh sư, không vì thanh danh chỗ mệt, hiển nhiên, Dương sư chính là người như vậy."),t("br"),t("br"),n._v("“Tại đây đã chữa trị hoàn chỉnh, ta cũng liền không nhiều đợi, cáo từ...”"),t("br"),t("br"),n._v("Lại trò chuyện một hồi, không nói thêm lời, Trương Cửu Tiêu xoay người rời đi."),t("br"),t("br"),n._v("Thấy hắn đi xa, nhớ tới trước đó thảo luận nội dung, Tôn Cường con mắt ánh sáng không ngừng lóe lên, mập mạp nắm đấm lần nữa nhéo một cái."),t("br"),t("br"),n._v("“Thiếu gia trước đó bảo ta hỏi thăm tuyệt phẩm linh thạch tin tức, trong lòng kiêng kị, không có cố gắng hỏi thăm, nếu biết lão gia thân phận, còn có cái gì có thể cố kỵ?”"),t("br"),t("br"),n._v("Cười ha ha một tiếng, mập mạp thân thể theo trên ghế đứng lên, vung tay áo một cái, bước nhanh ra ngoài đi đến."),t("br"),t("br"),n._v("Đương đại đệ nhất nhân quản gia, lão gia là cửu tinh danh sư, địa vị so với Thánh tử điện điện chủ, đều không kém chút nào, hỏi thăm một ít chuyện mà thôi, không tính là gì."),t("br"),t("br"),n._v("Thời gian không dài đến Tín điện."),t("br"),t("br"),n._v("Tại đây, không chỉ có thể củng cố nội tâm, rèn luyện phẩm chất, thuận lợi tu luyện giả độ kiếp, cũng là đủ loại tin tức truyền lại cùng giao lưu địa phương, hầu như toàn bộ Thánh tử điện học sinh nhiệm vụ, đều là từ nơi này tuyên bố đi ra."),t("br"),t("br"),n._v("Tìm một vòng, đi tới một cái rộng lớn đại điện, đi vào."),t("br"),t("br"),n._v("Gian phòng trong góc, ngồi một vị hai mươi tuổi thanh niên, một thân hắc bào, có vẻ hơi lười biếng."),t("br"),t("br"),n._v("“Xin lấy ra thân phận lệnh bài của ngươi!”"),t("br"),t("br"),n._v("Thấy hắn đi tới trước mặt, thanh niên xòe bàn tay ra."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Tôn Cường đem Trương Huyền cho hắn làm lệnh bài đưa tới."),t("br"),t("br"),n._v("“Một cái tân sinh quản gia?”"),t("br"),t("br"),n._v("Nhìn một cái, cười nhạo một tiếng, thanh niên tiện tay tướng lệnh bài ném tới, mí mắt vừa nhấc: “Nói đi, muốn đánh nghe cái gì tin tức? Thiếu gia các ngươi, mới vừa trở thành Thánh tử điện học sinh, chẳng lẽ liền muốn tham gia thí luyện?”"),t("br"),t("br"),n._v("Không ít vừa tới tuổi trẻ học sinh, đều sẽ tìm hắn mua sắm thí luyện tin tức, tìm đơn giản một chút dễ dàng đi hoàn thành."),t("br"),n._v(" “Thiếu gia của chúng ta không tham gia thí luyện...”"),t("br"),t("br"),n._v("Tôn Cường lắc đầu: “Là muốn nghe được một chút, Thánh tử điện ở nơi nào có thể hối đoái tuyệt phẩm linh thạch, hoặc là đem đối ứng linh dịch đan dược các loại!”"),t("br"),t("br"),n._v("“Tuyệt phẩm linh thạch?”"),t("br"),t("br"),n._v("Thanh niên nhíu nhíu mày, ngay sau đó xua tay: “Ta chỗ này không có loại tin tức này, ngươi quay về đi!”"),t("br"),t("br"),n._v("“Không có?”"),t("br"),t("br"),n._v("Tôn Cường nghi ngờ: “Ta hỏi thăm Tín điện tất cả địa phương, đều nói chỉ có ngươi tại đây có thể tìm tới, sao có thể có thể không có?”"),t("br"),t("br"),n._v("Sở dĩ trực tiếp tới cái này, cũng là bởi vì trước đó liền nghe ngóng."),t("br"),t("br"),n._v("Những người khác hầu như cũng không quá rõ ràng tuyệt phẩm linh thạch tin tức, chỉ có trước mắt vị này, có thể tìm tới, hơn nữa có thể hối đoái."),t("br"),t("br"),n._v("“Ta nói không có chính là không có, tiễn khách...”"),t("br"),t("br"),n._v("Khoát tay áo, thanh niên có vẻ hơi không kiên nhẫn, đang muốn dặn dò hạ nhân, đem người này đuổi ra ngoài, chỉ thấy một cái lão giả đi tới trước mặt."),t("br"),t("br"),n._v("“Ta cần một khối tuyệt phẩm linh thạch củng cố thực lực, phiền phức giúp ta hối đoái một chút...”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, lão giả lấy ra một đống thưa thớt khoáng thạch cùng Thánh thú lông tóc, móng vuốt đưa tới."),t("br"),t("br"),n._v("Nhìn một cái, Tôn Cường chỉ biết đồ tốt không ít."),t("br"),t("br"),n._v("Một chút lợi hại Thánh thú lông tóc, móng vuốt, cần đến làm vũ khí cùng khôi giáp, so luyện khí sư luyện chế đều cường đại hơn, trân quý dị thường."),t("br"),t("br"),n._v("“Trưởng lão chờ...”"),t("br"),t("br"),n._v("Dường như nhận ra lão giả thân phận, hẳn là Thánh tử điện một vị trưởng lão, thanh niên vội vàng xoay người đi trở về, thời gian không dài, mang theo một cái hộp ngọc đi tới."),t("br"),t("br"),n._v("Nhẹ nhàng mở ra, bên trong linh khí bức người, một viên linh thạch khí tức xao động, lóng lánh hào quang chói sáng."),t("br"),t("br"),n._v("“Đa tạ...”"),t("br"),t("br"),n._v("Nhìn thấy quả nhiên là một viên tuyệt phẩm linh thạch, lão giả thở phào nhẹ nhõm, cổ tay khẽ đảo đem hộp ngọc thu hồi, xoay người rời đi."),t("br"),t("br"),n._v("Thấy hắn rời đi, Tôn Cường lộ ra vẻ không vui: “Ngươi không nói không biết tuyệt phẩm linh thạch tin tức ư? Thế nào đối phương một cầm đồ vật, liền trực tiếp đổi?”"),t("br"),t("br"),n._v("“Vừa rồi vị kia là Thánh tử điện Tiền trưởng lão, bát tinh cấp bậc danh sư, ngươi một cái tân sinh quản gia, thừa dịp cái gì náo nhiệt? Cút sang một bên!”"),t("br"),t("br"),n._v("Thanh niên hơi không kiên nhẫn khoát tay áo."),t("br"),t("br"),n._v("“Có ý tứ gì?”"),t("br"),t("br"),n._v("Tôn Cường ánh mắt phát lạnh: “Ta mặc dù là tân sinh quản gia, nhưng cũng không phải là không trả tiền, một viên tuyệt phẩm linh thạch, mặc dù trân quý, muốn mua lời nói, còn có thể mua được...”"),t("br"),t("br"),n._v("“Mua được?”"),t("br"),t("br"),n._v("Thanh niên cười nhạo: “Ngươi có tư cách mua ư? Tuyệt phẩm linh thạch, toàn bộ đại lục đều mười phần thưa thớt, dùng một viên thiếu một viên, một cái tân sinh quản gia, liền muốn mua sắm... Thật sự là buồn cười!”"),t("br"),t("br"),n._v("Tuyệt phẩm linh thạch, bởi vì số lượng quá ít, giao dịch là muốn nhìn thân phận, thân phận địa vị không đủ, coi như muốn mua, cũng mua không được."),t("br"),t("br"),n._v("Bởi vậy, cho dù tại Thánh tử điện, cũng chưa từng thấy qua có dòng người thông."),t("br"),t("br"),n._v("“Không có tư cách? Ngươi ý tứ ta không có tư cách mua sắm?”"),t("br"),t("br"),n._v("Sầm mặt lại, Tôn Cường híp mắt lại."),t("br"),t("br"),n._v("“Tốt! Mau mau cút, đừng ở chỗ này phiền ta, nếu không, có tin ta hay không để cho người ta đem ngươi ném đi?”"),t("br"),t("br"),n._v("Tràn đầy không nhịn được khoát tay áo, thanh niên quát tháo."),t("br"),t("br"),n._v("“Đem ta ném đi?”"),t("br"),t("br"),n._v("Hất lên ống tay áo, Tôn Cường con mắt lập tức híp lại, trên người mang theo không thể xâm phạm khí tức, hét lớn một tiếng: “Làm càn! Ngươi có biết ta là ai không?”"),t("br"),t("br"),n._v("P/s: Chủ trang đủ rồi, giờ đến tớ trang bức:3"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1401"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1403"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);