(window.webpackJsonp=window.webpackJsonp||[]).push([[469],{823:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1399-ton-cuong-phan-no"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1399-ton-cuong-phan-no"}},[n._v("#")]),n._v(" Chương 1399: Tôn Cường phẫn nộ")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1399: Tôn Cường phẫn nộ"),g("br"),g("br"),n._v("Hoàn toàn yên tĩnh."),g("br"),g("br"),n._v("Tất cả trưởng lão hàm răng cắn chặt, từng cái không ngừng run rẩy, giống như là thấy quái vật đồng dạng biểu lộ, nhìn về phía Tôn Cường."),g("br"),g("br"),n._v("Trước mắt vị này thực lực, liền Chiêm sư đều chống lại không được, trực tiếp chửi “Gan chó”, còn muốn bắt người..."),g("br"),g("br"),n._v("Coi như muốn bắt, cũng phải bắt được mới được ah!"),g("br"),g("br"),n._v("Bọn họ những người này thật muốn ra tay, có thể đoán được, sẽ bị trực tiếp độc lật, một cái đều không còn sót lại."),g("br"),g("br"),n._v("“Ngươi nói cái gì?”"),g("br"),g("br"),n._v("Không chỉ đám người bộ dáng này, Hồ Nhất Vi cũng sững sờ ngay tại chỗ, ánh mắt híp lại."),g("br"),g("br"),n._v("Hơn 20 năm gần đây, hắn một mực đi theo lão gia sau lưng, có hay không quản gia, hạ nhân, biết đến rất rõ ràng, phần lớn sự tình đều là hắn tự mình xử lý, người trước mắt này, thấy đều chưa thấy qua ngược lại cũng thôi, thế mà trực tiếp như vậy nói chuyện cùng hắn!"),g("br"),g("br"),n._v("“Ta nói cái gì không nghe thấy a?”"),g("br"),g("br"),n._v("Khoát tay áo, Tôn Cường mặt mũi không kiên nhẫn: “Nói đi, ta muốn hỏi hỏi, ngươi giả mạo lão gia hạ nhân, muốn làm gì? Đổi lại thời gian khác, như vậy giả mạo, khả năng rất khó bị phát hiện, mà bây giờ... Nằm mơ đều không có nghĩ đến, gặp được ta vị này thật a?”"),g("br"),g("br"),n._v("“Thật?”"),g("br"),g("br"),n._v("Thấy đối phương khí tức trầm ổn, không có chút nào căng thẳng, Hồ Nhất Vi nhịn không được cách đó không xa Chiêm sư: “Chiêm Thiên Thừa, tên này đến cùng là ai?”"),g("br"),g("br"),n._v("“Các ngươi không nhận ra...”"),g("br"),g("br"),n._v("Chiêm sư khóe miệng giật một cái."),g("br"),g("br"),n._v("Vốn cho rằng, đều là Dương sư hạ nhân, hai người nên đã sớm gặp mặt qua, nằm mơ đều không có nghĩ đến, vừa chạm mặt liền giương cung bạt kiếm, dường như hai người đừng nói chưa thấy qua, nghe đều không có nghe qua."),g("br"),g("br"),n._v("“Loại tiểu nhân vật này, ta làm sao biết?”"),g("br"),g("br"),n._v("Hồ Nhất Vi hất lên ống tay áo."),g("br"),g("br"),n._v("Hắn gia chủ người, đương đại người mạnh nhất một trong, vòng xã giao kém nhất cũng đều là cửu tinh danh sư, một cái liền Thánh vực cũng chưa tới gia hỏa, chỗ nào có thể quen biết?"),g("br"),g("br"),n._v("“Hắn... Tự xưng Dương sư quản gia, vừa rồi chúng ta dùng Phong Sư điện đo lường qua, trong cơ thể quả thực nắm giữ Dương sư lưu lại chúc phúc...”"),g("br"),g("br"),n._v("Chiêm sư giải thích."),g("br"),g("br"),n._v("Cho đối phương trong cơ thể lưu lại chúc phúc người, thực lực mạnh mẽ, khoáng tuyệt cổ kim, loại trừ Dương sư, thật đúng là nghĩ không ra những người khác."),g("br"),g("br"),n._v("“Nắm giữ chúc phúc?”"),g("br"),g("br"),n._v("Sửng sốt một chút, Hồ Nhất Vi lần nữa nhìn về phía trước mắt người béo, chỉ thấy hắn lông mày nhăn lại, đồng dạng nhìn lại, cũng nhịn không được nữa: “Chủ nhân nhà ta, làm chuyện gì đều ưa thích độc lai độc vãng, liền đệ tử đều không thể nào thu, làm sao có thể thu quản gia? Người béo, đến cùng chuyện gì xảy ra?”"),g("br"),g("br"),n._v("Coi như muốn thu quản gia, cũng thu cái ra dáng, người này thực lực thấp như vậy, có thể làm gì?"),g("br"),g("br"),n._v("“Làm càn! Lão gia nhà ta, như là Thần Long, muốn nhận dạng gì quản gia, do chính hắn quyết định, ngươi thì tính là cái gì, cũng dám nghi ngờ?”"),g("br"),g("br"),n._v("Tức giận hừ một tiếng, Tôn Cường bàn tay lớn vẫy một cái, xoay người ngồi tại vừa mới lấy ra trên ghế: “Nói đi, ngươi đến cùng là ai? Tại sao giả mạo lão gia hạ nhân?”"),g("br"),g("br"),n._v("Cái gì ngoạn ý đây!"),g("br"),g("br"),n._v("Hiện tại người, thật sự là càng ngày càng khoa trương!"),g("br"),g("br"),n._v("Một cái giả, thấy được ta vị này thật, vậy mà mảy may đều không sợ, thậm chí còn chạy đến Thánh tử điện tới giả mô hình làm dạng..."),g("br"),g("br"),n._v("Lá gan thật là lớn!"),g("br"),g("br"),n._v("“Giả mạo?”"),g("br"),g("br"),n._v("Hồ Nhất Vi khí sắp nổ tung: “Ngươi cũng đã biết lão phu là ai?”"),g("br"),g("br"),n._v("Nếu là đổi lại trước kia, loại tiểu nhân vật này dám ở trước mặt mình hung hăng, khẳng định đã sớm độc chết."),g("br"),g("br"),n._v("Đường đường Độc điện tả hộ pháp, thiên hạ dùng độc đệ nhất nhân, lúc nào bị người từng nói như vậy."),g("br"),g("br"),n._v("“Ta quản ngươi là ai?”"),g("br"),g("br"),n._v("Tôn Cường hơi có vẻ không kiên nhẫn: “Ngươi cũng đã biết ta là người như thế nào? Lão gia nhà chúng ta, đương đại người mạnh nhất một trong, Danh Sư đường tổng bộ thái thượng trưởng lão, ta thân là quản gia của hắn, đại biểu hắn tôn nghiêm cùng thân phận, ngươi một cái giả mạo, cũng dám nói nhảm, nhưng biết nơi này là nơi nào?”"),g("br"),g("br"),n._v("Giả cũng dám cùng hắn hung hăng, thật sự là đem ngươi có thể lên ngày!"),g("br"),n._v(" “Ngươi nói Dương sư là chủ nhân của ngươi, nhưng có bằng chứng?”"),g("br"),g("br"),n._v("Mặc dù nhanh muốn chọc giận chết, Hồ Nhất Vi vẫn là nhịn xuống, khẽ nói."),g("br"),g("br"),n._v("Những năm này, Dương sư tự thân dạy dỗ, để tính tình của hắn tiêu ma không ít, nếu không, có nhân hòa hắn nói như vậy, đã sớm huyên náo toàn bộ Thánh tử điện đều nổ tung."),g("br"),g("br"),n._v("“Bằng chứng? Vừa rồi tiểu Chiêm bọn họ đã đo lường qua, trong cơ thể ta nắm giữ lão gia chúc phúc, ngươi đây? Ngươi nhưng có cái gì, có thể chứng minh thân phận? Ăn nói bừa bãi người người đều biết, không bỏ ra nổi đến, liền là giả!”"),g("br"),g("br"),n._v("Tôn Cường cười lạnh."),g("br"),g("br"),n._v("Mặc dù đo lường thân phận thời điểm, dùng chính là thiếu gia chân khí, nhưng thiếu gia là lão gia học sinh, có thể có hiệu quả như thế, nhất định là lão gia lưu lại thủ đoạn, nói thành hắn chúc phúc, cũng không tính nói dối."),g("br"),g("br"),n._v("“Ta...”"),g("br"),g("br"),n._v("Chẳng muốn nói nhảm, cổ tay khẽ đảo, một viên lệnh bài ném đi đi ra, Hồ Nhất Vi lông mày nâng lên: “Đây là chủ nhân đưa cho ta đại biểu thân phận thủ lệnh!”"),g("br"),g("br"),n._v("“Thủ lệnh?”"),g("br"),g("br"),n._v("Chiêm sư đám người nhìn sang, rất nhanh đồng thời gật đầu."),g("br"),g("br"),n._v("“Đây là Danh Sư đường tổng bộ đặc thù thủ lệnh!”"),g("br"),g("br"),n._v("“Quả thực là Dương sư, không làm được giả...”"),g("br"),g("br"),n._v("Thân là Thánh tử điện danh sư, những vật này, đương nhiên sẽ không nhìn lầm."),g("br"),g("br"),n._v("“Thật?”"),g("br"),g("br"),n._v("Tôn Cường sửng sốt một chút, đưa tay tướng lệnh bài lấy tới, cúi đầu nhìn."),g("br"),g("br"),n._v("Lệnh bài chỉ lớn cỡ lòng bàn tay, mặt trước in Danh Sư đường đặc thù huy chương, mặt trái thì là một cái thể triện “Dương” chữ."),g("br"),g("br"),n._v("Toàn thân không biết tài liệu luyện chế, cho người ta một loại nặng nề khí tức, đơn thuần cấp bậc, đã không kém gì thượng phẩm Thánh khí!"),g("br"),g("br"),n._v("Không nói cái khác, không nói trước chân thực hay không, đơn cái lệnh bài này bản thân, liền nhất định có giá trị không nhỏ, huống chi trong đó còn ẩn chứa Danh Sư đường như ẩn như hiện nhân tộc số mệnh."),g("br"),g("br"),n._v("“Thế mà không cho ta...”"),g("br"),g("br"),n._v("Nhìn một hồi, Tôn Cường càng nghĩ càng là uất ức."),g("br"),g("br"),n._v("Quá bất công đi!"),g("br"),g("br"),n._v("Đem thiếu gia ném cho ta, để cho ta chăm sóc ngược lại cũng thôi, vật gì đều không có lưu, vậy thì có chút quá đáng!"),g("br"),g("br"),n._v("Thiếu gia là học sinh của ngươi, để hắn tự lực cánh sinh, không can thiệp phát triển, có thể... Ta là quản gia, tiền không cho, chúc phúc không cho, rõ ràng có tốt như vậy lệnh bài, cũng không cho..."),g("br"),g("br"),n._v("Đồng dạng là hạ nhân, thế nào cảm giác cùng mẹ kế nuôi đồng dạng?"),g("br"),g("br"),n._v("Không được, lần sau gặp lão gia, nhất định phải cố gắng nói ra một cái, nhất định phải một hai dạng tín vật, nếu không, người người cũng hoài nghi, về sau còn thế nào ra ngoài gặp người?"),g("br"),g("br"),n._v("Còn thế nào đại biểu Danh Sư đường tổng bộ uy nghiêm?"),g("br"),g("br"),n._v("“Thế nào, thứ này, khả năng chứng minh thân phận của ta?”"),g("br"),g("br"),n._v("Thấy hắn bộ dáng này, Hồ Nhất Vi nói."),g("br"),g("br"),n._v("“Đã tiểu Chiêm bọn họ nói là thật, cái kia chính là thật... Nhìn tới ngươi cùng ta đồng dạng, đều là lão gia hạ nhân!”"),g("br"),g("br"),n._v("Mặc dù cảm thấy rất không công bằng, Tôn Cường vẫn là chậm lại."),g("br"),g("br"),n._v("Lão gia loại này cường giả, có một, hai cái hạ nhân, không tính là gì... Bất kể nói thế nào, hắn vẫn là rất lớn độ."),g("br"),g("br"),n._v("“Thân là chủ nhân hạ nhân, đi theo phía sau hắn, một tấc cũng không rời, ngươi lúc nào trở thành hắn quản gia, ta vì sao không biết?”"),g("br"),g("br"),n._v("Thấy đối phương xác nhận thân phận của mình, Hồ Nhất Vi lạnh lùng nhìn lại."),g("br"),g("br"),n._v("“Lão gia thần long thấy đầu mà không thấy đuôi, làm tất cả mọi chuyện, ngươi đều có thể biết được?”"),g("br"),g("br"),n._v("Đầu lâu nâng lên, Tôn Cường giống như là trở lại trước kia, ánh mắt bên trong mang theo xa xăm cùng cảm khái: “Lão gia theo thương hội đem ta tìm ra, nhìn trúng nhân phẩm của ta cùng bản tính, muốn ta chăm sóc thiếu gia, một đường che chở hắn phát triển... Lúc ấy ta liền quyết định, muốn làm hắn cả đời hạ nhân!”"),g("br"),g("br"),n._v("“Đây đều là trước đây thật lâu sự tình, không đề cập tới cũng được! Đúng, ngươi mới vừa nói, là lão gia hạ nhân? Đã như vậy, ta là quản gia, về sau liền nghe ta dặn dò đi! Ai, khỏi cần đội ơn, đây cũng là ta nên vì lão gia san sẻ...”"),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1407"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1409"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);