(window.webpackJsonp=window.webpackJsonp||[]).push([[47],{402:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1019-dat-tay"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1019-dat-tay"}},[n._v("#")]),n._v(" Chương 1019: Dắt tay")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Nàng tựa hồ có chút thất thần, liền Trương Huyền đi tới, cũng không biết, ánh trăng trong sáng tựa như sương trắng đồng dạng rơi vào trên người, cho người ta một loại nhạt như tiên tử, bất cứ lúc nào cũng sẽ tung bay cảm giác."),t("br"),t("br"),n._v("“Nghĩ gì thế, nhập thần như vậy?”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười, đi tới trước mặt."),t("br"),t("br"),n._v("“Không có gì...”"),t("br"),t("br"),n._v("Lấy lại tinh thần, Lạc Nhược Hi lắc đầu, đứng dậy, nhìn một cái ánh trăng nhàn nhạt: “Có thể hay không theo ta ra ngoài đi một chút?”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trương Huyền hưng phấn nhảy lên: “Đương nhiên, bằng lòng cực kỳ!”"),t("br"),t("br"),n._v("Nhìn thấy hắn bộ dáng này, Lạc Nhược Hi cười khổ lắc đầu: “Ngươi làm chuyện gì, đều như thế không che giấu ư?”"),t("br"),t("br"),n._v("Người khác đi cùng với nàng, cho dù rất vui, cũng sẽ tận lực che giấu, giả bộ như một bộ rất có thân phận, rất có kềm chế bộ dáng, cái tên này ngược lại tốt, suy nghĩ gì làm gì, làm việc tính tình thật, vô ưu vô lự, để cho người ta hâm mộ."),t("br"),t("br"),n._v("“Ta...” Trương Huyền có chút khẩn trương: “Thật ra thì vẫn luôn tại che giấu...”"),t("br"),t("br"),n._v("“Ồ?” Nghe hắn còn có thể che giấu, Lạc Nhược Hi thấy hứng thú, đôi mắt đẹp nhìn lại: “Nói nghe một chút!”"),t("br"),t("br"),n._v("“Ta bản thân là cái không yêu tranh đấu, lại mười phần khiêm tốn người, kết quả ngươi cũng thấy đấy...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Khiêm tốn?” Nhìn thấy hắn nghiêm túc bộ dạng, Lạc Nhược Hi cười khúc khích, vừa rồi trên mặt cô đơn quét sạch, cũng nhịn không được nữa: “Che giấu thật tốt, ta một chút không nhìn ra...”"),t("br"),t("br"),n._v("Cái tên này đi đến đâu, đều cao điệu không còn hình dáng, trở thành trong mắt mọi người lấp lánh nhất minh tinh... Giờ phút này lại còn nói, bản thân không yêu tranh đấu, mười phần khiêm tốn..."),t("br"),t("br"),n._v("Mặt đâu?"),t("br"),t("br"),n._v("Người khác không biết xấu hổ, nàng sẽ cảm thấy chán ghét, chẳng biết tại sao, cùng cái tên này cùng một chỗ, ngược lại cảm thấy vô cùng thả lỏng, có một loại yên tĩnh vô cùng cảm giác."),t("br"),t("br"),n._v("“Quả nhiên không tin!”"),t("br"),t("br"),n._v("Thấy vẻ mặt của cô bé, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Ở sâu trong nội tâm, hắn thật sự là rất biết điều, có thể tạo hóa trêu ngươi, quá ưu tú, đi tới chỗ nào, cũng giống như trong đêm tối đom đóm, sáng rực chói mắt, trốn đều không tránh khỏi..."),t("br"),t("br"),n._v("“Khanh khách!”"),t("br"),t("br"),n._v("Thấy hắn vẻ mặt thành thật, Lạc Nhược Hi lần nữa nở nụ cười."),t("br"),t("br"),n._v("Hai người vừa nói vừa cười, đạp trên bóng đêm, đi ra Danh Sư đường phủ đệ."),t("br"),t("br"),n._v("Đêm lạnh như nước, nhiệt độ so ban ngày thấp xuống không ít, trốn ở gia người, tất cả đều đi ra, trên đường phố tràn đầy dòng người."),t("br"),t("br"),n._v("Lạc Nhược Hi không biết dùng phương pháp gì tiến hành ngụy trang, ngoại nhân xem ra, mặc dù cũng rất xinh đẹp, nhưng chẳng phải xuất chúng, bởi vậy, trên đường mọi người chỉ là sợ hãi thán phục, ngược lại không có gây nên quá nhiều chú ý."),t("br"),t("br"),n._v("Đi một hồi thật lâu, Trương Huyền ngừng lại, chỉ về phía trước: “Hỏa Nguyên thành bởi vì khí hậu nguyên nhân, rất nhiều mỹ vị là địa phương khác không bằng, nơi này có cái khách sạn, không bằng ở đây nghỉ ngơi một chút, nếm thử nơi đó đặc sắc?”"),t("br"),t("br"),n._v("Lạc Nhược Hi nhìn về phía trước, chỉ thấy một cái cao lớn kiến trúc bên trên, viết bốn chữ lớn “Như mộng quán rượu”."),t("br"),t("br"),n._v("“Phù sinh như mộng, cười một tiếng mênh mông, không bằng đối rượu làm ca... Ngôi tửu lâu này rất có ý cảnh!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, cô gái nhẹ gật đầu."),t("br"),t("br"),n._v("Trong tửu lâu, trang trí mười phần xa hoa, thoạt nhìn cũng rất là ấm áp, bốn phía chạy anh bạn, đều đạt đến Hóa Phàm ngũ trọng thực lực, vừa nhìn liền biết ngôi tửu lâu này, không đơn giản."),t("br"),t("br"),n._v("“Hai vị khách quan, mời vào bên trong...”"),t("br"),t("br"),n._v("Một cái anh bạn tiến lên đón, an bài một cái gần cửa sổ tình lữ chỗ ngồi, Trương Huyền nhìn một cái, thấy đối phương không có tức giận, yên tâm thoải mái ngồi xuống."),t("br"),t("br"),n._v("“Đem bọn ngươi nơi này đặc sắc, đều tới bên trên một phần...”"),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Anh bạn lui xuống, chỉ chốc lát rượu ngon, thức ăn ngon tất cả đều đã bưng lên."),t("br"),t("br"),n._v("Không thể không nói, rượu nơi này món ăn thật không tệ, Hỏa Nguyên thành phong phú địa hỏa, không chỉ để luyện khí, luyện đan chức nghiệp ở đây rất là nổi tiếng, ủ chế rượu ngon cũng cam thuần mỹ vị, để cho người ta uống một ngụm liền hãm sâu trong đó, lưu luyến quên về."),t("br"),t("br"),n._v("“Cái này đuôi Kim giang cá hồi, là Nhạn Lâm sơn Kim giang hồ mới có đặc thù giống loài, mười phần thưa thớt, mười năm mới có thể lớn một lạng, không lân, thịt tươi, phối hợp hoàng tửu nung, hương vị rất tốt...”"),t("br"),t("br"),n._v("“Đây là Thanh Tước thú lá gan, trực tiếp chưng chín, ít thả một chút dầu vừng, liền có thể ăn vào, ẩn chứa trong đó linh khí nồng nặc không nói, hương vị vẫn là tuyệt mỹ...”"),t("br"),t("br"),n._v("“Đây là trúc nhọn Thanh Diệp thịt, tài liệu chính là Hỏa Nguyên thành đặc thù Thanh Diệp trúc nhọn, nấu nướng lên, như là thịt khô, rất có nhai đầu...”"),t("br"),t("br"),n._v("Một bên ăn, Trương Huyền một bên giới thiệu."),t("br"),t("br"),n._v("Những này mỹ vị, chỉ cần nếm bên trên một ngụm, thư viện liền có thể tạo ra thư tịch, đừng nói chỉ là kỹ càng giới thiệu, cho dù thuận miệng nói ra nấu nướng bên trên hơn mười đầu thiếu hụt, cũng có thể không mang theo lặp lại."),t("br"),t("br"),n._v("“Ngươi đối thức ăn ngon cũng có đọc lướt qua?”"),t("br"),t("br"),n._v("Nghe hắn nói như vậy kỹ càng, Lạc Nhược Hi tràn đầy kỳ quái."),t("br"),t("br"),n._v("Kẻ trước mắt này, tuổi không lớn lắm, không chỉ thực lực mạnh, phụ tu chức nghiệp lợi hại, càng là bao hàm toàn diện, hình như cái gì cũng biết đồng dạng."),t("br"),t("br"),n._v("Nhất là thức ăn ngon, xem như hạ cửu lưu chức nghiệp bên trong, tầm thường nhất, bình thường đều sẽ xuất hiện tại trong cung đình, cho quan lại quyền quý phối đồ ăn, thí nghiệm mới món ăn, không có quá cao điểm vị."),t("br"),t("br"),n._v("Đường đường danh sư học viện viện trưởng, thế mà học qua cái này, suy nghĩ một chút đều có chút kỳ quái."),t("br"),t("br"),n._v("“Xem qua liên quan thư tịch, cho nên biết một chút...” Trương Huyền giải thích."),t("br"),t("br"),n._v("“Xem qua sách liền có thể nói cặn kẽ như vậy? Lợi hại!”"),t("br"),t("br"),n._v("Lạc Nhược Hi khen ngợi một tiếng."),t("br"),t("br"),n._v("Nàng ăn cơm, lướt qua liền thôi, nhất cử nhất động, hiện ra tốt gia giáo, không giống Trương Huyền, nuốt ngấu nghiến, không có nửa điểm u nhã."),t("br"),t("br"),n._v("“Có tâm sự gì nói ra có lẽ sẽ rất nhiều, ta mặc dù thực lực không đủ, không ít chuyện còn có thể giải quyết.”"),t("br"),n._v("Ăn uống no nê, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("Hắn đã sớm nhìn ra cô gái khả năng có tâm sự, cho nên trên đường đi nói chêm chọc cười, dẫn đến đối phương bật cười, bất quá... Hiện tại xem ra, hiệu quả quá mức bé nhỏ."),t("br"),t("br"),n._v("“Không có gì...” Lạc Nhược Hi sửng sốt một chút, lập tức lắc đầu: “Chỉ là gặp một số việc, có chút nhớ nhung nhà mà thôi!”"),t("br"),t("br"),n._v("“Nhớ nhà? Ngươi... Thật chẳng lẽ là Thánh Nhân quý tộc Lạc gia người?”"),t("br"),t("br"),n._v("Trương Huyền đem nghi ngờ trong lòng hỏi lên."),t("br"),t("br"),n._v("Liên quan tới cô gái này chuyện, danh sư học viện lưu truyền một đống lớn, cơ bản đều là Thánh Nhân về sau, địa vị kính trọng."),t("br"),t("br"),n._v("Nếu không, cũng không có khả năng để Mộc sư đường đường thất tinh danh sư, thái độ như thế."),t("br"),t("br"),n._v("“Thánh Nhân quý tộc?” Cô gái cười lắc đầu, cũng không thừa nhận, cũng không phủ nhận, ngược lại nhìn qua: “Người nhà của ngươi đâu? Đều ở nơi nào, làm sao chưa từng nghe ngươi nhắc qua?”"),t("br"),t("br"),n._v("Thấy nàng không nguyện ý nhiều lời, Trương Huyền không tại vấn đề này tiếp tục dây dưa, ánh mắt lộ ra một chút cô đơn, nói: “Ta không có người thân, là cô nhi!”"),t("br"),t("br"),n._v("“Cô nhi?”"),t("br"),t("br"),n._v("Lạc Nhược Hi sững sờ."),t("br"),t("br"),n._v("“Ừm, từ nhỏ đến lớn, chưa từng thấy người thân...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn theo trái đất xuyên qua tới, không có một cái nào người thân, muốn nói cô độc, tuyệt đối không ai sánh nổi."),t("br"),t("br"),n._v("Cho dù không nói hắn, tiền thân từ khi có ký ức, liền chưa thấy qua người thân, có thể trở thành học viện lão sư, cũng là hao tốn không biết bao lớn cố gắng, kết quả... Còn dạy học sinh tẩu hỏa nhập ma, suýt chút nữa bị thu về và huỷ tư cách!"),t("br"),t("br"),n._v("Thê không người có thể đưa ra phải."),t("br"),t("br"),n._v("“Thật không tiện, ta không phải cố ý nhấc lên...” Lạc Nhược Hi vội vàng xua tay."),t("br"),t("br"),n._v("“Không có gì, thói quen, như vậy cũng tốt, không có gì vướng víu, muốn đi đến đâu liền đi tới na!” Trương Huyền cười cười."),t("br"),t("br"),n._v("Một người cũng rất tốt, cùng nhau đi tới, loại trừ mấy cái học sinh, không có gì có thể lo lắng."),t("br"),t("br"),n._v("“Ngươi tâm tính thật tốt...” Nhìn về phía thanh niên trước mắt, thấy hắn không có chút nào che giấu, sống thật vui vẻ, Lạc Nhược Hi ánh mắt lộ ra khen ngợi."),t("br"),t("br"),n._v("Không có bối cảnh, không có nhân mạch, nhờ vào cố gắng của mình, mới hai mươi tuổi liền thành Hồng Viễn danh sư học viện viện trưởng, vị này Trương Huyền mặc dù thoạt nhìn không đáng tin cậy, trên thực tế lại là đáng tin nhất!"),t("br"),t("br"),n._v("Thật giống như những học sinh kia đối với hắn tín nhiệm vô điều kiện, những trưởng lão kia vô điều kiện thần phục... Cũng nói rõ ưu tú."),t("br"),t("br"),n._v("Rõ ràng chuyện rất khó, chỉ cần hắn tại, liền có thể dễ dàng giải quyết, chỉ bằng vào điểm này, liền có thể để vô số nhân vọng trần không kịp."),t("br"),t("br"),n._v("Bên người nàng không thiếu có rất nhiều ưu tú người đồng lứa, trong đó tương đồng tuổi tác bên dưới, tất cả đều so trước mắt vị này cường đại hơn rất nhiều, nhưng... Những người này điểm xuất phát cao ah, nếu là tương đồng điểm xuất phát, muốn đạt tới người thanh niên này trình độ, khẳng định không có mấy cái!"),t("br"),t("br"),n._v("Tuổi còn trẻ liền đạt tới Tòng Thánh đỉnh phong, để học sinh khăng khăng một mực, thống lĩnh một cái học viện, làm cho tất cả mọi người đều cam tâm tình nguyện thừa nhận... Loại người này ô vuông mị lực, là không biết bao nhiêu người, đều không thể so sánh."),t("br"),t("br"),n._v("Nhất là tâm tính, rõ ràng gặp được khó khăn, nhưng mỗi lần đều có thể cùng mặt mũi đúng, chỉ cần không vẫn lạc, về sau, thành tựu tuyệt đối không thể đo lường."),t("br"),t("br"),n._v("Bất tri bất giác, Lạc Nhược Hi đối trước mắt vị này cách nhìn có biến hoá rất lớn."),t("br"),t("br"),n._v("Trước kia, chỉ là tự dưng cảm thấy cái tên này có chút gần gũi, mà bây giờ, cảm thấy hắn núp ở phía sau, chiếu lấp lánh đồ vật, là như vậy chói lóa mắt, để vô số người ảm đạm phai mờ."),t("br"),t("br"),n._v("Bất tri bất giác, để nàng đối trước mắt người thanh niên này, sinh ra khác nhau cảm xúc."),t("br"),t("br"),n._v("Mặc dù chỉ có một tia, lại là trước kia chưa bao giờ có."),t("br"),t("br"),n._v("“Trở về đi!”"),t("br"),t("br"),n._v("Lại trò chuyện một hồi, quay trở về."),t("br"),t("br"),n._v("Trăng sáng giữa trời, đem hai người cái bóng kéo thon dài, gió nhẹ nổi đến, thiếu nữ trên thân tản mát ra mùi thơm nhàn nhạt, ngửi tại trong miệng, không nói ra được thoải mái."),t("br"),t("br"),n._v("Trương Huyền nội tâm chưa hề cảm giác được như vậy yên lòng, bàn tay không tự chủ được đưa tới, đụng phải nữ hài tay cõng, tựa như chạm đến một đống bông vải, mềm mại mịn."),t("br"),t("br"),n._v("Lạc Nhược Hi thân thể cứng đờ, vội vàng đem tay rút về, ôm ở trước ngực, hơi đỏ mặt: “Ta đi về trước!”"),t("br"),t("br"),n._v("Nói xong vội vã đi thẳng về phía trước, chỉ chốc lát biến mất ở trước mắt."),t("br"),t("br"),n._v("“Đi...”"),t("br"),t("br"),n._v("Mặt mũi xấu hổ, Trương Huyền vỗ trán một cái: “Đều tại ta...”"),t("br"),t("br"),n._v("Hảo hảo đi đường, có lẽ còn có thể nhiều trò chuyện một hồi, làm gì dây vào tay người ta? Đối phương nếu là trách tội, về sau chẳng phải liền bằng hữu đều làm không được?"),t("br"),t("br"),n._v("Thật sự là ngu!"),t("br"),t("br"),n._v("Một mặt bất đắc dĩ, đang định trở lại chỗ ở, ngày mai nhìn một chút đối phương thái độ, chỉ mong không nên tức giận, liền nghe đến cách đó không xa trong đường tắt, một giọng nói vang lên."),t("br"),t("br"),n._v("“Gặp lại sau thân nhân của ta, bằng hữu của ta, là ta mất hết mặt mũi, để các ngươi hổ thẹn...”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy một cái chừng ba mươi tuổi trung niên nhân, quỳ gối một cái sân cửa ra vào, một mặt đau thương."),t("br"),t("br"),n._v("Trung niên nhân trên thân mang theo máu tươi, mặt mũi phong trần, thoạt nhìn có chút chật vật, hẳn là mới vừa đã trải qua nguy hiểm gì sự tình, vừa mới trở về."),t("br"),t("br"),n._v("“Gặp lại sau!”"),t("br"),t("br"),n._v("Dập đầu mấy cái, cắn răng một cái, trung niên nhân đứng dậy, cổ tay khẽ đảo, lòng bàn tay nhiều ra một thanh trường kiếm, đối với mình cổ liền quệt quá khứ."),t("br"),t("br"),n._v("“Tự sát?”"),t("br"),t("br"),n._v("Không nghĩ tới đi đường, gặp một cái tự sát, nhướng mày, Trương Huyền cong ngón búng ra."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một đạo quang mang bắn tới, trung niên nhân lòng bàn tay trường kiếm bị đánh trúng, “Ông!” Thoáng cái, cắm vào cách đó không xa trên vách tường, không ngừng lay động."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1027"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1029"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);