(window.webpackJsonp=window.webpackJsonp||[]).push([[48],{403:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1020-tu-la-kim"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1020-tu-la-kim"}},[n._v("#")]),n._v(" Chương 1020: Tử La kim")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Trường kiếm bị đánh bay, trung niên nhân sững sờ, quay sang liền thấy đi tới thanh niên."),t("br"),t("br"),n._v("“Sâu kiến còn sống ngày nào biết ngày ấy, ngươi vì sao phải tự sát?”"),t("br"),t("br"),n._v("Trương Huyền mở miệng."),t("br"),t("br"),n._v("Đi tới trước mặt, cũng thấy rõ, trung niên nhân thực lực, đã đạt đến Hóa Phàm cửu trọng sơ kỳ, tại Hỏa Nguyên thành tuyệt đối được cho một phương cao thủ."),t("br"),t("br"),n._v("“Làm phiền tiền bối nhớ, ta chỉ cầu chết một lần...”"),t("br"),t("br"),n._v("Biết khoảng cách xa như vậy, có thể một ngón tay bắn bay trường kiếm của hắn, thực lực tất nhiên cao hơn hắn rất nhiều, trung niên nhân cũng không bởi vì đối phương tuổi trẻ mà coi thường, cúi đầu đến cùng, trong mắt tràn đầy cô đơn."),t("br"),t("br"),n._v("“Chừng ba mươi tuổi liền tu luyện tới như ngươi loại này cảnh giới, xem như không sai thiên tài đi, nguyên nhân gì, tuyệt vọng như vậy? Cam tâm muốn chết?”"),t("br"),t("br"),n._v("Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Cho dù Hỏa Nguyên thành tài nguyên phong phú, chừng ba mươi tuổi liền có thể đạt tới cấp bậc này, cũng thiên phú không thấp."),t("br"),t("br"),n._v("Có thực lực có thiên phú, làm gì không được? Nhưng nghĩ quẩn tự sát?"),t("br"),t("br"),n._v("“Làm phiền tiền bối chiếu cố hỏi, ta bây giờ không có sống tiếp động lực...”"),t("br"),t("br"),n._v("Trung niên nhân lắc đầu: “Hỏa Nguyên thành, địa hỏa hùng hồn, nổi danh nhất chức nghiệp, là luyện khí sư. Ta bảy tuổi liền bắt đầu học tập, tại Tôn Tấn luyện khí đại tông sư làm hai mươi bốn năm học đồ, vốn cho rằng có thể thu ta đi vào môn hạ, ai biết... Liên tục mười năm khảo hạch đều thất bại! Như vậy kém thiên phú, như vậy vụng về, sống còn có cái gì ý nghĩa? Quả thực thẹn với người nhà, thẹn với cái nghề nghiệp này, còn không bằng chết đi coi như xong...”"),t("br"),t("br"),n._v("“Làm hai mươi bốn năm học đồ?” Trương Huyền sững sờ: “Ngươi bây giờ... Liền nhất tinh luyện khí sư đều không phải là?”"),t("br"),t("br"),n._v("Hóa Phàm cửu trọng cường giả, dựa theo cấp bậc, ít nhất là ngũ tinh đỉnh phong luyện khí sư, vẫn chỉ là học đồ... Thật hay giả?"),t("br"),t("br"),n._v("Trên đời còn có như vậy vụng về người?"),t("br"),t("br"),n._v("“Ừm! Ta mỗi lần luyện khí, đều không thể khống chế tốt địa hỏa nhiệt độ, cuối cùng đem khí thai luyện hỏng... Lần khảo hạch này, Tôn Tấn luyện khí sư, cũng nhịn không được nữa, đem ta trục xuất sư môn, hiện tại liền học đồ đều không phải là. Người nhà đều hi vọng ta có thể trở thành luyện khí sư, dương danh lập vạn, kết quả lại bị trục xuất, còn có mặt mũi nào gặp người...”"),t("br"),t("br"),n._v("Càng nói càng cảm thấy khó chịu, trung niên nhân buồn từ đó tới."),t("br"),t("br"),n._v("Trương Huyền nháy con mắt."),t("br"),t("br"),n._v("Hóa Phàm cửu trọng cường giả, bắt đầu từ lúc bẩy tuổi học tập luyện khí, đến bây giờ lại ngay cả nhất tinh luyện khí sư đều không có khảo hạch thành công, cho dù lại không có thiên phú, cũng sẽ không không có thiên phú đạo loại tình trạng này đi!"),t("br"),t("br"),n._v("Nếu như là thật, quả thực rất khó tiếp nhận tiến công."),t("br"),t("br"),n._v("“Rốt cuộc xảy ra vấn đề gì, liền nhất tinh luyện khí sư đều khảo hạch không thông qua?”"),t("br"),t("br"),n._v("Trương Huyền kỳ quái."),t("br"),t("br"),n._v("Nhất tinh luyện khí sư, cơ sở đơn giản, Thông Huyền cảnh võ giả, liền có thể làm được, trước mắt vị này, Hóa Phàm cửu trọng, linh hồn cũng đã nhận được tu luyện, lực khống chế chính xác, đơn giản như vậy làm sao có thể luyện chế hay sao?"),t("br"),t("br"),n._v("“Ta cũng không biết, chỉ biết là mỗi lần khảo hạch, đều không thể đem binh khí luyện chế thành công.” Trung niên nhân lắc đầu, trong mắt không có một chút tự tin, xem ra liên tục nhiều lần khảo hạch để hắn đánh mất tất cả lòng tin."),t("br"),t("br"),n._v("“Không cách nào luyện chế thành công?” Dừng lại một chút, Trương Huyền nhìn qua: “Ngươi có thể hay không luyện chế một kiện cấp thấp nhất vũ khí, cho ta xem một chút?”"),t("br"),t("br"),n._v("Đường đường Hóa Phàm cửu trọng cường giả, liền nhất tinh luyện khí sư đều khảo hạch có điều, đây là trước đó nghĩ cũng không dám nghĩ, nói ra quả thực chính là nói mơ giữa ban ngày, khiến cho người khó có thể tin."),t("br"),t("br"),n._v("“Đa tạ tiền bối cứu tính mạng của ta, nhưng mà ta đã quyết định muốn chết, cho dù tiếp tục luyện chế, cũng là chuyện vô bổ, vẫn là để ta loại này người vô dụng tự sát quên đi, miễn cho tiếp tục mất mặt!” Trung niên nhân khom người nói."),t("br"),t("br"),n._v("“Tự sát có nhiều thời gian, tại hạ đối luyện khí cũng rất có nghiên cứu, có lẽ có thể nhìn ra ngươi tồn tại vấn đề, bảo ngươi thuận lợi khảo hạch thành công.”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Đối phương nếu là thật muốn tự sát, hắn cũng không xen vào, chẳng qua là cảm thấy đường đường Hóa Phàm cửu trọng cường giả, liền nhất tinh luyện khí sư đều kiểm tra có điều, thực sự có chút không thể tưởng tượng."),t("br"),t("br"),n._v("“Cái này...” Trung niên nhân trên mặt biến ảo không ngừng."),t("br"),t("br"),n._v("“Dù sao ngươi đều là muốn tự sát, nếu như ta không giải quyết được, lại tự sát cũng không muộn!” Trương Huyền cười cười."),t("br"),t("br"),n._v("“Tốt!” Trung niên nhân nhẹ gật đầu, răng cắn chặt."),t("br"),t("br"),n._v("Cũng đúng, hắn đều là muốn tự sát người, đã không có cái gì có thể lại mất đi, cho dù đối phương nhìn không ra cái gì, cũng không khẩn yếu, cùng lắm thì chết một lần."),t("br"),t("br"),n._v("“Bên kia có một cái ta chuyên môn dùng để luyện khí tĩnh thất, tất cả vật phẩm đều có, đi qua đi!”"),t("br"),t("br"),n._v("Nói xong đi đầu dẫn đường, thời gian không dài, hai người tới một cái không lớn gian phòng."),t("br"),t("br"),n._v("Nơi này địa hỏa, luyện khí lô đỉnh, cái gì cần có đều có, cách đó không xa trên giá còn bày đầy rất nhiều khoáng thạch, không ít đều là trên thị trường không thấy được, xem ra vị này trung niên nhân mặc dù luyện khí cấp bậc không cao, kim loại hiếm lại là sưu tập rất nhiều."),t("br"),t("br"),n._v("Nhìn quanh một tuần, đột nhiên rơi vào một cái khoáng thạch phía trên, mí mắt không khỏi hơi nhúc nhích một chút."),t("br"),t("br"),n._v("“Đây là... Tử La kim?”"),t("br"),t("br"),n._v("Tử La kim là một loại mười phần mỏ hiếm, hòa tan tiến vào đặc thù đồ vật bên trong, có thể để cho uy lực đại tăng."),t("br"),t("br"),n._v("“Sử dụng thứ này, cộng thêm đem đối ứng thủ pháp luyện chế, hoàn toàn có thể để cho Kim Nguyên đỉnh đột phá thực lực bây giờ!”"),t("br"),t("br"),n._v("Hai mắt tỏa ánh sáng, Trương Huyền tràn đầy kích động."),t("br"),t("br"),n._v("Trước đó vẫn nghĩ, giúp Kim Nguyên đỉnh tăng thực lực lên, chỉ bất quá cần có khoáng thạch một mực không có tìm được, chuyện cũng liền gác lại."),t("br"),n._v(" Không nghĩ tới tùy tiện cứu một cái muốn tự sát người, vậy mà tại hắn tĩnh thất phát hiện loại này khoáng thạch."),t("br"),t("br"),n._v("“Ta bắt đầu!”"),t("br"),t("br"),n._v("Không thấy được hắn biểu lộ biến hóa, trung niên nhân quát khẽ một tiếng, đi tới luyện khí lô đỉnh trước mặt."),t("br"),t("br"),n._v("Bàn tay đưa tay về phía trước, chân khí trong cơ thể cháy hừng hực, lập tức đem địa hỏa dẫn đi ra."),t("br"),t("br"),n._v("Cực nóng hỏa diễm gào thét, đem tĩnh thất bức tường chiếu rọi đến thấu đỏ, bàn tay một trảo, một cái cực lớn chuỳ sắt xuất hiện tại lòng bàn tay, ngay sau đó một khối tinh thiết, để vào trong đó, một lát sau, bị thiêu đốt xốp xuống."),t("br"),t("br"),n._v("Đinh đinh đang đang!"),t("br"),t("br"),n._v("Đập ở phía trên, rất nhanh liền để Khối sắt xuất hiện hình dạng, biến thành một chuôi sắc bén trường kiếm."),t("br"),t("br"),n._v("Không thể không nói, vị này trung niên nhân, đối khí thai rèn luyện đã đạt đến mức lô hỏa thuần thanh, so với hắn vị này lục tinh đỉnh phong luyện khí sư đều cường đại hơn hơn nhiều."),t("br"),t("br"),n._v("Thời gian không lâu, khí thai thành hình, lại vồ một cái, tính toán tiến hành tôi thép, ai biết nhưng vào lúc này, địa hỏa đột nhiên cuộn trào mãnh liệt mà đến, trong nháy mắt, liền đem vừa mới rèn luyện tốt trường kiếm đốt thành nước thép, trước đó tất cả cố gắng, hủy hoại chỉ trong chốc lát."),t("br"),t("br"),n._v("“Lại thất bại, ta liền nói ta không có khả năng luyện chế thành công...” Mặt mũi chán nản, trung niên nhân ánh mắt lộ ra thất lạc."),t("br"),t("br"),n._v("Vốn cho rằng lần này có thể thay đổi kết cục, kết quả còn cùng trước đó giống nhau như đúc, không có biến hóa chút nào."),t("br"),t("br"),n._v("“Cái này...” Cùng hắn chán nản khác biệt, đem trọn cái luyện khí quá trình nhìn ở trong mắt, Trương Huyền nhíu mày."),t("br"),t("br"),n._v("Lấy hắn hiện tại nhãn lực, cho dù không sử dụng Thiên Đạo thư viện, cũng có thể nhìn ra đối phương luyện khí bên trên mấu chốt cùng vấn đề."),t("br"),t("br"),n._v("Đối phương rèn luyện khí thai trình tự phương pháp, không có bất kỳ cái gì sai lầm, nhưng tại rèn luyện bên trên, chân khí trong cơ thể cùng địa hỏa xuất hiện phản xung hiện tượng, từ đó làm cho luyện chế thành công khí thai, một cái chớp mắt liền hòa tan sạch sẽ..."),t("br"),t("br"),n._v("Nói cách khác, tôi thép trình tự cũng không sai, nhưng mà chân khí trong cơ thể hắn cùng địa hỏa thuộc tính vừa vặn hoàn toàn trái lại, một khi tiến hành tôi thép, liền sẽ gây nên địa hỏa xao động, để trước đó khí thai biến thành đồ bỏ đi, từ đó làm cho thất bại trong gang tấc."),t("br"),t("br"),n._v("“Chân khí cùng địa hỏa xung đột lẫn nhau... Lục tinh đỉnh phong luyện khí sư hẳn là đều có thể nhìn ra?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Nếu như là cái gì lớn thiếu hụt, nhìn không ra ngược lại cũng thôi, loại này trong luyện tập trụ cột nhất, chỉ cần đạt đến lục tinh đỉnh phong, không có khả năng không biết!"),t("br"),t("br"),n._v("Đã như vậy, đối phương trong miệng vị kia Tôn Tấn lục tinh đại tông sư là gì không có nói ra? Càng không có chỉ điểm?"),t("br"),t("br"),n._v("Vị này trung niên nhân không phát hiện được, ngược lại là tình có thể hiểu, dù sao thực lực của hắn mặc dù đến, bởi vì liền nhất tinh luyện khí sư đều không có khảo hạch thành công là không có tư cách nhìn cấp bậc cao hơn thư tịch."),t("br"),t("br"),n._v("Mà loại này xung đột lẫn nhau lực lượng, chỉ có đạt tới lục tinh đỉnh phong cấp bậc mới có thể biết."),t("br"),t("br"),n._v("“Không đúng...” Trong lòng cân nhắc, đột nhiên Trương Huyền lông mày nhảy một cái, chân khí xung đột lẫn nhau là chỉ tu luyện công pháp cùng luyện khí xuất hiện bác bội, đã cái tên này bắt đầu từ lúc bẩy tuổi đi học tập luyện khí, làm sao có thể xuất hiện loại này cấp thấp nhất sai lầm?"),t("br"),t("br"),n._v("“Trừ phi là có người cố ý...” Con mắt lập tức híp lại."),t("br"),t("br"),n._v("Lang băm hại người, dung sư nhỡ người, trừ phi vừa bắt đầu liền có người cố ý để hắn tu luyện sai lầm công pháp, lúc này mới xuất hiện trước mắt loại cục diện này."),t("br"),t("br"),n._v("Chỉ là tu luyện sai lầm công pháp... Đối với đối phương có chỗ tốt gì?"),t("br"),t("br"),n._v("Tràn đầy cảm thấy lẫn lộn, Trương Huyền con mắt rơi vào bốn phía chứa đựng khoáng thạch trên giá, trong lòng đột nhiên động một cái."),t("br"),t("br"),n._v("“Những quáng thạch này đều là ngươi sưu tập?”"),t("br"),t("br"),n._v("“Đúng!” Không biết hắn tại sao lại hỏi như vậy, trung niên nhân nhẹ gật đầu: “Hỏa Nguyên thành khắp nơi đều là địa hỏa, hỏa diễm nồng đậm chỗ ẩn chứa đại lượng khoáng thạch... Nhờ vào chân khí trong cơ thể thu hút, mỗi lần đều có thể tìm tới không ít.”"),t("br"),t("br"),n._v("“Nhờ vào chân khí thu hút?”"),t("br"),t("br"),n._v("“Đúng! Chúng ta pháp quyết tu luyện đều là Tôn Tấn luyện khí sư truyền thụ cho, cố gắng tu luyện, tự nhiên sẽ đối với những quáng thạch này sinh ra mẫn cảm, rất dễ dàng tìm tới rất nhiều...”"),t("br"),t("br"),n._v("Trung niên nhân nhẹ gật đầu."),t("br"),t("br"),n._v("“Các ngươi? Cùng các ngươi tu luyện đồng dạng công pháp người có rất nhiều?” Trương Huyền nhìn lại."),t("br"),t("br"),n._v("“Là có rất nhiều, năm đó cùng một chỗ đi theo học tập học đồ, chừng hơn 30 vị, có điều, đại bộ phận đều đang tìm kiếm khoáng thạch thời điểm bị địa hỏa nuốt hết, đốt thành tro bụi. Còn lại chỉ có ta cùng một cái khác!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, trung niên nhân nói."),t("br"),t("br"),n._v("“Người kia có phải hay không cũng đồng dạng không cách nào trở thành chân chính luyện khí sư?”"),t("br"),t("br"),n._v("Giống như là nghĩ thông suốt cái gì, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("“Vâng! Hắn so ta lớn tuổi một tuổi, đồng dạng không cách nào khảo hạch thành công, năm ngoái thực sự không chịu nổi áp lực, đã tự sát...” Nhớ tới vị kia cùng mình cùng nhau đồng bọn, trung niên nhân lắc đầu."),t("br"),t("br"),n._v("Học tập nhiều năm như vậy luyện khí, kết quả lại không cách nào trở thành chân chính luyện khí sư, trong lòng thất lạc có thể tưởng tượng được, hắn có thể nhiều chống một năm, đã coi như là tâm trí tương đối kiên định."),t("br"),t("br"),n._v("“Tự sát?” Trương Huyền ngẩn ngơ, nhìn quanh một tuần, tiếp tục hỏi: “Vậy các ngươi... Tìm kiếm được khoáng thạch đều đi nơi nào? Không phải chỉ những này đi!”"),t("br"),t("br"),n._v("Đã nắm giữ tìm kiếm khoáng thạch năng lực, đã nhiều năm như vậy, hẳn là tìm được rất đa tài là, không có khả năng chỉ có trong phòng những thứ này."),t("br"),t("br"),n._v("“Là tìm được rất nhiều khoáng thạch, chẳng qua đại bộ phận đều lên giao cho Tôn Tấn luyện khí sư xem như học phí! Từ nhỏ đã cùng hắn học tập, truyền thụ rất nhiều tri thức, mới để cho có hôm nay, học phí tự nhiên muốn giao.”"),t("br"),t("br"),n._v("Trung niên nhân gật đầu."),t("br"),t("br"),n._v("“Nộp học phí? Thì ra là thế, ta hiểu được!”"),t("br"),t("br"),n._v("Bừng tỉnh hiểu ra, Trương Huyền mắt sáng lên, vẻ mặt lạnh lùng đến đáng sợ."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1028"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1030"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);