(window.webpackJsonp=window.webpackJsonp||[]).push([[480],{834:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1409-luyen-binh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1409-luyen-binh"}},[n._v("#")]),n._v(" Chương 1409: Luyện binh")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1409: Luyện binh"),t("br"),t("br"),n._v("Trấn an vài câu mang theo ủy khuất khôi lỗi, Trương Huyền lúc này mới nhấc chân hướng tầng hai đi đến."),t("br"),t("br"),n._v("Tầng thứ hai, đồng dạng không phải thuần thú, trận pháp các loại hắn am hiểu chức nghiệp, mà là luyện khí."),t("br"),t("br"),n._v("Đi vào phòng lập tức nhìn thấy một thanh niên, đứng tại lô đỉnh trước mặt, cầm trong tay đại chùy, lửa than thiêu đốt hồng, không ngừng đập, một kiện binh khí tại đánh phía dưới, chậm rãi thành hình, lóng lánh hàn quang."),t("br"),t("br"),n._v("Lần này danh sư lâu khảo hạch, dính dáng tám loại khác biệt phụ tu, trước đó liền đạt tới bát tinh chức nghiệp, một cái đều không có tuyển, tất cả đều là còn không có cầm tới huy chương."),t("br"),t("br"),n._v("Luyện khí sư, ban đầu ở Hồng Viễn học viện, thông qua qua lục tinh khảo hạch, đằng sau liền lại không tiếp xúc, lần này vừa vặn cũng tranh thủ một chút."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thấy hắn đi tới, thanh niên ngừng lại, một chỉ cách đó không xa luyện khí lô đỉnh: “Tại hạ luyện khí sư công hội Thánh tử, Ngô Hữu Đạo! Nơi này có một khối khoáng thạch, ngươi bây giờ bắt đầu luyện chế, chỉ cần một ngày thời gian bên trong, có thể luyện thành thượng phẩm Thánh khí, coi như thông qua!”"),t("br"),t("br"),n._v("Nhìn về phía cái kia luyện khí lô đỉnh, Trương Huyền quả nhiên nhìn thấy một viên khoáng thạch, đang tại hỏa diễm bên trong thiêu đốt, cực nóng sóng lớn không ngừng đánh tới, có điều, khoáng thạch nhưng không có mảy may dấu hiệu hòa tan."),t("br"),t("br"),n._v("“Tử Anh Thiên Hỏa thạch?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Tử Anh Thiên Hỏa thạch, mệnh danh bát tinh cấp bậc khó khăn nhất luyện hòa tan khoáng thạch, rất nhiều chân chính bát tinh luyện khí sư, đều khó mà luyện hóa, vị này thế mà để cho mình ngay từ đầu, liền luyện chế cái này, khảo hạch độ khó, quả nhiên không nhỏ."),t("br"),t("br"),n._v("Bất quá, có lợi có hại, loại này khoáng thạch khó mà luyện hóa, chỉ khi nào rèn luyện thành vũ khí, thấp nhất đều muốn trung phẩm Thánh khí đỉnh phong, hơi thủ pháp cao minh, nhẹ nhõm đạt tới thượng phẩm cấp bậc."),t("br"),t("br"),n._v("Có thể nói, đối phương cái khảo hạch này, khó khăn nhất cũng không phải là luyện chế ra thượng phẩm Thánh khí, mà là như thế nào hòa tan cái này viên khoáng thạch, cũng đem hắn chế tạo thành binh khí thích hợp."),t("br"),t("br"),n._v("“Tốt! Hòa tan Tử Anh Thiên Hỏa thạch khảo nghiệm nhất đối luyện khí lý giải, một ngày thời gian, ngươi đều hòa tan không được, cửa này cũng không cần qua!”"),t("br"),t("br"),n._v("Ngô Hữu Đạo khoát tay áo, không tiếp tục để ý Trương Huyền, tiếp tục tiến hành rèn đúc."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Biết đây là khảo nghiệm một loại, Trương Huyền cũng không nhiều lời, Minh Lý Chi Nhãn vận chuyển, nhìn sang."),t("br"),t("br"),n._v("Lô đỉnh cùng lửa than, không có vấn đề chút nào, chỉ là Tử Anh Thiên Hỏa thạch điểm nóng chảy quá cao, cần phải mượn địa hỏa mới có thể dung luyện, thứ này coi như thúc giục chân khí nhiều hơn nữa, cũng không đạt tới nhiệt độ."),t("br"),t("br"),n._v("Nhưng đối phương vẫn chưa cấp cho địa hỏa trận pháp, nói rõ chỉ có thể sử dụng những thứ này."),t("br"),t("br"),n._v("“Cái này khảo nghiệm có chút khó...”"),t("br"),t("br"),n._v("Không thể không nói, cái khảo hạch này, so với trong tưởng tượng còn muốn khó khăn."),t("br"),t("br"),n._v("“Bất quá, muốn làm khó ta, không có đơn giản như vậy...”"),t("br"),t("br"),n._v("Đổi lại một chút bát tinh luyện khí sư, gặp được loại tình huống này, đều sẽ cảm giác đến khó mà hoàn thành, nhưng đối Trương Huyền tới nói, cũng không có phức tạp như vậy."),t("br"),t("br"),n._v("Xem xong luyện khí sư công hội thư tịch, đối luyện khí lý giải, sớm đã đạt đến bát tinh đỉnh phong cấp bậc, so với một chút cửu tinh sơ kỳ đều không yếu, coi như không mượn thư viện, cũng ít nhất có mấy chục loại phương pháp, đem hòa tan."),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, đi tới lô đỉnh trước mặt, Trương Huyền ngón tay một chút, mấy chục đạo chân khí liền từ ngón tay bắn đi ra, đem lửa than bao quanh bao phủ."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Bị chân khí bao phủ, lửa than hỏa diễm giống như là truyền vào khí oxy đồng dạng, lập tức kịch liệt bùng cháy, do trước đó hỏa hồng thay đổi xanh thẳm, tản mát ra kinh người nhiệt lượng, dường như đem không khí đều đốt có chút vặn vẹo."),t("br"),t("br"),n._v("“Đây là... Bát tinh Tụ Linh trận?”"),t("br"),t("br"),n._v("Một bên đang tại đập Ngô Hữu Đạo sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("Rất nhiều lợi hại vũ khí phía trên, đều sẽ điêu khắc trận pháp đặc biệt phù văn, bởi vậy lợi hại luyện khí sư, đối với trận pháp đều mười phần tinh thông, vốn cho rằng đối phương sẽ tăng thêm một chút đặc thù tài liệu, để lửa than bùng cháy càng thêm dồi dào, nằm mơ đều không có nghĩ đến, trực tiếp bố trí một cái cỡ nhỏ Tụ Linh trận!"),t("br"),t("br"),n._v("Khỏi cần trận kỳ, chỉ bằng vào chân khí, tại như vậy trong phạm vi nhỏ, sắp đặt xuất trận pháp... Mấu chốt nhất tốc độ nhanh như vậy, làm sao làm được?"),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Dồi dào linh khí truyền vào bên dưới, lửa than nhiệt độ càng ngày càng cao, có điều, khoảng cách có thể luyện hóa Tử Anh Thiên Hỏa thạch, dường như còn có một số khoảng cách, tử sắc khoáng thạch phản xạ chói lọi, tại hỏa diễm bên trong thiêu đốt bên dưới, không có biến hoá quá lớn, vẫn như cũ đều không có muốn dấu hiệu hòa tan."),t("br"),t("br"),n._v("“Linh khí hội tụ tại trong ngọn lửa, cứ việc có thể để cho bùng cháy càng dốc sức, hỏa diễm càng tập trung cực nóng, nhưng muốn luyện hóa loại cấp bậc này khoáng thạch, vẫn còn có chút chênh lệch...”"),t("br"),n._v("Lắc đầu, Ngô Hữu Đạo cảm khái một tiếng, lời còn chưa dứt, chỉ thấy đối diện thanh niên, một chân đối trước mắt lô đỉnh đạp tới."),t("br"),t("br"),n._v("“Đang!” Một tiếng, lô đỉnh phát ra vang lên, đột nhiên vặn vẹo thân thể, liều mạng tụ lại hỏa diễm, vốn là thô to ngọn lửa lập tức biến thành to bằng mũi kim, kiếm mang, hướng về phía khoáng thạch cắt chém."),t("br"),t("br"),n._v("“Đây là... Tụ hỏa dung luyện pháp? Cần lô đỉnh vô điều kiện phối hợp, chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Ngô Hữu Đạo lần nữa ngây người."),t("br"),t("br"),n._v("Tụ hỏa dung luyện pháp, là đem hỏa diễm hội tụ tại một chỗ, để nhiệt độ lần nữa tăng lên, loại phương pháp này, cứ việc rất tốt, nhưng đối lô đỉnh yêu cầu cực lớn, cần tín nhiệm lẫn nhau cùng rất tốt phối hợp mới được, đối phương cùng trước mắt cái đỉnh này, mới thấy không đến mười mấy cái hô hấp, thế nào như thế nghe lời?"),t("br"),t("br"),n._v("Đang cảm thấy khó tin, chỉ thấy đứng tại cách đó không xa lô đỉnh, đột nhiên giãy dụa to béo thân thể, mấy bước đi tới trước chân, nắp lò mở ra, thanh âm như sấm vang lên: “Có thể hay không mượn ngươi Thanh Diễm thạch dùng một chút!”"),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Không nghĩ tới lô đỉnh cùng hắn nói chuyện, Ngô Hữu Đạo có chút sững sờ, còn chưa kịp trả lời, chỉ thấy lô đỉnh lần nữa vặn vẹo: “Cám ơn!”"),t("br"),t("br"),n._v("Nói xong, cũng mặc kệ hắn có đồng ý hay không, duỗi ra một cái chân vạc, tại trên bàn nhẹ nhàng vạch một cái."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Một viên khoáng thạch lăn vào đối phương trong lò lửa."),t("br"),t("br"),n._v("!"),t("br"),t("br"),n._v("Hỏa diễm cùng Thanh Diễm thạch tiếp xúc, lập tức bùng nổ bùng cháy, hỏa diễm nhiệt độ lần nữa bạo tăng."),t("br"),t("br"),n._v("“Còn cướp?”"),t("br"),t("br"),n._v("Lông mày nhảy loạn, Ngô Hữu Đạo da mặt không ngừng run rẩy."),t("br"),t("br"),n._v("Cái này lô đỉnh là của hắn, đi theo không biết bao nhiêu năm, lần khảo hạch này, làm cho đối phương sử dụng một chút mà thôi..."),t("br"),t("br"),n._v("Vốn cho rằng chỉ là miễn cưỡng mượn dùng, nằm mơ đều không có nghĩ đến, không chỉ giúp hắn thi triển tụ hỏa dung luyện pháp, càng trực tiếp cướp hắn khoáng thạch..."),t("br"),t("br"),n._v("Ngươi còn có biết hay không chủ nhân của mình ai?"),t("br"),t("br"),n._v("Chính khí không biết làm sao, lô đỉnh lần nữa nhìn qua: “Mượn ngươi máu tươi dùng một chút...”"),t("br"),t("br"),n._v("Nói xong, lô đỉnh bỗng nhiên khều lên, từ không trung thẳng tắp ngồi xuống."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Bị đập trúng ngực, Ngô Hữu Đạo máu tươi phun ra, huyết dịch đồng thời rơi vào đỉnh lô bên trong, hỏa diễm lần thứ ba bạo tăng."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Đi qua mấy lần nhiệt độ tăng thêm, Tử Anh Thiên Hỏa thạch không thể kiên trì được nữa, “Tạch tạch!” Một tiếng, xuất hiện vết rách, ngay sau đó trở nên mềm mại, dần dần hòa tan."),t("br"),t("br"),n._v("Nhìn thấy thành công, một bên Trương Huyền ánh mắt sáng lên, lần nữa đi tới trước mặt, ngón tay duỗi ra, chân khí tuôn ra, bỗng nhiên hướng về phía trước một chút."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Khoáng thạch theo lô đỉnh bên trong bay đi ra, cầm trong tay chuỳ sắt bỗng nhiên đánh, ba cái hô hấp về sau, lần nữa run lên, rèn luyện tốt khí thai lập tức rơi vào đặc thù lãnh dạ bên trong."),t("br"),t("br"),n._v("Một đám khói trắng sôi trào, rất nhanh, khí thai bị lấy ra đi ra, bàn tay run lên, Trương Huyền nhẹ nhàng cười một tiếng: “Luyện xong...”"),t("br"),t("br"),n._v("“Xong rồi?”"),t("br"),t("br"),n._v("Mang theo nghi ngờ, Ngô Hữu Đạo vội vàng nhìn, chỉ nhìn liếc mắt, da mặt cuồng rút: “Đây là... Vũ khí?”"),t("br"),t("br"),n._v("Chỉ thấy trên bàn một cái cục gạch, đặt ngang ở phía trên, vuông vức, nào có nửa điểm vũ khí bộ dáng."),t("br"),t("br"),n._v("(QQ đọc có cái nhà văn ca hát hoạt động, lão nhai tham gia, ngay cả hát năm đầu bài hát, khom khom kinh điển, khoáng tuyệt cổ kim, người cảm thấy, đã đạt đến bát tinh ma âm sư tiêu chuẩn, mọi người có thể đi nghe một chút, cho cái khen ngợi, chút cái tán. Dù sao, như thế toàn tài nhà văn, đã rất ít gặp.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1417"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1419"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);