(window.webpackJsonp=window.webpackJsonp||[]).push([[488],{843:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1416-theo-ta-đi-truong-gia"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1416-theo-ta-đi-truong-gia"}},[n._v("#")]),n._v(" Chương 1416: Theo ta đi Trương gia")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1416: Theo ta đi Trương gia"),t("br"),t("br"),n._v("Công pháp lâm công pháp, cứ việc đều rất mạnh, nhưng đếm kỹ căn nguyên, tất cả đều là căn cứ vào Khôi Thánh đám người truyền thừa."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, một khi xuất hiện cổ thánh cấp bậc công pháp, trên tấm bia đá ý niệm sẽ tự động bị áp chế, sinh lòng kính sợ..."),t("br"),t("br"),n._v("Vị này Trương sư quay một vòng, văn tự liền biến thành như vậy, chẳng lẽ tu luyện loại cấp bậc này pháp quyết?"),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mới nhìn không lên những công pháp khác, cái gọi là học tập, chỉ là tham khảo?"),t("br"),t("br"),n._v("“Dương sư đã đạt tới loại trình độ này ư?”"),t("br"),t("br"),n._v("Trong lòng phỏng đoán, tràn đầy kính sợ."),t("br"),t("br"),n._v("Vị này Trương Huyền là Dương sư học sinh, có thể học được loại cấp bậc này công pháp, cũng liền cho thấy, Dương sư có khả năng đạt đến loại cảnh giới này, hoặc là... Đạt được chân chính cổ thánh truyền thừa."),t("br"),t("br"),n._v("Bất kể bên nào, đều tuyệt không phải hắn có thể vọng thêm phỏng đoán."),t("br"),t("br"),n._v("“Chuyện này ngàn vạn không thể tiết lộ ra ngoài...”"),t("br"),t("br"),n._v("Lông mày nâng lên, Chiêm sư âm thầm cảnh giác."),t("br"),t("br"),n._v("Nếu như suy đoán là thật, chờ Trương Huyền đi ra, nhất định phải cố gắng khai báo, tuyệt đối không nên tùy ý hiển lộ công pháp, nếu không, bị Dị Linh tộc nhân hoặc là một ít sinh lòng ác ý chức nghiệp giả phát hiện, sinh mệnh đều sẽ xuất hiện nguy hiểm."),t("br"),t("br"),n._v("Hắn hiện tại cứ việc đạt đến chân chính Thánh vực cửu trọng, nhưng trên thực tế lại chỉ là nửa bước cửu tinh cấp bậc danh sư, chân chính cửu tinh, là vượt qua Thánh vực cửu trọng tồn tại."),t("br"),t("br"),n._v("Đạt tới loại thực lực này, nghe nói có thể có được thiên địa chi hỏa rèn luyện, bất kể thân thể vẫn là linh hồn, đều sẽ đạt được tẩy lễ, thậm chí liền tuổi thọ, đều sẽ lần nữa tăng thêm."),t("br"),t("br"),n._v("Loại tu vi này, đã là bách độc bất xâm, coi như trước kia thật trúng độc, cũng có thể bị ngọn lửa thiêu đốt sạch sẽ, lại không lưu lại bất cứ dấu vết gì."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, những năm này, hắn vẫn luôn muốn xung kích, đáng tiếc... Đã lâu như vậy, liền Thánh vực cửu trọng trung kỳ đều không có đạt tới."),t("br"),t("br"),n._v("Loại cảnh giới này, tuỳ tiện nhắc tới thăng một điểm, đều là to lớn bay vọt, lại không giống như kiểu trước đây dễ dàng."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Trong lòng đang cảm khái, chỉ thấy tĩnh thất gian phòng mở ra, một bóng người đi ra."),t("br"),t("br"),n._v("“Trương sư ngươi... Thực lực!”"),t("br"),t("br"),n._v("Quay đầu nhìn, con mắt lập tức trợn tròn, Chiêm sư thân thể nhoáng một cái, suýt chút nữa không có trực tiếp ngã sấp xuống."),t("br"),t("br"),n._v("Đối phương liền đi vào hai canh giờ trái phải, thế nào tu vi... Một lần từ nửa bước Lĩnh Vực cảnh, trực tiếp đạt tới Lĩnh Vực cảnh đỉnh phong?"),t("br"),t("br"),n._v("Cái này tương đương với tấn cấp một cái đại cấp bậc, có phần quá nhanh đi!"),t("br"),t("br"),n._v("“Có cảm giác ngộ, cho nên đột phá nhanh một chút, về sau, có thể sẽ càng ngày càng chậm...”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, Trương Huyền giải thích."),t("br"),t("br"),n._v("Thiên đạo công pháp, phối hợp đầy đủ linh thạch, tiến bộ xác thực đáng sợ, nhưng tu vi càng ngày càng cao, tốc độ tiến bộ, rõ ràng so trước kia muốn chậm, nói như vậy, cũng không tính lừa gạt."),t("br"),t("br"),n._v("“Tu luyện đột phá nhanh là chuyện tốt, nhưng cũng muốn chú ý có chừng có mực, quá nhanh, rất dễ dàng để chân khí trở nên phù phiếm...”"),t("br"),t("br"),n._v("Tràn đầy chấn kinh, một lát sau, Chiêm sư vẫn là an ủi nói."),t("br"),t("br"),n._v("Một ít quán đỉnh, kích hoạt huyết mạch cùng thể chất đặc thù, tiến bộ đều rất nhanh, trong thời gian ngắn liền có thể để tu vi đạt tới đỉnh phong, nhưng thật muốn làm như vậy, tu vi sẽ trở nên có chút phù phiếm, còn muốn tiến bộ, sẽ trở nên rất khó."),t("br"),t("br"),n._v("Thật giống như một số người đánh bạc trúng thưởng lớn, về sau rất khó tiếp tục đi làm dần dần từng bước công tác đồng dạng."),t("br"),t("br"),n._v("Chênh lệch quá lớn, tâm tính liền không chịu nổi."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, cho dù là kích hoạt huyết mạch cùng thể chất, dưới tình huống bình thường, cũng đều là tấn thăng một cái hoặc là mấy cái cấp bậc về sau, muốn dừng lại một đoạn thời gian, củng cố tu vi, có thể hoàn mỹ phát huy thực lực, lại tiếp tục đột phá."),t("br"),t("br"),n._v("“Ta biết, ta đã tại nửa bước Lĩnh Vực cảnh, củng cố hơn một ngày, thời gian không ngắn, cũng nên đột phá...”"),t("br"),t("br"),n._v("Trương Huyền thâm dĩ vi nhiên gật đầu."),t("br"),t("br"),n._v("“Một ngày?” Da mặt co lại, Chiêm sư suýt chút nữa không có ngã sấp xuống, nhịn không được nhìn qua: “Ngươi Thánh vực ngũ trọng củng cố bao lâu?”"),t("br"),t("br"),n._v("Nửa bước Lĩnh Vực cảnh, bình thường thiên tài đứng đầu, đều cần tiêu tốn thời gian mấy năm củng cố, ngươi một ngày thời gian còn kém không nhiều... Mấu chốt trên mặt vẫn là đương nhiên biểu lộ, có muốn hay không đánh như vậy đánh người?"),t("br"),t("br"),n._v("“Thánh vực ngũ trọng... Chậm trễ thời gian có chút nhiều, đại khái hơn hai mươi ngày, ta đi vào Thánh tử điện cùng ngày đột phá Xuất Khiếu cảnh...”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("“Hơn hai mươi ngày...”"),t("br"),t("br"),n._v("Lần nữa lắc lư, Chiêm sư nhìn qua: “Cái kia... Ngươi từ bắt đầu tu luyện tới hiện tại, đại khái hao tốn bao lâu thời gian?”"),t("br"),t("br"),n._v("“Từ tu luyện bắt đầu?”"),t("br"),n._v("Không nghĩ tới đối phương có thể như vậy hỏi, Trương Huyền tính toán một chút: “Đại khái khoảng mười ba tháng, hơn một năm!”"),t("br"),t("br"),n._v("Nếu không phải xuyên qua tới, tại Thiên Huyền vương quốc loại địa phương nhỏ này, mà là tại Thánh tử điện, chỉ sợ một tháng thời gian, liền có thể đạt tới thực lực bây giờ."),t("br"),t("br"),n._v("Trọn nhiều mười ba lần..."),t("br"),t("br"),n._v("Tưởng tượng, liền cảm thấy tràn đầy tâm tắc."),t("br"),t("br"),n._v("“...” Chiêm sư đột nhiên rất không muốn cùng trước mắt vị này nói chuyện."),t("br"),t("br"),n._v("Mười ba tháng, từ võ giả tu luyện tới Thánh vực lục trọng đỉnh phong... Tốc độ này, so Lạc gia tiểu công chúa đều nhanh không biết bao nhiêu, nhìn cái tên này biểu lộ, dường như cảm thấy quá chậm..."),t("br"),t("br"),n._v("Còn biết xấu hổ hay không?"),t("br"),t("br"),n._v("“Đúng rồi, Chiêm sư, ngươi nhưng có Thánh vực thất trọng công pháp bí tịch? Có thể hay không mượn một ít cho ta xem một chút?”"),t("br"),t("br"),n._v("Không để ý tới trong lòng đối phương suy nghĩ, Trương Huyền nói."),t("br"),t("br"),n._v("Đã tu vi đạt tới Lĩnh Vực cảnh đỉnh phong, nhìn một chút còn có thư tịch chưa, sưu tập ra vào Hư cảnh, còn thừa lại hai cái tuyệt phẩm linh thạch, thừa cơ đột phá được rồi."),t("br"),t("br"),n._v("Nhàn rỗi cũng là nhàn rỗi."),t("br"),t("br"),n._v("“Thánh vực thất trọng công pháp? Loại cấp bậc này, trên người của ta không có, có điều, muốn nhìn lời nói, vẫn như cũ muốn đi khôi lỗi ngõ hẻm, xông qua cửa ải, sẽ cho cùng một lần lựa chọn bí tịch cơ hội...”"),t("br"),t("br"),n._v("Chiêm sư lắc đầu, nói."),t("br"),t("br"),n._v("Hắn loại thực lực này, Thánh vực thất trọng công pháp đã không còn tác dụng gì nữa, đương nhiên sẽ không tùy thân mang theo."),t("br"),t("br"),n._v("“Ah!” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Khôi lỗi ngõ hẻm hắn đi qua một lần, hiện tại toàn bộ ngõ nhỏ đều là phế tích, thực sự thật không tiện lại đi một chuyến."),t("br"),t("br"),n._v("Bất quá, đối phương không có, cũng không tiện tiếp tục truy vấn, đang muốn tiếp tục nói chuyện, đột nhiên nhìn thấy trước mắt Chiêm sư lông mày giương lên, cổ tay lật qua lật lại bên trong, một cái đưa tin ngọc phù xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("“Hắn tìm ta làm cái gì?”"),t("br"),t("br"),n._v("Nhìn một cái ngọc phù, Chiêm sư nhíu nhíu mày, ngón tay ở phía trên một điểm, khuôn mặt trở nên càng thêm kỳ lạ: “Không phải tìm ta, là... Muốn gặp ngươi!”"),t("br"),t("br"),n._v("“Gặp ta?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ: “Ai?”"),t("br"),t("br"),n._v("“Kiếm Tần Sinh!” Chiêm sư nói."),t("br"),t("br"),n._v("“Hắn?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Kiếm Tần Sinh chính là đời thứ tư điện chủ Kiếm Lưu Thủy hậu bối tử tôn, Lưu Thủy kiếm quyết, chính là từ đối phương nơi đó học được."),t("br"),t("br"),n._v("Lần trước đem hắn đánh một trận tơi bời về sau, lại không tin tức, thế nào ngày hôm nay đột nhiên muốn tìm bản thân, hơn nữa đưa tin đều truyền đến Chiêm sư nơi này?"),t("br"),t("br"),n._v("“Ra ngoài đi, hắn ngay tại bên ngoài chờ lấy...”"),t("br"),t("br"),n._v("Chiêm sư nói."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Không biết đối phương mục đích, có điều, cấp cho hắn không ràng buộc quan sát thư tịch, mới có hiện tại kiếm đạo tu vi, trong lòng một mực mang theo cảm kích, đã muốn gặp, đương nhiên sẽ không chối từ."),t("br"),t("br"),n._v("Hai người rất nhanh rời đi nội viện, quả nhiên thấy Kiếm Tần Sinh đang đứng tại bên cạnh cái ao một cái trong lương đình, thấy bọn họ đi tới, ánh mắt sáng lên, vội vàng bay tới."),t("br"),t("br"),n._v("“Quyền điện chủ, Trương sư!”"),t("br"),t("br"),n._v("Liền ôm quyền, Kiếm Tần Sinh nhìn lại."),t("br"),t("br"),n._v("“Không biết tiền bối tìm ta làm cái gì?”"),t("br"),t("br"),n._v("Hành lễ xong xuôi, hàn huyên đôi câu, Trương Huyền mở miệng hỏi."),t("br"),t("br"),n._v("“Cái này... Là có chuyện muốn làm phiền ngươi!”"),t("br"),t("br"),n._v("Dừng lại một hồi, mang theo xấu hổ, Kiếm Tần Sinh mở miệng."),t("br"),t("br"),n._v("“Trương sư... Có thể hay không theo ta đi một chuyến Trương gia?”"),t("br"),t("br"),n._v("(Trương Huyền muốn đi Trương gia, đại cao trào sắp mở ra. Mọi người có thể chú ý lão nhai công chúng số, Wechat lục soát “Hoành Tảo Thiên Nhai”, tăng thêm chú ý là đủ.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1424"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1426"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);