(window.webpackJsonp=window.webpackJsonp||[]).push([[492],{847:function(n,h,i){"use strict";i.r(h);var g=i(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1420-hung-mong-kiem-thanh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1420-hung-mong-kiem-thanh"}},[n._v("#")]),n._v(" Chương 1420: Hưng Mộng kiếm thánh")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1420: Hưng Mộng kiếm thánh"),i("br"),i("br"),n._v("“Vì cái gì?”"),i("br"),i("br"),n._v("Sắc mặt tái xanh, Trương Huyền rời khỏi phẫn nộ."),i("br"),i("br"),n._v("Biết rõ không hạnh phúc, vẫn còn muốn đi làm, đường đường đại lục đệ nhị gia tộc, cũng không phải Trương gia phụ thuộc, liền điểm ấy tôn nghiêm cũng không cần ư?"),i("br"),i("br"),n._v("Nói thật, nếu không phải chính tai nghe được Lạc gia thiếu gia nói như vậy, cũng hoài nghi có phải hay không nghe được tin tức giả!"),i("br"),i("br"),n._v("Đây chính là thân muội muội của ngươi ah!"),i("br"),i("br"),n._v("“Có một số việc, ngươi bây giờ địa vị cùng thân phận, còn không thể biết, liền xem như ta, cũng kiến thức nửa vời, không rõ lắm.”"),i("br"),i("br"),n._v("Nhìn ra lửa giận của hắn, Lạc Huyền Thanh có chút chẳng biết tại sao, có điều, có việc cầu người, vẫn lắc đầu một cái: “Ta chỉ biết là, vì việc hôn sự này, gia tộc đã hi sinh quá nhiều, một khi cự tuyệt, không chỉ Lạc gia, nhân tộc cũng có thể xuất hiện nguy nan...”"),i("br"),i("br"),n._v("“Nhân tộc?” Trương Huyền da mặt run rẩy."),i("br"),i("br"),n._v("Liên cái hôn nhân mà thôi, liên không được nhân tộc còn có nguy hiểm, thật hay giả?"),i("br"),i("br"),n._v("Có muốn hay không khoa trương như vậy?"),i("br"),i("br"),n._v("Cái này có chút thái quá đi!"),i("br"),i("br"),n._v("“Được rồi, ở chung lâu như vậy, đối ngươi nhân phẩm cũng coi như hiểu rõ, có thể cùng ngươi lặng lẽ kể một ít, nhưng mà, chuyện này dính dáng thực sự quá lớn, không nên đem tin tức truyền ra, nếu không, tai hoạ về sau vô tận!”"),i("br"),i("br"),n._v("Nhìn đối phương bộ dạng, không nói rõ ràng, chắc chắn sẽ không giúp hắn giết người, chần chờ một chút, Lạc Huyền Thanh nói: “Thật ra thì... Trương gia cùng Lạc gia thông gia, vô cùng có khả năng quan hệ đến... Truyền thế thiên phù!”"),i("br"),i("br"),n._v("“Truyền thế thiên phù?” Trương Huyền trên đầu gân xanh bỗng nhiên nhảy lên."),i("br"),i("br"),n._v("Thứ này trước hắn liền nghe qua, là Khổng sư năm đó lưu lại, tìm kiếm Thánh điện, tìm tới Xuân Thu đại điển chìa khoá, thông gia thế mà liên lụy đến cái này?"),i("br"),i("br"),n._v("Nếu thật là dính dáng Khổng sư mạnh nhất pháp bảo, nói cùng nhân tộc số mệnh cùng vận mệnh liên quan đến, cũng không đủ."),i("br"),i("br"),n._v("“Đúng vậy a, có điều, tin tức này, ta cũng là từ đại trưởng lão cùng gia chủ một lần đối thoại nghe lén đến, tình huống cụ thể như thế nào, cũng không rõ ràng!”"),i("br"),i("br"),n._v("Lạc Huyền Thanh lắc đầu."),i("br"),i("br"),n._v("Đem muội muội tự tay đẩy vào hố lửa, hắn lại làm sao nguyện ý?"),i("br"),i("br"),n._v("Nếu không phải bị bất đắc dĩ, đừng nói là hắn, coi như tộc nhân khẳng định cũng không nguyện ý, dù sao, thiên phú như vậy, muội muội sau này thực lực, thậm chí có khả năng vượt qua tiên tổ..."),i("br"),i("br"),n._v("Thật muốn như vậy, Trương gia lại như thế nào?"),i("br"),i("br"),n._v("Không đáng kể chút nào."),i("br"),i("br"),n._v("Trừ phi, Trương gia vị kia tiểu thiên tài, so với nàng thiên phú còn đáng sợ hơn..."),i("br"),i("br"),n._v("Bất quá, khả năng này cực nhỏ, nhiều năm như vậy, hắn duy nhất thấy một cái thiên phú có thể cùng muội muội sánh ngang, cũng chỉ có trước mắt vị này Trương sư mà thôi."),i("br"),i("br"),n._v("“Nguyên nhân chính là như vậy, liền xem như ta, cũng không cách nào cự tuyệt, muội muội đồng dạng không cách nào cự tuyệt, mấu chốt nhất là, còn dính dáng nàng... Được rồi, Trương sư, ngươi hẳn là rõ ràng, chuyện này, ta không thể đi làm, những người khác lại không tin, chỉ có thể đến tìm ngươi!”"),i("br"),i("br"),n._v("Giải thích xong, Lạc Huyền Thanh nhìn lại."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Trương Huyền nói không ra lời."),i("br"),i("br"),n._v("Hắn biết đối phương khó xử."),i("br"),i("br"),n._v("Viên Hiểu, Bích Hồng Âm đám người, cùng đối phương quan hệ cứ việc rất tốt, nhưng mỗi một cái đều là thế lực lớn hậu nhân, chuyện này quan hệ Lạc gia cùng Trương gia danh dự, tự nhiên không thể xin nhờ bọn họ."),i("br"),i("br"),n._v("Có lẽ nhớ tới, đáng giá tín nhiệm, hơn nữa không có gia tộc bối cảnh, cũng chỉ có bản thân."),i("br"),i("br"),n._v("“Thế nào?”"),i("br"),i("br"),n._v("Lần nữa nhìn qua, Lạc Huyền Thanh một mặt mong đợi."),i("br"),i("br"),n._v("“Xin lỗi, ta thực sự không cách nào đồng ý!”"),i("br"),i("br"),n._v("Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Coi như sau lưng dính dáng lại lớn, dính dáng nhiều hơn nữa... Cho ta có liên can gì?"),i("br"),n._v("Dựa vào cái gì, để cho ta bỏ qua hạnh phúc, đi tác thành người khác?"),i("br"),i("br"),n._v("Lại nói, để cho mình chém giết bản thân... Coi như đồng ý, cũng làm không được ah!"),i("br"),i("br"),n._v("“Vì cái gì?”"),i("br"),i("br"),n._v("Trước mắt vị này, trước kia chỉ cần mở miệng, tất nhiên sẽ trực tiếp đồng ý, không nghĩ tới lần này lại từ chối không tiếp, Lạc Huyền Thanh lông mày nhăn lại."),i("br"),i("br"),n._v("“Không tại sao, là bởi vì... Ta hiện tại có chuyện, muốn đi một chuyến Trương gia, không có thời gian đi Thanh Nguyên đế quốc, đi thẩm tra ngươi nói vị này, càng không thời gian đi giết người!”"),i("br"),i("br"),n._v("Dừng lại một chút, Trương Huyền cuối cùng vẫn không có thừa nhận thân phận."),i("br"),i("br"),n._v("Thật muốn thừa nhận, cũng không phải ở trước mặt đối phương, mà là tại Trương gia, lấy một loại vương giả tư thái, mà không hiện tại!"),i("br"),i("br"),n._v("“Đi Trương gia? Đến đó làm gì?”"),i("br"),i("br"),n._v("Lạc Huyền Thanh cau mày."),i("br"),i("br"),n._v("“Trương gia tổ chức gia tộc đại hội, Kiếm Tần Sinh tiền bối hi vọng ta có thể giúp hắn khiêu chiến Hưng Mộng kiếm thánh hậu bối, báo thù rửa hận!” Trương Huyền giải thích."),i("br"),i("br"),n._v("“Kiếm Tần Sinh? Vị này kiếm lão quái? Ta biết chuyện của bọn hắn, không sai, cái này đích xác là hao tổn Trương gia mặt mũi cơ hội tốt, không thể không đi...” Sửng sốt một chút, Lạc Huyền Thanh bừng tỉnh hiểu ra."),i("br"),i("br"),n._v("“Giữa bọn hắn đến cùng chuyện gì? Thật chẳng lẽ chỉ là lĩnh ngộ chân giải khác biệt?”"),i("br"),i("br"),n._v("Trương Huyền nhìn qua, có chút hiếu kỳ."),i("br"),i("br"),n._v("Kiếm Tần Sinh lĩnh ngộ chân giải là Kiếm Lưu Thủy bộ kia, thuộc về loại hình phòng ngự, Hưng Mộng kiếm thánh kiếm pháp, cứ việc chưa có xem qua, lại có thể đoán được, hẳn là cùng hắn hoàn toàn khác biệt."),i("br"),i("br"),n._v("Nếu không, Trương gia kiếm đạo thiên tài Trương Húc muốn học tập, cũng sẽ không bị trực tiếp đuổi đi."),i("br"),i("br"),n._v("“Thật ra thì không nhiều phức tạp, ngươi cũng đã biết Hưng Mộng kiếm thánh, cái danh xưng này nguồn gốc?” Lạc Huyền Thanh nhìn qua."),i("br"),i("br"),n._v("Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Hắn chỉ biết là vị này Hưng Mộng kiếm thánh là Trương gia hiện nay quyền gia chủ, vị kia tiểu thiên tài cha ruột, còn tên thật tên gì, lại vì sao có cái danh xưng này, cũng không rõ ràng."),i("br"),i("br"),n._v("“Chẳng lẽ, tên của hắn kêu... Trương Hưng Mộng?”"),i("br"),i("br"),n._v("Trương Huyền cau mày."),i("br"),i("br"),n._v("Cái tên này, nói thật có chút quá phụ nữ hóa, hoàn toàn không phù hợp Trương gia gia chủ, siêu cấp cao thủ hình ảnh."),i("br"),i("br"),n._v("“Đương nhiên không phải là Trương Hưng Mộng, cái gọi là Hưng Mộng kiếm thánh, trên thực tế ẩn chứa hai người tên, kiếm thánh tên thật bên trong, mang một cái ‘Hưng’ chữ, vợ hắn tên bên trong mang một cái ‘Mộng’ chữ, cũng chính là cái gọi là Hưng kiếm thánh cùng Mộng kiếm thánh, hai người liên hợp, kiếm pháp mạnh mẽ, Danh Sư đường tổng bộ các trưởng lão, đều muốn kiêng kị mấy phần, không dám đối kháng chính diện!”"),i("br"),i("br"),n._v("Lạc Huyền Thanh giải thích, đồng thời ánh mắt lộ ra nồng đậm bội phục."),i("br"),i("br"),n._v("Trương Huyền tắc lưỡi."),i("br"),i("br"),n._v("Có thể trở thành Danh Sư đường tổng bộ trưởng lão, thực lực nhất định ứng vượt qua Thánh vực cửu trọng, mạnh như thế người, cũng vì đó kiêng kị... Hai người này liên thủ uy lực, quả thực mạnh mẽ."),i("br"),i("br"),n._v("“Đúng vậy a, hai người năm đó ngang dọc đại lục, hầu như không người có thể địch, coi như ngươi lão sư, cũng đối hắn tôn sùng rất nhiều! Về sau, Hưng kiếm thánh kế thừa Trương gia quyền gia chủ chi vị, đạt được Trương gia ảo diệu nhất truyền thừa, thực lực mạnh hơn, kiếm pháp cũng càng thêm tinh diệu... Mà Mộng kiếm thánh, thì rất ít xuất hiện, dần dà, Hưng Mộng kiếm thánh tên tuổi, liền toàn bộ rơi vào Hưng kiếm thánh trên người, ngược lại Mộng kiếm thánh, có rất ít người biết!”"),i("br"),i("br"),n._v("Lạc Huyền Thanh nói."),i("br"),i("br"),n._v("Thân là Lạc gia hạch tâm con cháu, những này mặc dù là bí văn, nhưng cũng biết rất rõ ràng."),i("br"),i("br"),n._v("“Thì ra là thế... Nhưng, cái này cùng Kiếm Tần Sinh tiền bối có quan hệ gì? Chẳng lẽ, lúc trước hắn từng bại bởi Hưng kiếm thánh, bị đánh rất thảm?”"),i("br"),i("br"),n._v("Tràn đầy nghi ngờ, Trương Huyền tiếp tục xem tới."),i("br"),i("br"),n._v("Không phải cực lớn thù hận, hẳn là sẽ không thừa dịp đối phương gia tộc đại hội thời điểm, tiến lên đi!"),i("br"),i("br"),n._v("Như vậy tương đương triệt để không nể mặt mũi."),i("br"),i("br"),n._v("“Bại? Nào chỉ là bại...”"),i("br"),i("br"),n._v("Lạc Huyền Thanh mặt mũi cười khổ: “Nếu như ta biết đến tin tức không có sai lầm, Kiếm Tần Sinh tiền bối, đã từng là... Mộng kiếm thánh điên cuồng theo đuổi người!”"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1428"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1430"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);