(window.webpackJsonp=window.webpackJsonp||[]).push([[499],{854:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1426-sinh-ma-lam-thanh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1426-sinh-ma-lam-thanh"}},[n._v("#")]),n._v(" Chương 1426: Sinh mà làm Thánh")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1426: Sinh mà làm Thánh"),i("br"),i("br"),n._v("“Phùng Tử Dật cũng xông qua danh sư lâu?”"),i("br"),i("br"),n._v("Một hồi yên lặng, Chiêm sư bọn người nói không ra lời nói tới."),i("br"),i("br"),n._v("Danh sư lâu vài vạn năm đến, hết thảy cũng liền ba người xông qua, mấy ngày nay làm sao vậy, một hơi xông hai cái, hơn nữa tất cả đều thành công?"),i("br"),i("br"),n._v("“Hắn dùng bao lâu thời gian?”"),i("br"),i("br"),n._v("Một vị trưởng lão nhịn không được hỏi."),i("br"),i("br"),n._v("Trương sư dùng một phút trái phải, cái này Phùng Tử Dật dùng bao lâu?"),i("br"),i("br"),n._v("“Trọn một ngày...”"),i("br"),i("br"),n._v("Nói chuyện trưởng lão giải thích nói."),i("br"),i("br"),n._v("“Một ngày thời gian, không tính quá nhanh, nhưng cũng coi như được tuyệt đỉnh, mới vừa trở thành bát tinh danh sư, chư vị điện chủ ý niệm liền chấn động, có thể hay không hắn liền là tân nhiệm điện chủ?”"),i("br"),i("br"),n._v("Chiêm sư phỏng đoán nói."),i("br"),i("br"),n._v("“Rất có thể!”"),i("br"),i("br"),n._v("Tất cả trưởng lão đồng thời gật đầu."),i("br"),i("br"),n._v("Trương sư cứ việc vượt quan tốc độ rất nhanh, nhưng xông qua về sau, đồng thời không có xuất hiện bất kỳ biến cố, mà vị này Phùng Tử Dật, mới vừa trở thành bát tinh danh sư, liền dẫn tới biến hóa như thế, chẳng lẽ... Hắn đã chiếm được Khôi Thánh công nhận, trở thành điện chủ duy nhất người ứng cử?"),i("br"),i("br"),n._v("“Vị này Phùng Tử Dật, khẳng định muốn quay về Danh Sư đường tổng bộ, làm sao... Thiết lập hắn vì người ứng cử?”"),i("br"),i("br"),n._v("“Vậy thì không biết, Khôi Thánh là Viễn Cổ Thánh giả, nhìn xa trông rộng, có lẽ thấy được chúng ta nhìn không ra sự tình!”"),i("br"),i("br"),n._v("“Nói thật, nếu như điện chủ thật ở thời đại này xuất hiện, ta cảm thấy Trương sư khả năng càng lớn! Nghe nói, hắn là Phùng Tử Dật sư huynh, bất kể cái nào một hạng, đều vượt qua người bình thường thực sự quá nhiều!”"),i("br"),i("br"),n._v("“Điện chủ tuyển chọn điều kiện, là nắm giữ Minh Lý Chi Nhãn, Trương sư cứ việc thiên tư vô song, so với Lạc gia tiểu công chúa đều không kém chút nào, có thể... Nếu như không có loại năng lực này, đồng dạng uổng công!”"),i("br"),i("br"),n._v("“Cũng phải...”"),i("br"),i("br"),n._v("Đám người nghị luận ầm ĩ, mỗi người mày nhíu lại nhanh."),i("br"),i("br"),n._v("Thật muốn có điện chủ người ứng cử, Trương sư cùng Phùng Tử Dật cũng có thể, cụ thể là ai, bọn họ cũng không dám xác nhận, có điều, phần lớn người đều khuynh hướng Phùng Tử Dật."),i("br"),i("br"),n._v("“Tám chín phần mười là Phùng Tử Dật, Trương sư hẳn là về sau muốn trở về tiếp nhận Dương sư địa vị...”"),i("br"),i("br"),n._v("Thương nghị một hồi, ai cũng không bỏ ra nổi chủ ý, Chiêm sư mở miệng nói: “Tốt rồi, như là đã xác nhận điện chủ người ứng cử, chuyện này, bất kỳ người nào cũng đừng truyền đi, về sau đối đãi vị này tương lai điện chủ, không thể có bất kỳ thờ ơ, bất kỳ cái gì nhu cầu, đều tận lực thỏa mãn...”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Đám người đồng thời gật đầu, ngậm miệng không nói."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Tòng Thánh tử điện đến Trương gia khoảng cách rất xa, cho dù ngồi chính là Kiếm Tần Sinh thú sủng, bay qua cũng cần tiếp cận một tháng."),i("br"),i("br"),n._v("Rảnh rỗi nhàm chán, nghiên cứu điện chủ lệnh, Trương Huyền bắt đầu củng cố tu vi."),i("br"),i("br"),n._v("Tốn ba ngày ba đêm, Lĩnh Vực cảnh đỉnh phong thực lực, triệt để củng cố, lại lần nữa rèn luyện một lần thân thể cùng nguyên thần, lúc này mới cảm thấy nhàn rỗi xuống."),i("br"),i("br"),n._v("Ngay sau đó cùng Kiếm Tần Sinh thảo luận kiếm pháp, người sau được lợi rất nhiều."),i("br"),i("br"),n._v("Lảo đảo, lại phi hành hơn hai mươi ngày."),i("br"),i("br"),n._v("“Lại có đại khái hai ngày thời gian, liền đến Trương gia...”"),i("br"),i("br"),n._v("Nhìn phía dưới liên miên chập trùng sơn mạch dòng sông, Kiếm Tần Sinh xác nhận một lần vị trí, tràn đầy kích động."),i("br"),i("br"),n._v("Hai ngày sau, liền có thể rửa sạch nhục nhã, đem cái kia không ai bì nổi, ưa thích trang bức Hưng Mộng kiếm thánh đạp tại dưới chân, suy nghĩ một chút đều cảm thấy tràn đầy kích động."),i("br"),i("br"),n._v("“Đối phương gia tộc đại hội, chúng ta liền như thế đi qua khiêu chiến, có thể hay không phá hư danh sư bên trong ‘Lễ’ ?”"),i("br"),i("br"),n._v("Trương Huyền nhìn qua."),i("br"),i("br"),n._v("Mặc dù rất muốn giáo huấn một lần Trương gia, nhưng dù sao cũng là danh sư, làm việc vẫn là muốn có chừng mực, không thể tùy ý mà làm."),i("br"),i("br"),n._v("“Thật ra thì, cũng không phải là ta nghĩ đi khiêu chiến, mà là bọn họ mời, Trương gia làm đại lục đệ nhất gia tộc, gia tộc thịnh hội, tự nhiên ý định lập uy... Có thể thừa cơ giáo huấn, không thể thích hợp hơn!”"),i("br"),i("br"),n._v("Kiếm Tần Sinh khẽ nói."),i("br"),i("br"),n._v("Cũng không phải là hắn muốn tới, mà là đối phương mời."),i("br"),n._v("Mục đích là vì hướng thế nhân biểu hiện ra Trương gia mạnh mẽ!"),i("br"),i("br"),n._v("Trước đó, chỉ có thể bị động trở thành đối phương bàn đạp, hiện tại có Trương sư, vị này lĩnh ngộ hai loại chân giải siêu cấp thiên tài, đối phương hậu bối, coi như lợi hại hơn nữa, lại như thế nào?"),i("br"),i("br"),n._v("Còn có thể so ra mà vượt hắn sao?"),i("br"),i("br"),n._v("“Mời?” Trương Huyền nghi ngờ."),i("br"),i("br"),n._v("“Đúng vậy a, Trương gia chỉ thích như vậy làm, mỗi một lần gia tộc đại hội, đều sẽ mời rất nhiều người, không riêng gì ta, ma âm, luyện đan, luyện khí, thuần thú... Rất nhiều chức nghiệp, đều sẽ nhận được mời, đáp ứng lời mời đi tới! Nói là tới dự lễ, trên thực tế thì là để người trẻ tuổi tiến hành đủ loại tỷ thí...”"),i("br"),i("br"),n._v("Mặc dù tràn đầy không vừa lòng, Kiếm Tần Sinh vẫn như cũ cũng không khỏi cảm khái: “Bất quá, mặc dù bọn hắn làm như vậy vì nổi bật Trương gia lợi hại, nhưng cũng không thể không bội phục, quả thực có mạnh mẽ vốn liếng... Cùng độ tuổi tu luyện giả, có thể vượt qua Trương gia thiên tài, xác thực không nhiều!”"),i("br"),i("br"),n._v("“Mời các đại chức nghiệp?”"),i("br"),i("br"),n._v("Trương Huyền sững sờ, ngay sau đó nhớ tới lúc trước Bích Hồng Âm."),i("br"),i("br"),n._v("Năm đó vị kia tiểu thiên tài ra đời thời điểm, hình như cũng mời không ít chức nghiệp giả đi tới, lão sư của nàng, đang tại trong đó."),i("br"),i("br"),n._v("“Lần trước Trương gia gia tộc đại hội, là tại 20 năm trước, vị kia tiểu thiên tài đầy tháng lễ bên trên, lúc ấy ta mấy cái học sinh, toàn bộ thua ở một người tay...”"),i("br"),i("br"),n._v("Nhớ tới lúc trước một màn, Kiếm Tần Sinh mặt mũi đắng chát."),i("br"),i("br"),n._v("Hưng Mộng kiếm thánh thân truyền, một người một kiếm, liền đem hắn tất cả học sinh đều đánh không có sức đánh trả, mặt mũi hoàn toàn không có."),i("br"),i("br"),n._v("Nguyên nhân chính là như vậy, những năm này, một mực nhẫn nhịn khẩu khí, không chỗ phát tiết."),i("br"),i("br"),n._v("Nếu không phải gặp được vị này Trương sư, khả năng đời này đều đừng hy vọng có thể báo thù rửa hận."),i("br"),i("br"),n._v("“Tiểu thiên tài đầy tháng lễ... Đúng, đã ngươi tham gia lần kia đại hội, có thể thấy được qua tiểu thiên tài?”"),i("br"),i("br"),n._v("Có chút hiếu kỳ, Trương Huyền nhịn không được nhìn qua."),i("br"),i("br"),n._v("“Đương nhiên gặp qua, mặc dù vừa ra đời không lâu sau, lại cho người ta một loại đủ lăng lệ chi ý, như là ẩn chứa thiên địa đại đạo, tùy tiện nhìn lên một cái, liền sẽ cảm thấy đạo tâm bất ổn, mặc cảm!”"),i("br"),i("br"),n._v("Nhớ tới hai mươi năm trước một màn, Kiếm Tần Sinh không cùng tự chủ cảm khái: “Không thể không nói, Hưng kiếm thánh cái tên này, không chỉ đối kiếm đạo lý giải không yếu, hài tử cũng là thật sẽ xảy ra... Ta còn chưa bao giờ thấy qua, thiên phú như vậy hài tử.”"),i("br"),i("br"),n._v("“Một cái vừa ra đời hài tử... Có thể có cái gì thiên phú? Cái này nói có chút khoa trương đi!”"),i("br"),i("br"),n._v("Trương Huyền vẻ mặt khó coi."),i("br"),i("br"),n._v("Nếu như cái tên này thật như này lợi hại, khẳng định đã sớm uy chấn thiên hạ, làm sao có thể một lần cũng không có xuất hiện qua?"),i("br"),i("br"),n._v("Trương gia cũng đối hắn ngậm miệng không nói, giữ kín như bưng?"),i("br"),i("br"),n._v("“Khoa trương?”"),i("br"),i("br"),n._v("Kiếm Tần Sinh lắc đầu: “Không có chút nào khoa trương!”"),i("br"),i("br"),n._v("“Ồ?”"),i("br"),i("br"),n._v("“Ta nói chính là sự thật, đã ngươi không tin, vậy ta hỏi ngươi, ngươi có thể thấy được qua, trời sinh Thánh giả?”"),i("br"),i("br"),n._v("Kiếm Tần Sinh nói."),i("br"),i("br"),n._v("“Trời sinh Thánh giả?” Trương Huyền cau mày, tràn đầy không hiểu."),i("br"),i("br"),n._v("“Vừa ra đời, liền đạt đến Thánh vực cấp bậc, sinh mà làm Thánh, căn bản khỏi cần tu luyện!”"),i("br"),i("br"),n._v("Kiếm Tần Sinh gật đầu."),i("br"),i("br"),n._v("“Ra đời liền là Thánh giả? Cái này... Cái này sao có thể?”"),i("br"),i("br"),n._v("Trương Huyền con ngươi co rụt lại."),i("br"),i("br"),n._v("Hắn vì tu luyện tới Thánh vực, hao tốn không biết bao nhiêu tâm huyết cùng cố gắng, hao phí hơn nửa năm, đối phương vừa ra đời liền trở thành Thánh giả, cái này... Làm sao có thể?"),i("br"),i("br"),n._v("Còn chưa từng nghe nói qua, có người có thể như vậy."),i("br"),i("br"),n._v("Coi như Lạc Nhược Hi, cũng không đạt tới đi!"),i("br"),i("br"),n._v("“Đúng vậy a, ta lần đầu tiên nhìn thấy, cũng cảm thấy khó tin, có thể sự thật liền là như vậy...”"),i("br"),i("br"),n._v("Cảm khái một tiếng, Kiếm Tần Sinh đột nhiên nhớ tới cái gì, nhìn tới tới: “Đúng rồi, suy nghĩ kỹ một chút, vị kia tiểu thiên tài dung mạo, cùng ngươi ngược lại là giống nhau đến mấy phần...”"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1434"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1436"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);