(window.webpackJsonp=window.webpackJsonp||[]).push([[50],{405:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1021-hen-ha-ton-tan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1021-hen-ha-ton-tan"}},[n._v("#")]),n._v(" Chương 1021: Hèn hạ Tôn Tấn")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Nghe xong những này, hắn đã cơ bản có thể xác định, đối phương trong miệng cái này Tôn Tấn luyện khí sư, căn bản là không có tính toán thu hắn làm đồ, mà là đem hắn trở thành tìm kiếm khoáng thạch công cụ!"),t("br"),t("br"),n._v("Trân quý khoáng thạch, bình thường đều là vỏ quả đất chấn động hình thành, sẽ cùng địa hỏa nương theo mà sinh, bởi vậy, người sau chỗ tụ tập, khoáng thạch tất nhiên rất nhiều."),t("br"),t("br"),n._v("Vị này Tôn Tấn, làm cho đối phương cố ý tu luyện cùng địa hỏa xung đột lẫn nhau chân khí, rất dễ dàng cảm ứng được hỏa diễm nồng đậm chỗ, tìm kiếm khoáng thạch liền sẽ làm ít công to, mà tu luyện loại này pháp quyết, điều khiển không được địa hỏa, luyện khí cũng đã thành không có khả năng!"),t("br"),t("br"),n._v("Nói cách khác, đối phương từ vừa mới bắt đầu bồi dưỡng, không có ý định để hắn trở thành luyện khí sư."),t("br"),t("br"),n._v("Hỏa Nguyên thành, trân quý khoáng thạch rất nhiều, luyện khí sư cho dù kiếm lại tiền, cùng bán ra những quáng thạch này so, cũng kém không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("“Làm người sư giả, thế mà làm ra loại sự tình này...”"),t("br"),t("br"),n._v("Trương Huyền giận dựng tóc gáy."),t("br"),t("br"),n._v("Không có bị hắn gặp được thì cũng thôi đi, đã gặp được, làm sao có thể bỏ qua!"),t("br"),t("br"),n._v("Loại này bại hoại, người người phải trừ diệt."),t("br"),t("br"),n._v("Đối phương mặc dù không phải danh sư, nhưng đánh lấy truyền thừa ngụy trang, làm lấy dạy hư học sinh chuyện, so với cái kia không chịu trách nhiệm lão sư, còn muốn đáng ghét."),t("br"),t("br"),n._v("Không nói cái khác, cùng người trung niên này đồng dạng hơn ba mươi vị học đồ, nhận được lục tinh luyện khí đại tông sư tự mình chỉ điểm, vốn cho rằng có thể dương danh lập vạn, càng đi càng xa, nằm mơ đều không có nghĩ đến, cuối cùng tất cả đều chết đang tìm kiếm khoáng thạch bên trên, cho dù trước mắt vị này may mắn còn sống, cuối cùng cũng không cách nào trở thành luyện khí sư, buồn bực suy nghĩ muốn tự sát."),t("br"),t("br"),n._v("Mà đối với Tôn Tấn tới nói, chỉ là thu một nhóm học đồ, không phải là chân truyền, cũng không phải giảng bài... Không thành được luyện khí sư, danh khí cũng không có tổn thất quá lớn thương, truyền đi ngược lại làm cho người cảm thấy, là đám người này, không hảo hảo học tập, đi theo lục tinh luyện khí sư sau lưng, đều chẳng làm nên trò trống gì, quả thực vô dụng..."),t("br"),t("br"),n._v("Vị này trung niên nhân, hẳn là chính là không chịu nổi loại này chỉ trích, mới nghĩ không ra..."),t("br"),t("br"),n._v("Không phải chính tai chỗ nghe, khả năng nằm mộng cũng nghĩ không ra đường đường Hỏa Nguyên thành luyện khí sư công hội, lại có thứ bại hoại như vậy."),t("br"),t("br"),n._v("Truyền đạo học nghề giải thích nghi hoặc, đều là sư giả."),t("br"),t("br"),n._v("Thân là thượng cửu lưu luyện khí sư, nếu quả thật muốn tuyển nhận tìm kiếm khoáng thạch tử sĩ, hẳn là cũng có thể tìm tới, chỉ bất quá, như vậy một khi bị phát hiện tất nhiên có nhục thanh danh, mà tuyển nhận một chút học đồ, trên danh nghĩa chỉ điểm, không chỉ danh dự không tổn hao gì, còn có thể tốt hơn điều động... Đợi sử dụng hết, đá một cái bay ra ngoài, như là cỏ rác, thật là độc ác."),t("br"),t("br"),n._v("“Cùng lục tinh luyện khí sư học nhiều năm như vậy, liền một kiện cấp thấp nhất phàm khí đều luyện chế không ra, đi đến bất kỳ địa phương nào, đều chịu đến cực lớn cười nhạo, thậm chí người nhà đều bị liên lụy, thà rằng như vậy, còn không bằng chết đi coi như xong!”"),t("br"),t("br"),n._v("Thấy ngăn cản hắn tự sát vị tiền bối này, cúi đầu trầm tư, trung niên nhân tưởng là đối phương đồng dạng không có cách nào, trong mắt tràn đầy thất vọng."),t("br"),t("br"),n._v("Nếu như chỉ là hắn bị người chế giễu ngược lại cũng thôi, mấu chốt nhất là, người nhà đều bị liên lụy, đây mới là hắn không thể thừa nhận."),t("br"),t("br"),n._v("“Không cần nản chí, ta đã tìm ra ngươi không cách nào luyện khí nguyên nhân căn bản! Chỉ cần tin tưởng lời của ta, không chỉ có thể để ngươi luyện khí thành công, còn có thể để ngươi trở thành ngũ tinh luyện khí sư, thậm chí đạt tới lục tinh!”"),t("br"),t("br"),n._v("Thấy hắn đấu chí hoàn toàn không có, bất cứ lúc nào cũng sẽ tìm chết, Trương Huyền an ủi."),t("br"),t("br"),n._v("Nói như vậy cũng không phải ăn nói bừa bãi, cố ý an ủi đối phương, mà là hắn có năng lực như thế."),t("br"),t("br"),n._v("Người khác sửa đổi công pháp rất khó, nhưng lấy kiến thức của hắn cùng ánh mắt, lần nữa sáng chế một bộ pháp quyết mới, để hắn học tập, sửa đổi trước đó tai hại, cũng không phải là không thể được."),t("br"),t("br"),n._v("Đương nhiên, điều kiện tiên quyết là phải biết hắn tu luyện môn công pháp này cụ thể là cái gì."),t("br"),t("br"),t("br"),n._v("Vừa rồi luyện khí, dùng Minh Lý Chi Nhãn tinh tế quan sát, đối phương tu luyện pháp quyết cũng không toàn, tìm không thấy kỹ càng, cho dù biết, đều không có phương pháp giải quyết."),t("br"),t("br"),n._v("“Trở thành ngũ tinh, lục tinh luyện khí sư?”"),t("br"),t("br"),n._v("Trung niên nhân sững sờ, lắc đầu, trong mắt thất vọng càng thêm nồng đậm."),t("br"),t("br"),n._v("Vốn cho rằng trước mắt cái này, thực lực mạnh mẽ, thật sự có phương pháp trợ giúp hắn, nằm mơ đều không có nghĩ đến, mới mở miệng cũng có chút nói bậy."),t("br"),t("br"),n._v("Liền nhất tinh đều không thành được, cái gì vũ khí đều không thể luyện chế, lại thế nào có thể trở thành ngũ tinh, thậm chí lục tinh luyện khí sư?"),t("br"),t("br"),n._v("Không phải đùa giỡn hay sao?"),t("br"),t("br"),n._v("“Ngươi có phải hay không cảm giác ta rất buồn cười, muốn tại ta trước khi chết, chế giễu một phen?”"),t("br"),t("br"),n._v("Nhìn lại, trung niên nhân thanh âm uể oải, giống như là mất đi toàn bộ đấu chí."),t("br"),t("br"),n._v("“Chế giễu?”"),t("br"),t("br"),n._v("Không nghĩ tới cái tên này đột nhiên nói ra lời này, Trương Huyền sững sờ, lập tức hiểu được."),t("br"),t("br"),n._v("Đối phương là cái liền nhất tinh luyện khí sư đều khảo hạch chẳng qua đồ bỏ đi, đối với hắn nói ngũ tinh, lục tinh, quả thực là quá mức."),t("br"),t("br"),n._v("Để cho người ta xem ra, rõ ràng tại đùa cợt, không phải trợ giúp."),t("br"),t("br"),n._v("“Yên tâm, ta nói có thể làm được, liền nhất định có thể làm được!”"),t("br"),t("br"),n._v("Cũng không giải thích, Trương Huyền trên mặt lộ ra nồng đậm tự tin."),t("br"),t("br"),n._v("Thực sự không giải quyết được, cùng lắm thì liền đem hắn tu vi toàn bộ phế bỏ, truyền thụ bản rút gọn Thiên Đạo công pháp, Hóa Phàm cửu trọng mà thôi, cho dù lại tu luyện từ đầu, hẳn là cũng không bao lâu."),t("br"),t("br"),n._v("“Nhất định có thể làm được? Liền Tôn Tấn luyện khí sư đều làm không được, để cho ta như thế nào tin tưởng ngươi? Ngươi thật sự là luyện khí sư?” Trung niên nhân ánh mắt lộ ra hoài nghi."),t("br"),t("br"),n._v("Chỉ có chân chính luyện khí sư, mới biết được luyện khí khó khăn, đối phương lời thề son sắt, nói cực kỳ dễ dàng, không phải là khoác lác a?"),t("br"),t("br"),n._v("“Đương nhiên!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Trương Huyền lấy ra luyện khí sư huy chương, phía trên sáu viên sao lấp lánh chói mắt."),t("br"),t("br"),n._v("“Lục tinh luyện khí sư?”"),t("br"),t("br"),n._v("Nhìn một cái, trung niên nhân lắc đầu: “Ta không tin, huy chương thứ này, có thể là người khác, cũng có thể là giả, trừ phi luyện khí cho ta nhìn... Có thể luyện chế ra Linh cấp tuyệt phẩm vũ khí, ta mới tin tưởng ngươi là lục tinh luyện khí sư!”"),t("br"),t("br"),n._v("Đối phương tuổi tác quá nhỏ, chỉ có chừng hai mươi tuổi, hắn gặp qua có thể đạt tới lục tinh luyện khí sư, nhỏ tuổi nhất đều không có thấp hơn năm mươi, tự nhiên khó tin."),t("br"),t("br"),n._v("Huy chương thứ này, muốn trước xác định đối phương cụ thể họ tên, mới có thể xác nhận có phải thật vậy hay không, hiện tại liền đối phương là ai đều không rõ ràng, cho dù cầm là của người khác đồ vật, hắn không phân biệt được ah."),t("br"),n._v("“Cái này...” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Hắn người luyện khí sư này, là mới vừa đi danh sư học viện liền khảo hạch, không thể giả được, bày ra cho đối phương nhìn cũng không có gì."),t("br"),t("br"),n._v("Hiện tại muốn làm, là làm cho đối phương tin tưởng mình, chuyện về sau mới tốt làm, nếu không, một lòng muốn chết, nói thiên hoa loạn trụy cũng không có cái gì tác dụng."),t("br"),t("br"),n._v("“Làm sao? Luyện chế không ra? Nói như vậy, lời nói mới rồi cũng là giả, chỉ là vì an ủi ta?”"),t("br"),t("br"),n._v("Thấy hắn chần chừ, trung niên nhân vẻ mặt càng ngày càng ảm đạm."),t("br"),t("br"),n._v("“Dĩ nhiên không phải, luyện khí với ta mà nói, không tính là gì!”"),t("br"),t("br"),n._v("Thấy đối phương đã mất đi lòng tin, không lộ một tay, cố gắng trước đó đều đem uổng phí, mỉm cười, Trương Huyền đi tới trước mặt."),t("br"),t("br"),n._v("“Mượn dùng ngươi khoáng thạch dùng một chút!”"),t("br"),t("br"),n._v("Đi tới góc tường giá đỡ trước mặt, đi hai bước, tuyển ra bốn loại khoáng thạch ném vào cách đó không xa luyện khí lô."),t("br"),t("br"),n._v("Chân khí cổ động, một lát sau, địa hỏa lập tức dâng lên, bốn khối khoáng thạch bị ngọn lửa thiêu đốt, lập tức bắt đầu hòa tan, lộ ra bên trong tinh thuần kim loại, chiếu lấp lánh."),t("br"),t("br"),n._v("Cái này bốn loại đều là ngoại giới hiếm thấy khoáng thạch, mỗi một loại đều có giá trị không nhỏ, thoáng cái lấy ra bốn cái, chỉ sợ cũng liền Hỏa Nguyên thành loại địa phương này mới có thể làm đến."),t("br"),t("br"),n._v("Năm ngón tay gảy nhẹ, để bốn loại kim loại tại hỏa diễm bên trong tiếp xúc, từ từ dung hợp, Trương Huyền nhẹ nhàng cười một tiếng, nhìn lại: “Luyện chế vũ khí, đơn giản nhất chính là rèn luyện khí thai, cái này trình tự, tốn thời gian nhiều nhất, nhưng nhất không biểu hiện luyện khí năng lực, ta liền không biểu hiện ra cho ngươi xem...”"),t("br"),t("br"),n._v("Nói chuyện, đầu ngón tay hội tụ chân khí, hướng cực nóng địa hỏa bên trong bỗng nhiên một chút, dung hợp tốt khoáng thạch, trực tiếp bay ra, rơi vào cách đó không xa trong ao."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Khói bụi dâng lên, lăng không một trảo, một cái dung hợp lại cùng nhau khối sắt lớn liền xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Đây là... Vũ khí?”"),t("br"),t("br"),n._v("Nhìn thấy cái này cục sắt, trung niên nhân khóe miệng giật một cái."),t("br"),t("br"),n._v("Đừng nói học đồ, cho dù tiền thối lại heo tới, luyện chế cũng so cái này đẹp mắt đi..."),t("br"),t("br"),n._v("Mặt mũi kỳ lạ, tiện tay nối qua, đang muốn nhìn thấy đối phương đến cùng luyện chế ra cái gì, con ngươi bỗng nhiên co rụt lại."),t("br"),t("br"),n._v("Hắn luyện khí trình độ mặc dù không cao, nhưng đi theo lục tinh luyện khí sư sau lưng hơn hai mươi năm, ánh mắt cùng kiến thức vẫn phải có."),t("br"),t("br"),n._v("Trước mắt cái này khối sắt mặc dù thoạt nhìn cực xấu xí, để cho người ta buồn nôn, cấp bậc dĩ nhiên đã đạt đến Linh cấp tuyệt phẩm."),t("br"),t("br"),n._v("Tiện tay cầm lấy khoáng thạch, liền rèn luyện đều không có rèn luyện, toàn bộ quá trình liền một phút đồng hồ cũng chưa tới, liền luyện chế ra tuyệt phẩm Linh khí... Đây cũng quá đáng sợ đi!"),t("br"),t("br"),n._v("Không cẩn thận luyện chế, đều lợi hại như vậy, một khi cẩn thận... Có thể luyện ra bao nhiêu lợi hại vũ khí?"),t("br"),t("br"),n._v("“Đúng ta có mắt không tròng...”"),t("br"),t("br"),n._v("Vội vàng hướng về phía trước, thành khẩn quỳ mọp xuống đất."),t("br"),t("br"),n._v("Giờ khắc này, tâm phục khẩu phục!"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Hắn mặc dù là không thể giả được lục tinh đỉnh phong luyện khí sư, nhưng... Chỉ ở dung hợp, tôi thép bên trên lợi hại, còn đơn giản nhất rèn luyện khí thai... Một khiếu không hiểu."),t("br"),t("br"),n._v("Lấy trình độ của hắn, cũng chỉ có thể luyện chế ra cục sắt, phức tạp hơn, liền không làm được."),t("br"),t("br"),n._v("Bất quá, chỉ bằng vào chiêu này, làm cho đối phương tin tưởng, cũng đã đầy đủ."),t("br"),t("br"),n._v("“Được rồi, đã ngươi xác định thân phận của ta, phải nghe theo ta phân phó, chỉ cần thành công, không chỉ giúp ngươi trở thành chân chính luyện khí sư, còn có thể giúp ngươi vạch trần người nào đó giả nhân giả nghĩa mặt nạ!”"),t("br"),t("br"),n._v("Thấy đối phương đối với mình tin tưởng không nghi ngờ, Trương Huyền nói."),t("br"),t("br"),n._v("“Vạch trần giả nhân giả nghĩa mặt nạ?” Trung niên nhân không hiểu."),t("br"),t("br"),n._v("“Không sai!” Trương Huyền gật gật đầu: “Không biết ngươi nói vị kia Tôn Tấn luyện khí sư, ở nơi nào?”"),t("br"),t("br"),n._v("“Hắn tại... Luyện khí sư công hội!” Trung niên nhân nhẹ gật đầu."),t("br"),t("br"),n._v("“Mang ta tới tìm hắn, một lát nữa, bất kể ta nói cái gì, làm cái gì, cũng không cần chen vào nói, nghe theo phân phó là được!”"),t("br"),t("br"),n._v("Trương Huyền khai báo."),t("br"),t("br"),n._v("Thân là Danh Sư đường đường chủ, Tôn Tấn loại này bại hoại, đã gặp gỡ, liền không thể nể quá hoá hỏng, nhất định phải để hắn nguyên hình vạch trần ở trước mặt mọi người, nếu không, để hắn tiếp tục gieo họa, còn không biết bao nhiêu người, phải bị mơ hồ."),t("br"),t("br"),n._v("Danh sư, không chỉ muốn bảo vệ nhân tộc, càng phải bảo vệ thuộc về “Sư” danh dự, tuyệt không thể để một con chuột cứt, hỏng sư đạo truyền thừa tôn nghiêm."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Trung niên nhân nhẹ gật đầu."),t("br"),t("br"),n._v("Cùng đối phương nói đồng dạng, hắn cùng lắm thì chính là chết một lần, không có gì đáng sợ, ngộ nhỡ trước mắt vị này có thể sáng tạo kỳ tích, giải quyết chính mình vấn đề, liền thật sự có cứu."),t("br"),t("br"),n._v("“Đem cái này khoáng thạch mang theo, đến lúc đó hữu dụng!”"),t("br"),t("br"),n._v("Vừa muốn ra khỏi phòng, Trương Huyền chỉ hướng trên giá Tử La kim."),t("br"),t("br"),n._v("Trung niên nhân nhẹ gật đầu, thu vào trữ vật giới chỉ, lúc này mới mấy bước đi tới phía trước dẫn đường."),t("br"),t("br"),n._v("Luyện khí sư công hội cách nơi này không xa, chỉ có ba con đường phố, thời gian không dài liền đến đến trong đó."),t("br"),t("br"),n._v("Mặc dù trời đã tối, nhưng thân là toàn bộ Hỏa Nguyên thành nóng nảy nhất chức nghiệp, nơi này vẫn như cũ đèn đuốc sáng trưng, dòng người như thoi đưa."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1029"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1031"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);