(window.webpackJsonp=window.webpackJsonp||[]).push([[503],{858:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1430-xong-tran-phap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1430-xong-tran-phap"}},[n._v("#")]),n._v(" Chương 1430: Xông trận pháp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1430: Xông trận pháp"),t("br"),t("br"),n._v("“Nhị Linh Ngự Thiên trận, cấp chín phòng ngự trận pháp, do Trương tổ sư sắp đặt mà thành, Thánh vực cửu trọng khó mà xông vào, trận pháp tụ tập, hình thành long hổ xu thế, bởi vậy, cũng kêu long hổ trận. Thiếu hụt: Thứ nhất, đối với linh khí tiêu hao rất nhiều; Thứ hai, trung tâm trận pháp, là Long linh cùng hổ linh duy trì, có thể để cho trận pháp linh hoạt đa dạng, nhưng cũng lại nhận linh tính ảnh hưởng; Thứ ba...”"),t("br"),t("br"),n._v("Về trận pháp tin tức cùng thiếu hụt, đi vào trong óc."),t("br"),t("br"),n._v("Hết thảy mười bảy chỗ thiếu hụt."),t("br"),t("br"),n._v("Cấp chín trận pháp, đã nắm giữ tự chủ linh tính, linh tính trấn áp, mới có thể cùng thực vật đồng dạng, kèm theo thời gian trôi qua, không ngừng tăng cường, cho dù có thiếu hụt, cũng không phải bình thường người có thể phát giác, thậm chí loại bỏ."),t("br"),t("br"),n._v("“Loại cấp bậc này trận pháp, tại cấp chín bên trong, đều tính được là khá cao...”"),t("br"),t("br"),n._v("Không nhìn không biết, xem xét mới hiểu được, rốt cuộc mạnh cỡ nào."),t("br"),t("br"),n._v("Cứ việc chưa có xem qua cửu tinh cấp bậc trận pháp thư tịch, nhưng trước mắt trận pháp này bất kể là biến hóa, vẫn là uy lực, đều vượt ra khỏi tưởng tượng ra."),t("br"),t("br"),n._v("“Nghiên cứu một chút...”"),t("br"),t("br"),n._v("Đang muốn cẩn thận nghiên cứu, chỉ thấy lời mới vừa nói lão giả, đi tới trước mặt, hai tay ôm quyền."),t("br"),t("br"),n._v("“Hai vị, còn mời đem trữ vật giới chỉ giao ra, yên tâm, chúng ta sẽ ở trước mặt phong ấn, sẽ không mở ra, cũng sẽ không kiểm tra, đợi rời đi thời điểm, lập tức trả lại...”"),t("br"),t("br"),n._v("“Giao ra nhẫn?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Thứ này chưa hề rời đi bản thân, còn không có đi vào Trương gia, liền để hắn giao ra, có phần quá bá đạo chút!"),t("br"),t("br"),n._v("“Không sai, đây là Trương gia quy định, muốn đi vào trận pháp, cần đem đồ vật của mình phong tồn. Yên tâm, Trương gia nắm giữ các ngươi cần có bất kỳ đồ vật, chỉ cần quen thuộc một chút liền có thể sử dụng, không cần lo lắng!”"),t("br"),t("br"),n._v("Lão giả nhẹ gật đầu."),t("br"),t("br"),n._v("“Đây cũng không phải là Trương gia quyết định quy củ, một ít Thánh địa, cũng có tương tự ràng buộc, chỉ cần là người ngoài, nhất định phải đem nhẫn gỡ xuống, thậm chí ngay cả mình bội kiếm đều không thể mang vào...”"),t("br"),t("br"),n._v("Thấy hắn vẻ mặt không tốt, Kiếm Tần Sinh đem chiếc nhẫn của mình lấy xuống, nhẹ nhàng đưa tới, rơi vào đối phương lòng bàn tay, gật đầu nói."),t("br"),t("br"),n._v("Hắn tới qua nơi này, biết quy củ."),t("br"),t("br"),n._v("Không riêng gì Trương gia, một ít Thánh địa, tỷ như Danh Sư đường tổng bộ, tiến vào bên trong phía trước, cũng nhất định phải đem nhẫn lấy xuống."),t("br"),t("br"),n._v("Vừa đến, đại biểu tôn trọng; Thứ hai, cũng chờ tại ngăn cản sạch nguy cơ, miễn cho xuất hiện biến cố."),t("br"),t("br"),n._v("Nghe nói năm đó liền có một vị danh sư, bị Dị Linh tộc nhân bắt giữ, dùng không gian gấp chứa đựng Dị Linh đại quân, từ đó làm cho một phương thế lực lớn, tổn thất nặng nề, suýt chút nữa toàn quân bị diệt."),t("br"),t("br"),n._v("Trương gia thân là đại lục đệ nhất gia tộc, cùng một ít Thánh địa không khác, coi như không sợ loại thủ đoạn này, cũng cần cẩn thận một chút."),t("br"),t("br"),n._v("“Nếu như ta không muốn lưu lại đâu?”"),t("br"),t("br"),n._v("Thấy được lão giả nhận lấy nhẫn, ngay sau đó phong ấn, cũng không dò xét, Trương Huyền vẫn là không nhịn được cau mày."),t("br"),t("br"),n._v("Trong nhẫn của hắn, nắm giữ Ngoan Nhân đầu lâu, Khưu Ngô cung di tích, cùng không ít bí mật... Không ít cũng không thể tiết lộ!"),t("br"),t("br"),n._v("Đối phương tuy nói phong ấn, nhưng... Không ở bên người, khó tránh khỏi sẽ không xuất hiện biến cố, lại nói, ngộ nhỡ lần này đi vào cùng đối phương gây ra mâu thuẫn, bọn họ lặng lẽ điều tra, bản thân nhất định gặp được càng ma túy hơn phiền."),t("br"),t("br"),n._v("Không phải không tin đối phương, mà là Trương gia tại trong ấn tượng của hắn, thực sự quá tiện, căn bản không đáng tín nhiệm."),t("br"),t("br"),n._v("“Người tới là khách, không muốn lưu lại cũng không sao, chỉ cần có thể thông qua  khảo hạch, mà không bị công kích, cũng có thể đem đồ vật mang vào!”"),t("br"),t("br"),n._v("Lão giả cũng không tức giận, mà là một mặt thản nhiên."),t("br"),t("br"),n._v("Nhìn tới không muốn đem nhẫn lưu lại người, cũng không ít, hắn đã sớm trải qua nhiều lần, có kinh nghiệm."),t("br"),t("br"),n._v("“Xông trận?”"),t("br"),t("br"),n._v("Trương Huyền nghi ngờ."),t("br"),t("br"),n._v("“Là dạng này, Trương gia cái này thủ hộ đại trận, chỉ cần vận chuyển, không chỉ có thể phòng ngự cường giả, ẩn chứa không gian năng lực đồ vật, cũng là có thể dò xét đi ra, không cho phép đi vào, cưỡng ép dẫn đi, sẽ gặp phải áp chế cùng công kích, Thánh vực cửu trọng cường giả, đều khó mà chống lại, xông trận, liền là mang theo những vật này, trực tiếp đi vào!”"),t("br"),n._v(" Kiếm Tần Sinh nói."),t("br"),t("br"),n._v("Đang tại nói chuyện, lại có người ngồi phi hành Thánh thú mà đến, cùng Trương Huyền đồng dạng, không nguyện ý giao ra nhẫn, nhấc chân hướng trận pháp vọt tới."),t("br"),t("br"),n._v("“Là liên minh đế quốc một vị trưởng lão, thực lực cùng ta không kém bao nhiêu!”"),t("br"),t("br"),n._v("Nhận ra thân phận của người này, Kiếm Tần Sinh thấp giọng."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Vị trưởng lão này vừa tiến vào trận pháp, bốn phía lập tức dấy lên hỏa diễm, một đầu xích kim sắc hỏa diễm cự long, từ trên trời giáng xuống, sắc bén móng vuốt, bỗng nhiên đè ép xuống, tựa như muốn đem không gian đều bóp nát."),t("br"),t("br"),n._v("Không nghĩ tới trận pháp vận chuyển cường đại như thế, trưởng lão mặt bên trên không chịu được có chút bối rối, cổ tay nâng lên, một thanh trường kiếm xuất hiện, nhất thời kiếm khí ngang dọc."),t("br"),t("br"),n._v("Không thể không nói, vị trưởng lão này thực lực, thập phần cường đại, coi như Địa Ngục Thanh Long thú tới đối đầu, đều muốn tạm thời tránh né, không cách nào chính diện ứng đối."),t("br"),t("br"),n._v("Bất quá, dù vậy, đối mặt hỏa diễm cự long, cũng có vẻ quá yếu, không đến hai cái hiệp, liền bị đánh trúng đầu, thẳng tắp từ không trung rớt xuống, ngã xuống đất, một thân cháy đen."),t("br"),t("br"),n._v("Trữ vật giới chỉ tại hỏa diễm công kích đến, cũng trực tiếp nổ tung, bảo vật mất trên đất, trong đó không ít, bởi vậy tổn hại, bị hao tổn cực lớn."),t("br"),t("br"),n._v("Trận pháp dừng lại, khôi phục như lúc ban đầu, Trương gia một vị lão giả, mặt không thay đổi đi tới trước mặt."),t("br"),t("br"),n._v("“Không còn nhẫn, hiện tại có thể tùy ý đi vào...”"),t("br"),t("br"),n._v("Điều chỉnh một hồi, khôi phục một chút nguyên khí, vị trưởng lão này lúc này mới khuôn mặt khó coi đi vào trong."),t("br"),t("br"),n._v("Quả nhiên trận pháp không còn công kích."),t("br"),t("br"),n._v("“Trương sư, đem nhẫn cho đi, chỉ là tỷ thí kiếm pháp, bọn họ sẽ chuẩn bị kỹ càng thích hợp trường kiếm, không cần lo lắng vũ khí chưa đủ vấn đề...”"),t("br"),t("br"),n._v("Thấy cảnh này, Kiếm Tần Sinh cũng khuôn mặt hơi trắng bệch, quay đầu nói ra."),t("br"),t("br"),n._v("Trước mắt vị trưởng lão này cùng thực lực của hắn tương đương, tiến vào trận pháp liền hai chiêu đều không có tiếp lấy, liền bị đánh thê thảm vô cùng, hắn đi vào, khẳng định cũng gần như, trước mắt vị này, coi như kiếm pháp Thông Huyền, thật sự là tu vi kém quá nhiều, thật muốn xông vào, nhất định thảm hại hơn!"),t("br"),t("br"),n._v("“Đem nhẫn giao ra, vẫn là xông một chút, theo ngươi ý nguyện, có điều, kết quả, chúng ta Trương gia không chịu trách nhiệm!”"),t("br"),t("br"),n._v("Vị trưởng lão này lần nữa nhìn lại, vẻ mặt thản nhiên."),t("br"),t("br"),n._v("Hắn tự tin trước mắt người thanh niên này, biết lựa chọn ra sao."),t("br"),t("br"),n._v("Mới vừa nhìn thấy một vị Thánh vực bát trọng siêu cấp cường giả bị đánh thê thảm vô cùng, hẳn là rõ ràng, cá nhân thực lực, căn bản không có khả năng chống lại Trương gia đại trận này."),t("br"),t("br"),n._v("“Kết quả không chịu trách nhiệm? Ngươi xác định?”"),t("br"),t("br"),n._v("Hít sâu một hơi, Trương Huyền ngẩng đầu lên."),t("br"),t("br"),n._v("“Ta xác định!” Vị trưởng lão này gật đầu."),t("br"),t("br"),n._v("“Ngươi có thể đương gia làm chủ?” Trương Huyền tiếp tục hỏi."),t("br"),t("br"),n._v("“Khụ khụ, Trương sư, vị này là Trương gia tam trưởng lão Trương Vô Trần, địa vị kính trọng... Hắn nói như vậy, tự nhiên có thể làm chủ!”"),t("br"),t("br"),n._v("Kiếm Tần Sinh giải thích."),t("br"),t("br"),n._v("“Trương gia tam trưởng lão? Địa vị xem như không thấp...”"),t("br"),t("br"),n._v("Lên tiếng, Trương Huyền mỉm cười: “Đã ngươi có thể làm chủ, hơn nữa cũng nói, kết quả Trương gia không chịu trách nhiệm, vậy ta quyết định... Xông trận pháp!”"),t("br"),t("br"),n._v("Trưởng lão đều theo thực lực, quyền lợi tiến hành phân cấp, có thể trở thành tam trưởng lão, vị này tại Trương gia địa vị, nhất định không thấp, đã nắm giữ quyền nói chuyện cùng chủ sự quyền."),t("br"),t("br"),n._v("“Xông trận pháp?”"),t("br"),t("br"),n._v("Kiếm Tần Sinh sắc mặt trắng nhợt, tràn đầy lo lắng: “Trương sư... Nghĩ lại!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1438"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1440"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);