(window.webpackJsonp=window.webpackJsonp||[]).push([[504],{860:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-135-ba-nam-phut-đong-ho-su-tinh"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-135-ba-nam-phut-đong-ho-su-tinh"}},[n._v("#")]),n._v(" Chương 135: Ba năm phút đồng hồ sự tình")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v(" “Tại hạ không biết đại nhân là vì ta chỉ điểm, có mắt mà không thấy núi thái sơn, mong rằng đại nhân chuộc tội!” "),g("br"),g("br"),n._v(" Tiến vào phòng, nhìn thấy ngồi ngay ngắn tại gian phòng chính giữa Trương Huyền, Lăng Thiên Vũ hết sức lo sợ."),g("br"),g("br"),n._v(" Ngày hôm qua hắn thái độ đối với vị danh sư này xác thực không được, lại vẫn hoài nghi, có thể làm cho vào cửa, đã xem như là không sai."),g("br"),g("br"),n._v(" “Không cái gì chuộc tội không chuộc tội!” Trương Huyền vung vung tay: “Ta không như thế hẹp hòi!” "),g("br"),g("br"),n._v(" Một bên Tôn Cường con mắt trợn tròn."),g("br"),g("br"),n._v(" Ngươi để người ta vứt tại trước cửa bị dầm sương một đêm, sáng sớm lại từ từ cơm nước xong mới để đi vào, này không gọi hẹp hòi?"),g("br"),g("br"),n._v(" “Vâng, là!” Lăng Thiên Vũ hơi đỏ mặt, chính muốn tiếp tục nói, liền thấy Trương Huyền xòe bàn tay ra, vẻ mặt hờ hững nhìn sang: “Biết ta tại sao để ngươi chờ ở bên ngoài một đêm sao?” "),g("br"),g("br"),n._v(" “Ta...” "),g("br"),g("br"),n._v(" Lăng Thiên Vũ một mặt lúng túng."),g("br"),g("br"),n._v(" Ngươi không liền cảm thấy cho ta thất lễ ngươi, cố ý cho ta hạ mã uy sao?"),g("br"),g("br"),n._v(" Ta biết sai rồi còn không được sao?"),g("br"),g("br"),n._v(" “Có phải là cảm thấy ta cố ý làm khó dễ ngươi?” Trương Huyền nhìn sang."),g("br"),g("br"),n._v(" “Không... Thất lễ!” Lăng Thiên Vũ liền vội vàng lắc đầu."),g("br"),g("br"),n._v(" “Không cần phủ nhận, nghĩ như vậy cũng rất bình thường!” Trương Huyền tựa hồ cùng không tức giận, trong thanh âm mang theo tiếng nói nặng sâu xa: “Kỳ thực, để ngươi ở bên ngoài nghỉ ngơi một đêm, là muốn tốt cho ngươi!” "),g("br"),g("br"),n._v(" “Vì muốn tốt cho ta?” "),g("br"),g("br"),n._v(" Lần này không những Lăng Thiên Vũ nghi hoặc, một bên Tôn Cường cũng đầy mặt quái lạ."),g("br"),g("br"),n._v(" Để cho người khác đứng ở trước cửa các một đêm, là vì tốt cho hắn?"),g("br"),g("br"),n._v(" Đây là cái gì lý luận?"),g("br"),g("br"),n._v(" “Nếu như ta không nhìn lầm, thê tử ngươi đã bệnh nặng bất cứ lúc nào cũng sẽ tử vong, mà ngươi tìm đến ta mục đích, là không phải là muốn ta ra tay, cứu nàng một mạng?” Trương Huyền nhìn sang."),g("br"),g("br"),n._v(" “Vâng!” "),g("br"),g("br"),n._v(" Nghe được đối phương một miệng nói ra ý nghĩ của chính mình, Lăng Thiên Vũ chấn động toàn thân."),g("br"),g("br"),n._v(" “Muốn cho ta cứu người, cần phải đem thê tử ngươi tiếp lại đây, nhưng ngươi hôm qua vẫn như cũ cùng Đề Nam Huyết Ngọc tiếp xúc, trong cơ thể nhưng còn có thôn phệ người tinh khí thần khí tức, nếu như ta đáp ứng, lần thứ hai cùng nàng tiếp xúc, e sợ còn không đưa tới, người cũng đã ngã xuống.” "),g("br"),g("br"),n._v(" Trương Huyền cảm thán một tiếng, nói tiếp: “Vì lẽ đó, để ngươi đứng bên ngoài một đêm, thứ nhất, có thể đem trong cơ thể đặc thù khí tức triệt để toả ra sạch sẽ; Thứ hai, cũng có thể cho ngươi không nên gấp gáp về nhà, tránh khỏi nguy hiểm phát sinh. Đương nhiên, nếu như không nghe lời, xoay người rời đi, thê tử ngươi hẳn phải chết, sau đó tự nhiên cũng là không cần trở lại, người chết coi như là danh sư, cũng không cách nào cứu sống.” "),g("br"),g("br"),n._v(" Nghe nói như thế, Lăng Thiên Vũ biến sắc mặt, mồ hôi lạnh chảy ròng ròng."),g("br"),g("br"),n._v(" Kỳ thực đối phương để hắn đứng bên ngoài suốt cả đêm, sâu trong nội tâm hay là rất căm tức, tuy rằng ngoài miệng không dám nói, đáy lòng nhưng có chút không phục."),g("br"),g("br"),n._v(" Danh sư, không những có thể chỉ điểm, năng lực mạnh, càng phải có phong độ, không thể bởi vì một điểm việc nhỏ, liền tính toán chi li."),g("br"),g("br"),n._v(" Mà vị danh sư này, dưới cái nhìn của hắn, rõ ràng chính là cố ý chỉnh người, để hắn khó coi."),g("br"),g("br"),n._v(" Nghe đến nơi này, mới rõ ràng... Thực sự là hiểu lầm, đối phương thực sự là để tâm lương khổ."),g("br"),g("br"),n._v(" Như vậy đối với người ta, đối phương còn như vậy vì chính mình suy nghĩ..."),g("br"),g("br"),n._v(" Thực sự là danh sư phong độ, để người tin phục."),g("br"),g("br"),n._v(" Cùng hắn so sánh, thực sự là tiểu nhân chi tâm đo lòng quân tử."),g("br"),g("br"),n._v(" Cảm kích đồng thời, cũng âm thầm vui mừng."),g("br"),g("br"),n._v(" Cay đắng thiệt thòi tối hôm qua chịu đựng, không phải vậy, thật phải đi về, nhất định sẽ hối hận một đời."),g("br"),g("br"),n._v(" Một bên Tôn Cường cũng con mắt trợn tròn."),g("br"),g("br"),n._v(" Lão gia chính là lão gia, danh sư chính là danh sư, mỗi một bước đều có thâm ý."),g("br"),n._v(" Vốn là cũng cho rằng là cố ý chỉnh hắn, bây giờ mới biết, lão gia ý nghĩ không phải hắn loại này tiểu nhân vật có thể phỏng đoán."),g("br"),g("br"),n._v(" “Đa tạ Đại nhân!” "),g("br"),g("br"),n._v(" Lăng Thiên Vũ liền muốn bên dưới bái."),g("br"),g("br"),n._v(" “Đi đem người mang tới đi, để ta xem một chút, nếu như đã bệnh đến quá nặng, ta e sợ cũng không có cách nào.” Trương Huyền xua tay."),g("br"),g("br"),n._v(" “Vâng!” Lăng Thiên Vũ kích động con mắt một đỏ, vội vàng mang theo Trình Viễn đại sư lùi ra."),g("br"),g("br"),n._v(" Hai người lui ra, Trương Huyền nhìn về phía một bên Tôn Cường."),g("br"),g("br"),n._v(" “Tiểu Cường, ta đáp ứng ngươi giúp ngươi thăng cấp võ giả tứ trọng, đương nhiên sẽ không tư lợi mà bội ước, đánh một bộ quyền pháp, để ta nhìn ngươi một chút trạng thái!” "),g("br"),g("br"),n._v(" “Vâng, lão gia!” "),g("br"),g("br"),n._v(" Nghe đến lão gia phải giúp hắn thăng cấp, Tôn Cường hưng phấn gật đầu liên tục."),g("br"),g("br"),n._v(" Rất nhanh một bộ quyền pháp đánh xong, Tôn Cường không những không có hưng phấn, còn lộ ra vẻ lúng túng: “Lão gia, ta ngày hôm qua bị thương... Thực lực giảm mạnh, xung kích Bì Cốt cảnh mà nói, e sợ ngày hôm nay lực không có đến...” "),g("br"),g("br"),n._v(" Đột phá cảnh giới, đều muốn điều chỉnh trạng thái, đạt đến cao nhất thời gian mới có thể thừa thế xông lên, ngày hôm qua mới vừa bị đánh, trên người còn có tổn thương, tinh lực tất nhiên không đủ, e sợ lão gia chỉ điểm lợi hại đến đâu, cũng rất khó thành công."),g("br"),g("br"),n._v(" Không đánh quyền còn không biết, vừa động thủ, lập tức cảm thấy thương thế đau đớn, chân khí vận chuyển đều có chút không thông suốt."),g("br"),g("br"),n._v(" “Không cần lo lắng!” Trương Huyền đánh gãy hắn, cười nhìn sang: “Ngươi sở dĩ nhiều như vậy năm thẳng vây ở Chân Khí cảnh, là bởi vì tu luyện chân khí quá mức vẩn đục, không cách nào thông suốt kinh mạch, làm được bổ dưỡng thân thể, thẳng tiếp tục như vậy, chân khí thậm chí sẽ lắng đọng, lại không cách nào tiến bộ là việc nhỏ, thậm chí còn sẽ lùi về sau.” "),g("br"),g("br"),n._v(" “Vâng!” Tôn Cường gật đầu."),g("br"),g("br"),n._v(" Gần nhất hai năm, hắn xác thực cảm thấy thể lực đang không ngừng suy giảm, thực lực đang thong thả tiến bộ, nếu không là liều mạng tu luyện giữ lại, e sợ hiện tại cũng có thể rơi xuống Chân Khí cảnh sơ kỳ."),g("br"),g("br"),n._v(" “Võ giả tứ trọng Bì Cốt cảnh, chân khí thông suốt toàn thân, để bắp thịt màng xương sức mạnh tăng cường, dựa theo đạo lý, của ngươi vẩn đục chân khí tốt rất khó chảy qua toàn thân, nhưng hiện tại không giống! Hôm qua bị đánh, thân thể bị hao tổn, trước lắng đọng chân khí cũng bị đánh văng ra, kinh mạch ràng buộc thông suốt không ít...” "),g("br"),g("br"),n._v(" “Lão gia... Ngày hôm qua là... Cố ý để ta chịu đòn?” "),g("br"),g("br"),n._v(" Trương Huyền lời còn chưa nói hết, Tôn Cường chấn động toàn thân, cảm động nước mắt chảy ròng."),g("br"),g("br"),n._v(" Trước lão gia cố ý để Lăng Thiên Vũ chờ ở ngoài cửa, là vì để cho hắn tốt, vốn tưởng rằng để ta chịu đòn, là bởi vì sai lầm, bây giờ mới biết... Cũng là vì ta!"),g("br"),g("br"),n._v(" Thương xót ta hiểu lầm hắn gạt ta..."),g("br"),g("br"),n._v(" “Ế?” "),g("br"),g("br"),n._v(" Nhìn thấy bản thân vị này mập quản gia, một lần cảm động lệ nóng doanh tròng, Trương Huyền có chút không hiểu rõ nổi."),g("br"),g("br"),n._v(" Ta chỉ là muốn nói ngươi bị thương không ảnh hưởng tu luyện, làm sao... Ngươi nghĩ tới đây đi tới? Đầu óc cũng quá phát tản đi đi..."),g("br"),g("br"),n._v(" Bất quá như vậy cũng tốt, có thể càng tốt hơn thành lập ta danh sư uy nghiêm..."),g("br"),g("br"),n._v(" “Khặc khặc!” Lúc này ho khan một tiếng: “Không sai, ngươi có thể lĩnh ngộ, cũng không uổng phí ta một phen bồi dưỡng. Được rồi, không cần phải nói những cái này, nếu như ta không đoán sai, Lăng Thiên Vũ chắc là cho ngươi đan dược chữa trị vết thương đi!” "),g("br"),g("br"),n._v(" “Ta...” Tôn Cường hơi đỏ mặt."),g("br"),g("br"),n._v(" Lão gia vì ta không ngại bị hiểu lầm, ta còn không thấy ngại bắt người ta đan dược..."),g("br"),g("br"),n._v(" Người và người so thực sự là khác biệt rất lớn..."),g("br"),g("br"),n._v(" “Không có gì, chỉ cần trung thành vì ta làm việc, nắm ít thứ cũng không sao.” Đoán ra hắn đang suy nghĩ gì, Trương Huyền đánh gãy: “Đem đan dược lấy ra, chuẩn bị một chút, hiện tại liền chuẩn bị xung kích võ giả tứ trọng.” "),g("br"),g("br"),n._v(" “Hiện tại liền xung kích?” Không nghĩ tới nhanh như vậy, Tôn Cường có chút không thích ứng: “Lão gia, chẳng lẽ không cần tìm cái tĩnh thất? Ở đây xung kích, ta sợ còn không thành công, Lăng đại nhân liền đến, làm lỡ chuyện của ngươi...” "),g("br"),g("br"),n._v(" “Không cần, ba, năm phút đồng hồ liền giải quyết sự tình, tìm cái gì tĩnh thất.” Trương Huyền xua tay."),g("br"),g("br"),n._v(" “Ba, năm phút đồng hồ? Liền có thể... Đột phá?” "),g("br"),g("br"),n._v(" Tôn Cường sợ hết hồn, con mắt trợn tròn, không thể tin được nhìn về phía trước mắt lão gia."),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-143"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-145"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);