(window.webpackJsonp=window.webpackJsonp||[]).push([[506],{861:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1432-đai-truong-lao-phong-thai"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1432-đai-truong-lao-phong-thai"}},[n._v("#")]),n._v(" Chương 1432: Đại trưởng lão phong thái")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1432: Đại trưởng lão phong thái"),t("br"),t("br"),n._v("Trương gia trải qua hơn vạn năm sinh sôi, so với phong hào đế quốc Đế đô, càng thêm bao la, cái này Nhị Linh Ngự Thiên trận cứ việc xây dựa lưng vào núi, bao phủ cực kỳ rộng rãi phạm vi, lại cũng chỉ là bảo vệ Trương gia cửa vào."),t("br"),t("br"),n._v("Chính là bởi vì có này thủ hộ, mới có thể sừng sững vài vạn năm, lông tóc không thương, dù là trải qua mấy lần nguy cơ, đều không có người có thể chân chính rung chuyển, tạo thành tổn thương."),t("br"),t("br"),n._v("Vốn cho rằng, tiên tổ xây dựng trận pháp này, vững như thành đồng, loại trừ chân chính cường giả đỉnh phong, không người có thể phá hư, không nghĩ tới một cái chỉ có Lĩnh Vực cảnh đỉnh phong gia hỏa, một chân đạp xuống đi, liền muốn duy trì không được, bất cứ lúc nào cũng sẽ sụp đổ."),t("br"),t("br"),n._v("“Dừng tay...”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa vội vàng hét lớn."),t("br"),t("br"),n._v("Bất quá, lúc này đã chậm, trận pháp tại kịch liệt lắc lư bên dưới, không thể kiên trì được nữa, trong nháy mắt biến mất, rất nhiều kiến trúc rầm rầm sụp đổ."),t("br"),t("br"),n._v("Khói bụi bay lên, vô số thân ảnh vọt ra, từng cái trợn mắt hốc mồm, không biết xảy ra chuyện gì."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Trương Vô Trần chỉ cảm thấy toàn thân mềm nhũn, té ngã trên đất."),t("br"),t("br"),n._v("Giờ phút này rốt cuộc minh bạch, đối phương trước đó vì sao dặn dò, hắn có thể chịu trách nhiệm đây... Nguyên lai, đã sớm dự liệu được tình huống trước mắt!"),t("br"),t("br"),n._v("Thủ hộ gia tộc đại trận sụp đổ, vô số kiến trúc hủy đi... Coi như hắn là tam trưởng lão, cũng đảm đương không nổi!"),t("br"),t("br"),n._v("Có thể nói đối Trương gia danh dự, đều là một đánh lớn đánh!"),t("br"),t("br"),n._v("Kiếm Tần Sinh cũng cổ co rút lại một chút, thân thể cứng đờ."),t("br"),t("br"),n._v("Hắn rất thống hận Hưng kiếm thánh cướp đi ưa thích người, hận không thể tới Trương gia làm loạn, để hắn rất mất thể diện, có thể... Còn không có đi vào đối phương gia tộc, liền làm trận pháp sụp đổ, vô số kiến trúc hủy đi, làm ra động tĩnh lớn như vậy..."),t("br"),t("br"),n._v("Trực tiếp dọa đến khuôn mặt bạc màu."),t("br"),t("br"),n._v("Trong lòng không khỏi cảm thấy rất ngờ vực, không biết đến cùng xin kẻ trước mắt này tới, là làm đúng, vẫn là làm sai."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Cùng đám người chấn kinh khác biệt, Trương Huyền sửng sốt một chút, ngay sau đó giật mình."),t("br"),t("br"),n._v("Hắn trước kia không tiếp xúc qua cấp chín trận pháp, cũng không biết uy lực như thế nào, hiển nhiên cũng không nghĩ tới, đột nhiên để hắn sụp đổ, sẽ tạo thành lớn như thế uy thế."),t("br"),t("br"),n._v("Bất quá, tạo thành liền tạo thành, dù sao tới mục đích đúng là đá quán, đối phương đều thừa nhận có thể phụ trách, cũng liền không cần thiết che giấu."),t("br"),t("br"),n._v("Có bao lớn năng lực, liền thi triển bao nhiêu năng lực!"),t("br"),t("br"),n._v("Không ép vị kia trang bức tiểu thiên tài, chủ động hiện thân, liền không gọi Trương Huyền!"),t("br"),t("br"),n._v("“Tam trưởng lão, chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Trong lòng đang cảm khái, liền nghe đến một hồi dồn dập tiếng gió vang lên, ngay sau đó một bóng người bất ngờ xuất hiện tại đầy đất phế tích trước mặt, vẻ mặt vô cùng nghi hoặc nhìn lại."),t("br"),t("br"),n._v("“Bẩm báo đại trưởng lão... Ta ở đây thu lấy trữ vật giới chỉ, vị này Thánh tử điện tới thiên tài, muốn xông trận pháp, kết quả... Liền biến thành như vậy!”"),t("br"),t("br"),n._v("Trương Vô Trần vội vàng đem sự tình nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Xông trận pháp?”"),t("br"),t("br"),n._v("Đại trưởng lão cũng là ngẩn ngơ, lẫn nhau vội vàng hướng Trương Huyền nhìn sang, chỉ thấy thanh niên, một mặt thản nhiên nhìn qua, không khẩn trương chút nào, lông mày nhíu, nhịn không được mở miệng: “Còn không biết vị bằng hữu này thân phận?”"),t("br"),t("br"),n._v("“Tại hạ Trương Huyền, lần này cùng sư đệ Kiếm Tần Sinh tới, mở mang kiến thức một chút Trương gia tuyệt đỉnh kiếm thuật!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Trương Huyền?”"),t("br"),t("br"),n._v("Đại trưởng lão mặt mũi nghi ngờ, có điều, không có nói nhiều, mà là quay đầu khoát tay áo: “Vô Chân, mang vị này Trương Huyền cùng Tần Sinh kiếm thánh đi phòng khách nghỉ ngơi!”"),t("br"),t("br"),n._v("“Đại trưởng lão...”"),t("br"),t("br"),n._v("Nghe nói muốn cho đối phương nghỉ ngơi, không đi so đo làm sập nhiều như vậy kiến trúc, tiêu hủy trận pháp sự tình, Trương Vô Trần một mặt lo lắng."),t("br"),t("br"),n._v("“Tốt rồi, đã vị này Trương Huyền bạn nhỏ, thông qua quy định, xông qua trận pháp, mặc kệ kết quả như thế nào, đều là chúng ta Trương gia, hẳn là gánh chịu!”"),t("br"),t("br"),n._v("Vẻ mặt thản nhiên, đại trưởng lão nhìn không ra mừng buồn."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Ôm quyền khom người, Trương Vô Trần mặc dù không cam tâm, nhưng cũng không có bất kỳ biện pháp nào."),t("br"),n._v(" Trương gia, đại trưởng lão nhất ngôn cửu đỉnh, bất kỳ người nào cũng không dám phản bác."),t("br"),t("br"),n._v("Trước đó gia chủ chủ sự, hiện tại hắn đột nhiên có chuyện rời đi, tất cả mọi chuyện đều có trước mắt vị này làm chủ."),t("br"),t("br"),n._v("“Đại trưởng lão, trận pháp hủy đi, muốn xây dựng lại, cần ít nhất thời gian mấy tháng, trữ vật giới chỉ như thế nào thu lấy cùng bảo tồn...”"),t("br"),t("br"),n._v("Một vị trưởng lão nhịn không được nói."),t("br"),t("br"),n._v("Trận pháp có thể giám định người đến trên người, có hay không mang không gian loại bảo vật, hiện tại hủy đi, không cách nào dò xét, đằng sau người tới, làm sao bây giờ?"),t("br"),t("br"),n._v("“Đi tới Trương gia liền là khách nhân, bảo tồn trữ vật giới chỉ, chỉ là tuân thủ tiên tổ lễ tiết thôi, trước đó ta vẫn cảm thấy không ổn, chỉ bất quá ngại vì tổ tông quy củ, không nguyện ý nói nhiều. Hiện tại vị tiểu huynh đệ này giúp một tay hủy đi, trận pháp không cần xây dựng, trước đó bảo tồn rất nhiều trữ vật đồ vật, cũng xin trả lại cho các vị khách nhân!”"),t("br"),t("br"),n._v("Mỉm cười, đại trưởng lão mở miệng."),t("br"),t("br"),n._v("“Trả lại? Nhưng...” Không nghĩ tới hắn làm ra loại này quyết định, vị trưởng lão này biến sắc."),t("br"),t("br"),n._v("Nhiều như vậy người ngoài đi tới Trương gia, ngộ nhỡ có người mưu đồ quấy rối, chẳng phải phiền phức rất lớn?"),t("br"),t("br"),n._v("“Không cần khẩn trương, ta Trương gia, đã được mọi người tôn xưng là đại lục đệ nhất gia tộc, tin tưởng còn không người dám ở chỗ này làm loạn!”"),t("br"),t("br"),n._v("Vung tay áo một cái, đại trưởng lão ánh mắt lộ ra nồng đậm tự tin và ngạo khí."),t("br"),t("br"),n._v("“Cái này... Là!”"),t("br"),t("br"),n._v("Cảm nhận được trên người đối phương khí tức, vị trưởng lão này dừng lại một chút, cuối cùng nhẹ gật đầu."),t("br"),t("br"),n._v("“Không hổ là Trương gia đại trưởng lão, lòng dạ cùng khí độ không tệ...”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Vốn cho rằng người của đối phương tới, sẽ đối với bản thân chỉ trích, không nghĩ tới một điểm phương diện này ý tứ đều không có, còn thừa cơ làm ra loại này quyết định."),t("br"),t("br"),n._v("Làm như vậy, không chỉ bảo vệ đại lục đệ nhất gia tộc mặt mũi, càng khiến người ta vì đó lòng dạ cảm thấy bội phục, có thể nói nhất cử lưỡng tiện."),t("br"),t("br"),n._v("Càng quan trọng hơn là, xác lập Trương gia thực lực, để cho người ta càng thêm sợ hãi."),t("br"),t("br"),n._v("“Phong thái không sai, chỉ là không biết vì sao vị kia tiểu thiên tài, làm sao như vậy kém cỏi...”"),t("br"),t("br"),n._v("Cảm khái một câu, đồng thời hơi nghi hoặc một chút."),t("br"),t("br"),n._v("Trong lòng hắn, vị kia tiểu thiên tài, rõ ràng đính hôn ước, lại trang bức không xuất hiện, nhất định là cái hèn hạ vô sỉ tiểu nhân, thậm chí cho hắn cố ý che giấu Trương gia, cũng bị đóng lên vô sỉ nhãn mác."),t("br"),t("br"),n._v("Giờ phút này nhìn thấy đại trưởng lão việc làm, đột nhiên cảm thấy, người còn không tồi."),t("br"),t("br"),n._v("Có lẽ, liền xem như Trương gia, cũng chưa chắc người người đều rất kém cỏi."),t("br"),t("br"),n._v("Vị kia tiểu thiên tài, có lẽ chỉ là ví dụ thôi."),t("br"),t("br"),n._v("“Hai vị mời tới bên này...”"),t("br"),t("br"),n._v("Một cái lão giả đi tới trước mặt, chính là đại trưởng lão vừa rồi phân phó Vô Chân."),t("br"),t("br"),n._v("Thất trưởng lão, Trương Vô Chân."),t("br"),t("br"),n._v("“Cảm ơn!”"),t("br"),t("br"),n._v("Kiếm Tần Sinh không dám nói nhiều, vội vàng theo ở phía sau."),t("br"),t("br"),n._v("Trương Huyền cũng theo sát trên đó."),t("br"),t("br"),n._v("Thấy bọn họ rời đi, Trương Vô Trần lần nữa đi tới đại trưởng lão trước mặt, nghi ngờ truyền âm tới: “Đây chính là Trương gia liên miên biểu tượng, thật... Cứ tính như vậy?”"),t("br"),t("br"),n._v("“Không tính là, chẳng lẽ, ngươi còn muốn tự mình ra tay, giáo huấn hắn một trận?”"),t("br"),t("br"),n._v("Đại trưởng lão mí mắt vừa nhấc."),t("br"),t("br"),n._v("Trương Vô Trần da mặt co lại, nói không ra lời."),t("br"),t("br"),n._v("“Chúng ta Trương gia, thân là danh sư làm chủ đạo gia tộc, không thể cùng những nghề nghiệp khác đồng dạng, quy tắc liền là quy tắc, đã xác định, liền muốn tuân thủ, vị này Trương Huyền, có thể phá hư, là chính hắn năng lực, đừng nói không truy cứu, ta còn muốn để ngươi, đem tin tức truyền lại đến toàn cả gia tộc, làm cho tất cả mọi người cũng biết!”"),t("br"),t("br"),n._v("Đại trưởng lão thản nhiên nói."),t("br"),t("br"),n._v("(Khai giảng, trước sáng sớm 8:30 họp đến tiếp cận mười hai giờ, đổi mới xong, xin lỗi.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1440"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1442"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);