(window.webpackJsonp=window.webpackJsonp||[]).push([[508],{863:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1434-tai-ha-khong-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1434-tai-ha-khong-su"}},[n._v("#")]),n._v(" Chương 1434: Tại hạ Khổng sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1434: Tại hạ Khổng sư"),t("br"),t("br"),n._v("“Tam trưởng lão... Tự mình đi nhìn liền biết!”"),t("br"),t("br"),n._v("Vô Chân trưởng lão cũng không biết là không biết sao nói, vẫn là không muốn nói, xoay người dẫn đường, Trương Vô Trần theo sát phía sau, rất mau tới đến phòng khách vị trí."),t("br"),t("br"),n._v("Trương gia làm đại lục đệ nhất gia tộc, hàng năm đều sẽ tiếp thụ lấy vô số đến đây thăm hỏi cùng khiêu chiến cường giả, cái gọi là phòng khách, trên thực tế là một mảnh dung nạp khách nhân khu vực, phạm vi cực lớn."),t("br"),t("br"),n._v("Mới đi đến bên ngoài phòng khách mặt quảng trường, Trương Vô Trần nhìn thấy cái gì, lông mày nhịn không được nhăn lại, ngừng lại, chỉ về phía trước."),t("br"),t("br"),n._v("“Cái này... Tượng đâu?”"),t("br"),t("br"),n._v("Trước mắt quảng trường, đứng sừng sững một loạt tiên tổ tượng, mỗi một cái khi còn sống, đều có vô cùng chói lọi sự tích, vì nhân tộc làm cống hiến rất lớn, bởi vậy, dựng bia chép sử, điêu khắc tượng thánh, ăn hưởng lấy cung cấp hậu nhân truyền tụng."),t("br"),t("br"),n._v("Đem tượng để ở chỗ này, cũng có thể tốt hơn biểu hiện ra Trương gia công lao, khiến người khác biết, vì đại lục hòa bình, Trương gia đã từng trả giá cố gắng."),t("br"),t("br"),n._v("Có thể trở thành đệ nhất gia tộc, không riêng gì thực lực, càng quan trọng hơn là, vì nhân tộc ném đầu lâu tung nhiệt huyết, hi sinh vô số sinh mệnh."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, vô số đến đây Trương gia khách nhân, đều kính nể vô cùng."),t("br"),t("br"),n._v("Lúc này, cái này khu vực cần phải đi qua tượng, thế mà tất cả đều không thấy, mặt đất lưu lại một cái cái hố to, đến cùng xảy ra chuyện gì?"),t("br"),t("br"),n._v("“Tượng... Ta muốn cùng ngươi nói, liền là chuyện này...”"),t("br"),t("br"),n._v("Trên mặt vặn vẹo như là mướp đắng, Vô Chân trưởng lão muốn giải thích, nhưng lại không biết trả lời như thế nào, táo bón đồng dạng nhẫn nhịn hồi lâu: “Tam trưởng lão vẫn là tự mình xem đi!”"),t("br"),t("br"),n._v("Thấy hắn không nói ra được, Trương Vô Trần càng thêm nghi ngờ, vội vã đi thẳng về phía trước, mới vừa vòng qua quảng trường, liền nghe đến một cái cứng nhắc thanh âm khàn khàn vang lên."),t("br"),t("br"),n._v("Thanh âm này, không giống loài người phát ra, mà giống như là máy lò xo hoặc là những vật khác, nghe mười phần mất tự nhiên."),t("br"),t("br"),n._v("“Thuyết thư xướng hí khuyến nhân phương, ba đầu đại lộ đi trung tâm. Thiện ác đến cùng cuối cùng cũng có báo, nhân gian chính đạo là tang thương!”"),t("br"),t("br"),n._v("Thanh âm khô quắt, tiếp tục nói: “Tại hạ Trương Lăng Nhiên, Trương gia thứ một trăm năm mươi bảy đời huyền tôn, sống ở hơn hai vạn năm trước, nhất chói lọi sự tích, là một mình xông vào Địa quật, chém giết ba vị Dị Linh tộc Hoàng giả, phá hủy bọn họ xâm lấn ý đồ. Từng một kiếm xé rách bốn đầu làm hại nhân gian Thánh vực cửu trọng Thánh thú, cùng tà sát Tôn giả đại chiến bảy ngày bảy đêm, đồng thời đem hắn giết chết... Đã mọi người muốn nghe, ta liền kỹ càng cùng mọi người nói một chút chuyện khi đó!”"),t("br"),t("br"),n._v("“Trương Lăng Nhiên? Lăng Nhiên tiên tổ?”"),t("br"),t("br"),n._v("Nghe được thanh âm này, Trương Vô Trần khóe miệng giật một cái, vội vàng vòng qua bức tường nhìn sang, ngay sau đó nhìn thấy một đống đi tới Trương gia khách nhân, đang vây quanh ở một cái tượng bên cạnh."),t("br"),t("br"),n._v("Tượng thì đang sống, miệng há mở, không ngừng nói bản thân cuộc đời sự tích, sinh động như thật, làm người say mê, thỉnh thoảng nghe được trong đám người, vang lên khen hay thanh âm."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lông mày một hồi nhảy loạn."),t("br"),t("br"),n._v("Trương Vô Trần suýt chút nữa không có té ngã trên đất."),t("br"),t("br"),n._v("Những này tiên tổ tượng bên cạnh, đều ghi lại hiển hách công lao, mục đích là để cho người ta quan sát, hiện tại làm sao... Sống, còn ở nơi này thuyết thư?"),t("br"),t("br"),n._v("Cố nén sắp kích động đến mức phát điên, nhìn về phía trước mắt Lăng Nhiên tiên tổ tượng đá."),t("br"),t("br"),n._v("Chỉ thấy tay hắn múa dậm chân, không có chút nào trước đó, cao cao tại thượng phong thái, ngược lại giống như là cái người viết tiểu thuyết đồng dạng, đem bản thân chói lọi sự tích, từng đầu nói ra, như cùng ở tại kể chuyện xưa."),t("br"),t("br"),n._v("“Tại hạ Trương Tiêu Tác, Trương gia thứ hai trăm mười hai đời huyền tôn, từng nhận chức Trương gia quyền gia chủ, sáng lập ra võ kỹ, lưu truyền đại lục, vô số người tu tập...”"),t("br"),t("br"),n._v("“Tại hạ Trương Mặc Phong, Trương gia thứ hai trăm hai mươi lăm đời huyền tôn, từng tại Thánh tử điện lưu lại ghi chép... Ai, ngươi đừng chạy, nghe ta nói sự tích của ta, không nghe, tin hay không đánh ngươi...”"),t("br"),t("br"),n._v("Đi thẳng về phía trước, cách đó không xa lần nữa nhìn thấy hai cái tượng đá, một cái cùng trước đó Lăng Nhiên tiên tổ đồng dạng, hướng người giảng giải, một cái khác, thì đuổi theo một người khách nhân, còn kém xé đối phương lỗ tai, để hắn lắng nghe."),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Tiếp tục tiến lên, lại nhìn thấy một vị tiên tổ tượng, ngồi tại khách nhân trên người, khuôn mặt dữ tợn nói chuyện xưa của mình... Trương Vô Trần cũng nhịn không được nữa, quay đầu hỏi tới."),t("br"),t("br"),n._v("Những này tiên tổ tượng, đại biểu Trương gia công lao, cũng đại biểu Trương gia uy nghiêm, làm sao tất cả đều sống... Nắm lấy khách nhân, muốn cho bọn họ kể chuyện xưa?"),t("br"),n._v("Mấu chốt nhất là... Không nghe liền muốn bị đánh..."),t("br"),t("br"),n._v("“Vừa rồi ta đưa vị kia Trương Huyền tới... Giới thiệu với hắn những này tiên tổ chói lọi sự tích...”"),t("br"),t("br"),n._v("Vô Chân trưởng lão mặt sắc có chút trở nên trắng, nói lắp bắp: “Sau đó, sau đó... Cứ như vậy!”"),t("br"),t("br"),n._v("“Giới thiệu tiên tổ chói lọi sự tích, làm sao lại biến thành như vậy?”"),t("br"),t("br"),n._v("Thấy hắn biểu lộ không đúng, Trương Vô Trần quát tháo: “Tốt đem trước sự tình, một năm một mười nói ra, ta muốn kỹ càng quá trình...”"),t("br"),t("br"),n._v("“Đúng... Giới thiệu một hồi, ta nghĩ lên một số việc, cảm khái một câu, nói... Hiện tại người, sớm đã nhân tâm không cổ, tiên tổ lưu lại sự tích, không có người nguyện ý cẩn thận giải, cũng không có mảy may kính sợ cùng tôn sùng...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Vô Chân trưởng lão chậm rãi nói."),t("br"),t("br"),n._v("Hiện tại người, so trước kia táo bạo nhiều, hắn mang theo rất nhiều khách nhân, trên đường đi qua nơi đây, có thể có rất ít người có thể đứng ở tượng đá trước mặt, cẩn thận quan sát Trương gia tiên tổ công lao... Phần lớn đều cảm thấy những người này đều chết lâu, công lao các loại, không tính là gì."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mới phát giác được sinh khí, cho Trương Huyền lúc giới thiệu, nói ra."),t("br"),t("br"),n._v("“Ta nói xong ngần này.. Trương Huyền Trương sư nói, hắn có thể thay đổi loại tình huống này, sau đó... Liền cẩn thận ra chạm đến mỗi một cái tượng đá, cho bọn hắn khải linh...”"),t("br"),t("br"),n._v("Vô Chân trưởng lão mặt sắc đỏ lên."),t("br"),t("br"),n._v("Nói thật, chuyện này lớn nhất nguyên nhân gây ra vẫn là bởi vì hắn, nếu không phải cảm giác khác cảm kích, đối phương cũng sẽ không ra tay."),t("br"),t("br"),n._v("Hiện tại tốt rồi, những này tượng đá bị khải linh về sau, ngăn chặn khách nhân liền bắt đầu diễn thuyết, không nghe còn không được, toàn bộ phòng khách, hiện tại cũng thành một đoàn loạn cháo."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Trương Vô Trần suýt chút nữa phun máu."),t("br"),t("br"),n._v("Tiên tổ tượng đá ở lại chỗ này, chỉ là hướng người giới thiệu Trương gia công lao, như bây giờ, bắt lấy người khác lỗ tai nói ra, cùng tự biên tự diễn khác nhau ở chỗ nào?"),t("br"),t("br"),n._v("“Không đúng... Trên quảng trường tiên tổ tượng đá nhiều như vậy... Ngươi ý tứ, hắn một hơi, tất cả đều khải linh?”"),t("br"),t("br"),n._v("Buồn bực về sau, có chút không dám tin tưởng."),t("br"),t("br"),n._v("Các đời tiên tổ, lưu lại họ tên cùng truyền thuyết, chừng trên trăm vị, nhiều như vậy tượng, liền xem như cửu tinh khải linh sư tới, cũng không cách nào toàn bộ khải linh đi, làm sao toàn bộ thành công, hơn nữa thoạt nhìn, từng cái... Như thế có ý nghĩ?"),t("br"),t("br"),n._v("“Đúng vậy a, không chỉ có như vậy...”"),t("br"),t("br"),n._v("Vô Chân trưởng lão nhớ tới cái gì, bờ môi lần nữa run rụt lại."),t("br"),t("br"),n._v("“Không chỉ có cái gì?”"),t("br"),t("br"),n._v("Trương Vô Trần chỉ cảm thấy da đầu muốn nổ tung, vội vã nhìn qua."),t("br"),t("br"),n._v("“Cái này... Ngay ở phía trước, ta thực sự không dám nói...”"),t("br"),t("br"),n._v("Dường như không biết trả lời như thế nào, Vô Chân trưởng lão lần nữa chỉ về phía trước."),t("br"),t("br"),n._v("Trong mắt tràn đầy nghi ngờ, Trương Vô Trần tiếp tục tiến lên, lại vòng qua một vách tường, còn chưa kịp xoay người, lại nghe được một giọng nói vang lên, mang theo dày nặng cùng trầm ổn."),t("br"),t("br"),n._v("“Tại hạ Khổng sư...”"),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Trương Vô Trần té ngã trên đất."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1442"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1444"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);