(window.webpackJsonp=window.webpackJsonp||[]).push([[51],{406:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1022-tim-phien-toai"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1022-tim-phien-toai"}},[n._v("#")]),n._v(" Chương 1022: Tìm phiền toái")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Tôn Tấn là công hội thực lực mạnh nhất luyện khí sư một trong, thu rất nhiều đệ tử, cũng có rất nhiều tùy tùng, ta chỉ là một cái học đồ, hơn nữa bị đuổi, là không có tư cách dẫn ngươi gặp hắn...”"),t("br"),t("br"),n._v("Nghe được Trương Huyền muốn trực tiếp đi gặp Tôn Tấn, trung niên nhân chần chờ một chút đạo."),t("br"),t("br"),n._v("Hỏa Nguyên thành địa hỏa phong phú, luyện khí sư cùng luyện đan sư đều có địa vị cực cao, không phải bất luận kẻ nào muốn gặp liền có thể thấy, nhất là Tôn Tấn loại này, đạt đến lục tinh trở lên đỉnh phong luyện khí sư."),t("br"),t("br"),n._v("“Không có tư cách? Yên tâm, ta dẫn ngươi đi thấy hắn!”"),t("br"),t("br"),n._v("Hừ một tiếng, hỏi rõ ràng đối phương vị trí, Trương Huyền đi đầu đi tới."),t("br"),t("br"),n._v("Hỏa Nguyên thành luyện khí sư công hội quả thực phồn hoa, toàn bộ đại sảnh chất đầy bán ra vũ khí cùng mua sắm binh khí tu luyện giả, không ít nhất tinh, nhị tinh luyện khí sư, phụ trách tiếp đãi, để đến đây mọi người trở nên càng thêm có trật tự."),t("br"),t("br"),n._v("Dọc theo đại điện đi trong chốc lát, đi tới một cái rộng lớn đại sảnh, đang định đi vào, hai vị tứ tinh cấp luyện khí sư liền đến đến phía trước, nhận ra đằng sau trung niên nhân, tất cả đều lộ ra cười lạnh."),t("br"),t("br"),n._v("“Ta tưởng rằng ai, đây không phải chúng ta học hơn 20 năm luyện khí nhưng vẫn là học đồ Tống Chân ư? Làm sao, chưa từ bỏ ý định, còn muốn lại tới kiểm tra một lần thử xem?”"),t("br"),t("br"),n._v("“Kiểm tra nhiều hơn nữa lần cũng là vô dụng, cũng không phải không có kiểm tra qua! Đồ bỏ đi chính là đồ bỏ đi, lại đến bao nhiêu lần cũng không thể thay đổi bản chất!”"),t("br"),t("br"),n._v("Hai người khắp khuôn mặt là khinh miệt vui vẻ, rất hiển nhiên, trung niên nhân chuyện bọn họ đều đã biết."),t("br"),t("br"),n._v("“Ta...” Nghe được hai người chế giễu, trung niên nhân Tống Chân, vẻ mặt trở nên trắng, muốn nói cái gì nhưng cuối cùng từ đầu đến cuối nói không nên lời."),t("br"),t("br"),n._v("“Được rồi, không cần ngươi nha ta nha, lập tức cút đi, lão sư căn bản cũng không muốn gặp ngươi, cho dù tiếp tục tới cũng vô dụng!”"),t("br"),t("br"),n._v("Bên trong một cái khoát tay áo, mặt mũi không kiên nhẫn."),t("br"),t("br"),n._v("Tống Chân đang muốn muốn giải thích, chỉ thấy Trương Huyền giơ bàn tay lên, ngắt lời hắn, nhìn lại: “Không phải hắn muốn gặp các ngươi lão sư, là ta! Để Tôn Tấn ra nghênh tiếp!”"),t("br"),t("br"),n._v("“Để lão sư nghênh đón? Ngươi thì tính là cái gì!”"),t("br"),t("br"),n._v("Nghe được cái tuổi này không lớn thanh niên, lớn như thế hơi thở, thế mà để bọn hắn lão sư ra nghênh tiếp, canh giữ ở cửa ra vào vị này tứ tinh luyện khí sư, hừ lạnh một tiếng, mặt mũi cười nhạo."),t("br"),t("br"),n._v("Lão sư hắn thân phận gì, liền xem như Hỏa Nguyên thành thành chủ muốn gặp, cũng nhất định phải trước thời hạn đưa lên bái thiếp, kẻ trước mắt này thoạt nhìn cũng chỉ 20 tới tuổi bộ dáng, thế mà nói khoác không biết ngượng, để hắn nghênh đón, quả thực không biết trời cao đất rộng."),t("br"),t("br"),n._v("“Ta đếm ba tiếng, lập tức rời đi, nếu không tin hay không đem ngươi ném đi?” Một vị khác luyện khí sư cũng nhướng mày."),t("br"),t("br"),n._v("Nhìn hai người biểu lộ, căn bản cũng không có tính toán để hắn đi vào bộ dạng, Trương Huyền bất đắc dĩ lắc đầu: “Đã hắn không ra, vậy ta chỉ có bản thân tiến vào.”"),t("br"),t("br"),n._v("Nói xong, không tiếp tục để ý ngăn tại trước mặt hai người, nhanh chân đi vào trong."),t("br"),t("br"),n._v("Hắn đường đường danh sư học viện viện trưởng, cho dù đi gặp thất tinh danh sư, đối phương cũng không dám đem hắn ngăn tại bên ngoài."),t("br"),t("br"),n._v("“Láo xược! Ngươi muốn làm gì?” Thấy hắn cứng rắn hướng bên trong xông, hai vị tứ tinh luyện khí sư vẻ mặt đồng thời biến đổi, vội vàng ngăn tại phía trước, lật bàn tay một cái, lực lượng tuôn ra, liền muốn động thủ."),t("br"),t("br"),n._v("Bất quá, công kích còn không có phát ra, liền cảm thấy một cỗ lực lượng khổng lồ lao qua."),t("br"),t("br"),n._v("Bành bành!"),t("br"),t("br"),n._v("Hai người thân thể đồng thời kìm lòng không được bay ngược ra ngoài, ngã rầm trên mặt đất, hơi đỏ mặt, phun ra máu tươi."),t("br"),t("br"),n._v("Hai người này vừa nhìn, liền cáo mượn oai hùm đã quen, bình thường đối một chút thân phận so với bọn hắn thấp người cũng không ít bắt nạt, cho nên Trương Huyền cũng không có có thể nương tay, chân khí trong cơ thể vừa để xuống, liền để hắn bị trọng thương."),t("br"),t("br"),n._v("Tứ tinh luyện khí sư, chẳng qua Kiều Thiên cảnh cấp bậc, liền xem như hắn tùy tiện thả ra chân khí đều là ngăn cản không nổi."),t("br"),t("br"),n._v("“Không xong, có người xông vào!”"),t("br"),t("br"),n._v("Giãy dụa lấy đứng dậy, một vị tứ tinh luyện khí sư lên tiếng rống to."),t("br"),t("br"),n._v("Thanh âm rất lớn, lập tức vang vọng toàn bộ công hội đại điện."),t("br"),t("br"),n._v("“Xông vào? Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Vô số người đem ánh mắt đồng loạt nhìn qua."),t("br"),t("br"),n._v("Không để ý tới những này chú ý, Trương Huyền vẻ mặt lạnh nhạt tiếp tục hướng đi vào trong đi."),t("br"),t("br"),n._v("Trung niên nhân Tống Chân, vốn cho rằng trước mắt vị này, sẽ đến đến nơi đây tìm kiếm giải quyết bản thân tình huống phương pháp, không nghĩ tới trực tiếp xông vào, sợ tới mức rụt cổ một cái, chần chờ một chút, cắn răng một cái, vẫn là theo sát tới."),t("br"),t("br"),n._v("Bất kể nói thế nào, đối phương cũng là vì hắn mới xông vào, thân là người trong cuộc, làm sao có thể lui về phía sau?"),t("br"),t("br"),n._v("Thấy hắn bắt kịp, chưa hề nói dư thừa nói nhảm, Trương Huyền thoả mãn nhẹ gật đầu."),t("br"),t("br"),n._v("Mặc dù không cam lòng loại này giả sư giả, muốn xuất thủ giải quyết, nhưng sau lưng vị này người trong cuộc, nếu như sợ đầu sợ đuôi lời nói, đối với hắn mà nói cũng khá là phiền toái."),t("br"),t("br"),n._v("“Dám xông vào công hội! Tự tìm cái chết!”"),t("br"),t("br"),n._v("“Nhanh ngăn lại cái tên này!”"),t("br"),t("br"),n._v("Nghe được vừa rồi vị kia tứ tinh luyện khí sư tiếng la, trong phòng lập tức đã tuôn ra sáu, bảy vị trung niên nhân."),t("br"),t("br"),n._v("Đều người mặc luyện khí sư trường bào, đại bộ phận đều là tứ tinh, ngũ tinh, những người này vừa xuất hiện, liền ngăn tại hai người phía trước, không nói hai lời, đủ loại công kích nhao nhao hạ xuống."),t("br"),t("br"),n._v("Hỏa Nguyên thành luyện khí sư công hội, địa vị tôn quý, không phải là cái gì người muốn xông liền có thể xông, nếu như không thêm vào ngăn lại, về sau còn mặt mũi nào, cho nên những người này căn bản cũng không có suy nghĩ nhiều, trước đem trước mắt vị này bắt lại lại nói."),t("br"),t("br"),n._v("“Đánh trống reo hò!”"),t("br"),t("br"),n._v("Thấy bọn họ trực tiếp ra tay, Trương Huyền cười lạnh một tiếng, cũng không nói chuyện, hai tay chắp sau lưng, tiếp tục đi đến phía trước."),t("br"),t("br"),n._v("Bành bành bành bành!"),t("br"),n._v(" Đến đây công kích mọi người, toàn bộ bay ngược ra ngoài, công kích lực lượng càng mạnh, té càng nặng, nguyên một đám vẻ mặt trắng bệch, máu tươi phun mạnh."),t("br"),t("br"),n._v("Ngũ tinh luyện khí sư, đã đạt tới Hóa Phàm bát trọng, cửu trọng, thực lực như thế, thế mà liền để hắn xuất thủ tư cách đều không có, liền bị đánh bay, sau lưng Tống Chân, sợ tới mức vẻ mặt trắng bệch, thân thể run rẩy."),t("br"),t("br"),n._v("Trước đó đối phương một ngón tay bắn rớt trường kiếm trong tay của hắn, liền biết thực lực không yếu, chỉ là không nghĩ tới đáng sợ như vậy!"),t("br"),t("br"),n._v("Từ nhỏ đến lớn, gặp qua người mạnh nhất, chính là luyện khí sư công hội hội trưởng, trước mắt vị này, chỉ sợ so với đối phương còn cường đại hơn."),t("br"),t("br"),n._v("“Nhanh, có người xông vào...”"),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Mấy người này ngã xuống đất, lại vọt tới một đống người, hầu như đều là thuần một sắc ngũ tinh luyện khí sư, vừa xuất hiện, liền đối Trương Huyền công kích, có điều, những công kích này đồng dạng không có hiệu quả lẫn nhau, cùng trước đó những người kia đồng dạng, nằm một chỗ."),t("br"),t("br"),n._v("Liên tục đánh bại ba đợt người công kích, hai người đã đi vào trước mắt đại sảnh."),t("br"),t("br"),n._v("“Tống Chân, ngươi có ý tứ gì?”"),t("br"),t("br"),n._v("Đi vào đại điện, chỉ thấy một cái lão giả lạnh lùng nhìn lại, trong mắt mang theo tức giận."),t("br"),t("br"),n._v("Bên cạnh hắn thì là hai vị đạt tới lục tinh cấp bậc luyện khí sư."),t("br"),t("br"),n._v("“Ta...” Nhìn thấy vị lão giả này, Tống Chân sắc mặt trắng nhợt, dường như xây dựng ảnh hưởng đã lâu, sợ tới mức lời nói đều nói không ra."),t("br"),t("br"),n._v("“Không có ý tứ gì, chỉ là muốn tới hỏi một chút, gieo họa Tống Chân dạng này học đồ, để bọn hắn dào dạt hi vọng, nhưng lại không có bất kỳ biện pháp nào, thân là lục tinh đỉnh phong luyện khí sư, ngươi lương tâm làm sao có thể an?”"),t("br"),t("br"),n._v("Không để ý tới đối phương trách mắng, Trương Huyền thân thể xoay một cái, ngồi tại đối phương trên chỗ ngồi, lông mày nâng lên, nhìn lại."),t("br"),t("br"),n._v("Lão giả da mặt co quắp thoáng cái, vẻ mặt âm trầm: “Ngươi là ai? Nơi này là luyện khí sư công hội, không phải mặc cho giương oai chỗ!”"),t("br"),t("br"),n._v("“Giương oai?” Trương Huyền lắc đầu: “Ta chỉ là đến giúp Tống Chân đòi cái công đạo thôi, không tính là giương oai!”"),t("br"),t("br"),n._v("“Công đạo? Hắn lấy cái gì công đạo? Học tập hai mươi năm, lại ngay cả nhất tinh luyện khí sư đều khảo hạch có điều, loại phế vật này, có gì công đạo mà nói! Cũng chính là tôn khí sư khoan dung rộng lượng, mới chứa chấp lâu như vậy, nếu không, loại này đồ rác rưởi, liền đợi tại công hội tư cách đều không có!”"),t("br"),t("br"),n._v("Lão giả bên cạnh một vị lục tinh luyện khí sư đi tới."),t("br"),t("br"),n._v("Vị này thoạt nhìn chừng bốn mươi tuổi, đã đạt đến Tòng Thánh cấp bậc, nhìn Tống Chân một cái, giữa hai lông mày mang theo đùa cợt."),t("br"),t("br"),n._v("“Ta cùng Tôn Tấn nói chuyện, có phần ngươi chen miệng?”"),t("br"),t("br"),n._v("Trương Huyền sầm mặt lại: “Quỳ xuống!”"),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Vị này lục tinh luyện khí sư, con ngươi co rụt lại, ngay sau đó liền cảm thấy một đạo lực lượng khổng lồ lao qua, liền cơ hội phản kháng đều không có, đầu gối mềm nhũn quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Dám ở chỗ này láo xược, thật là lớn gan chó!”"),t("br"),t("br"),n._v("Nhìn thấy một câu liền để đồng bạn quỳ trên mặt đất, một vị khác lục tinh luyện khí sư lập tức giận dữ, tiếng gầm gừ bên trong, lòng bàn tay nhiều ra một thanh trường kiếm, trực tiếp đâm tới."),t("br"),t("br"),n._v("Kiếm mang như dải lụa bắn ra, mang theo nồng đậm hàn khí, tu vi của đối phương mặc dù chỉ có Thánh vực nhất trọng sơ kỳ, nhưng trường kiếm trong tay dĩ nhiên đã đạt đến Linh cấp tuyệt phẩm!"),t("br"),t("br"),n._v("Tới phối hợp, cho dù Thánh vực nhất trọng trung kỳ cường giả, đều khó mà chống lại."),t("br"),t("br"),n._v("Không để ý tới đối phương gào thét, Trương Huyền cong ngón búng ra, vị này lục tinh luyện khí sư trường kiếm trong tay lập tức tuột tay, ngay sau đó rơi vào lòng bàn tay của hắn, phát ra “Đinh!” Một tiếng, linh tính nhận chủ."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Binh khí của mình, nhận những người khác làm chủ, vị này lục tinh luyện khí sư, giống như là giống như gặp quỷ, sắc mặt trắng nhợt, máu tươi phun tới, thời gian nháy mắt liền bị trọng thương."),t("br"),t("br"),n._v("“Ngươi đến cùng người nào?”"),t("br"),t("br"),n._v("Nhìn thấy thanh niên trước mắt, một câu để cho mình một cái học sinh quỳ xuống, ngón tay búng một cái, để một kiện Linh cấp tuyệt phẩm khí nhận chủ, Tôn Tấn rốt cục vẻ mặt thay đổi."),t("br"),t("br"),n._v("“Ta chỉ là tới thay Tống Chân đến đòi công đạo!”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua: “Thế nào, không muốn nói?”"),t("br"),t("br"),n._v("“Hắn đi theo ta hai mươi năm, liền nhất tinh luyện khí sư đều khảo hạch phải không công, thiên tư quá kém, liên quan gì đến ta?” Tôn Tấn cắn răng."),t("br"),t("br"),n._v("“Chuyện không liên quan tới ngươi?”"),t("br"),t("br"),n._v("Dùng ngón tay móc móc lỗ tai, Trương Huyền mang theo thất vọng: “Lời này ta không thích nghe... Nói thật.”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Thấy đối phương bộ dáng này, dường như ăn chắc hắn, Tôn Tấn gân xanh nhảy lên, mang theo dữ tợn: “Ta nói chính là lời nói thật, thiên phú đây là không có cách nào, không tin ngươi có thể hỏi Tống Chân, chính hắn liền nhất tinh vũ khí đều luyện chế không được, có quan hệ gì với ta...”"),t("br"),t("br"),n._v("“Xem ra lời nói thật đối với ngươi mà nói... Rất khó! Đã như vậy, ta tới cấp cho ngươi nhắc nhở một chút...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền nhìn qua: “Hắn tu luyện chân khí, thích hợp tìm kiếm khoáng thạch, nhưng cùng địa hỏa xung đột lẫn nhau, không cách nào luyện khí... Ngươi có thể nói cho ta biết, đây rốt cuộc chuyện gì xảy ra ư?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Biến sắc, Tôn Tấn sau lưng nắm đấm xiết chặt: “Hắn chỉ là ta một cái bình thường học đồ, tu luyện công pháp gì, ta làm sao biết? Làm sao, ta đường đường lục tinh luyện khí sư, còn cần quan tâm một cái học đồ tu luyện như thế nào? Thật sự là như vậy, ta sự tình khác cũng không cần làm!”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nghe được hắn, Tống Chân vẻ mặt đỏ lên, tràn đầy cuống cuồng: “Ta tu luyện công pháp, là tay ngươi đem tay truyền thụ, còn bình thường chỉ điểm, ngươi sao lại thế... Không biết?”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1030"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1032"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);