(window.webpackJsonp=window.webpackJsonp||[]).push([[512],{867:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1438-truong-huyen-muon-nghiem-tuc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1438-truong-huyen-muon-nghiem-tuc"}},[n._v("#")]),n._v(" Chương 1438: Trương Huyền muốn nghiêm túc")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1438: Trương Huyền muốn nghiêm túc"),t("br"),t("br"),n._v("“Đây là... Tuyết Nê thủ pháp!”"),t("br"),t("br"),n._v("“Truyền thuyết là năm đó Ngô Đạo Tổ sư, quan sát chim bay lưỡng lự, mà sáng lập ra vẽ tranh thủ đoạn, tuyết nê hồng trảo, đảo mắt tây đông... Dùng loại thủ pháp này vẽ tranh, ý cảnh xa xăm, còn không có xuất hiện nội dung, đã câu hồn phách người!”"),t("br"),t("br"),n._v("“Bộ này thủ pháp, vài vạn năm đến, có thể học được, lác đác không có mấy, không nghĩ tới Tử Tình tài nữ chẳng những học được, còn thi triển như vậy trôi chảy, tỷ thí lần này, khẳng định thắng chắc!”"),t("br"),t("br"),n._v("“Xem ra, chỉ cần thư hoạ không cao hơn cửu tinh, không người có thể vượt qua...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bốn phía một hồi xôn xao."),t("br"),t("br"),n._v("Vị này Trương gia đệ nhất tài nữ, vừa ra tay, bút lông, giấy, mực nước, thủ pháp, thậm chí tâm cảnh... Đều không có kẽ hở, hoàn mỹ để cho người ta sợ hãi thán phục."),t("br"),t("br"),n._v("“Không tệ...”"),t("br"),t("br"),n._v("Trương Huyền cũng âm thầm gật đầu."),t("br"),t("br"),n._v("Thân là bát tinh thư họa sư, nhãn lực của hắn so bốn phía đám người, càng thêm cường đại, tự nhiên đó có thể thấy được trước mắt cô gái này, quả thực không dễ dàng đối phó."),t("br"),t("br"),n._v("So với lúc trước xông danh sư lâu gặp phải vị kia Thánh tử, đều chỉ mạnh không yếu!"),t("br"),t("br"),n._v("Thánh tử điện, cứ việc tập hợp trong đại lục vô số thiên tài, nhưng cũng không phải trong đó Thánh tử, liền là thế hệ trẻ tuổi thiên hạ đệ nhất."),t("br"),t("br"),n._v("Thật giống như khải linh sư công hội Trương Phong, mặc dù là Thánh tử, khải linh chi thuật ảo diệu vô song, nhưng thật muốn cùng vị kia mới đi vào tổng bộ thiên tài Vương Dĩnh so, vẫn là hơi có không bằng... Nếu không, đối phương cũng sẽ không tìm Trương Huyền hỗ trợ."),t("br"),t("br"),n._v("Đạo lý giống vậy."),t("br"),t("br"),n._v("Thánh tử điện vị kia Thánh tử, thư hoạ bên trên rất mạnh, nhưng dường như còn xa không bằng trước mắt vị này."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Bút lông nhẹ nhàng trên không trung vung vẩy, cho người ta một loại đẹp hưởng thụ, Trương Tử Tình cả người, máy móc đồng dạng, khống chế không sai chút nào, rơi vào lưu quang trên giấy mỗi một cây đường cong, mỗi một giọt mực nước, đều tản mát ra làm người ta say mê ánh sáng, để cho người ta nhìn một cái, liền rơi vào trong đó, không cách nào tự kềm chế."),t("br"),t("br"),n._v("“Trương sư... Nàng đều vẽ lên nhiều như vậy, ngươi cũng nhanh lên một chút bắt đầu đi, nếu không, thật muốn thua...”"),t("br"),t("br"),n._v("Đang tại quan sát, liền nghe đến bên cạnh một cái gấp gáp thanh âm vang lên."),t("br"),t("br"),n._v("Quay đầu nhìn, chỉ thấy Kiếm Tần Sinh vẫn như cũ che cái trán, tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("“Trước không vội vã...”"),t("br"),t("br"),n._v("Nhìn một cái trước mắt nhang lớn, nhìn thời gian còn lâu, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn vẽ tranh mười phần đơn giản, mười, hai mươi cái hô hấp liền có thể hoàn thành, không cần thiết đuổi theo."),t("br"),t("br"),n._v("Biết trước mắt vị này từng xông qua danh sư lâu, vượt qua thư họa sư công hội Thánh tử, thư hoạ một đạo, thập phần cường đại, Kiếm Tần Sinh không nói thêm lời, tràn đầy xấu hổ nhìn qua: “Không vội vã là tốt rồi... Khụ khụ, Trương sư, có thể hay không cùng ngươi thảo luận chút chuyện?”"),t("br"),t("br"),n._v("“Sư đệ cứ nói đừng ngại!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Là dạng này... Ngươi bây giờ đại biểu là kiếm đạo... Có thể hay không đừng lão làm ra những nghề nghiệp khác...”"),t("br"),t("br"),n._v("Dừng lại một chút, Kiếm Tần Sinh nói."),t("br"),t("br"),n._v("Nói thật, hắn thật có chút hối hận, đem cái tên này mang tới!"),t("br"),t("br"),n._v("Tới là so kiếm, am hiểu kiếm pháp cao thủ còn không có nhìn thấy, trước đem người ta trận pháp làm sập, sau đó, khải linh làm cho đầy sân đều là tượng đá... Ngay sau đó lại muốn cùng người tỷ thí thư hoạ..."),t("br"),t("br"),n._v("Trận pháp, khải linh, thư hoạ, giám bảo, một cái so một cái trâu..."),t("br"),t("br"),n._v("Ngươi thật... Chỉ là tới so kiếm, có thể hay không chú ý một chút thân phận của mình?"),t("br"),t("br"),n._v("Ngươi bây giờ thế nhưng là sư huynh của ta, những nghề nghiệp khác bên trên như thế khoa trương, thật được không?"),t("br"),t("br"),n._v("“Cái này... Nói cũng phải!”"),t("br"),t("br"),n._v("Bị hắn hỏi một chút, Trương Huyền tràn đầy xấu hổ gãi đầu một cái: “Là ta thiếu cân nhắc...”"),t("br"),t("br"),n._v("Chỉ nghĩ cho Trương gia một hạ mã uy, quên cái này gốc, lấy kiếm pháp thân phận đi tới Trương gia, kiếm còn chưa bắt đầu so, trước làm ra nhiều như vậy chức nghiệp, quả thực khó coi, cho người ta một loại không làm việc đàng hoàng cảm giác."),t("br"),t("br"),n._v("“Không có việc gì, chỉ cần ngươi nhớ, chúng ta là tới so kiếm là tốt rồi...”"),t("br"),t("br"),n._v("Thấy hắn thừa nhận, Kiếm Tần Sinh không tiện nói tiếp, đành phải nhẹ gật đầu."),t("br"),t("br"),n._v("Nếu không phải là bởi vì hắn, đối phương đều khó có khả năng tới, cho dù có chút sai lầm, cũng là cảm kích, trách cứ không đến."),t("br"),n._v(" “Ta nhớ...”"),t("br"),t("br"),n._v("Nhẹ gật đầu, Trương Huyền đang muốn nói tiếp, liền nghe đến phía trước một hồi khen hay thanh âm."),t("br"),t("br"),n._v("“Quá lợi hại, nhanh như vậy liền hoàn thành!”"),t("br"),t("br"),n._v("“Bức tranh đẹp quá, Tử Tình tiên tử, có thể hay không đem bức họa này bán cho ta?”"),t("br"),t("br"),n._v("“Ngắn như vậy thời gian, liền vẽ tranh thành công, không hổ là thư họa sư công hội đều ngàn năm không gặp thiên tài...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bốn phía tràn đầy thảo luận, Trương Huyền ngẩng đầu nhìn lại, chỉ thấy cô bé trước mắt, trên không vung vẩy bút lông đã ngừng lại, một bức họa xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("Là một bộ núi trúc."),t("br"),t("br"),n._v("Mặc dù không có cái khác thực vật phụ trợ, nhưng mỗi một cây cây trúc, cũng giống như mọc ở trên giấy đồng dạng, xanh biêng biếc, tại gió nhẹ thổi bên dưới, không ngừng lắc lư, bất cứ lúc nào cũng sẽ từ bên trong mọc ra."),t("br"),t("br"),n._v("Hết thảy mười mấy cây trúc, mỗi một cây, đều tựa hồ có tình cảm của mình cùng sinh mệnh, hình thái khác nhau, không có một chút chỗ tương tự."),t("br"),t("br"),n._v("“Cho vẽ ra đồ vật trao cho linh tính, tựa như đang sống, lợi hại!”"),t("br"),t("br"),n._v("Trương Huyền lần nữa gật đầu."),t("br"),t("br"),n._v("Vốn cho rằng tùy tiện làm một bức họa liền có thể chiến thắng, hiện tại xem ra, đồng thời không có dễ dàng như vậy."),t("br"),t("br"),n._v("Trước mắt cô gái này trình độ, so với hắn tưởng tượng còn cao minh hơn không ít."),t("br"),t("br"),n._v("Thư hoạ, cũng không phải là càng phức tạp, màu sắc càng rực rỡ liền cấp bậc càng cao, đại âm hi thanh, đại tượng vô hình, rất nhiều chân chính danh họa, thật ra thì đều mười phần đơn giản."),t("br"),t("br"),n._v("Thật giống như kiểm nghiệm một cái đầu bếp là có hay không lợi hại, liền để hắn làm một đạo sợi khoai tây đồng dạng."),t("br"),t("br"),n._v("Càng đơn giản, càng biểu hiện công lực."),t("br"),t("br"),n._v("Trước mắt mảnh này cây trúc, thoạt nhìn hời hợt, có chút “Lục thực” mùi vị, nhưng trên thực tế, cẩn thận quan sát, liền có thể phát hiện, trong đó mỗi một cây, cũng giống như đang sống, có được thuộc về mình đặc biệt sinh mệnh."),t("br"),t("br"),n._v("Hơn nữa, mỗi một cây ở giữa, hư thực gặp lại, xen vào nhau tinh tế, đã có thế giới trong tranh hình thức ban đầu."),t("br"),t("br"),n._v("Nếu không phải đối phương tuổi còn nhỏ, nhận hạn chế tu vi, chỉ sợ đã có thể làm ra đỉnh phong nhất tác phẩm hội họa, xung kích cửu tinh thư họa sư."),t("br"),t("br"),n._v("“Khó trách những người khác nói, cửu tinh tác phẩm hội họa không ra, muốn vượt qua rất khó...”"),t("br"),t("br"),n._v("Cảm xúc một tiếng, Trương Huyền cảm nhận được nồng đậm áp lực."),t("br"),t("br"),n._v("Muốn vượt qua đối phương bức họa này, chỉ sợ chỉ có chân chính cửu tinh tác phẩm."),t("br"),t("br"),n._v("“Thế nào, Trương sư đây là muốn chủ động nhận thua ư?”"),t("br"),t("br"),n._v("Đang tại suy tư, liền nghe đến nữ hài thanh âm vang lên."),t("br"),t("br"),n._v("Ngẩng đầu nhìn lại, chỉ thấy Trương Tử Tình khóe miệng mang theo cười lạnh."),t("br"),t("br"),n._v("Hiện tại nàng rốt cuộc minh bạch, vì sao đối phương không cần cược nàng tranh, mà là một viên tuyệt phẩm linh thạch, nàng tranh trân quý như vậy, thật muốn thua, đối phương nhất định thua không nổi..."),t("br"),t("br"),n._v("Lúc này mới cố làm ra vẻ huyền bí, tốt cho cái bậc thang xuống."),t("br"),t("br"),n._v("“Nhận thua? Cái này sao có thể?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Ngươi vẽ rất tốt, đã như vậy, ta cũng muốn lấy ra chút bản lĩnh thật sự...”"),t("br"),t("br"),n._v("Cảm xúc một tiếng, cổ tay khẽ đảo, lòng bàn tay nhiều ra một thanh trường kiếm, trên không trung run một cái, lắc đầu thu về nhẫn, lại lấy ra một chuôi, đồng dạng run một cái, lần nữa lắc đầu thu về."),t("br"),t("br"),n._v("Như thế liên tục, rốt cuộc tìm được một chuôi dường như thích hợp trường kiếm, cầm tại lòng bàn tay, nhìn chung quanh một vòng, đột nhiên bàn chân trên mặt đất đạp mạnh."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Dưới chân một cái phiến đá phẳng lấy bay lên, rơi vào trước mắt."),t("br"),t("br"),n._v("“Ta bắt đầu...”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, Trương Huyền cầm trong tay trường kiếm, hướng về phía phiến đá liền đâm tới."),t("br"),t("br"),n._v("“Ngươi không dùng bút lông? Không dùng lưu quang giấy... Dùng kiếm cùng phiến đá vẽ tranh?”"),t("br"),t("br"),n._v("Thân thể mềm mại vụt qua, Trương Tử Tình một cỗ không minh nghiệp hỏa nhất thời bay lên, suýt chút nữa không có nổ tung."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1446"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1448"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);