(window.webpackJsonp=window.webpackJsonp||[]).push([[514],{869:function(n,h,t){"use strict";t.r(h);var g=t(44),r=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1440-nuong-than-ga-cho"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1440-nuong-than-ga-cho"}},[n._v("#")]),n._v(" Chương 1440: Nương thân gả cho?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1440: Nương thân gả cho?"),t("br"),t("br"),n._v("“Ngô Đạo kính, ngươi nói... Ta thua?”"),t("br"),t("br"),n._v("Khó tin, cũng nhịn không được nữa, Trương Tử Tình hỏi."),t("br"),t("br"),n._v("“Không sai, hắn tác phẩm này, dùng đơn giản nhất vẽ tranh phương pháp, nhưng nhắm thẳng vào chân ý, bình thường cửu tinh thư họa sư đều không thể làm được, tác phẩm của ngươi cứ việc cũng không tệ, nhưng lại xa xa không bằng...”"),t("br"),t("br"),n._v("Ngô Đạo kính lên tiếng."),t("br"),t("br"),n._v("Loại cấp bậc này vũ khí, đã có thuộc về mình linh tính, có thể cùng người đối thoại giao lưu."),t("br"),t("br"),n._v("“Hắn liền dùng kiếm ở phía trên vẽ lên mấy lần, liền một đạo dấu vết đều không có lưu lại... Làm sao có thể cửu tinh thư họa sư đều làm không được...”"),t("br"),t("br"),n._v("Trương Tử Tình tóc nhanh lấy xuống."),t("br"),t("br"),n._v("Mở mắt nói lời bịa đặt, cũng không thể nói như vậy?"),t("br"),t("br"),n._v("Nhiều người như vậy đều nhìn tận mắt hắn vẽ tranh, tiện tay trên mặt đất cầm lấy một khối phiến đá, liền dùng kiếm để điêu khắc... Phía trên cái gì hoa văn đều không có, ở đâu ra tranh?"),t("br"),t("br"),n._v("“Cẩn thận đến trước mặt quan sát, nhất định có thể phát hiện đặc thù...”"),t("br"),t("br"),n._v("Thấy nàng không hiểu, Ngô Đạo kính lắc lư một cái, không nói thêm lời, dường như vẫn như cũ đắm chìm trong mỹ lệ tác phẩm bên trong, không cách nào tự kềm chế."),t("br"),t("br"),n._v("Chần chờ một chút, mấy bước đi tới cái bàn trước mặt, Trương Tử Tình cúi đầu hướng trước mắt phiến đá nhìn sang."),t("br"),t("br"),n._v("Phiến đá mặt ngoài còn có vừa rồi từ mặt đất rút ra lúc, lưu lại bụi bặm, lúc trước thợ đá điêu khắc đi ra dấu vết vẫn còn, từng đạo hoa văn, cùng mặt đất hòn đá giống nhau như đúc, không có bất kỳ cái gì đặc thù."),t("br"),t("br"),n._v("“Phía trên này không có tranh ah...”"),t("br"),t("br"),n._v("Nhìn kỹ một chút, cũng không phát hiện phía trên có kiếm khí dấu vết lưu lại, càng nghĩ càng kỳ lạ, thần thức khẽ động, lan tràn đi ra."),t("br"),t("br"),n._v("Con mắt không nhìn thấy, có lẽ thần thức, có thể quan sát xảy ra vấn đề."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Thần thức một tướng trước mắt đá bao phủ, Trương Tử Tình thân thể nhất thời một chút cứng đờ, thân thể mềm mại không ngừng run rẩy: “Cái này, cái này...”"),t("br"),t("br"),n._v("Bờ môi không ngừng run rẩy, dường như khó tin nhìn thấy."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Trương Vô Trần mấy người cũng nhíu nhíu mày, nhịn không được thần thức lan tràn đi ra, hướng về phía trước nhìn tới."),t("br"),t("br"),n._v("Xem xét phía dưới, đồng dạng ngây người tại chỗ."),t("br"),t("br"),n._v("“Không sai, cái này đá, là ta dùng kiếm khí vẽ ra tới, cũng không phải là chân thực tồn tại, trước đó cái kia đá, sớm đã vỡ thành bột phấn, bị ta thu vào trữ vật giới chỉ...”"),t("br"),t("br"),n._v("Thấy bọn họ nhìn ra vấn đề, Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Vừa rồi kiếm khí ngang dọc, người ngoài thoạt nhìn, trên tảng đá cái gì đều không có lưu lại, trên thực tế, chỉ dùng kiếm khí, lấy vừa rồi cái kia đá vì hàng mẫu, lần nữa vẽ lên một cái giống nhau như đúc."),t("br"),t("br"),n._v("Vẫn là thất tinh thư họa sư thời điểm, hắn liền lĩnh ngộ hư không lâm thư, có thể trên không trung vẽ tranh, để cho người ta khó phân thật giả, lúc này đạt tới bát tinh đỉnh phong, tự nhiên càng không thành vấn đề."),t("br"),t("br"),n._v("Mặc dù là kiếm khí tổ hợp mà thành, không dùng thần thức quan sát, coi như cửu tinh thư họa sư, cũng khó có thể phân biệt."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, mặc kệ là Trương Tử Tình, vẫn là Vô Trần, Vô Chân hai vị trưởng lão, đều không có phát giác được dị thường, nằm mơ đều không có nghĩ đến, trước đó tiện tay từ dưới đất nhặt lên đá, đã biến mất, trước mắt đây là một bộ “Tranh”!"),t("br"),t("br"),n._v("“Vẽ đá, chúng ta thế mà đều không có nhìn ra...”"),t("br"),t("br"),n._v("“Tử Tình chỉ là để cây trúc có linh tính, thoạt nhìn giống thật, mà hắn đã đã vượt ra loại cảnh giới này, lấy giả đánh tráo, ai cũng nhận không ra...”"),t("br"),t("br"),n._v("Hiểu được, Trương Vô Trần, Trương Vô Chân hai vị trưởng lão khó mà tin được."),t("br"),t("br"),n._v("Chân chính cửu tinh thư họa sư, vẽ ra lời nói, có thể lấy giả đánh tráo, người thật khó phân biệt, không nghĩ tới, người thanh niên này cũng có thể làm được."),t("br"),t("br"),n._v("Chẳng lẽ, hắn đã là cửu tinh?"),t("br"),t("br"),n._v("Nếu thực như thế, quá kinh khủng đi!"),t("br"),t("br"),n._v("“Ta... Thua!”"),t("br"),n._v("Bàn tay nhẹ nhàng vuốt ve trước mắt “Nham thạch”, phát hiện cùng thật không có gì khác nhau, không phải thần thức dò xét, không thể nhận ra cảm giác, Trương Tử Tình khuôn mặt trắng bệch, tự lẩm bẩm."),t("br"),t("br"),n._v("Vốn cho rằng đối phương là tuỳ tiện đâm ra kiếm khí, làm sao đều không có nghĩ đến, thật sự là đang vẽ tranh, hơn nữa, còn như vậy có linh tính, ai cũng phân biệt không ra."),t("br"),t("br"),n._v("Khó trách Ngô Đạo kính nói nàng thua... Lại thua, nát bét, căn bản không tại cùng một cái đẳng cấp."),t("br"),t("br"),n._v("“Chỉ dùng kiếm, liền vẽ ra loại cấp bậc này tác phẩm, ngươi... Dùng bút lông, cái kia mạnh bao nhiêu?”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, hướng thanh niên trước mắt nhìn lại."),t("br"),t("br"),n._v("Đối phương nói nghiêm túc, nàng còn tưởng rằng muốn xuất ra toàn bộ năng lực, không nghĩ tới chỉ dùng kiếm, liền làm ra một bộ nàng đều không cách nào vượt qua tác phẩm... Thật muốn dùng tới bút lông, lại nên mạnh cỡ nào?"),t("br"),t("br"),n._v("“Ta không có lừa ngươi, là thật nghiêm túc đang vẽ tranh!”"),t("br"),t("br"),n._v("Thấy nàng tràn đầy hoảng sợ, Trương Huyền gật đầu cười: “Nói thật, ngươi đối thư hoạ lý giải, quả thực rất mạnh, nếu không phải vì vượt qua ngươi, ta khả năng tùy tiện tìm rễ cỏ dại, liền vẽ lên, không có khả năng dùng kiếm! Thật ra thì, thật muốn dụng tâm vẽ tranh, kiếm cùng bút lông, không có khác nhau quá nhiều.”"),t("br"),t("br"),n._v("“Cỏ dại?”"),t("br"),t("br"),n._v("Da mặt co lại, Trương Tử Tình: “Thứ này làm sao vẽ tranh?”"),t("br"),t("br"),n._v("“Rất đơn giản...”"),t("br"),t("br"),n._v("Nhìn chung quanh một vòng, Trương Huyền ngón tay một điểm, cách đó không xa trong hoa viên, một cây cỏ dại bay tới, rơi vào lòng bàn tay, nhẹ nhàng nhất câu."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Trong ao nước bay tới, rơi vào cỏ dại bên trên, Trương Huyền hai ngón tay nắm sợi cỏ, từ từ trên không trung vũ động."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Không khí bốn phía lập tức sền sệt lên, trong khoảnh khắc, một cái rộng lớn dòng sông xuất hiện ở trước mặt mọi người, lao nhanh gào thét, tựa như lúc nào cũng sẽ chảy ra đến, đem trọn cái viện tử đều bao phủ."),t("br"),t("br"),n._v("“Hư không vẽ tranh, không dùng mực nước, dùng nước vẽ ra dòng sông?”"),t("br"),t("br"),n._v("Trương Tử Tình nuốt ngụm nước bọt."),t("br"),t("br"),n._v("Nàng vẽ tranh, muốn dùng trân quý nhất bút lông, cùng trân quý nhất mực nước, vẫn cảm thấy như vậy, mới có thể đem tác phẩm cảnh giới, đẩy tới đỉnh phong nhất, nhìn thấy đối phương mới hiểu được, căn bản không phải chuyện như vậy."),t("br"),t("br"),n._v("Hạ bút thành văn, không có gì không thể làm tranh, không có gì không thể vào tranh... Đây mới thật sự là thư hoạ đại tông sư, chân chính đối thư hoạ nghiên cứu đến tận xương tủy siêu cấp thiên tài."),t("br"),t("br"),n._v("“Bức họa này... Cũng so với ta Sơn Trúc đồ ý cảnh cao hơn!”"),t("br"),t("br"),n._v("Nhìn một hồi, Trương Tử Tình mặc dù không muốn thừa nhận, nhưng cũng không thể không thừa nhận, đối phương dùng cỏ dại cùng ao nước, tùy tiện vẽ sông này chảy, so với nàng vừa rồi bộ kia Sơn Trúc đồ vẫn như cũ cao minh không ít."),t("br"),t("br"),n._v("“Nha... Vừa rồi tranh cái kia đá, ta đối thư hoạ lý giải lại tiến bộ một ít, cho nên hiện tại dùng cỏ dại, cũng vẽ không tệ...”"),t("br"),t("br"),n._v("Trương Huyền giải thích nói."),t("br"),t("br"),n._v("Học được bát tinh thư họa sư năng lực về sau, hắn rất ít vẽ tranh, vừa rồi cái kia đá, xem như lần đầu tiên nghiêm túc, kết quả... Từ đó nhận được không ít kinh nghiệm quý báu, cho nên... Đối họa đạo lý giải, càng sâu một bước."),t("br"),t("br"),n._v("Bởi vậy, tùy tiện dùng cỏ dại vẽ, cũng để ý cảnh vượt qua đối phương."),t("br"),t("br"),n._v("“Lại tiến bộ?”"),t("br"),t("br"),n._v("Thân thể lần nữa lắc lư, Trương Tử Tình trước mắt biến thành màu đen."),t("br"),t("br"),n._v("Nàng tự nhận là tại thư hoạ bên trên, có được không người có thể so sánh thiên phú, có thể cùng trước mắt vị này so, kém vẫn như cũ quá xa, không thể so sánh nổi!"),t("br"),t("br"),n._v("“Tử Tình tiên tử, ta nhớ được ngươi từng nói qua, chỉ cần thế hệ trẻ tuổi, có người có thể tại cầm kỳ thư họa bên trên vượt qua ngươi, bất kể là ai, đều sẽ nương thân gả cho... Không biết có phải hay không thật?”"),t("br"),t("br"),n._v("Đột nhiên, trong đám người một thanh niên hô lên."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Tử Tình sững sờ, ngay sau đó lần nữa nhìn về phía cách đó không xa Trương Huyền, chân mày hơi nhíu lại."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1448"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1450"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=r.exports}}]);