(window.webpackJsonp=window.webpackJsonp||[]).push([[516],{871:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1441-truong-giang"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1441-truong-giang"}},[n._v("#")]),n._v(" Chương 1441: Trương Giang")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1441: Trương Giang"),t("br"),t("br"),n._v("Thân là đệ nhất tài nữ, danh khí lớn, lại thêm dung mạo mỹ lệ, mỗi ngày đều có không ít tỏ tình, để nàng phiền muộn không thôi."),t("br"),t("br"),n._v("Bởi vậy, từng ngang nhiên tuyên bố, bất kể là ai, chỉ cần tuổi tác không cao hơn ba mươi, có thể tại cầm, kỳ, thư, họa vượt qua nàng, liền sẽ không chút do dự gả cho đối phương!"),t("br"),t("br"),n._v("Nói như vậy, chỉ là cái chối từ, tránh khỏi người khác quấy rối, dù sao, có thể thắng được nàng hầu như không tồn tại..."),t("br"),t("br"),n._v("Ai nghĩ đến, thực sự có người tại thư hoạ bên trên vượt qua, hơn nữa dễ dàng như vậy!"),t("br"),t("br"),n._v("“Người này tính cách mặc dù không tốt lắm, nói chuyện cũng không dễ nghe, nhưng... Có bản lĩnh thật sự, có thể gả cho hắn, cũng là không tệ...”"),t("br"),t("br"),n._v("Người khác nhấc lên, trong lòng nhịn không được toát ra ý nghĩ."),t("br"),t("br"),n._v("“Không biết... Trương sư có thể hay không biết đánh đàn?”"),t("br"),t("br"),n._v("Cũng lại kìm nén không được, nhìn lại."),t("br"),t("br"),n._v("“Hiểu sơ một hai!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Mặc dù đã là bát tinh ma âm sư, nhưng là vẫn phải khiêm tốn chút."),t("br"),t("br"),n._v("“Cái kia... Đánh cờ đâu?”"),t("br"),t("br"),n._v("“Hoàn toàn không hiểu!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Chức nghiệp học không ít, nhưng mà không có học qua cờ thuật, có thể nói một chữ cũng không biết."),t("br"),t("br"),n._v("“Không biết không sao, ta có thể dạy ngươi... Trương sư có bằng lòng hay không đi ta thanh nhã tiểu trúc, cùng nghiên cứu cầm kỳ thư họa?”"),t("br"),t("br"),n._v("Khuôn mặt đỏ lên, Trương Tử Tình nói."),t("br"),t("br"),n._v("Nói như vậy, đã coi như là thổ lộ."),t("br"),t("br"),n._v("“Cầm kỳ thư họa?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Loại này phụ tu, chỉ là tùy tiện học một ít, tiêu khiển giải trí thôi, thư hoạ cũng chỉ học không đủ hai ngày thời gian, chuyên môn nghiên cứu... Liền thôi!”"),t("br"),t("br"),n._v("Hắn tới là muốn đánh mặt Trương gia, ép tiểu thiên tài xuất hiện, nào có thời gian học cái gì cầm kỳ thư họa?"),t("br"),t("br"),n._v("Lại nói, nhìn vài cuốn sách là được, còn cần theo ngươi học?"),t("br"),t("br"),n._v("Nghe được đối phương cự tuyệt, Trương Tử Tình vẻ mặt khó coi, có điều, ngay sau đó lộ ra khó tin: “Ngươi nói, ngươi thư hoạ, tổng cộng học không đến hai ngày?”"),t("br"),t("br"),n._v("“Ừm!” Trương Huyền suy tư một chút, gật đầu: “Thời gian toàn bộ cộng lại, cũng không đến...”"),t("br"),t("br"),n._v("Học tập thư họa sư, chỉ là vì khảo hạch danh sư, dùng thời gian không nhiều, mỗi cái cấp bậc một canh giờ, hiện tại đạt tới bát tinh, cũng chỉ có tám canh giờ thôi, coi như cộng thêm đọc sách, hẳn là cũng sẽ không vượt qua hai ngày..."),t("br"),t("br"),n._v("Nói như vậy, không tính khoác lác."),t("br"),t("br"),n._v("Ừm, không nói một ngày, đã rất phù hợp hắn khiêm tốn bản tính."),t("br"),t("br"),n._v("“Không đến hai ngày thời gian, đối thư hoạ lý giải, cứ như vậy cao thâm?”"),t("br"),t("br"),n._v("Trương Tử Tình không tin."),t("br"),t("br"),n._v("“Đã thua, liền đem tiền đặt cược cho ta đi...”"),t("br"),t("br"),n._v("Không thèm để ý đối phương chấn kinh, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Bởi vì có tuyệt phẩm linh thạch, hắn mới đồng ý cá cược, đã thắng, đương nhiên muốn thực hiện."),t("br"),t("br"),n._v("“Linh thạch...”"),t("br"),t("br"),n._v("Lúc này mới nhớ tới vừa rồi cá cược, Trương Tử Tình mỉm cười: “Vừa rồi vị bằng hữu nào nói, chắc hẳn ngươi cũng nghe đến, chỉ cần cầm kỳ thư họa vượt qua ta, ta liền có thể gả cho hắn... Ngươi bây giờ thư hoạ đã vượt ta quá nhiều, chỉ cần cầm kỳ hai loại không kém... Cùng kết liền cành, cũng sẽ không không có khả năng...”"),t("br"),t("br"),n._v("Nói đến đây, khuôn mặt đỏ lên."),t("br"),t("br"),n._v("Một cái nữ hài, đem lời nói đến phân thượng này, xem như mười phần lớn mật."),t("br"),t("br"),n._v("Bất quá, gánh vác lấy đệ nhất tài nữ tên tuổi, thanh cao đã quen, thật vất vả gặp phải một vị, chân chính có thể vượt qua nàng, không khỏi cũng có chút ý nghĩ."),t("br"),t("br"),n._v("“Thế nào, ngươi là muốn trốn nợ, không nguyện ý trả cho ta cái kia một viên tuyệt phẩm linh thạch?”"),t("br"),t("br"),n._v("Trương Huyền da đầu nổ tung, con mắt trợn tròn, tràn đầy tức giận."),t("br"),t("br"),n._v("Vừa rồi đánh cược thời điểm, nói rất hay, thua liền cho, làm sao, hiện tại thật thua, không dám thừa nhận, còn muốn gả cho bản thân... Làm cái gì mộng đẹp!"),t("br"),n._v(" “A?”"),t("br"),t("br"),n._v("Trương Tử Tình cắn răng."),t("br"),t("br"),n._v("“Thua thì thua, một viên tuyệt phẩm linh thạch nhất định phải cho!” Trương Huyền nghĩa chính ngôn từ: “Khác làm chút vô dụng, muốn trốn nợ, không được!”"),t("br"),t("br"),n._v("Lần này chuyên môn đánh mặt Trương gia, thay Lạc Nhược Hi trút giận, lại toàn bộ nữ nhân trở về, còn thế nào có mặt gặp người?"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Trương Tử Tình thật muốn điên rồi."),t("br"),t("br"),n._v("Náo loạn nửa ngày, bản thân tại trong lòng đối phương, liền một viên linh thạch giá cả cũng chưa tới..."),t("br"),t("br"),n._v("Đường đường Trương gia đệ nhất tài nữ, không bằng một viên tuyệt phẩm linh thạch... Truyền đi, mất mặt đều phải ném chết!"),t("br"),t("br"),n._v("“Cái này... Cái này, Trương Huyền thưởng thức... Thật đúng là đủ kỳ lạ!”"),t("br"),t("br"),n._v("Trương Vô Trần mở to hai mắt nhìn, nhẫn nhịn nửa ngày, mới nói ra một câu."),t("br"),t("br"),n._v("“Đúng vậy a, ta vẫn là lần đầu tiên, gặp phải có thể cự tuyệt Tử Tình, hơn nữa... Như thế hùng hồn!”"),t("br"),t("br"),n._v("Vô Chân trưởng lão cũng đầy là khó tin."),t("br"),t("br"),n._v("Lợi hại người gặp qua không ít, nhưng anh hùng nan quá mỹ nhân quan, trước mắt vị này ngược lại tốt, Trương Tử Tình như vậy tài nữ, mỹ nữ đưa ra loại yêu cầu này, đều bị cự tuyệt, chỉ vì một viên tuyệt phẩm linh thạch..."),t("br"),t("br"),n._v("Nói thật, thật muốn cưới đối phương, lấy nàng giao thiệp cùng năng lực, tuyệt phẩm linh thạch mà thôi, còn không muốn bao nhiêu muốn bao nhiêu?"),t("br"),t("br"),n._v("“Đoán không sai, hắn là không muốn cùng Trương gia có quá nhiều liên quan!”"),t("br"),t("br"),n._v("Cảm xúc xong, Trương Vô Trần cũng hiểu được, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Đối phương đối với trận pháp, khải linh, giám bảo, thư hoạ lý giải sâu như vậy, đủ thấy là cái cực đoan thông minh người, làm sao không lý giải Trương Tử Tình ý tứ, biết còn nói ra lời kia, rõ ràng là không muốn cùng Trương gia chạm phải quá nhiều."),t("br"),t("br"),n._v("“Trương Huyền, tại hạ Trương gia Trương Giang, có dám cùng ta so sánh với một hồi?”"),t("br"),t("br"),n._v("Cự tuyệt Trương Tử Tình, dẫn đến Trương gia tuấn kiệt nổi giận, một thanh niên tung người từ trong đám người bay ra, rơi vào trước mặt."),t("br"),t("br"),n._v("“So cái gì?”"),t("br"),t("br"),n._v("Trương Huyền mí mắt vừa nhấc."),t("br"),t("br"),n._v("Trương gia thế hệ trẻ tuổi, lẫn trong đám người, cố ý tìm hắn để gây sự, đã sớm nhìn ở trong mắt, dù sao lần này tới liền là muốn làm lớn chuyện, chủ động xông lại tự nhiên tốt nhất."),t("br"),t("br"),n._v("“Tại hạ khác không có sở trường, nhưng thuần thú nhất đạo, không ai bằng, ngươi nhục ta Trương gia tài nữ, có dám so với ta thi thuần thú?”"),t("br"),t("br"),n._v("Trương Giang nói."),t("br"),t("br"),n._v("“Như thế nào so?”"),t("br"),t("br"),n._v("“Rất đơn giản, ba năm trước đây, Trương gia bắt đến một đầu hung mãnh Thánh thú, đến bây giờ đều không có người có thể thuần phục, ta cũng không có thành công, chỉ cần ngươi có thể thành công, ta liền thừa nhận ngươi thuần thú phương pháp, so ta càng tốt hơn!”"),t("br"),t("br"),n._v("Trương Giang hừ lạnh."),t("br"),t("br"),n._v("“Ta nếu thuần phục không được đâu?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Cái tên này rõ ràng tại đào hố, chính ngươi đều thuần phục không được, khẳng định rất khó, ta đồng dạng thuần phục không được, chẳng phải thua?"),t("br"),t("br"),n._v("Hắn thuần thú mặc dù lợi hại, nhưng cũng chỉ hạn chế tại nắm giữ Long tộc huyết mạch, hoặc là thực lực so với hắn thấp một ít Thánh thú, tu vi quá cao, cũng không có biện pháp quá tốt."),t("br"),t("br"),n._v("“Ta nghe nói, ngươi có thể đem đại trận linh tính tuỳ tiện thuần phục, làm sao, để ngươi thuần phục Thánh thú, cũng không dám?”"),t("br"),t("br"),n._v("Trương Giang khóe miệng nâng lên, nhìn lại: “Ta cùng đầu kia Thánh thú, chỉ ở chung được ba ngày, độ thân thiện, đã đạt đến hai mươi lăm, nếu như ngươi trong vòng ba ngày, có thể vượt qua cái này, dù là không có thuần phục thành công, ta cũng thừa nhận vượt qua ta!”"),t("br"),t("br"),n._v("“Hai mươi lăm?”"),t("br"),t("br"),n._v("Gặp có quy tắc, Trương Huyền lúc này mới nhẹ gật đầu, nhìn lại: “Nếu như... Ta đem đầu thánh thú này thuần phục, nó quy ta sao?”"),t("br"),t("br"),n._v("Cái này muốn trước nói tốt, dù sao đối phương bắt, quy bản thân liền thuần một chút, không về, quên đi."),t("br"),t("br"),n._v("“Cứ việc đầu thánh thú này là gia chủ tự tay bắt đến, ý định muốn tự thân thuần phục, nhưng ngươi có thể thành công, đem hắn mang đi cũng không sao!”"),t("br"),t("br"),n._v("Trương Giang gật đầu, mắt sáng lên, ánh mắt lộ ra vẻ tàn nhẫn: “Bất quá... Nếu như ngươi thua, ta cũng không hỏi ngươi muốn tuyệt phẩm linh thạch, chỉ cần ngươi quỳ xuống cho Tử Tình xin lỗi, sau đó... Lập tức cho ta cút khỏi Trương gia!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1449"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1451"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);