(window.webpackJsonp=window.webpackJsonp||[]).push([[517],{872:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1442-co-muon-bao-thu-hay-khong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1442-co-muon-bao-thu-hay-khong"}},[n._v("#")]),n._v(" Chương 1442: Có muốn báo thù hay không?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1442: Có muốn báo thù hay không?"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Tuy là không xác định, có thể hay không thuần phục đối phương nói tới đầu kia Thánh thú, nhưng vượt qua trước mắt vị này không khó lắm."),t("br"),t("br"),n._v("Huống chi, vẫn là tiểu thiên tài cha bắt đến, bất kể như thế nào trước thuần phục lại nói, nếu không, như thế nào đem tên kia bức đi ra?"),t("br"),t("br"),n._v("Như thế nào thay Kiếm Tần Sinh trút giận?"),t("br"),t("br"),n._v("Nếu như tên tiểu thiên tài kia, thật không dám đi ra, vậy trước tiên đem hắn cha, đánh một trận tơi bời lại nói... Ai bảo hắn sinh ra không biết xấu hổ như vậy nhi tử... Tử không nuôi, lỗi của cha!"),t("br"),t("br"),n._v("“Vậy thì bắt đầu đi...”"),t("br"),t("br"),n._v("Thấy hắn đồng ý, Trương Giang ánh mắt hiện lên nhất đạo hưng phấn, bàn tay lớn vẫy một cái, sau lưng một cái lão giả lập tức lui ra ngoài, biến mất tại mọi người trước mắt, thời gian không dài, lần nữa trở về, cổ tay khẽ đảo, một cái to lớn chiếc lồng xuất hiện trong đám người ở giữa trên quảng trường."),t("br"),t("br"),n._v("Chiếc lồng đường kính chừng hơn bốn mươi mét, không biết tài liệu gì luyện chế mà thành, toàn thân đen kịt, mặt ngoài đủ loại hoa văn vây quanh, hiển nhiên bố trí lợi hại đại trận, để bị cầm tù trong đó sinh mệnh, không thể chạy trốn."),t("br"),t("br"),n._v("Lồng bên trong, một đầu bộ dáng cùng sư tử có chút tương tự Thánh thú, nằm rạp trên mặt đất, lười biếng hướng bốn phía nhìn một vòng, ngay sau đó ngủ tiếp tới."),t("br"),t("br"),n._v("Cái tên này, cái đầu không tính quá lớn, thân dài chỉ có bốn năm mét, hơn nữa cũng không phát ra bất kỳ thanh âm cùng khí tức, nhưng ở tràng đám người, chỉ nhìn liếc mắt, tất cả đều con ngươi co rụt lại."),t("br"),t("br"),n._v("“Là Hỏa Diễm Kỳ Lân thú!”"),t("br"),t("br"),n._v("“Đây chính là trưởng thành liền có thể đạt tới Thánh vực cửu trọng siêu cấp Thánh thú, Trương gia dạng này siêu cấp gia tộc, đều số lượng không nhiều... Khó trách ngay cả Trương Giang loại này thuần thú thiên tài, đều thuần phục không được!”"),t("br"),t("br"),n._v("“Loại này Thánh thú, nếu như có thể thuần phục một đầu, chẳng khác nào trong nháy mắt nắm giữ Thánh vực cửu trọng sức chiến đấu...”"),t("br"),t("br"),n._v("“Ha ha, nào có dễ dàng như vậy, cái tên này nắm giữ Thượng Cổ Kỳ Lân huyết mạch, kiêu căng khó thuần, đừng nói muốn thuần phục, tới gần cũng khó khăn, nếu là có thể thành công, Hưng Mộng kiếm thánh, cũng không trở thành đem hắn lưu tại Trương gia ba năm, đều không thả ra đi!”"),t("br"),t("br"),n._v("“Này ngược lại là...”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, đám người tất cả đều khuôn mặt ngưng trọng."),t("br"),t("br"),n._v("“Tam trưởng lão, có muốn hay không ngăn cản một chút?”"),t("br"),t("br"),n._v("Chần chờ phút chốc, Vô Chân trưởng lão cũng nhịn không được nữa, nhìn lại."),t("br"),t("br"),n._v("“Khỏi cần...” Trương Vô Trần lắc đầu."),t("br"),t("br"),n._v("“Nhưng... Hỏa Diễm Kỳ Lân thú muốn thuần phục, nhất định phải đi vào lồng sắt, mà một khi tiến vào bên trong, cái tên này liền sẽ cuồng bạo hại người... Ba năm qua, hết thảy tổn thương mười ba vị cửu tinh thuần thú sư, vị này Trương sư tới so kiếm, bị Thánh thú gây thương tích, ta sợ... Truyền đi không dễ nghe!”"),t("br"),t("br"),n._v("Vô Chân trưởng lão vội nói."),t("br"),t("br"),n._v("Không phải hắn lo lắng, mà là đầu thánh thú này tình huống, người ngoài không biết, hắn biết đến rất rõ ràng."),t("br"),t("br"),n._v("Quá khó thuần phục!"),t("br"),t("br"),n._v("Tại chiếc lồng bên ngoài, bất kể ăn ngon vẫn là bảo vật, đối phương nhìn cũng không nhìn liếc mắt, mà tiến vào trong lồng, sẽ lập tức gặp phải công kích... Bởi vì như vậy, ba năm qua tổn thương cửu tinh thuần thú sư vượt qua mười vị, Trương Giang để Trương Huyền thuần phục, rõ ràng không có ý tốt, là muốn mượn đầu này tất cả mọi người tay, giáo huấn đối phương một hồi."),t("br"),t("br"),n._v("“Ta biết những thứ này... Nếu quả thật gặp nguy hiểm, ta sẽ ra tay!” Trương Vô Trần nói."),t("br"),t("br"),n._v("Đối phương có thể nghĩ đến, hắn làm sao nghĩ không ra, sở dĩ không khuyên giải ngăn trở, là bởi vì vị này Trương Huyền vừa xuất hiện, liền cho ra chấn kinh thực sự quá nhiều, hắn muốn nhìn một chút, Trương Giang thiết kế cửa ải khó khăn này, đến cùng có thể hay không thông qua."),t("br"),t("br"),n._v("“Cái này... Tốt a!”"),t("br"),t("br"),n._v("Gặp tam trưởng lão nói như vậy, Trương Vô Trần không tại mở miệng, ngẩng đầu hướng Trương Huyền nhìn, lại thấy hắn cũng mày nhăn lại, tựa hồ tại suy tư cái gì."),t("br"),t("br"),n._v("“Hỏa Diễm Kỳ Lân thú...”"),t("br"),t("br"),n._v("Trương Huyền đứng tại chỗ, về loại này Thánh thú kiến thức, trong đầu không ngừng chảy xuôi."),t("br"),t("br"),n._v("Loại này Thánh thú tính tình bản tính, Thánh tử điện thuần thú công hội bên trong trong thư tịch, đều có ghi chép, hơi nhớ lại, liền hiểu vị này Trương Giang mục đích."),t("br"),t("br"),n._v("“Loại người này, không có Long tộc huyết mạch, thực lực lại siêu cường, muốn thuần phục, quả thực không quá dễ dàng...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, Trương Huyền có chút bất đắc dĩ."),t("br"),t("br"),n._v("Nói thật, sớm biết là cái tên này, hắn liền không đáp ứng, coi như hắn đánh thuần thú pháp rất lợi hại, đối mặt cường đại như thế gia hỏa, sợ cũng bất lực."),t("br"),n._v(" Đánh không lại... Như thế nào thuần phục?"),t("br"),t("br"),n._v("Thậm chí... Lấy thực lực của hắn, chỉ sợ còn chưa tới đến đối phương trước mặt, liền bị ngọn lửa nướng thành than cốc!"),t("br"),t("br"),n._v("“Nướng cháy?”"),t("br"),t("br"),n._v("Nghĩ đến đối phương đầy người hỏa diễm, so địa hỏa đều phải kinh khủng, Trương Huyền đột nhiên sửng sốt một cái."),t("br"),t("br"),n._v("“Thế nào? Có thể bắt đầu chưa?”"),t("br"),t("br"),n._v("Thấy hắn suy nghĩ, Trương Giang cười lạnh, khóe miệng nâng lên, nhìn lại."),t("br"),t("br"),n._v("“Có thể!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu: “Đem lồng mở ra đi!”"),t("br"),t("br"),n._v("Thấy hắn đồng ý như thế nhanh chóng, Trương Giang có chút kinh ngạc, ngay sau đó nhịn không được cười lên: “Tốt!”"),t("br"),t("br"),n._v("Ngay sau đó khoát tay chặn lại, vừa rồi đem chiếc lồng mang tới lão giả, vội vàng tiến lên, bàn tay một trảo, lồng sắt lập tức xuất hiện một cánh cửa, không lớn, vừa vặn có thể chui vào một người, Hỏa Diễm Kỳ Lân thú hình thể khó mà chạy ra."),t("br"),t("br"),n._v("Hít sâu một hơi, Trương Huyền hướng cửa đi tới."),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Kiếm Tần Sinh vội vàng ngăn cản."),t("br"),t("br"),n._v("Hắn đối thuần thú cứ việc hiểu được không nhiều, nhưng đối Hưng kiếm thánh biết rất nhiều, cái tên này ưa thích trang bức, hơn nữa cực đoan tự đại, loại người này đều không thể thuần phục, đủ thấy kẻ trước mắt này đáng sợ!"),t("br"),t("br"),n._v("Đáng sợ như vậy Thánh thú, trực tiếp đi vào... Thực sự quá nguy hiểm."),t("br"),t("br"),n._v("“Yên tâm đi!”"),t("br"),t("br"),n._v("Xoay đầu lại, Trương Huyền nhẹ gật đầu: “Ta sẽ biểu hiện ra trường kiếm, khiến người khác biết đến...”"),t("br"),t("br"),n._v("“Thi triển kiếm thuật?”"),t("br"),t("br"),n._v("Kiếm Tần Sinh lông mày nhảy một cái: “Ta không phải ý tứ này...”"),t("br"),t("br"),n._v("Vừa rồi hắn cảm thấy đối phương có chút không làm việc đàng hoàng, lấy so kiếm thân phận tới, kết quả nhưng phá trận, khải linh, thư hoạ, giám bảo, mới có ý nhắc nhở..."),t("br"),t("br"),n._v("Mà lần này ngăn lại đối phương, là thật lo lắng, sợ gặp nguy hiểm!"),t("br"),t("br"),n._v("Loại này Thánh thú trước mặt không nhúc nhích, đều sợ đối phương công kích, lại biểu hiện ra kiếm thuật, còn không bị tươi sống chém giết?"),t("br"),t("br"),n._v("“Ta rõ ràng...”"),t("br"),t("br"),n._v("Bàn tay quơ quơ, Trương Huyền lười nhác nói nhiều, thân thể nhảy lên, đã đi vào lồng sắt."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Vừa tiến vào chiếc lồng, lập tức giống đi vào một thế giới khác, sóng nhiệt đánh tới, muốn đem hắn toàn thân đều đốt trụi, trước mắt không gian tại cực nóng nhiệt độ bên dưới, đều giống như vặn vẹo, để cho người ta con mắt hoa mắt, thấy không rõ lắm."),t("br"),t("br"),n._v("“Hỏa Diễm Kỳ Lân thú, lấy địa hỏa làm thức ăn, thân thể có thể sinh ra hỏa vân, nhất niệm phía dưới, thiêu đốt hơn ba trăm dặm, Thánh Nhân quý tộc, cũng không dám tuỳ tiện đắc tội...”"),t("br"),t("br"),n._v("Nhớ tới về loại này Thánh thú giới thiệu, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Nói thật, có thể đem đầu này tất cả mọi người vây ở chỗ này, ba năm đều không trốn thoát được, chỉ sợ cũng chỉ có Trương gia mới có thể làm đến, đổi lại những người khác, chiếc lồng khẳng định sớm đã bị đốt thành tro bụi."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trong lòng đang cảm xúc, ngay sau đó liền cảm thấy bốn phía nhiệt độ càng ngày càng cao, vừa rồi nằm rạp trên mặt đất, không nhúc nhích lười biếng Hỏa Diễm Kỳ Lân thú, thấy có người đi vào, ánh mắt lộ ra hung tàn chi ý, chậm rãi đi tới."),t("br"),t("br"),n._v("“Kỳ Lân thú đúng không, ngươi hẳn là đó có thể thấy được, ta không phải Trương gia người!”"),t("br"),t("br"),n._v("Thấy nó đi tới, Trương Huyền cũng không bối rối, mà là mỉm cười: “Đường đường nắm giữ Kỳ Lân huyết mạch Thánh thú, bị Trương gia người, không có chút nào tôn nghiêm vây ở trong lồng, dằn vặt ba năm, ta liền muốn hỏi ngươi một câu...”"),t("br"),t("br"),n._v("“Ngươi có muốn báo thù hay không?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1450"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1452"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);