(window.webpackJsonp=window.webpackJsonp||[]).push([[53],{409:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1024-nguoi-la-truong-vien-truong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1024-nguoi-la-truong-vien-truong"}},[n._v("#")]),n._v(" Chương 1024: Ngươi là Trương viện trưởng?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Không chỉ Tống Chân phát điên, đối diện Chung hội trưởng ba người cũng từng người khóe miệng co giật, sắp nổ."),t("br"),t("br"),n._v("Luyện khí sư công hội ghế mặc dù đều rất trân quý, có giá trị không nhỏ, cũng chỉ là bình thường vật liệu gỗ, cùng người bình thường đánh nhau đều không có uy lực gì, dùng để đối phó bản thân tổ ba người thành Tử Nguyệt Tam Tinh trận... Quả thực chính là trần trụi vũ nhục."),t("br"),t("br"),n._v("“Đã ngươi tự tìm cái chết, cũng đừng trách chúng ta không khách khí!”"),t("br"),t("br"),n._v("Răng cắn chặt, Chung hội trưởng hừ lạnh, cổ tay lật qua lật lại ở giữa, kiếm khí như dải lụa, vây quanh tới."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Trong nháy mắt, liền đem bản thân công kích mạnh nhất kiếm chiêu, phát huy ra."),t("br"),t("br"),n._v("Bán Thánh cấp võ kỹ... Thiên Ti kết!"),t("br"),t("br"),n._v("Tâm giống như song lưới tơ, bên trong có ngàn ngàn kết!"),t("br"),t("br"),n._v("Chiêu này võ kỹ là hơn một ngàn năm trước, một vị tình si Kiếm thánh sáng chế, kiếm khí cũng không phải là lấy công kích chém giết làm chủ, mà là triền miên, một khi trúng chiêu, như là rơi vào nước bùn, không cách nào tự kềm chế."),t("br"),t("br"),n._v("Lấy thực lực của hắn bây giờ, dùng ra chiêu này, lại phối hợp thêm trận pháp, liền xem như Thánh vực nhị trọng trung kỳ cường giả đều rất khó ứng đối, xem ra hắn cũng biết thực lực của đối phương cùng đối với trận pháp lĩnh ngộ, trực tiếp dùng ra thủ đoạn lợi hại nhất, tranh thủ đem hắn một lần bắt lại."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Kiếm khí hình thành cái này đến cái khác không lớn vòng tròn, chỉ cần bị bao phủ ở bên trong, lợi hại hơn nữa cường giả đều muốn cởi một lớp da, đối diện thanh niên hình như không có phát hiện đáng sợ, xách theo ghế chân, tiếp tục đi tới."),t("br"),t("br"),n._v("“Tới thật đúng lúc!” Ánh mắt lóe lên, Chung hội trưởng cười lạnh, trường kiếm xoay một cái nhất câu."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Kiếm khí lập tức đem Trương Huyền, bao ở trong đó."),t("br"),t("br"),n._v("“Thành công?” Chung hội trưởng hưng phấn suýt chút nữa nhảy lên."),t("br"),t("br"),n._v("Vốn thi triển chiêu này, chỉ là muốn cuốn lấy đối phương, thăm dò thực lực chân chính của hắn, không nghĩ dễ dàng như vậy liền đem nó vây khốn."),t("br"),t("br"),n._v("Bị Thiên Ti kết vây khốn tu luyện giả, cho tới bây giờ, còn không có gặp qua, ai có thể tránh thoát."),t("br"),t("br"),n._v("Chính một mặt mừng rỡ, trong lòng cân nhắc xử lý như thế nào đối phương, chỉ thấy bị vây ở ở giữa thanh niên, thân thể kỳ lạ co rụt lại, giống như là biến mất, sau một khắc đã xuất hiện tại trước mắt của mình, mùi dầu nồng đậm ghế gỗ tử, húc đầu liền đập xuống."),t("br"),t("br"),n._v("“Ta...” Thân thể cứng đờ, không nghĩ rõ ràng đến cùng chuyện gì xảy ra, Chung hội trưởng cảm thấy trên mặt tê rần, ghế vuông vức góc cạnh, liền cứ thế mà bổ đi lên."),t("br"),t("br"),n._v("Bành bành bành bành!"),t("br"),t("br"),n._v("Ghế mặc dù uy lực không lớn, nhưng mà phối hợp đối phương chân khí, như là sắt thép đồng dạng trong nháy mắt liền đập đến hắn đầu đầy là máu."),t("br"),t("br"),n._v("“Dừng tay...” Cùng hắn cùng đi hai vị lục tinh đỉnh phong luyện khí sư cảm thấy sắp điên rồi."),t("br"),t("br"),n._v("Ba người bọn họ bố trí trận pháp, lại thi triển ra mạnh nhất tuyệt chiêu, thế mà còn bị đối phương đánh, mấu chốt nhất là, nếu như người ta dùng lợi hại hơn vũ khí ngược lại cũng thôi, chỉ tiện tay cầm dưới mông ngồi ghế... Suy nghĩ một chút đều buồn bực suy nghĩ hộc máu."),t("br"),t("br"),n._v("Bị cao thủ đánh, không quan trọng, nhưng nếu như cho phía ngoài rất nhiều luyện khí sư biết, bọn họ một vị hội trưởng hai vị trưởng lão bị người ta dùng ghế, hung hăng đánh một trận, đoán chừng cũng không mặt mũi thấy người."),t("br"),t("br"),n._v("Trong tiếng thét gào hai người, đồng loạt vọt lên, vì cứu hội trưởng, trong cơ thể của bọn họ chân khí, vận chuyển như là sôi trào, kiếm khí lôi kéo đến tựa như hai đạo trắng tinh tơ lụa."),t("br"),t("br"),n._v("Bất quá, còn chưa rơi vào trước mắt vị thanh niên này trên người, liền cảm thấy mắt tối sầm lại, đối phương ghế chẳng biết lúc nào cũng rơi vào trên mặt của bọn hắn."),t("br"),t("br"),n._v("Lốp bốp!"),t("br"),t("br"),n._v("Một tay nắm ghế chân, Trương Huyền lông mày dựng thẳng lên, cánh tay không ngừng vung vẩy, khí thế như rồng."),t("br"),t("br"),n._v("Thật sự là cho thể diện mà không cần."),t("br"),t("br"),n._v("Một mực hạ thủ lưu tình, chưa khỏi ý tứ ra tay, ba người bọn hắn ngược lại da mặt thật dày, lại là trận pháp, lại là tuyệt chiêu mạnh nhất, chơi đùa mồ hôi nhễ nhại."),t("br"),t("br"),n._v("Thánh vực nhị trọng Chiến sư cũng không là đối thủ, ba cái chỉ có nhất trọng gia hỏa, đừng nói chỉ là bày trận, cho dù đem Thất cô bát đại di toàn bộ kéo qua, lại thế nào chống đỡ được hắn tiến công?"),t("br"),t("br"),n._v("Cầm ghế một hồi cuồng quất, hai phút đồng hồ về sau, ba vị công hội nhất có thân phận luyện khí sư, liền đầu đầy là bao nằm trên mặt đất."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Một bên còn không có chậm tới Tôn Tấn, hoàn toàn bối rối."),t("br"),t("br"),n._v("Vốn cho rằng hội trưởng cùng hai đại trưởng lão đồng thời ra tay, kẻ trước mắt này tất nhiên sẽ bị dễ dàng chế ngự, tiếp nhận trừng trị, nằm mơ đều không có nghĩ đến, đối phương cầm cái ghế, liền đem ba người quật ngược..."),t("br"),t("br"),n._v("Ghế gỗ đối kháng Bán Thánh cấp vũ khí, đây là cái gì đấu pháp? Trước đó từ trước tới nay chưa từng gặp qua..."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Đem đến tìm phiền phức hội trưởng đám người rút trở mình, Trương Huyền đem ghế lần nữa thả xuống, ngồi tại cách đó không xa."),t("br"),t("br"),n._v("“Bây giờ có thể có thời gian, hảo hảo nghe ta nói sao?”"),t("br"),t("br"),n._v("“Ngươi... Rốt cuộc là ai?”"),t("br"),t("br"),n._v("Nằm mơ đều không có nghĩ đến cái này chỉ có 20 tới tuổi thanh niên, lại có thực lực cường đại như vậy, Chung hội trưởng răng cắn chặt: “Xông vào luyện khí sư công hội, đánh hội trưởng cùng trưởng lão, ta đã xem việc này đưa tin cho Danh Sư đường đường chủ, Phùng đường chủ là sẽ không bỏ qua các ngươi!”"),t("br"),t("br"),n._v("Danh Sư đường tọa trấn một phương, duy trì lấy một phương an bình, trước mắt vị này, mặc kệ thân phận gì, xông vào công hội, đả thương hội trưởng... Chính là không tuân thủ quy tắc, xúc phạm bọn họ quy định."),t("br"),t("br"),n._v("Để luyện khí sư công hội tổng bộ tới xử lý, về thời gian khẳng định không kịp, nhưng mà Hỏa Nguyên thành Danh Sư đường, một khi biết tin tức, tất nhiên hoả tốc chạy đến."),t("br"),t("br"),n._v("Cấp bậc của hắn mặc dù so Danh Sư đường đường chủ cao hơn, mệnh danh Hỏa Nguyên thành thứ nhất, nhưng mà trên thực tế sức chiến đấu kém xa tít tắp, lại thêm gần nhất không biết chuyện gì xảy ra, trong đường cao thủ nhiều như mây, một khi tới, ắt hẳn có thể đem cái tên này bắt được, để hắn biết nơi này không phải là cái gì người, đều có thể tùy tiện giương oai chỗ."),t("br"),t("br"),n._v("“Phùng đường chủ? Ngươi nói là Phùng Ích Huy?” Trương Huyền lộ ra nghiền ngẫm vui vẻ."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Chung hội trưởng cắn răng."),t("br"),t("br"),n._v("Hỏa Nguyên thành Danh Sư đường đường chủ Phùng Ích Huy, là hắn hảo hữu chí giao, hai người ở chung trên trăm năm, nghe được có người xông vào, bản thân liền đưa tin thông tri, dựa theo thời gian suy tính, bây giờ muốn nhất định lập tức liền muốn tới."),t("br"),t("br"),n._v("Trong lòng đang chờ mong, Phùng đường chủ đi tới đem cái tên này chế ngự, chỉ thấy ngồi tại cách đó không xa thanh niên ngẩng đầu lên, hướng ra phía ngoài nhìn lại: “Phùng đường chủ, nhìn thời gian dài như vậy náo nhiệt, có phải hay không cũng nên đi vào?”"),t("br"),t("br"),n._v("“Xem náo nhiệt? Phùng đường chủ... Đã tới?”"),t("br"),n._v("Trong lòng “Lộp bộp!” Thoáng cái, vội vàng ngẩng đầu nhìn lại, chỉ thấy một cái lão giả mặt mũi lúng túng đi đến."),t("br"),t("br"),n._v("“Trương sư!”"),t("br"),t("br"),n._v("Đi tới gian phòng, đối với cách đó không xa thanh niên cúi đầu đến cùng, sau đó lại lần nhìn lại: “Chung hội trưởng...”"),t("br"),t("br"),n._v("Vị này chính là Hỏa Nguyên thành Danh Sư đường đường chủ, Phùng Ích Huy."),t("br"),t("br"),n._v("Nhận được bạn già đưa tin, hắn trực tiếp bay tới, vốn cho rằng là ai làm loạn, nhìn thấy Trương Huyền, lập tức ngừng lại."),t("br"),t("br"),n._v("Vị này Trương viện trưởng mặc dù cùng hắn cùng là lục tinh luyện khí sư, nhưng thân là danh sư học viện viện trưởng, địa vị tự động cao hơn một cấp, đã cùng thất tinh danh sư tương đương, căn bản là hắn không cách nào so sánh."),t("br"),t("br"),n._v("Bởi vậy, rõ ràng nhìn thấy Chung hội trưởng bị đối phương dùng ghế điên cuồng đánh, vẫn như cũ không dám lên phía trước, trốn ở bên ngoài cất giấu... Chỉ là không nghĩ tới, vẫn là bị Trương sư phát hiện."),t("br"),t("br"),n._v("Chỉ có thể tràn đầy lúng túng đi ra."),t("br"),t("br"),n._v("“Trương sư? Hắn là... Danh sư?”"),t("br"),t("br"),n._v("Không chú ý đối phương lễ tiết, Chung hội trưởng trái tim co lại, vội vàng nhìn về phía cách đó không xa thanh niên."),t("br"),t("br"),n._v("Hắn không nói bản thân là lục tinh luyện khí sư ư? Làm sao thời gian nháy mắt còn nói là danh sư? Hơn nữa có thể để cho Phùng đường chủ như vậy tôn kính, họ Trương..."),t("br"),t("br"),n._v("“Chẳng lẽ... Là danh sư học viện viện trưởng, Trương Huyền Trương sư?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Trương sư có tới hay không Hỏa Nguyên thành hắn không biết, nhưng danh sư học viện hơn hai vạn học sinh, đến đây thí luyện sự tình, hắn nhưng là rõ rõ ràng ràng."),t("br"),t("br"),n._v("Hai vạn học sinh bên trong, có một vạn phụ tu luyện khí sư chức nghiệp, còn theo hắn nơi này điều đi không biết bao nhiêu luyện khí thiết bị... Chuyện mới quá khứ không hai ngày, tự nhiên biết rõ."),t("br"),t("br"),n._v("Học sinh đều tới, viện trưởng... Làm sao có thể không đến?"),t("br"),t("br"),n._v("“Không sai, ta là Trương Huyền!”"),t("br"),t("br"),n._v("Cũng không phủ nhận, Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Trương viện trưởng?” Cách đó không xa Tống Chân thân thể cứng ngắc, lập tức ánh mắt hừng hực."),t("br"),t("br"),n._v("Vị này Trương viện trưởng kế vị thời gian mặc dù không dài, danh khí nhưng vang vọng toàn bộ Hồng Viễn đế quốc, cho dù Hỏa Nguyên thành tương đối xa xôi, cũng sớm có nghe nói."),t("br"),t("br"),n._v("Như vậy nhân vật truyền kỳ, đã nói, hắn có thể trở thành chân chính luyện khí sư... Có lẽ thật có thể thành!"),t("br"),t("br"),n._v("Cùng nét mặt của hắn khác biệt, một bên Tôn Tấn thì con ngươi thu hẹp, toàn thân như nhũn ra."),t("br"),t("br"),n._v("Nếu như đối phương chỉ là luyện khí sư, bản thân tổng thể không thừa nhận, náo động đến lại lớn, cũng hết cách rồi, nếu là danh sư... Liền không nói được rồi!"),t("br"),t("br"),n._v("Danh sư nắm giữ người thường khó mà so sánh ánh mắt cùng kiến thức, Danh Sư đường càng là lực lượng vô cùng, thật muốn điều tra, cho dù nguỵ biện nhiều hơn nữa, cũng vô dụng!"),t("br"),t("br"),n._v("Huống chi còn có Trương Huyền vị này truyền kỳ danh sư tại!"),t("br"),t("br"),n._v("“Cho dù ngươi là... Danh sư học viện viện trưởng, xông vào luyện khí sư công hội, đối với chúng ta ra tay, cũng làm trái quy định đi...”"),t("br"),t("br"),n._v("Sợ tới mức mặt như màu đất, có điều, Chung hội trưởng vẫn là cắn răng cắn chặt."),t("br"),t("br"),n._v("Cho dù thân phận đối phương tôn quý,, xông tới không nói hai lời, đem nhiều người như vậy đả thương, cũng coi như vi phạm với quy định đi!"),t("br"),t("br"),n._v("“Quy định?”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Trương Huyền đứng dậy, trên thân mang theo viện trưởng uy nghiêm cùng khí thế: “Quy định, là dùng tới đối phó tuân thủ quy định người, mà đối với không tuân thủ người, tất cả phương pháp, cũng có thể sử dụng!”"),t("br"),t("br"),n._v("“Không tuân thủ?”"),t("br"),t("br"),n._v("Chung hội trưởng sững sờ: “Không biết chúng ta luyện khí sư công hội cái kia điểm không tuân thủ?”"),t("br"),t("br"),n._v("“Tống Chân, ngươi qua đây!”"),t("br"),t("br"),n._v("Không trả lời hắn, Trương Huyền xoay đầu lại."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Tống Chân nhẹ gật đầu, vội vàng đi tới trước mặt."),t("br"),t("br"),n._v("“Đưa ngươi chân khí thả ra ngoài!”"),t("br"),t("br"),n._v("Trương Huyền mí mắt vừa nhấc."),t("br"),t("br"),n._v("Tống Chân nhẹ gật đầu, vận chuyển chân khí, lập tức một đạo hùng hậu khí tức, tràn ngập cả phòng."),t("br"),t("br"),n._v("“Cái này... Loại này chân khí...”"),t("br"),t("br"),n._v("Chung hội trưởng nhướng mày, lập tức con ngươi thu hẹp."),t("br"),t("br"),n._v("Thân là lục tinh đỉnh phong luyện khí sư, đối phương trong cơ thể cỗ này chân khí, đến cùng cái gì thuộc tính, cùng địa hỏa có hay không có thể phù hợp, tự nhiên liếc thấy đi ra."),t("br"),t("br"),n._v("Có thể nói, loại này chân khí, muốn trở thành luyện khí sư, là tuyệt đối không thể nào."),t("br"),t("br"),n._v("“Đưa ngươi sự tình nói cho vị hội trưởng này nghe một chút đi!”"),t("br"),t("br"),n._v("Biết đối phương nhìn ra, Trương Huyền không nói thêm lời, tiếp tục nói."),t("br"),t("br"),n._v("“Được...” Tống Chân gật đầu: “Ta là Tôn Tấn luyện khí sư một cái học đồ, bảy tuổi bắt đầu học tập luyện khí, một thân tu luyện công pháp, đều là hắn truyền thụ...”"),t("br"),t("br"),n._v("Không có giấu diếm, đem trước cùng Trương Huyền nói, toàn bộ thuật lại một lần."),t("br"),t("br"),n._v("Mặc dù chuyện gì xảy ra, còn không biết rõ, nhưng vị này Trương viện trưởng, đã vì hắn ra mặt, tất nhiên không phải chuyện xấu."),t("br"),t("br"),n._v("“Tu luyện loại công pháp này, còn để các ngươi khảo hạch luyện khí sư? Cái này sao có thể? Trừ phi...”"),t("br"),t("br"),n._v("Nghe xong Tống Chân lời nói, Chung hội trưởng rốt cuộc minh bạch tới, vẻ mặt thoáng cái trở nên trắng bệch, lần nữa nhìn về phía cách đó không xa Tôn Tấn, âm trầm như sương."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1032"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1034"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);