(window.webpackJsonp=window.webpackJsonp||[]).push([[54],{408:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1025-danh-su-khong-đuoc-lan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1025-danh-su-khong-đuoc-lan"}},[n._v("#")]),n._v(" Chương 1025: Danh sư, không được lấn!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Thân là lục tinh đỉnh phong luyện khí sư, nghe đến đó, kết hợp với Tôn Tấn trước đó làm ra chế tác vì, nếu như lại không làm rõ ràng được chuyện gì xảy ra, quả thực có thể làm tràng tự sát."),t("br"),t("br"),n._v("Trong lòng đoán ra, trên mặt nhưng như cũ khó tin, Chung hội trưởng lần nữa nhìn về phía cách đó không xa Tôn Tấn."),t("br"),t("br"),n._v("Vị này, từng là hắn thích nhất trưởng lão, thậm chí cảm thấy đến có thể kế thừa y bát, trở thành hội trưởng... Làm sao lại làm ra loại chuyện này?"),t("br"),t("br"),n._v("Có thể sự thật liền bày ở trước mắt, đối phương đường đường danh sư học viện viện trưởng, nếu như không điều tra rõ ràng, không biết thật giả, tuyệt không có khả năng tự ô thân phận, ngang nhiên xông lại, gây ra động tĩnh lớn như vậy!"),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Răng cắn chặt, lạnh lùng nhìn sang, hận không thể đem kẻ trước mắt này lột."),t("br"),t("br"),n._v("Nếu như suy đoán là thật, không chỉ riêng này cái Tôn Tấn, mất mặt xấu hổ, toàn bộ Hỏa Nguyên thành luyện khí sư công hội, đều sẽ trở thành tổng bộ phản diện tài liệu giảng dạy, thậm chí liền chính hắn đều muốn chịu đến hậu nhân chỉ trích, cả một đời không ngẩng đầu được lên, để tiếng xấu muôn đời!"),t("br"),t("br"),n._v("“Ta...” Khóe miệng giật một cái, Tôn Tấn vội vàng giải thích: “Hội trưởng, ta chưa làm qua, thật không biết!”"),t("br"),t("br"),n._v("“Không biết?”"),t("br"),t("br"),n._v("Chung hội trưởng ánh mắt như điện: “Ngươi hẳn phải biết đối danh sư nói láo kết quả! Bây giờ nói lời nói thật, có lẽ còn có thể khẩn cầu mở ra một con đường, một khi hoàn toàn điều tra ra, đừng nói ta, cho dù Trương sư tại chỗ đưa ngươi chém giết, ta cũng nói không ra nửa cái ‘Không’ chữ!”"),t("br"),t("br"),n._v("Danh sư, không được lấn!"),t("br"),t("br"),n._v("Đối danh sư nói láo, một khi bị điều tra ra, tội lỗi tăng thêm, không chỉ bản nhân có thể bị tại chỗ đánh chết, có liên hệ máu mủ hậu bối, đều sẽ bị liên lụy, không cho phép sát hạch tới cửu lưu bất kỳ nghề nghiệp nào!"),t("br"),t("br"),n._v("Đó cũng không phải Danh Sư đường quyết định quy củ, mà là rất nhiều chức nghiệp, đối sư giả kính trọng, tập hợp phát xuống lời thề."),t("br"),t("br"),n._v("Liền “Sư” đều sẽ lấn, thật giống như liền cha mẹ đều lừa gạt đồng dạng, đã không có trong lòng chuẩn tắc, giữ lại sớm muộn gì đều sẽ trở thành gieo họa!"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Vẻ mặt trở nên trắng bệch, Tôn Tấn thân thể không ngừng run rẩy."),t("br"),t("br"),n._v("Cái quy củ này, hắn tự nhiên biết rõ, chỉ là... Ở sâu trong nội tâm, còn có chút mong đợi, thời gian lâu dài, đối phương không tra được."),t("br"),t("br"),n._v("“Thế nào, nói không ra lời?”"),t("br"),t("br"),n._v("Chung hội trưởng trong mắt thất vọng càng lúc càng nồng nặc."),t("br"),t("br"),n._v("Thật ra thì từ đối phương chần chờ trong chớp mắt kia, hắn cũng đã đoán được, thật chưa làm qua, tất nhiên sẽ dựa vào lí lẽ biện luận, mà không biết mặt mũi xoắn xuýt."),t("br"),t("br"),n._v("Hiện tại bộ dáng, đủ để chứng minh vấn đề."),t("br"),t("br"),n._v("“Đừng làm ôm thời gian lâu dài, Danh Sư đường khả năng không tra được may mắn tâm lý... Nếu như ngay cả chút chuyện này đều tra không rõ ràng, cũng liền không gọi Danh Sư đường!”"),t("br"),t("br"),n._v("Một bên Phùng Ích Huy đường chủ, cũng đem chuyện đoán đi ra, khuôn mặt trầm thấp."),t("br"),t("br"),n._v("Tại hắn quản lý phạm vi bên trong, xuất hiện loại tình huống này, bản thân không có phát giác, lại bị đi tới đây chỉ có hai ba ngày Trương viện trưởng phát hiện, đối với hắn mà nói cũng là một loại nghiêm trọng không làm tròn bổn phận."),t("br"),t("br"),n._v("Bị đoán đúng tâm sự, Tôn Tấn toàn thân cứng ngắc, không biết trả lời như thế nào."),t("br"),t("br"),n._v("Quả thực, chỉ cần bằng lòng truy xét, còn không có Danh Sư đường không tra được chuyện."),t("br"),t("br"),n._v("Chuyện của mình làm, cho dù thời gian lâu dài, hơn ba mươi vị học đồ người nhà, tất nhiên vẫn còn, chỉ cần tinh tế tìm kiếm hỏi thăm nhất định có thể tìm tới dấu vết để lại."),t("br"),t("br"),n._v("Một cái học đồ, không cách nào thông qua khảo hạch, là thiên phú quá kém, nhiều người như vậy, liền một vị đều không có thông qua, ngu nữa cũng có thể nhìn ra vấn đề."),t("br"),t("br"),n._v("Trước đó học đồ địa vị quá thấp, hơn nữa di động tính rất lớn, không có người chú ý ngược lại cũng thôi, hiện tại tập trung điều tra, liền khẳng định không che giấu được."),t("br"),t("br"),n._v("Huống chi..."),t("br"),t("br"),n._v("Con ngươi co rụt lại, cái tay còn lại chưởng, lặng lẽ sờ về phía ngón giữa tay phải mang theo trữ vật giới chỉ, chân khí trong cơ thể tuôn ra, tính toán đem hắn tiêu diệt, còn không có động tác, liền thấy một đạo kiếm mang gào thét hiện lên."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Ngón giữa tay phải bị trực tiếp chặt đứt, nhẫn cũng rơi trên mặt đất."),t("br"),t("br"),n._v("“Nếu như đoán không sai, hắn truyền thụ Tống Chân bộ công pháp này nên tại trong giới chỉ!”"),t("br"),t("br"),n._v("Chặt đứt cử động của đối phương, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Chung hội trưởng đưa tay chộp một cái, đem trên mặt đất trữ vật giới chỉ, cầm tới."),t("br"),t("br"),n._v("Lực lượng tuôn ra, đem phía trên linh hồn ấn ký xóa đi, cúi đầu nhìn một hồi, một lát sau một quyển sách bất ngờ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Nhẹ nhàng mở ra, vẻ mặt lập tức biến thành xanh xám, cả người đều tựa hồ sắp nổ, răng cắn chặt, đem hắn đưa cho cách đó không xa Tống Chân."),t("br"),t("br"),n._v("“Đây chính là ngươi tu luyện pháp quyết?”"),t("br"),t("br"),n._v("Đi tới trước mặt, Tống Chân nhìn một cái, nhịn không được gật đầu: “Chính là cái này, lúc trước chính là hắn truyền thụ cho chúng ta...”"),t("br"),t("br"),n._v("“Ngươi có lời gì nói?”"),t("br"),t("br"),n._v("Nắm thư tịch, Chung hội trưởng lần nữa nhìn qua, ánh mắt trở nên như là hàn băng."),t("br"),t("br"),n._v("“Ta...” Tôn Tấn cũng nhịn không được nữa, đầu gối mềm nhũn quỳ rạp xuống đất."),t("br"),t("br"),n._v("Nếu như nói trước đó còn có thể che giấu, tận lực kéo dài thời gian, quyển sách này theo hắn trữ vật giới chỉ bên trong bị tìm ra đến, nói nhiều hơn nữa, cũng bị mất ý nghĩa!"),t("br"),t("br"),n._v("“Đúng ta nhất thời bị ma quỷ ám ảnh... Nghĩ đến nơi này có như thế nhiều khoáng thạch, chỉ cần có thể tìm ra, liền có thể kiếm được càng nhiều, về sau cho dù không làm luyện khí sư, cũng có thể sống càng tốt hơn...”"),t("br"),t("br"),n._v("“Vì ngươi có thể sống đến càng tốt hơn, liền hại nhiều như vậy, vô tội tu luyện giả... Ngươi không xứng làm luyện khí sư, càng không xứng truyền thụ con cháu!”"),t("br"),n._v("Lắc đầu, Chung hội trưởng răng cắn chặt."),t("br"),t("br"),n._v("Luyện khí sư mặc dù không phải danh sư, nhưng cũng có truyền thừa, trước mắt vị này, lấy bồi dưỡng học đồ làm tên, trên thực tế nhưng làm lấy lợi mình hại người sự tình, nghiêm trọng vi phạm với “Sư” tồn tại ý nghĩa."),t("br"),t("br"),n._v("“Trương viện trưởng, Phùng đường chủ, ta quản giáo thuộc hạ không nghiêm, bản thân càng là có mắt không tròng, tin nhầm đối phương, mới ủ thành đại họa như thế... Bằng lòng từ đi luyện khí sư công hội hội trưởng thân phận, tìm kiếm cái này ba mươi vị học đồ người nhà, dùng hết một đời vì đó đền bù...”"),t("br"),t("br"),n._v("Bàn tay một chút, đem Tôn Tấn tu vi phong ấn lại, Chung hội trưởng nhìn lại."),t("br"),t("br"),n._v("Thân là luyện khí công hội hội trưởng, cái tên này tại trước mắt hắn làm ra việc như thế, hai mươi năm, không có chút nào phát giác, bản thân liền là nghiêm trọng không làm tròn trách nhiệm, loại trừ Tống Chân bên ngoài rất nhiều học đồ, người mặc dù chết rồi, tất nhiên còn có người nhà, bằng lòng dùng một đời, tiến hành trả lại."),t("br"),t("br"),n._v("“Chuyện này mặc dù không trách ngươi, nhưng có rất lớn trách nhiệm!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Trương Huyền nói."),t("br"),t("br"),n._v("Mặc dù tội lỗi không tại đối phương, nhưng mà thân là Tôn Tấn người lãnh đạo trực tiếp, không có phát hiện thuộc hạ phẩm hạnh, liền cấp cho thân phận trưởng lão, cấp cho càng lớn thuận tiện, để hắn không kiêng nể gì cả, hại nhiều người như vậy, bản thân liền không cách nào chối cãi."),t("br"),t("br"),n._v("Nếu như đối phương không phải công hội trưởng lão, không phải lục tinh luyện khí sư... Lại thế nào khả năng, có được lực lượng, che giấu đi hơn mười vị học đồ tử vong, mà không khiến người ta hoài nghi."),t("br"),t("br"),n._v("“Ta biết, đáng tiếc, hiện tại loại tình huống này, cho dù ta làm cái gì, đều không thể trả lại...”"),t("br"),t("br"),n._v("Nhìn Tống Chân một cái, Chung hội trưởng mặt mũi thất lạc."),t("br"),t("br"),n._v("Không nói những người chết kia, ngay cả trước mắt vị này, cho dù dùng ra cố gắng lớn nhất, cũng không cách nào lại thay đổi kết cục."),t("br"),t("br"),n._v("Đối phương tu luyện công pháp, cùng luyện khí xung đột lẫn nhau, cho dù lục tinh đỉnh phong luyện khí đại tông sư, cũng không có khả năng thay đổi những này, để vận mệnh đi đến quỹ đạo."),t("br"),t("br"),n._v("“Ngược lại cũng không phải không cách nào thay đổi...”"),t("br"),t("br"),n._v("Thấy vị hội trưởng này thái độ như thế, Trương Huyền hiểu tâm hắn suy nghĩ cái gì, lắc đầu, xòe bàn tay ra: “Đem quyển sách này cho ta xem một chút!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nhẹ gật đầu, đem theo Tôn Tấn trữ vật giới chỉ bên trong, lấy ra tu luyện công pháp, đưa tới."),t("br"),t("br"),n._v("Trương Huyền nối qua, tiện tay lật một lần, cúi đầu trầm tư."),t("br"),t("br"),n._v("Một lát sau, lấy ra giấy bút, rồng bay phượng múa, thời gian không dài, lần nữa viết một bộ pháp quyết, đưa cho cách đó không xa Tống Chân: “Dựa theo bộ này pháp quyết tu luyện!”"),t("br"),t("br"),n._v("Lúc này Tống Chân rốt cục hiểu rõ đến cùng chuyện gì xảy ra, lần nữa nhìn về phía Tôn Tấn, không có trước đó tôn trọng, ngược lại lộ ra nồng đậm hận ý."),t("br"),t("br"),n._v("Trở thành hắn học đồ, dào dạt lấy cực lớn hi vọng, giữ vững được ròng rã 20 năm, kết quả vẫn là chờ tới lấy giỏ trúc mà múc nước công dã tràng!"),t("br"),t("br"),n._v("Nối qua thư tịch, Tống Chân một mặt mê man."),t("br"),t("br"),n._v("Mặc dù hắn rất cảm kích trước mắt vị này Trương viện trưởng, nhưng mà cả đời mục tiêu, bị kết luận lại không thể có thể, đã không biết nên làm cái gì."),t("br"),t("br"),n._v("“Ta nói qua, sẽ để cho ngươi trở thành chân chính luyện khí sư, tự nhiên muốn nói được thì làm được!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng: “Đem quyển công pháp này tu luyện xong, hôm nay liền có thể khảo hạch ngũ tinh luyện khí sư, thậm chí... Lục tinh!”"),t("br"),t("br"),n._v("“Khảo hạch ngũ tinh? Lục tinh?”"),t("br"),t("br"),n._v("Tống Chân ngẩn ngơ, giật nảy mình, khó tin."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Trương Huyền cười gật đầu."),t("br"),t("br"),n._v("Tống Chân liền vội vàng đem thư tịch mở ra, phía trên bút mực còn không có hoàn toàn khô ráo, nhàn nhạt mùi mực truyền đến, phối hợp thêm bất cứ lúc nào cũng sẽ nhảy ra mặt giấy kiểu chữ, khiến cho người tâm thần thanh thản."),t("br"),t("br"),n._v("Rất nhanh, đem thư tịch nhìn một lần, Tống Chân cả người thân thể cứng ngắc, khó tin."),t("br"),t("br"),n._v("Không có danh sư chỉ điểm dưới tình huống, có thể tại chừng ba mươi tuổi, tu luyện tới Hóa Phàm cửu trọng, hắn trên việc tu luyện thiên phú không thể nghi ngờ, mặc dù chỉ là đơn giản nhìn một lần thư tịch bên trên nội dung, cũng đã biết, bộ công pháp này đáng sợ!"),t("br"),t("br"),n._v("Cùng đối phương nói đồng dạng, không chỉ thay đổi hắn trên việc tu luyện mấu chốt, còn có thể để tu vi tiến thêm một bước, đạt tới cảnh giới càng cao hơn!"),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Thấy hắn có chút ngẩn người, Trương Huyền cười cười."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Không do dự, vội vàng khoanh chân ngồi dưới đất, dựa theo bí tịch bên trên nội dung, bắt đầu tu luyện."),t("br"),t("br"),n._v("Thấy hắn tu luyện, Trương Huyền quay đầu nhìn về phía cách đó không xa lão giả: “Chung hội trưởng, khả năng còn muốn phiền phức thoáng cái, giúp hắn tìm kiếm ba cái đạt tới cấp sáu đan dược, theo thứ tự là Khai Cốt Thối Thể đan, Nghịch Chuyển Hoán Kinh đan...”"),t("br"),t("br"),n._v("Đối phương tu luyện bộ kia công pháp, ròng rã hai mươi năm, cho dù hắn lần nữa thiết kế thư tịch, để hắn kinh mạch nghịch chuyển, để chân khí thay đổi, muốn làm được có thể thuận lợi luyện khí, cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("Còn cần một chút đan dược phụ trợ."),t("br"),t("br"),n._v("Đổi trải qua đổi mạch, để trước đó cùng địa hỏa trái lại chân khí, cứ thế mà chuyển hóa thành gần, đổi lại bất luận kẻ nào đều làm không được, cũng chỉ có hắn nắm giữ Thiên Đạo công pháp loại này không có đường quanh co bí tịch, xem như cơ sở, mới có thể thuận lợi thôi diễn đi ra."),t("br"),t("br"),n._v("“Loại đan dược này tại cái khác chỗ rất khó tìm đến, nhưng mà tại Hỏa Nguyên thành không hề hiếm thấy... Chờ một chút!”"),t("br"),t("br"),n._v("Chung hội trưởng vội vã rời khỏi."),t("br"),t("br"),n._v("Hỏa Nguyên thành, địa hỏa phong phú, luyện khí cùng luyện đan chức nghiệp, là lưu hành nhất, lại thêm đủ loại trân quý dược liệu rất nhiều, địa phương khác không bằng đan dược, nơi này cũng đều có thể tìm tới, không biết tiêu tốn quá nhiều thời gian."),t("br"),t("br"),n._v("Cũng không lâu lắm, liền đi trở về, Trương Huyền nói tới ba loại đan dược, đều cầm tới."),t("br"),t("br"),n._v("Đem thuốc đưa cho Tống Chân, để hắn bên cạnh nuốt bên cạnh tu luyện, sau nửa canh giờ, đột nhiên trong cơ thể một tiếng nổ vang, khí tức cả người trong nháy mắt bạo tăng, đi qua một phen tu luyện, không chỉ đem chân khí thuộc tính thay đổi, thậm chí còn đột phá ràng buộc, tu vi đạt tới Bán Thánh cấp bậc!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1033"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1035"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);