(window.webpackJsonp=window.webpackJsonp||[]).push([[541],{896:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1464-đe-han-thong-gia"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1464-đe-han-thong-gia"}},[n._v("#")]),n._v(" Chương 1464: Để hắn thông gia")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1464: Để hắn thông gia"),t("br"),t("br"),n._v("“Vâng, lão sư dạy học, trong lòng ta, tuyệt đối đương đại đệ nhất!”"),t("br"),t("br"),n._v("Nghe được đại trưởng lão tán thưởng, Trương Cửu Tiêu liền vội vàng gật đầu."),t("br"),t("br"),n._v("Không nói cái khác, hắn bảy vị sư huynh, sư tỷ, mỗi một cái trưởng thành, đều nhất định oanh động đại lục, hiển hách dương danh."),t("br"),t("br"),n._v("Học sinh từng cái lợi hại như vậy, dạy học bên trên, ai có thể địch?"),t("br"),t("br"),n._v("Nghe hắn lời này, đại trưởng lão còn tưởng rằng là học sinh đối lão sư phát ra từ nội tâm tôn sùng, cũng không phản bác, cười cười, cổ tay khẽ đảo, lấy ra một cái lệnh bài đưa tới: “Nếu ngươi không muốn nói ra lão sư là ai, ta cũng liền không hỏi tới, bất quá... Hắn thay chúng ta Trương gia bồi dưỡng ra như ngươi vậy thiên tài, chính là chúng ta Trương gia đại ân nhân! Đây là gia tộc chí tôn khách quý lệnh, ngươi có thể đưa cho hắn lão nhân gia, chỉ cần cầm trong tay lệnh bài, bất cứ lúc nào chỗ nào, Trương gia người gặp lên, đều sẽ dâng lên tôn quý nhất lễ tiết, không thể làm càn!”"),t("br"),t("br"),n._v("“Đa tạ đại trưởng lão!”"),t("br"),t("br"),n._v("Thấy đối phương không tỉ mỉ hỏi tới, Trương Cửu Tiêu thở phào nhẹ nhõm, vội vàng nhận lấy lệnh bài."),t("br"),t("br"),n._v("Cái lệnh bài này dày nặng, mang theo xa xăm khí tức, vừa nhìn liền biết cực kỳ trân quý."),t("br"),t("br"),n._v("“Chí tôn khách quý lệnh, chúng ta Trương gia, từ trước tới nay, hết thảy cũng liền đưa ra qua ba cái, hắn là lão sư của ngươi, đưa ngươi đào tạo thành như vậy, đáng giá Trương gia dùng hết tất cả kết giao!”"),t("br"),t("br"),n._v("Đại trưởng lão gật đầu."),t("br"),t("br"),n._v("Trước mắt vị này Trương Cửu Tiêu huyết mạch độ tinh thuần, đã gần như kịp lúc trước tiểu thiên tài, có như vậy một người, Trương gia nhất định lần nữa đi đến huy hoàng, thậm chí... Sự kiện kia cũng thuận lợi giải quyết, không cần tiếp tục sầu muộn!"),t("br"),t("br"),n._v("Chỉ bằng vào điểm này, hắn vị lão sư này cống hiến lớn, đã đáng giá làm cho cả gia tộc đều đội ơn."),t("br"),t("br"),n._v("Đừng nói một viên khách quý lệnh, coi như nắm giữ cái gì quá phận yêu cầu, Trương gia chỉ cần có thể làm được, cũng tuyệt đối sẽ không cự tuyệt."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Trương Cửu Tiêu gật đầu."),t("br"),t("br"),n._v("Quả thực, lão sư thân là trời nhận danh sư, về sau nhất định là đứng tại đỉnh phong nhất nhân vật, loại người này, bất kể như thế nào kết giao, đều không quá đáng."),t("br"),t("br"),n._v("“Nếu ngươi lão sư, không cho nói nhiều, liền không tại tiếp tục hỏi thăm, có một việc, ngươi cần cẩn thận trả lời...”"),t("br"),t("br"),n._v("Khoát tay áo, đại trưởng lão không tại trong chuyện này tiếp tục xoắn xuýt, mà là nhìn qua: “Tối hôm qua, ngươi cùng Thánh tử điện tới vị kia Trương Huyền cùng rời đi, hiện tại có biết, hắn hiện tại ở đâu? Lại đi làm cái gì?”"),t("br"),t("br"),n._v("Vị này Trương Huyền là phá hư vương, nhất định phải cẩn thận, hơi không cẩn thận, vật gì liền không có, nhất định phải làm rõ đi nơi nào, có chỗ đề phòng."),t("br"),t("br"),n._v("Còn... Đem Trương Huyền cùng đối phương lão sư liên hệ với nhau, không hề nghĩ ngợi qua."),t("br"),t("br"),n._v("Tại hắn cũng trong mắt, vị này Trương Huyền, bất quá là cái có chút thiên phú người trẻ tuổi thôi, làm sao có thể làm ra, hắn đều hoàn thành không được sự tình?"),t("br"),t("br"),n._v("Huống chi, Trương Cửu Tiêu loại này tuyệt đỉnh thiên tài, tổng không đến mức, nhận một cái so với chính mình tuổi tác còn nhỏ người làm lão sư đi."),t("br"),t("br"),n._v("“Lão... Trương sư cùng ta là bạn! Ta hôm qua hỏi thăm một số chuyện... Đi nơi nào, cũng không hiểu rõ tình hình!”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Cửu Tiêu nói."),t("br"),t("br"),n._v("“Không biết?”"),t("br"),t("br"),n._v("Đại trưởng lão còn muốn nói chuyện, cổ tay khẽ đảo, một cái ngọc bài xuất hiện tại lòng bàn tay, cúi đầu nhìn một cái, lại không tiếp tục hỏi thăm, tiếp lấy nhìn lại: “Ngươi vừa mới vượt qua lôi kiếp, lại vận dụng huyết mạch chi lực, thân thể hết sức yếu ớt, ngay ở chỗ này tu dưỡng, ta còn có chút việc, hiện tại đi làm, chẳng mấy chốc sẽ trở về!”"),t("br"),t("br"),n._v("Nói xong, lấy ra một viên đan dược, đưa tới: “Đây là đặc biệt nhằm vào tộc nhân luyện chế, sau khi phục dụng, có thể khôi phục nhanh hơn kích hoạt huyết mạch lưu lại di chứng, tình huống bình thường, vận dụng huyết mạch chi lực, một tháng mới có thể tiếp tục tu luyện cùng chiến đấu, dùng loại thuốc này vật, mười ngày liền có thể triệt để khôi phục! Đương nhiên, huyết mạch của ngươi càng thêm tinh thuần, ba ngày hẳn là liền có thể làm dịu...”"),t("br"),t("br"),n._v("Trương gia huyết mạch, cùng Lạc gia huyết mạch đồng dạng, một khi kích hoạt, cần tĩnh dưỡng một tháng."),t("br"),n._v("Bất quá, nhằm vào loại tình huống này, trong tộc các đời cao thủ, không biết nghiên cứu bao lâu, mới phối chế ra loại đan dược này, có thể để người ta mau theo khôi phục, hơn nữa huyết mạch càng tinh khiết hơn khôi phục càng nhanh! Chỉ là thứ này, quá mức trân quý, hạch tâm con cháu, cho dù là Trương Thuần dạng này, cũng không có tư cách dùng."),t("br"),t("br"),n._v("Chỉ lưu truyền tại thế hệ trước bên trong."),t("br"),t("br"),n._v("Đối phương nói chuyện, Trương Cửu Tiêu lúc này mới cảm thấy toàn thân mỏi mệt, một chút lực lượng đều không có, lúc này há miệng đem viên thuốc nuốt xuống."),t("br"),t("br"),n._v("Dược vật vừa tiến vào trong cơ thể, ngay sau đó một đạo mạnh mẽ nhiệt lưu qua lại dạo chơi, ngay sau đó tiêu hao lực lượng, phục hồi từ từ, khí tức cả người càng ngày càng mạnh."),t("br"),t("br"),n._v("“Thuốc tốt!”"),t("br"),t("br"),n._v("Biết thật là không thể tìm ra tìm linh đan diệu dược, Trương Cửu Tiêu không nói thêm lời, mau theo khôi phục tổn thương thân thể cùng bị hao tổn huyết mạch."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thấy hắn khôi phục nhanh như vậy, đại trưởng lão thoả mãn nhẹ gật đầu, cũng không nói chuyện, xoay người đi ra khỏi phòng, thời gian không dài, gặp bay tới Trương Vô Trần, vẻ mặt lập tức trở nên ngưng trọng: “Trương Huyền trở lại chỗ ở?”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Trương Vô Trần gật đầu, tiếp lấy nhìn qua: “Trương Cửu Tiêu có hay không nói, đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("“Không có gì bất ngờ xảy ra, hắn hẳn là bái một vị cao nhân vi sư!”"),t("br"),t("br"),n._v("Đại trưởng lão nói."),t("br"),t("br"),n._v("“Cao nhân?”"),t("br"),t("br"),n._v("“Ừm, người này không biết dùng thủ đoạn gì, thế mà thay đổi huyết mạch của hắn độ tinh thuần, ta nghi ngờ... Chúng ta trong tộc, xuất hiện nội gián!”"),t("br"),t("br"),n._v("Mắt sáng lên, đại trưởng lão hừ một tiếng: “Dẫn hắn đi vào huyết trì, thần không biết quỷ không hay, càng làm cho hắn huyết mạch tấn cấp... Loại trừ trong tộc, thực sự nghĩ không ra người ngoài ai có thể làm được!”"),t("br"),t("br"),n._v("http://ngantruyen.com/"),t("br"),n._v("Vị này Trương Cửu Tiêu, hôm qua cũng không rời khỏi gia tộc, nói rõ hắn vị lão sư kia, ngay tại trong tộc!"),t("br"),t("br"),n._v("“Tước đoạt huyết mạch, chuyển di chiết cây, toàn cả gia tộc, chỉ có mấy người biết... Ngươi nghi ngờ là mấy người bọn hắn?” Tam trưởng lão giật mình."),t("br"),t("br"),n._v("“Chuyện này truyền thừa vài vạn năm, người học qua không ít, có hay không tiết ra ngoài, ta cũng không dám xác định, bất quá... Ta đem gia tộc chí tôn khách quý lệnh, cho Trương Cửu Tiêu, trong đó lặng lẽ ẩn giấu đi một đạo ý niệm, chỉ cần đem thứ này đưa ra ngoài, người lạ tiếp nhận, liền nhất định có chỗ phát giác...” Đại trưởng lão bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Cái kia... Trương Cửu Tiêu xử lý như thế nào?”"),t("br"),t("br"),n._v("Tam trưởng lão nhìn qua."),t("br"),t("br"),n._v("“Huyết mạch của hắn, ta đo lường qua, là tinh thuần Trương gia huyết thống, không có bất kỳ cái gì đặc thù, mặc kệ huyết mạch làm sao nhận được, xem như trong tộc đứng đầu nhất thiên tài. Về sau, tất cả tài nguyên toàn bộ khuynh hướng tới, tranh thủ để hắn tại năm trước, đạt tới Thánh vực cửu trọng, thậm chí cao hơn... Sau đó, thông gia!”"),t("br"),t("br"),n._v("Đại trưởng lão nói."),t("br"),t("br"),n._v("“Ừm, thực lực của hắn bây giờ, không dùng so, trong tộc thế hệ trẻ tuổi, cũng không có người có thể thắng được, để hắn thông gia, quả thực là lựa chọn tốt nhất!”"),t("br"),t("br"),n._v("Trương Vô Trần nhẹ gật đầu, nói tiếp: “Cái kia... Vị kia Trương Huyền, làm sao bây giờ?”"),t("br"),t("br"),n._v("“Người này có lẽ cùng Trương Cửu Tiêu chỉ là quen biết, không có quá nhiều đặc thù, mang ta đi nhìn một chút... Nếu như về sau có thể hiệu trung Trương gia, cùng Cửu Tiêu hỗ trợ lẫn nhau, tuyệt đối tăng thêm một sự giúp đỡ lớn, làm không được... Liền để hắn mau rời khỏi, không cần tiếp tục làm mưa làm gió!”"),t("br"),t("br"),n._v("Mắt sáng lên, đại trưởng lão hừ lạnh: “Nếu không, cho dù là Dương Huyền học sinh, cũng sẽ không lòng dạ mềm yếu!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1472"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1474"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);