(window.webpackJsonp=window.webpackJsonp||[]).push([[542],{897:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1465-truong-huyen-ca-cuoc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1465-truong-huyen-ca-cuoc"}},[n._v("#")]),n._v(" Chương 1465: Trương Huyền cá cược")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1465: Trương Huyền cá cược"),i("br"),i("br"),n._v("Không biết đã bị người theo dõi, Trương Huyền biến trở về dung mạo của mình, trở lại chỗ ở."),i("br"),i("br"),n._v("Tuy là đi ra thời gian không dài, chỉ có một đêm thêm vừa giữa trưa, nhưng sợ Trương Cửu Tiêu trên việc tu luyện xảy ra vấn đề, tinh thần cao độ tập trung, cả người đều có vẻ hơi mệt mỏi."),i("br"),i("br"),n._v("“Trương sư, Trương gia am hiểu kiếm pháp thiên tài, đã đợi chờ một hồi!”"),i("br"),i("br"),n._v("Vừa đi vào gian phòng, Kiếm Tần Sinh tới đón."),i("br"),i("br"),n._v("“Đã tới?”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Trương gia gia tộc thi đấu, kèm theo Trương Cửu Tiêu lăng không xuất thế, tiếp tục tỷ thí đi xuống đã không còn ý nghĩa, các phương khách và bạn như là đã đến, tự nhiên muốn tiến hành xuống một cái hạng mục."),i("br"),i("br"),n._v("Đi theo Kiếm Tần Sinh sau lưng đi vào gian phòng, ngay sau đó nhìn thấy ba cái thanh niên đứng tại trong đó, từng cái sống lưng thẳng đứng, trên người khí tức sôi trào, tựa như ba thanh trường kiếm, đứng sừng sững ở cách đó không xa, bất cứ lúc nào cũng sẽ cắt chém vạn vật."),i("br"),i("br"),n._v("“Ngươi chính là Trương Huyền? Ngươi tới khiêu chiến Trương gia kiếm pháp?”"),i("br"),i("br"),n._v("Thấy hắn đi tới, đi đầu một thanh niên, nhìn tới tới."),i("br"),i("br"),n._v("Vị này hơi có vẻ gầy yếu, khóe miệng một viên nốt ruồi son, có chút dữ tợn."),i("br"),i("br"),n._v("“Không sai!”"),i("br"),i("br"),n._v("Lười nhác nói nhiều, Trương Huyền ngồi xuống, nhàn nhạt nhìn qua, vừa quan sát, một bên khôi phục nguyên khí."),i("br"),i("br"),n._v("“Tại hạ Trương Húc, bọn họ là Trương Ngân, Trương Khâm, ba người chúng ta học qua mấy năm kiếm pháp, muốn cùng ngươi công bằng luận bàn!” Nốt ruồi son thanh niên Trương Húc nói."),i("br"),i("br"),n._v("“Tốt!”"),i("br"),i("br"),n._v("Gật đầu đáp ứng, Trương Huyền ngẩng đầu nhìn tới: “Các ngươi là từng cái lên, vẫn là cùng một chỗ?”"),i("br"),i("br"),n._v("Đồng ý Kiếm Tần Sinh tới, loại trừ tìm Trương gia phiền phức, cũng muốn xem bọn hắn kiếm thuật đến cùng đạt đến cảnh giới gì, cái gọi là “Tốc độ” chân giải, lại có gì loại bí ẩn chỗ."),i("br"),i("br"),n._v("“Ngạo mạn!”"),i("br"),i("br"),n._v("Không nghĩ tới cái tên này, vừa đến xem bọn họ tại không có gì, trực tiếp tọa hạ không nói, còn tuyên bố để thứ nhất lên lên, ba người tất cả đều tràn đầy không vui."),i("br"),i("br"),n._v("“Ngươi là Dương sư thân truyền, càng nhận được Lưu Thủy kiếm quyết truyền thừa, kiếm pháp hẳn là sẽ không quá yếu, ta Trương Khâm, khiêu chiến ngươi!”"),i("br"),i("br"),n._v("Cũng nhịn không được nữa, Trương Khâm tiến về phía trước một bước, trường kiếm trong tay run lên, phát ra nghẹn ngào vang lên: “Bất quá, tại tỷ thí phía trước, có dám cùng ta đánh cược?”"),i("br"),i("br"),n._v("“Đánh cược? Không biết cá cược vì sao?”"),i("br"),i("br"),n._v("Trương Huyền nhìn qua."),i("br"),i("br"),n._v("“Sự tích của ngươi, ta biết một chút, vừa đến Trương gia liền trắng trợn phá hư, gia tộc thủ hộ đại trận, phòng khách đều bị ngươi làm cho hoàn toàn thay đổi... Tuy là đại trưởng lão đám người khoan dung, không cho so đo, nhưng chúng ta thân là Trương gia con cháu, quyết không cho phép có người ở đây làm loạn. Rất đơn giản, lấy Trương gia kiếm pháp so với ngươi thi, ngươi thua, thề cả đời hiệu lực Trương gia, không thể phản bội! Thắng, tính mạng của chúng ta, tùy ngươi xử lý! Gia tộc phá hư sự tình, cũng có thể xóa bỏ.”"),i("br"),i("br"),n._v("Lông mày nâng lên, Trương Khâm nói."),i("br"),i("br"),n._v("“Hiệu trung Trương gia?”"),i("br"),i("br"),n._v("Trương Huyền cười nhạo."),i("br"),i("br"),n._v("Đối phương mới mở miệng, hắn liền hiểu được, cái gọi là khiêu chiến, chỉ là lấy cớ, mục đích là muốn đem hắn lưu lại, vì Trương gia hiệu trung."),i("br"),i("br"),n._v("Nếu không, nho nhỏ ba vị hạch tâm con cháu, làm sao có tư cách, quyết định chuyện lớn như vậy?"),i("br"),i("br"),n._v("“Thế nào, không dám đồng ý?”"),i("br"),i("br"),n._v("Trương Khâm cười lạnh, lộ ra đùa cợt chi ý."),i("br"),i("br"),n._v("“Không có gì không dám đồng ý, chỉ là các ngươi tính mạng đối ta không có cái gì tác dụng, các ngươi nếu như thua, ta có hai cái yêu cầu!”"),i("br"),i("br"),n._v("Nhàn nhạt nhìn qua, Trương Huyền nói."),i("br"),i("br"),n._v("“Mời nói!”"),i("br"),i("br"),n._v("Nhíu nhíu mày, Trương Húc nói."),i("br"),i("br"),n._v("Trong ba người, lấy hắn cầm đầu."),i("br"),i("br"),n._v("“Thứ nhất, ta muốn thấy nhìn các ngươi Trương gia kiếm pháp bí tịch!”"),i("br"),i("br"),n._v("Mí mắt vừa nhấc, Trương Huyền nói."),i("br"),i("br"),n._v("“Ta Trương gia kiếm pháp, là tiên tổ sáng tạo, không phải Trương gia huyết mạch không thể học tập cùng tu luyện, ngươi muốn nhìn, cũng không có gì, có điều, có thể hay không có chỗ lĩnh ngộ, liền nhìn thiên phú! Điểm ấy, ta có thể đồng ý.”"),i("br"),i("br"),n._v("Trương Húc gật đầu."),i("br"),i("br"),n._v("Trương gia kiếm pháp, không có huyết mạch, muốn luyện thành, gần như không có khả năng, coi như cho đối phương nhìn, cũng nhất định nhìn không ra như thế về sau, không cần thiết xoắn xuýt."),i("br"),i("br"),n._v("“Sảng khoái!” Trương Huyền gật đầu: “Thứ hai, ta muốn các ngươi gia chủ Hưng kiếm thánh, đè thấp tu vi so với ta kiếm!”"),i("br"),n._v(" Tới đều tới, đã vị kia tiểu thiên tài không tại, liền khiêu chiến thoáng cái vị kia Hưng kiếm thánh, thay Kiếm Tần Sinh trút giận!"),i("br"),i("br"),n._v("Tuy là không biết vị này Hưng kiếm thánh kiếm pháp đến cùng làm sao, chỉ cần hắn xem xong Trương gia bí tịch, nắm giữ ba loại kiếm ý, cùng cấp bậc bên dưới, không tin có thể có người vượt qua."),i("br"),i("br"),n._v("“Cùng gia chủ tỷ thí?”"),i("br"),i("br"),n._v("Nghe được hắn yêu cầu thứ hai, ba người liếc mắt nhìn nhau, mỗi người cau mày."),i("br"),i("br"),n._v("Bọn họ chỉ là trong tộc con cháu, không cách nào làm tộc trưởng chủ."),i("br"),i("br"),n._v("“Nếu như không làm chủ được, có thể đi thỉnh giáo cho các ngươi tới tỷ thí người, đáp ứng, ta liền đáp ứng các ngươi cá cược, tới giao đấu, nếu không, cá cược sự tình, như vậy coi như thôi!” Trương Huyền xua tay."),i("br"),i("br"),n._v("“Tốt, Trương sư ở đây chờ, chúng ta vậy thì hỏi thăm!”"),i("br"),i("br"),n._v("Thấy hắn nói kiên định như vậy, Trương Húc biết nói nhiều không ích gì, liếc mắt nhìn nhau, xoay người ra khỏi phòng, vừa đến bên ngoài, lập tức lấy ra đưa tin ngọc bài, tiến hành đưa tin."),i("br"),i("br"),n._v("“Trương sư, ngàn vạn không thể đồng ý...”"),i("br"),i("br"),n._v("Thấy bọn họ rời phòng, Kiếm Tần Sinh nhìn lại, lo lắng truyền âm."),i("br"),i("br"),n._v("“Ngươi không phải muốn ta tới tỷ thí kiếm pháp ư? Làm sao hiện tại không cho?” Trương Huyền cười một tiếng."),i("br"),i("br"),n._v("“Trong giọng nói của bọn họ có vấn đề, bọn họ nói là Trương gia kiếm pháp, vượt qua ngươi, cũng không phải là cá nhân vượt qua ngươi, nếu như đại trưởng lão tự thân ra tay, lấy Trương gia kiếm pháp vượt qua, ngươi chẳng phải đồng dạng đại biểu thua?”"),i("br"),i("br"),n._v("Kiếm Tần Sinh vội nói."),i("br"),i("br"),n._v("Vừa rồi vị kia Trương Khâm, thoạt nhìn lỗ mãng, trên thực tế, trong lời nói mang theo bẫy rập."),i("br"),i("br"),n._v("Hắn nói là: “Lấy Trương gia kiếm pháp, so với ngươi thi, ngươi thua, thề cả đời hiệu lực Trương gia, không thể phản bội...” Cũng không nói rõ, đến cùng ai sử dụng Trương gia kiếm, bọn họ thua, chỉ đại biểu cá nhân, hoàn toàn có thể để cho người mạnh hơn tới, chỉ cần Trương gia kiếm pháp chiến thắng, liền đại biểu thắng!"),i("br"),i("br"),n._v("Rõ ràng không công bằng!"),i("br"),i("br"),n._v("“Không sao, ta liền sợ bọn họ không đến!” Trương Huyền khóe miệng nâng lên."),i("br"),i("br"),n._v("Kiếm Tần Sinh có thể nghe ra, hắn làm sao nghe không hiểu, sở dĩ không nói ra, liền là muốn nhìn một chút Trương gia kiếm pháp, đến cùng có bao nhiêu lợi hại!"),i("br"),i("br"),n._v("Hơn nữa, đã xuất thủ... Vậy liền đánh tới bọn họ sợ hãi mới thôi!"),i("br"),i("br"),n._v("Hắn bên này cùng Kiếm Tần Sinh truyền âm đối thoại, Trương Vô Trần bên này cũng nhận được đưa tin."),i("br"),i("br"),n._v("“Cùng gia chủ tỷ thí? Hắn thật cảm thấy có thể vượt qua Trương Húc bọn họ?”"),i("br"),i("br"),n._v("Trương Vô Trần lắc đầu."),i("br"),i("br"),n._v("“Mặc kệ có thể thắng hay không qua, liên lụy đến gia chủ, chúng ta không thể vượt trở làm thay, ta hiện tại liền cho hắn đưa tin, có đáp ứng hay không cùng tiểu bối tỷ thí, từ chính hắn quyết định!”"),i("br"),i("br"),n._v("Cũng nhìn rõ ràng đưa tin nội dung, đại trưởng lão nói."),i("br"),i("br"),n._v("Hắn mặc dù là gia tộc đại trưởng lão, nhưng tộc trưởng có phải hay không ra tay, cũng không phải hắn có thể quyết sách."),i("br"),i("br"),n._v("“Tốt! Tộc trưởng mới đi hai ngày không đến, hẳn là sẽ không khoảng cách quá xa, đưa tin lời nói, hẳn là có thể nhẹ nhõm thu đến...” Trương Vô Trần nhẹ gật đầu, lấy ra một viên đưa tin ngọc bài, ngón tay một chút, một đạo tin tức truyền lại đi qua."),i("br"),i("br"),n._v("Khoảng cách Trương gia không biết bao xa không trung, một đầu phi hành Thánh thú cấp tốc phi hành, trên lưng đứng một người trung niên giống như là cảm ứng được cái gì, gặp ngọc bài lấy ra."),i("br"),i("br"),n._v("“Làm sao vậy?”"),i("br"),i("br"),n._v("Bên cạnh một vị mỹ phụ nhìn tới tới."),i("br"),i("br"),n._v("“Không có gì, tam trưởng lão đưa tin cho ta, nói Kiếm Tần Sinh lần này mang tới kiếm pháp thiên tài, nếu như có thể thắng Trương Húc đám người, muốn ta đè thấp tu vi tới giao đấu...” Trung niên nhân cười lắc đầu."),i("br"),i("br"),n._v("“Trương Húc nhận được ngươi ta thân truyền, cùng cấp bậc so kiếm muốn vượt qua... Đương đại đều không có mấy người!”"),i("br"),i("br"),n._v("Mỉm cười, mỹ phụ lắc đầu: “Có thể thắng hay không qua hắn cũng còn không biết, thế mà còn muốn khiêu chiến cùng cấp bậc ngươi... Kiếm Tần Sinh mang tới thiên tài, thật đúng là khẩu khí thật lớn!”"),i("br"),i("br"),n._v("“Ha ha, người trẻ tuổi có chút chí khí là hẳn là!”"),i("br"),i("br"),n._v("Cười ha ha một tiếng, trung niên nhân đối với vãn bối muốn khiêu chiến hắn chuyện này, cũng không thèm để ý."),i("br"),i("br"),n._v("“Bất quá, Kiếm Tần Sinh không phải hạng người lỗ mãng, có thể để cho vị thiên tài này, nói ra lời này, nhất định có chỗ dựa dẫm, đúng rồi... Ngươi hỏi một chút, cái này kiếm đạo thiên tài tên gọi là gì? Đại lục ở bên trên phàm là dùng kiếm lợi hại người trẻ tuổi, ngươi ta hẳn là cũng biết một chút, lúc nào, tìm được như vậy một cái, tự tin như vậy, cũng dám đối ngươi khiêu chiến?”"),i("br"),i("br"),n._v("Mỹ phụ nhìn qua."),i("br"),i("br"),n._v("“Ta hỏi một chút!”"),i("br"),i("br"),n._v("Nhẹ gật đầu, tiếp tục đưa tin trở về, một lát sau, ngọc trong tay nhãn hiệu, một hồi lắc lư, một cái tên hiện lên ở phía trên, nhìn thấy cái tên này, trung niên nhân thân thể cứng đờ."),i("br"),i("br"),n._v("“Làm sao vậy?” Mỹ phụ nghi ngờ."),i("br"),i("br"),n._v("Trung niên nhân yết hầu phát khô."),i("br"),i("br"),n._v("“Tam trưởng lão nói... Vị kia khiêu chiến người, gọi... Trương Huyền!”"),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1473"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1475"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);