(window.webpackJsonp=window.webpackJsonp||[]).push([[55],{410:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1026-ren-luyen-kim-nguyen-đinh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1026-ren-luyen-kim-nguyen-đinh"}},[n._v("#")]),n._v(" Chương 1026: Rèn luyện Kim Nguyên đỉnh")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thần tích, thật là thần tích ah!”"),t("br"),t("br"),n._v("Tự lẩm bẩm, Chung hội trưởng một mặt ngẩn người."),t("br"),t("br"),n._v("Một bộ công pháp, ba cái đan dược, cứ thế mà nghịch chuyển chân khí thuộc tính, đồng thời để hắn đột phá, cái này đã không thể xưng là chỉ điểm, hoàn toàn có thể gọi là thần tích!"),t("br"),t("br"),n._v("Tận mắt nhìn thấy, cũng cảm thấy như là nằm mơ, không thể tưởng tượng nổi."),t("br"),t("br"),n._v("“Chẳng lẽ... Đây mới là Trương viện trưởng thực lực?” Phùng đường chủ cũng khiếp sợ nói không ra lời."),t("br"),t("br"),n._v("Thân là danh sư, nhãn lực mạnh hơn, tự nhiên biết, đưa cho Tống Chân phần kia bí tịch, đến cỡ nào trân quý."),t("br"),t("br"),n._v("Lợi hại như thế pháp quyết, tiện tay sáng chế, càng là không giữ lại chút nào đưa người, đây mới thật sự là danh sư, đáng giá để bọn hắn học tập cả đời phẩm chất!"),t("br"),t("br"),n._v("Khó trách có thể trở thành danh sư học viện viện trưởng, để vô số trưởng lão học sinh bái phục, quả nhiên dưới cái thanh danh vang dội không hư sĩ."),t("br"),t("br"),n._v("“Đa tạ lão sư!”"),t("br"),t("br"),n._v("Hai người đang tại cảm khái, một bên Tống Chân tu luyện hoàn tất, mặt mũi là bội phục quỳ gối Trương Huyền trước mặt."),t("br"),t("br"),n._v("Giờ phút này hắn đã hoàn toàn tin tưởng đối phương, hiện tại cho dù trước mắt vị thanh niên này, để hắn giơ tay lên tự sát, khẳng định cũng sẽ không do dự một chút."),t("br"),t("br"),n._v("“Ta không phải lão sư của ngươi, không cần khách khí như thế.” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Truyền thụ ta công pháp, lại mang đến cho ta hy vọng sống sót... Đời này chỉ nhận ngươi một người, đến chết không thay đổi.”"),t("br"),t("br"),n._v("Quỳ trên mặt đất, nặng nề mà đập bên dưới mấy cái khấu đầu, Tống Chân ánh mắt lộ ra kiên nghị không thể lay động vẻ."),t("br"),t("br"),n._v("Không có đối phương, hắn khả năng hiện tại đã biến thành một cỗ thi thể, không chỉ có như vậy, người nhà vẫn như cũ sẽ nhấn chìm tại người khác chế giễu bên trong!"),t("br"),t("br"),n._v("Cách đó không xa vị này Tôn Tấn, vẫn như cũ biết ngụy trang lấy nanh vuốt của mình, làm cho không người nào có thể xem thấu chân chính diện mục, lừa gạt một nhóm lại một nhóm nằm mộng cũng muốn trở thành luyện khí sư nhân vật mới."),t("br"),t("br"),n._v("Vị này Trương viện trưởng, không chỉ có truyền thụ cho công pháp, sửa đổi thay đổi nhân sinh của hắn, báo thù cho hắn, cho dù không thu hắn vì học sinh, trong lòng mình cũng sẽ một mực lấy làm gương, cả đời sẽ không cải biến."),t("br"),t("br"),n._v("“Thôi được, mặc dù không biết thu ngươi làm thân truyền, đã gặp nhau, cũng coi như có duyên phận, hôm nay ngươi chính là ta luyện khí bên trên giảng bài học sinh.”"),t("br"),t("br"),n._v("Thấy đối phương ánh mắt kiên định, Trương Huyền than thở một tiếng."),t("br"),t("br"),n._v("Học sinh thân truyền, không chỉ muốn có thiên phú, còn có nhân phẩm rất nhiều khảo hạch, cùng trước mắt vị này thời gian chung đụng rất ngắn, tự nhiên không có khả năng trực tiếp thu lấy, nhưng thu làm luyện khí học sinh, vẫn là có thể."),t("br"),t("br"),n._v("Danh sư học trò khắp thiên hạ, giảng bài học sinh mà thôi, cùng Huyền Huyền hội mọi người đồng dạng, thu nhiều hơn mấy cái, cũng không đáng kể."),t("br"),t("br"),n._v("“Đa tạ lão sư tác thành!” Kích động lần nữa dập đầu, Tống Chân trong mắt tràn đầy thành kính."),t("br"),t("br"),n._v("Chung quanh nằm dưới đất rất nhiều luyện khí sư, đều lộ ra vẻ hâm mộ, thậm chí ngay cả mấy vị trưởng lão, tính cả Chung hội trưởng, cũng đều nguyên một đám mang theo tương đồng biểu lộ."),t("br"),t("br"),n._v("Trước mắt vị này Trương viện trưởng, thực lực mạnh mẽ, đối luyện khí lý giải càng là cao thâm khó dò, có thể trở thành học sinh của hắn, dù chỉ là trên danh nghĩa, về sau tất nhiên cũng tiền đồ vô lượng."),t("br"),t("br"),n._v("“Nếu là ngươi luyện khí lão sư, không cho ngươi trở thành lục tinh cấp luyện khí sư, thực sự băn khoăn.”"),t("br"),t("br"),n._v("Không để ý tới vẻ mặt của mọi người, nhẹ nhàng cười một tiếng, Trương Huyền nhìn lại, cổ tay khẽ đảo, một tòa cự đại lô đỉnh rơi trên mặt đất, đập lên một hồi khói bụi: “Đây là danh sư học viện trấn viện pháp bảo Kim Nguyên đỉnh, hạ phẩm Thánh khí! Ta vừa vặn tính toán đem hắn luyện chế lại một lần một phen... Đã ngươi trở thành học sinh của ta, liền do ngươi tới ra tay, chỉ cần có thể hoàn thành. Đối ngươi luyện khí có trợ giúp thật lớn, về sau trở thành thất tinh luyện khí sư cũng không thành vấn đề!”"),t("br"),t("br"),n._v("“Rèn luyện Thánh khí? Cái này, cái này... Ta chỉ là một cái học đồ, liền Phàm cấp vũ khí đều luyện chế không ra, khó mà chịu trách nhiệm nặng như thế mặc cho...”"),t("br"),t("br"),n._v("Tống Chân giật nảy mình."),t("br"),t("br"),n._v("Cái khác rất nhiều luyện khí sư, thậm chí Chung hội trưởng cũng không tại chỗ ngất đi."),t("br"),t("br"),n._v("Thánh khí, là thất tinh luyện khí sư mới có tư cách tiếp xúc, có thể rèn luyện một lần, quả thực có thể khiến người ta tại luyện khí bên trên, bất kể tâm lý, vẫn là kỹ xảo, đều có cực lớn tiến bộ."),t("br"),t("br"),n._v("Nhất là lục tinh đỉnh phong luyện khí sư, một khi đốn ngộ, nhờ vào đó đột phá, cũng chưa biết chừng."),t("br"),t("br"),n._v("Có thể... Để một cái học đồ, liền phàm khí đều không có luyện chế thành công qua tân thủ đi làm... Thật chẳng lẽ không sợ đường đường danh sư học viện trấn viện chi bảo, bị triệt để làm phế?"),t("br"),t("br"),n._v("“Chủ nhân, ngươi nói không phải thật sự a...”"),t("br"),t("br"),n._v("Không chỉ mọi người không chỉ mọi người bộ dáng này, cách đó không xa Kim Nguyên đỉnh cũng run lẩy bẩy, cảm giác bản thân sắp khóc: “Thật ra thì... Đỉnh Đỉnh cảm thấy thực lực bây giờ cũng còn rất tốt, đột nhiên không muốn thăng cấp...”"),t("br"),t("br"),n._v("Nó mặc dù muốn tăng lên thực lực, thế nhưng muốn chủ nhân tự mình ra tay ah..."),t("br"),t("br"),n._v("Để một cái không luyện qua khí gia hỏa động thủ, cái quỷ gì?"),t("br"),t("br"),n._v("Đây là nhìn ta không vừa mắt, muốn tìm lý do đem ta giết chết à..."),t("br"),t("br"),n._v("“Được rồi, lấy ở đâu nói nhảm nhiều như vậy!” Không thèm để ý mọi người khiếp sợ cùng Kim Nguyên đỉnh sợ hãi, Trương Huyền bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("Hắn hiện tại luyện khí sư trình độ, mặc dù đạt đến lục tinh đỉnh phong, nhưng mà muốn cho cái tên này tấn cấp, cũng không phải là đơn thuần luyện chế, tôi thép, chủ yếu nhất là đem Tử La tài chính vào trong đó... Cũng chính là cái gọi là rèn luyện khí thai!"),t("br"),t("br"),n._v("Mà hắn nhất không am hiểu chính là cái này."),t("br"),t("br"),n._v("Thật nếu để cho hắn ra tay, tại đây a bao nhiêu lợi hại luyện khí sư trước mặt, mất mặt xấu hổ không nói, Kim Nguyên đỉnh cũng rất có thể sẽ bị luyện chết tươi, thậm chí bị đập hoàn toàn thay đổi, lại không trước đó bộ dáng."),t("br"),t("br"),n._v("Vị này Tống Chân, cứ việc chỉ là học đồ, nhưng mà trước đó luyện khí thời điểm, dùng thư viện chuyên môn tra xét, cơ sở vững chắc, nắm giữ được mười phần kiên cố, để hắn tới dung hợp Tử La kim lời nói, tuyệt đối là thí sinh tốt nhất, thậm chí so lục tinh luyện khí sư, đều muốn thuận buồm xuôi gió."),t("br"),n._v(" Một khi thành công, sẽ đối với luyện khí có càng nhiều cảm ngộ, về sau trở thành lục tinh luyện khí sư, thậm chí cấp bậc cao hơn, cũng chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("Làm như vậy, vừa có thể giải quyết bản thân không biết rèn đúc khí thai xấu hổ, hiển lộ rõ ràng thân là cao nhân bản lĩnh, để mọi người càng thêm bái phục, còn có thể rèn luyện cái này tân thủ học sinh, không đến mức ném tên tuổi..."),t("br"),t("br"),n._v("Có thể nói một công ba việc, cớ sao mà không làm?"),t("br"),t("br"),n._v("“Được...” Thấy lão sư tức giận, Tống Chân không dám tiếp tục nói nhảm, đành phải nhẹ gật đầu, kiên trì đi tới."),t("br"),t("br"),n._v("Hắn một mặt coi thường cái chết, đối diện Kim Nguyên đỉnh, càng là không ngừng run rẩy, lại không có trước đó nghiền ép cường giả Thánh vực cuồng vọng cùng phách lối, ba con thô to đùi, bất cứ lúc nào cũng sẽ mềm xuống, trực tiếp nằm rạp trên mặt đất."),t("br"),t("br"),n._v("Nhìn thấy một người một đỉnh biểu lộ, Trương Huyền lắc đầu, cũng chỉ giải thích thêm, nói thẳng: “Tăng lên Thánh khí phẩm chất cực kỳ trọng yếu, chờ một lúc ta sẽ chỉ điểm ngươi như thế nào luyện chế, mảy may trình tự cũng không thể phạm sai lầm!”"),t("br"),t("br"),n._v("“Vâng!” Tống Chân thở dài một hơi."),t("br"),t("br"),n._v("“Đem ta để ngươi mang theo viên kia Tử La kim lấy ra, lần này rèn luyện cần dùng đến...”"),t("br"),t("br"),n._v("Trương Huyền không có chút nào ngừng lại, đem rèn luyện Kim Nguyên đỉnh trình tự nói: “Trước đem cái này viên khoáng thạch hòa tan, chân khí thôi thúc địa hỏa mười ba cái hô hấp về sau, đem Kim Nguyên đỉnh để vào trong đó, tiến hành dung hợp, hai mươi bảy hô hấp sau lấy ra, lợi dụng thiên kim rèn đúc pháp rèn luyện một trăm tám mươi bốn đầu chùy, mỗi một đập đều là dùng ngươi tám mươi phần trăm sức lực, yêu cầu rất đơn giản, muốn nhiều như vậy đập phía dưới, đem trọn cái Kim Nguyên đỉnh đều đập một lần, thoáng cái không thể nhiều, thoáng cái cũng không có thể thiếu...”"),t("br"),t("br"),n._v("Hắn không có truyền âm, mà là nói thẳng ra, vang vang thanh âm, nghe vào tất cả luyện khí sư trong tai, để mọi người nguyên một đám hai mặt nhìn nhau, ánh mắt lộ ra vẻ kinh hãi."),t("br"),t("br"),n._v("Tưởng là chỉ là đơn thuần chỉ điểm luyện khí, sẽ không quá khó, nghe được giờ mới hiểu được, cái này căn bản liền chỉ là luyện khí, mà là một loại tu hành!"),t("br"),t("br"),n._v("Không chỉ muốn đối Kim Nguyên đỉnh, Tử La kim, thậm chí địa hỏa tất cả thuộc tính đều như lòng bàn tay, càng phải đối Tống Chân thực lực cùng năng lực, có kỹ càng hiểu rõ."),t("br"),t("br"),n._v("Đây cũng không phải là nhãn lực vấn đề, trong lòng tính toán, tất nhiên cũng nhiều vô cùng!"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nghe xong đối phương, Tống Chân biết lão sư đã đem hắn khả năng gặp phải tất cả sự tình, đều suy tính đến, nhịn không được thở phào nhẹ nhõm, đem nghe được nội dung thuộc làu ở trong lòng, lúc này mới bàn tay một trảo đem Tử La kim ném vào địa hỏa."),t("br"),t("br"),n._v("Nơi này là luyện khí sư công hội, chỉ cần là gian phòng, đều sẽ có nối liền địa hỏa trận pháp, vừa mới tấn thăng Bán Thánh lực lượng thôi thúc bên dưới, hỏa diễm hừng hực mà lên, rất mau đem Tử La tài chính hóa, ngay sau đó đem Kim Nguyên đỉnh bỏ vào..."),t("br"),t("br"),n._v("Đủ loại thủ pháp luyện chế, nghiêm ngặt dựa theo lời nói của đối phương tiến hành, công bằng."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Trương Huyền thoả mãn nhẹ gật đầu."),t("br"),t("br"),n._v("Cùng đoán đồng dạng, đối phương tại rèn luyện khí thai phương diện, quả thực đạt đến đăng phong tạo cực trình độ, thậm chí so một chút lục tinh đỉnh phong luyện khí sư đều cường đại hơn."),t("br"),t("br"),n._v("Cũng khó trách, hắn những năm này không cách nào đột phá, tất cả tinh lực cùng tâm huyết, đều dùng tại phía trên này, lúc này mới nắm giữ như vậy năng lực."),t("br"),t("br"),n._v("Sau nửa canh giờ, Kim Nguyên đỉnh một tiếng tựa như chuông lớn đồng dạng nổ vang, trước đó chỉ có hạ phẩm Thánh khí lực lượng, liên tục tăng lên, khí tức cường đại, đem mọi người chèn ép liên tiếp lui về phía sau."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Ngừng lại, kim quang lóng lánh, cho người ta một loại cao cao tại thượng cảm giác."),t("br"),t("br"),n._v("“Trung phẩm Thánh khí? Quá tốt rồi...”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Nhìn một cái, là hắn biết, thành công."),t("br"),t("br"),n._v("Lúc này Kim Nguyên đỉnh, hoàn mỹ dung hợp Tử La kim, đã đạt đến trung phẩm Thánh vực cấp bậc, thực lực đã có thể so với Thánh vực tứ trọng đỉnh phong!"),t("br"),t("br"),n._v("Gặp lại Kim Diệp Vương đám người lời nói, hoàn toàn có thể một đỉnh đập chết, không cần tiếp tục hao tốn sức lực, để hắn uống thuốc phiền toái như vậy."),t("br"),t("br"),n._v("“Ta không chết? Không chỉ không chết, thực lực vẫn rất thăng lên? Quá tốt rồi...”"),t("br"),t("br"),n._v("Bên này tràn đầy vui mừng, cách đó không xa Kim Nguyên đỉnh, lập tức hưng phấn mà nhảy dựng lên."),t("br"),t("br"),n._v("Vốn cho rằng để một cái không biết luyện khí học đồ, rèn luyện bản thân, không chết cũng xấp xỉ, không nghĩ tới chẳng những thành công, còn để nó thực lực đại tiến, ròng rã tăng lên một cái cấp bậc, cao hứng hận không thể đem cách đó không xa Tống Chân ôm hôn một cái."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Thời khắc này Tống Chân, cũng không nghĩ tới bản thân sẽ thành công, con mắt ánh sáng lóe lên, tràn đầy kích động."),t("br"),t("br"),n._v("Mặc dù lần này, là lão sư từng bước một chỉ điểm, nhưng có lần này trải qua, đối luyện khí có cực lớn lòng tin, về sau khảo hạch lục tinh, thậm chí đợi thực lực đến, khảo hạch thất tinh đều không thành vấn đề!"),t("br"),t("br"),n._v("Có thể nói... Một cơ hội này, chẳng khác nào đặt vững hắn luyện khí cơ sở, để hắn tiền đồ vô lượng."),t("br"),t("br"),n._v("Đem học viện trấn viện chi bảo, không có chút nào chần chờ đưa cho bản thân rèn luyện, phần này tín nhiệm, phần ân tình này, chính là hắn cả một đời đều không thể báo đáp."),t("br"),t("br"),n._v("“Được rồi, đã ngươi có chỗ lĩnh ngộ, về sau trở thành luyện khí sư đem không thành vấn đề!”"),t("br"),t("br"),n._v("Bàn tay một trảo, đem Kim Nguyên đỉnh thu hồi nhẫn, Trương Huyền khoát tay áo: “Phùng đường chủ, chuyện còn lại liền giao cho ngươi xử lý đi, ta đi trước!”"),t("br"),t("br"),n._v("Nói xong, xoay người đi ra ngoài, chỉ chốc lát liền biến mất tại mọi người trước mắt."),t("br"),t("br"),n._v("“Xong chuyện phủi áo đi, không cầu bất luận cái gì đền ơn... Đây mới thật sự là danh sư!”"),t("br"),t("br"),n._v("Nhìn thấy hắn xoay người rời khỏi, không chần chờ chút nào, mọi người kìm lòng không được cảm khái."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1034"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1036"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);