(window.webpackJsonp=window.webpackJsonp||[]).push([[569],{923:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1490-han-thuy-luu-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1490-han-thuy-luu-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1490: Hàn Thủy Lưu 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1490: Hàn Thủy Lưu "),t("br"),t("br"),n._v("“Dương Huyền?”"),t("br"),t("br"),n._v("Trần Nhạc Dao giật nảy mình: “Ngươi, ngươi... Muốn mượn dùng Dương sư tên tuổi?”"),t("br"),t("br"),n._v("Dương Huyền Dương sư, người khác không biết, nàng thế nhưng là nghe qua."),t("br"),t("br"),n._v("Danh Sư đường đệ nhất thái thượng trưởng lão, địa vị cao, có thể xưng đương đại đệ nhất, thực lực lại càng không cần phải nói... Liền xem như lão sư của ngươi, cũng không thể như vậy mượn dùng đi..."),t("br"),t("br"),n._v("“Bây giờ muốn làm cho đối phương đi ra, chỉ có thể mượn dùng... Triệu Nhã là hắn đồ tôn, có lẽ sẽ không tức giận!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt thản nhiên."),t("br"),t("br"),n._v("Trần Nhạc Dao nói không ra lời."),t("br"),t("br"),n._v("Nói thật, trước mắt vị này, thật đúng là to gan lớn mật."),t("br"),t("br"),n._v("Cưỡng ép Tòng Thánh tử điện lao ra ngược lại cũng thôi, còn giả mạo sư phụ của mình... Không bị phát hiện ngược lại cũng thôi, cái này phải bị phát giác, còn không trực tiếp nháo đến Danh Sư đường tổng bộ?"),t("br"),t("br"),n._v("Đột nhiên, có chút hối hận đồng ý đối phương muốn cứu Triệu Nhã."),t("br"),t("br"),n._v("Thiếu cung chủ vị này vô pháp vô thiên lão sư, làm như vậy đi xuống, thật không biết sẽ gây ra dạng gì động tĩnh lớn tới."),t("br"),t("br"),n._v("“Yên tâm đi, không có việc gì!”"),t("br"),t("br"),n._v("Thấy nàng bộ dáng này, biết lo lắng cái gì, Trương Huyền xua tay."),t("br"),t("br"),n._v("Giả mạo Dương Huyền, hắn sớm đã thuận buồm xuôi gió, chắc chắn sẽ không phạm sai lầm."),t("br"),t("br"),n._v("Tình huống hiện tại, tên của hắn quả thực không thể dùng, nhưng Danh Sư đường tổng bộ Dương Huyền, có thể cho người vô cùng chấn động mạnh khiếp sợ, có lẽ liền có thể dễ như trở bàn tay tìm tới Triệu Nhã rơi xuống."),t("br"),t("br"),n._v("Dù sao vị kia thật Dương Huyền, tại Trương gia bế quan, thẳng đến hắn rời đi, cũng không có xuất hiện, nói rõ khoảng cách đi ra còn không biết bao lâu, không cần lo lắng."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Biết đã cưỡi đến lập tức không có cách nào xuống, Trần Nhạc Dao đành phải tay lấy ra bái thiếp, chân khí du động, ở phía trên lưu lại tên."),t("br"),t("br"),n._v("“Không cần viết Danh Sư đường, chỉ viết ‘Dương Huyền’ hai chữ là được!”"),t("br"),t("br"),n._v("Thấy nàng điêu khắc đi ra chữ viết, Trương Huyền nói."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Không biết vì sao như vậy dặn dò, nhưng biết đã như vậy yêu cầu, khẳng định có mục đích của mình, Trần Nhạc Dao không nói thêm lời, lần nữa tay lấy ra bái thiếp đem tên viết lên đi."),t("br"),t("br"),n._v("Làm xong những này, lúc này mới đi tới đại môn trước mặt, gõ gõ."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Hộ vệ đi ra."),t("br"),t("br"),n._v("“Lão gia nhà chúng ta, muốn thăm hỏi gia chủ của các ngươi người, mong rằng thông báo!”"),t("br"),t("br"),n._v("Trần Nhạc Dao đem bái thiếp đưa tới."),t("br"),t("br"),n._v("Nàng giờ phút này cũng biết bản thân nên nói cái gì."),t("br"),t("br"),n._v("“Đã trễ thế như vậy, lão gia nhà chúng ta đã nghỉ ngơi, có chuyện gì ngày mai lại đến đi!”"),t("br"),t("br"),n._v("Cau mày lắc đầu, hộ vệ cũng không tiếp thu."),t("br"),t("br"),n._v("“Ta khuyên ngươi vẫn là đem bái thiếp đón lấy cho thỏa đáng, một khi chậm trễ, không phải ngươi một cái hộ vệ có thể chống lại!”"),t("br"),t("br"),n._v("Thấy hắn không thu, nhíu nhíu mày, Trần Nhạc Dao cổ tay khẽ đảo, đem một cái lệnh bài lấy ra ngoài."),t("br"),t("br"),n._v("“Băng Nguyên cung hạch tâm tử đệ?”"),t("br"),t("br"),n._v("Hộ vệ không biết Dương Huyền là ai, nhưng cái lệnh bài này vẫn là nhận ra, giật nảy mình, vội vàng nhận lấy bái thiếp: “Hai vị bên trong sau đó, ta vậy thì đi thông bẩm!”"),t("br"),t("br"),n._v("Đường đường Băng Nguyên cung hạch tâm tử đệ, đều gọi hô lão gia, nói rõ sau lưng vị kia, địa vị ít nhất đều là trưởng lão, quả thực không phải hắn một cái hộ vệ có thể đắc tội."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thu về lệnh bài, Trần Nhạc Dao trở lại Trương Huyền sau lưng, hai người chậm rãi đi vào viện lạc, tại đón khách phòng ngồi xuống."),t("br"),t("br"),n._v("An bài tốt nước trà, cầm bái thiếp, hộ vệ vội vã Hướng gia chủ gian phòng đi đến."),t("br"),t("br"),n._v("Tuy là đến đêm khuya, nhưng vị gia chủ này cũng không nghỉ ngơi, còn tại cùng tộc nhân thảo luận sự tình gì, đi vào gian phòng, hộ vệ vội vàng ôm quyền: “Tộc trưởng, bên ngoài có người cầu kiến!”"),t("br"),t("br"),n._v("“Lúc này ai tới tìm ta?”"),t("br"),t("br"),n._v("Hàn gia gia chủ Hàn Thủy Lưu, nhíu nhíu mày."),t("br"),t("br"),n._v("“Đây là bái thiếp...”"),t("br"),n._v(" Không dám nói nhiều, hộ vệ đem bái thiếp đẩy tới."),t("br"),t("br"),n._v("Hàn Thủy Lưu nhận lấy, từ từ mở ra, chỉ thấy phía trên chỉ có hai chữ “Dương Huyền”, không còn gì khác chữ viết."),t("br"),t("br"),n._v("“Dương Huyền?”"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ, Hàn Thủy Lưu nhìn lại: “Người kia thân phận gì?”"),t("br"),t("br"),n._v("Bình thường bái thiếp, đều muốn cho thấy thân phận, làm cho đối phương biết là ai, muốn như vậy chỉ viết một cái tên, còn chưa bao giờ thấy qua."),t("br"),t("br"),n._v("“Là cái chừng ba mươi tuổi người trung niên, người mặc danh sư trường bào, trước ngực không có huy chương, không biết đẳng cấp...”"),t("br"),t("br"),n._v("Suy nghĩ một chút, hộ vệ nói."),t("br"),t("br"),n._v("“Danh sư? Không có đẳng cấp huy chương?”"),t("br"),t("br"),n._v("Hàn Thủy Lưu càng thêm kỳ lạ."),t("br"),t("br"),n._v("Nếu là danh sư, bình thường đều sẽ ghi chú đẳng cấp, để cho người ta coi trọng, không có huy chương, lại không viết thân phận, mấu chốt nhất nửa đêm tới chơi, nơi nào xuất hiện gia hỏa?"),t("br"),t("br"),n._v("“Đoán chừng là không biết từ nơi nào tới người ngông cuồng, đánh trước phát, ngày mai để hắn tới...”"),t("br"),t("br"),n._v("Nghĩ một lát không nghĩ ra, Hàn Thủy Lưu tiện tay đem bái thiếp đặt ở một bên, thản nhiên nói."),t("br"),t("br"),n._v("“Lão gia... Vị này Dương sư tùy tùng là... Băng Nguyên cung hạch tâm tử đệ!”"),t("br"),t("br"),n._v("Thấy hắn không có ý định tiếp kiến, hộ vệ vội nói."),t("br"),t("br"),n._v("“Tùy tùng là Băng Nguyên cung hạch tâm tử đệ?”"),t("br"),t("br"),n._v("Hàn Thủy Lưu thần sắc cứng lại: “Bản thân là danh sư, gọi Dương Huyền... Chẳng lẽ, chẳng lẽ...”"),t("br"),t("br"),n._v("Thì thầm đôi câu, vẻ mặt đột nhiên biến đổi: “Là hắn?”"),t("br"),t("br"),n._v("“Tộc trưởng, là ai?”"),t("br"),t("br"),n._v("Phía dưới tới thảo luận chuyện trưởng lão, tất cả đều nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Danh Sư đường tổng bộ, có một vị thần long thấy đầu mà không thấy đuôi thái thượng trưởng lão, thực lực cực mạnh... Tên liền gọi Dương Huyền... Có thể hay không cả hai có quan hệ?”"),t("br"),t("br"),n._v("Hàn Thủy Lưu nói."),t("br"),t("br"),n._v("“Danh Sư đường tổng bộ thái thượng trưởng lão? Loại nhân vật này, sinh hoạt tại trong mây, làm sao lại tới này...”"),t("br"),t("br"),n._v("Vị trưởng lão này lắc đầu."),t("br"),t("br"),n._v("Bọn họ Hàn gia, tại Hàn Băng thành cứ việc địa vị không thấp, tại Băng Nguyên cung cũng có nhất định lực ảnh hưởng, nhưng ở Danh Sư đường tổng bộ trước mặt, vẫn là kém quá nhiều."),t("br"),t("br"),n._v("Đường đường thái thượng trưởng lão, làm sao có thể tự thân tới, hơn nữa còn đưa lên bái thiếp, nói ra đều bị người khó tin."),t("br"),t("br"),n._v("“Ta cũng không tin, nhưng mà... Thân là danh sư, hơn nữa gọi Dương Huyền, thật không biết còn có những người khác...”"),t("br"),t("br"),n._v("Hàn Thủy Lưu lắc đầu: “Các ngươi theo ta cùng đi, không phải, ngược lại cũng thôi, thật sự là hắn, tự thân tới, không có đi nghênh đón, chúng ta Hàn gia, chỉ sợ phải đối mặt lật đổ tai ương...”"),t("br"),t("br"),n._v("Cứ việc đại lục ở bên trên trọng danh rất nhiều người, nhưng để cho Dương Huyền danh sư, loại trừ vị kia, thật đúng là chưa từng nghe qua những người khác."),t("br"),t("br"),n._v("Tuy là ở sâu trong nội tâm, cũng cảm thấy đối phương sẽ tìm hắn khả năng rất nhỏ, thế nhưng không dám đánh cam đoan, bọn họ dạng này tiểu gia tộc, vẫn là cẩn thận một chút cho thỏa đáng."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Tất cả trưởng lão đồng thời gật đầu, theo sát tại sau lưng đi ra ngoài, rất mau tới đến đón khách phòng địa phương, ngay sau đó nhìn thấy một cái sắc mặt vàng như nến người trung niên, đang ngồi ở tại chỗ, vẻ mặt lạnh nhạt uống nước trà."),t("br"),t("br"),n._v("“Phía trước thế nhưng là Dương sư?”"),t("br"),t("br"),n._v("Vừa tiến vào gian phòng, Hàn Thủy Lưu lập tức ôm quyền."),t("br"),t("br"),n._v("Tu vi của hắn sớm đã đạt đến Thánh vực bát trọng đỉnh phong, nhưng mà vẫn như cũ nhìn không thấu người trước mắt này tu vi, thoạt nhìn bình thản, nhưng cho người ta một loại khiến người ta run sợ cảm giác."),t("br"),t("br"),n._v("Liền giống như ngồi vị này, chỉ cần động thủ, bọn họ toàn cả gia tộc, đều sẽ chống lại không được."),t("br"),t("br"),n._v("“Ta vừa vặn đi ngang qua nơi đây, có một số việc, muốn tìm người hỏi thăm, đêm khuya mạo muội quấy rầy, còn để Hàn gia chủ chớ nên trách móc!”"),t("br"),t("br"),n._v("Trương Huyền đứng dậy."),t("br"),t("br"),n._v("Đồng dạng là ôm quyền hành lễ, lễ nghi mười phần, nhưng hắn trong cơ thể một cách tự nhiên tản mát ra một loại cao cao tại thượng mùi vị, cứ việc không phải tận lực, nhưng như cũ cho người ta một loại, cao thủ tịch mịch ảo giác."),t("br"),t("br"),n._v("“Cao thủ, nhất định là cao thủ...”"),t("br"),t("br"),n._v("Hàn Thủy Lưu con ngươi co rụt lại."),t("br"),t("br"),n._v("(Canh thứ nhất đến! Gấp đôi nguyệt phiếu, không ném liền lãng phí. Khà khà!)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1498"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1500"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);