(window.webpackJsonp=window.webpackJsonp||[]).push([[572],{928:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1491-mot-cong-ba-viec-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1491-mot-cong-ba-viec-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1491: Một công ba việc 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1491: Một công ba việc "),t("br"),t("br"),n._v("Đối phương cứ việc không có biểu hiện ra tu vi đến cùng cao bao nhiêu, nhưng bình tĩnh phong thái, cái loại này phiêu dật tựa như rời xa hồng trần thần thái, vừa nhìn liền biết thế sự xoay vần, đã trải qua vô số sóng to gió lớn."),t("br"),t("br"),n._v("Loại người này, coi như không phải vị kia cái gọi là “Dương sư”, chỉ sợ thân phận địa vị không thấp, cũng không phải từ hắn một tiểu nhân vật, có thể chống lại."),t("br"),t("br"),n._v("Biết điểm ấy, lại không gia chủ ngạo mạn, vội vàng ôm quyền: “Dương sư chuyện này, ngươi có thể tới gia tộc bọn ta, là vinh hạnh của chúng ta, không biết có gì phân phó, chỉ cần chúng ta Hàn gia có thể làm được, sẽ làm sẽ không cự tuyệt!”"),t("br"),t("br"),n._v("“Cũng không có gì, ta đi ngang qua lân cận, gặp phải Thánh thú đánh lén, tiện tay đem hắn tổn thương, có điều, niệm hắn không phải cố ý gây ra, hơn nữa lại có thần phục chi tâm, ông trời có đức hiếu sinh, liền muốn giúp một tay trị liệu thoáng cái. Ta vân du tứ hải, trên người chưa từng mang dược vật, nghe nói Hàn gia nắm giữ dược liệu, đặc biệt tới cầu lấy!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền nói."),t("br"),t("br"),n._v("“Cầu lấy dược liệu?” Hàn Thủy Lưu sửng sốt một chút: “Không biết... Là cái gì Thánh thú, lại cần loại nào dược vật?”"),t("br"),t("br"),n._v("Cùng là bị thương, Thánh thú thể chất thuộc tính khác biệt, dược vật cũng không giống nhau, liền giống như Hỏa Diễm Kỳ Lân thú, cần hỏa thuộc tính dược vật, hiệu quả trị liệu càng tốt hơn, Địa Ngục Thanh Long thú thì cần muốn thuộc tính âm hàn."),t("br"),t("br"),n._v("Biết đối phương cái gì Thánh thú, loại nào dược vật, cũng tốt tốt hơn tìm kiếm dược liệu."),t("br"),t("br"),n._v("“Chính là bọn họ...”"),t("br"),t("br"),n._v("Trương Huyền cổ tay rung lên, hai đầu Thánh thú rơi vào trước mặt mọi người, mới vừa xuất hiện, lập tức tản mát ra khí tức kinh người, tựa hồ muốn đại sảnh lật tung."),t("br"),t("br"),n._v("“Thánh vực cửu trọng Địa Ngục Thanh Long thú?”"),t("br"),t("br"),n._v("“Thánh vực cửu trọng đỉnh phong Hỏa Diễm Kỳ Lân thú?”"),t("br"),t("br"),n._v("“Tiện tay đem cái này hai đầu Thánh thú kích thương, hắn không có việc gì?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy bất ngờ xuất hiện hai đầu tất cả mọi người, gian phòng bên trong một hồi yên lặng, Hàn gia tất cả trưởng lão, tất cả đều thân thể phát run, nói không ra lời."),t("br"),t("br"),n._v("Trước đó, còn có người còn cảm thấy, đường đường Hàn gia gia chủ chạy tới nghênh đón, có chút chuyện bé xé ra to, nhìn thấy hai cái này tất cả mọi người, không còn gì khác tâm tư."),t("br"),t("br"),n._v("Có thể tiện tay đem Thánh vực cửu trọng đỉnh phong, Thánh vực cửu trọng sơ kỳ hai đầu Thánh thú kích thương, vẻ mặt mây trôi nước chảy, không đáng giá nhắc tới... Nên thực lực gì?"),t("br"),t("br"),n._v("Tưởng tượng liền cảm thấy kinh khủng."),t("br"),t("br"),n._v("“Ta cần Chu Dương thảo, cùng hồng hoa, Thanh Diệp tử căn, Lưu Ly đằng mạn...”"),t("br"),t("br"),n._v("Thuận miệng nói ra mười mấy dạng dược liệu, Trương Huyền lắc đầu, một mặt than thở: “Tạm thời cần những này, bọn họ đột nhiên đánh lén, ta không quan sát phía dưới, dùng nhiều một phần mười lực lượng... Tạo thành kết quả này, thực sự sơ sẩy!”"),t("br"),t("br"),n._v("Hàn Thủy Lưu lông mày lần nữa nhảy một cái, cẩn thận hướng hai đầu Thánh thú nhìn sang, quả nhiên phát hiện cái này hai đầu tất cả mọi người bị thương không nhẹ, miệng vết thương một cỗ đặc thù lực lượng không ngừng nhúc nhích, vượt rất xa Thánh vực cửu trọng, không phải tuỳ tiện liền có thể hóa giải."),t("br"),t("br"),n._v("“Dùng nhiều một phần mười, cứ như vậy mạnh...”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, vội vàng suy tư vừa rồi đối phương nói dược vật, một lát sau vẻ mặt trở nên có chút không dễ nhìn, dừng lại một chút, vội vàng ôm quyền: “Dương sư, những thuốc này...”"),t("br"),t("br"),n._v("“Yên tâm, đã ta cần, sẽ dựa theo giá thị trường cho ngươi thanh toán, sẽ không bạc đãi!” Trương Huyền cười nói."),t("br"),t("br"),n._v("http://ngantruyen.com/"),t("br"),n._v("“Ta không phải ý tứ này, mà là... Ngươi nói mười mấy dạng dược vật, gia tộc bọn ta, nhiều nhất có thể tìm ra bảy loại, còn có tám loại là không bỏ ra nổi tới...”"),t("br"),t("br"),n._v("Hàn Thủy Lưu vội nói."),t("br"),t("br"),n._v("“Bảy loại?” Trương Huyền lông mày nhíu một cái, trên mặt không vui."),t("br"),t("br"),n._v("Không khí chung quanh lập tức ngưng kết xuống, giống như là biến thành thực chất."),t("br"),t("br"),n._v("Một cỗ cường đại uy áp, bất ngờ từ trên người hắn tản ra, muốn đem thiên địa đều xé rách."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),n._v("Cảm nhận được cỗ khí tức này, Hàn Thủy Lưu cùng tất cả trưởng lão toàn thân lập tức không ngừng run lên, hình như bả vai nhiều hơn một tòa núi lớn, đầu gối như nhũn ra, bất cứ lúc nào cũng sẽ quỳ rạp xuống đất."),t("br"),t("br"),n._v("“A, không có ý tứ, nhất thời thất thần...”"),t("br"),t("br"),n._v("Liền tại bọn hắn sắp không kiên trì nổi thời điểm, trước mắt người trung niên nhẹ nhàng cười một tiếng, liền cùng gió hóa mưa, áp lực tiêu hết, dường như vừa rồi cảm giác áp bách, chưa hề xuất hiện qua đồng dạng."),t("br"),t("br"),n._v("“Không có gì...”"),t("br"),t("br"),n._v("Áp lực biến mất, Hàn Thủy Lưu cùng tất cả trưởng lão, đồng thời cảm thấy sau lưng ướt đẫm mồ hôi áo cõng, không nói ra được hoảng sợ."),t("br"),t("br"),n._v("“Để các vị bị sợ hãi, ta là nghĩ đến, không có dược liệu, cái này hai đầu gia hỏa, muốn khôi phục, có thể muốn tiêu tốn chút thời gian, không có khống chế tốt lực lượng trong cơ thể, quan tâm sẽ bị loạn, lỗi lầm của ta...”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền tràn đầy áy náy."),t("br"),t("br"),n._v("Nhìn thấy hắn bộ dáng này, Hàn Thủy Lưu nào dám nói nhảm, dừng lại một chút, cắn răng một cái: “Dương sư... Thật ra thì không cần lo lắng, tuy là chúng ta Hàn gia không có cái này tám loại dược liệu, nhưng Băng Nguyên cung, hẳn là có, nếu như Dương sư thật cần, ta có thể giúp một tay tìm kiếm, lấy ta Hàn gia cùng Băng Nguyên cung quan hệ, mấy bụi dược liệu mà thôi, hẳn là sẽ không khó xử!”"),t("br"),t("br"),n._v("“Ồ? Ngươi có thể giúp đỡ tìm kiếm?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Vâng!” Hàn Thủy Lưu gật đầu: “Chúng ta Hàn gia chịu trách nhiệm toàn bộ Băng Nguyên cung ăn sơ, cùng đơn giản một chút dược vật, xưa nay cùng tông môn Bạch trưởng lão quan hệ rất gần, có thể ủy thác nàng giúp một tay tìm kiếm!”"),t("br"),t("br"),n._v("“Vậy làm phiền Hàn gia chủ, ta hiện tại liền cần, không biết gia chủ có thể hay không báo cho vị này Bạch trưởng lão, để hắn tự thân đưa tới? Cái này hai đầu Thánh thú thương thế mặc dù không tính quá mức nghiêm trọng, nhưng dược vật tìm tới càng nhanh, liền khôi phục càng nhanh, tránh khỏi lưu lại di chứng!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Tự thân đưa tới, không có vấn đề! Dương sư ở đây chờ, ta vậy thì cho Bạch trưởng lão đưa tin, để nàng tự thân tới một chuyến...” Hàn Thủy Lưu nhẹ gật đầu, ngay sau đó vội vã xoay người rời đi."),t("br"),t("br"),n._v("Các trưởng lão khác biết trước mắt vị này mạnh mẽ, vội vàng an bài hắn đến Hàn gia huy hoàng nhất đại sảnh, trà ngon phục vụ."),t("br"),t("br"),n._v("“Thật lợi hại...”"),t("br"),t("br"),n._v("Một bên Trần Nhạc Dao đem một màn này nhìn ở trong mắt, cũng khiếp sợ tột đỉnh, môi đỏ mở ra, lại khó khép lại."),t("br"),t("br"),n._v("Tốt một chiêu vừa đập vừa cào."),t("br"),t("br"),n._v("Thả ra Kỳ Lân thú, Thanh Long thú, chẳng những dọa đến Hàn gia tâm phục khẩu phục, càng có thể mượn cơ hội tìm kiếm trị liệu cái này hai đầu Thánh thú dược vật, để hắn khôi phục nhanh chóng... Mấu chốt nhất là, có lý do tốt hơn, để Bạch trưởng lão tới!"),t("br"),t("br"),n._v("Một công ba việc!"),t("br"),t("br"),n._v("Dựa theo ý nghĩ của nàng, trực tiếp tới, làm cho đối phương giúp một tay liên hệ Bạch trưởng lão... Bây giờ suy nghĩ một chút, đã rơi xuống tầm thường."),t("br"),t("br"),n._v("Dù sao, một vị sâu không lường được danh sư, chỉ đích danh muốn gặp Băng Nguyên cung trưởng lão... Để cho người ta không nghi ngờ đều là giả."),t("br"),t("br"),n._v("Một khi đem tin tức truyền lại đến Băng Nguyên cung, chỉ sợ đã đánh cỏ động rắn."),t("br"),t("br"),n._v("Thuận lý thành chương, lại xác lập uy tín cùng thực lực, để cho người ta không có chút nào nghi ngờ, phần này năng lực, quả thực đáng sợ!"),t("br"),t("br"),n._v("Nếu không phải tận mắt thấy đối phương từ Trương Huyền ngụy trang mà thành, thật nghi ngờ, có phải thật vậy hay không Dương sư đến rồi!"),t("br"),t("br"),n._v("Ngồi ở đại sảnh chờ lấy uống trà, đại khái hơn mười phút, chỉ thấy Hàn Thủy Lưu vội vã chạy tới, đi theo phía sau một cái mang theo mũ trung niên phụ nhân."),t("br"),t("br"),n._v("“Dương sư, Bạch trưởng lão tới...”"),t("br"),t("br"),n._v("Tiến lên một bước, Hàn Thủy Lưu ôm quyền."),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu, còn chưa lên tiếng, đối diện trung niên phụ nhân, nhướng mày, con mắt rơi ở bên người nữ hài trên người: “Nhạc Dao, ngươi làm sao tại đây?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1499"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1501"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);