(window.webpackJsonp=window.webpackJsonp||[]).push([[573],{929:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1492-đia-mach-hoa-sen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1492-đia-mach-hoa-sen"}},[n._v("#")]),n._v(" Chương 1492: Địa mạch hoa sen")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1492: Địa mạch hoa sen"),t("br"),t("br"),n._v("Trần Nhạc Dao đi Thánh tử điện, toàn bộ Băng Nguyên cung đều biết tình, lúc này mới đi ba cái tháng sau, làm sao đột nhiên xuất hiện ở đây?"),t("br"),t("br"),n._v("“Bạch trưởng lão, ta đi theo Dương sư tới... Có chuyện muốn làm!”"),t("br"),t("br"),n._v("Trần Nhạc Dao vội vàng đứng dậy."),t("br"),t("br"),n._v("Đây là tông môn nàng quen thuộc nhất trưởng lão, không nguyện ý lừa gạt."),t("br"),t("br"),n._v("“Dương sư?”"),t("br"),t("br"),n._v("Bạch trưởng lão lúc này mới đem con mắt rơi vào Trương Huyền trên người."),t("br"),t("br"),n._v("Trước mắt vị này, tuổi tác thoạt nhìn không lớn, một thân tu vi, lại sâu không lường được, để cho người ta nhìn không ra sâu cạn."),t("br"),t("br"),n._v("“Dương sư ta nghe nói qua không ít, không biết... Các hạ là vị nào?”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, Bạch trưởng lão nhìn lại, trên mặt không tốt."),t("br"),t("br"),n._v("Trần Nhạc Dao là nàng coi trọng nhất Băng Nguyên cung tử đệ, không tại Thánh tử điện tu luyện, lại bị người trước mắt này mang về, trong lòng tràn đầy không vui."),t("br"),t("br"),n._v("“Tại hạ... Dương Huyền!”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Dương Huyền? Cái nào Dương Huyền?”"),t("br"),t("br"),n._v("Thì thầm một câu, Bạch trưởng lão nhớ tới cái gì, lông mày nhảy một cái: “Danh Sư đường tổng bộ Dương Huyền?”"),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Đối diện Trương Huyền cũng không trả lời, ngược lại cười nhạt một tiếng, đứng dậy: “Bạch trưởng lão, khuôn mặt thoạt nhìn hồng nhuận, trên thực tế nhưng có khí huyết ngưng trệ tại hoài hải huyệt, nóng lạnh xung đột, dẫn đến trong cơ thể nóng lạnh luân phiên, nếu như không nhìn lầm... Trưởng lão dưới mũ tóc, hẳn là hoa râm chi sắc đi!”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Bạch trưởng lão sững sờ, con mắt trợn tròn."),t("br"),t("br"),n._v("Nàng từ trước tới nay đều mang mũ gặp người, chính là vì che giấu hoa râm tóc, ngay cả quan hệ gần nhất Trần Nhạc Dao, cũng không biết việc này... Người trước mắt này, liếc mắt một cái liền nhìn ra?"),t("br"),t("br"),n._v("“Không cần khẩn trương, đánh một quyền nhìn một chút...”"),t("br"),t("br"),n._v("Không để ý tới nàng chấn kinh, hai bước đi tới trước mặt, thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("Nghe nói như thế, Bạch trưởng lão ánh mắt ngẩn ngơ, kìm lòng không được đánh một quyền, ngay sau đó kịp phản ứng, sắc mặt tái xanh, đang muốn quát tháo, liền nghe lời nói của đối phương vang lên lần nữa: “Ngươi Băng Huyền công, từ nhỏ tu luyện, hơn nữa chưa hề tiếp xúc qua khác giới, cố thủ thuần âm, quả thực đạt đến cực hạn, nhưng... Ngươi có phát hiện hay không, tu vi của ngươi, đạt tới cảnh giới bây giờ, đã không cách nào lại tăng thêm mảy may, dù là dùng nhiều hơn nữa dược vật, tìm bao nhiêu danh sư, đều không có phương pháp đột phá Thánh vực cửu trọng?”"),t("br"),t("br"),n._v("“Ngươi, làm sao ngươi biết?”"),t("br"),t("br"),n._v("Bạch trưởng lão lui về phía sau."),t("br"),t("br"),n._v("Nàng đã sớm đạt tới Thánh vực bát trọng đỉnh phong, đáng tiếc, thủy chung không cách nào đột phá, mượn không biết bao nhiêu phương pháp, đều không có hiệu quả gì, đến bây giờ, đã có chút tuyệt vọng rồi."),t("br"),t("br"),n._v("Chỉ là... Chuyện này, chỉ có trong tộc một chút thân thiết trưởng lão biết, trước mắt vị này thuận miệng nói ra... Bởi vì nhìn nàng đánh quyền?"),t("br"),t("br"),n._v("Không có mạnh như vậy nhãn lực đi!"),t("br"),t("br"),n._v("“Thánh vực cửu trọng Vũ Không cảnh, trên dưới tứ phương gọi là vũ, vũ không, muốn đối không gian có càng lớn lĩnh ngộ mới có thể làm đến, Băng Huyền công, mặc dù là có một không hai công pháp, nhưng dùng băng hàn làm cơ hội, tìm kiếm đột phá, cùng ngươi khí tràng, hơi có không hợp! Lúc này mới dẫn đến trong cơ thể ngươi nóng lạnh không đều, tóc xuất hiện hoa râm chi sắc...”"),t("br"),t("br"),n._v("Trương Huyền mí mắt vừa nhấc, lần nữa nhìn qua: “Ngươi mỗi lần ban đêm tu luyện, có phải hay không đều sẽ cảm giác đến tâm khô khó bình? Cảm thấy trong cơ thể có nội hỏa tuôn ra?”"),t("br"),t("br"),n._v("“Cái này... Đúng!”"),t("br"),t("br"),n._v("Muốn phủ nhận, dừng lại một chút, Bạch trưởng lão vẫn gật đầu, mang theo nghi ngờ nhìn qua: “Dương sư nói tới khí tràng... Là cái gì? Băng Huyền công, ta tu luyện trên trăm năm, đều không có vấn đề, làm sao lại không hợp?”"),t("br"),t("br"),n._v("Đối phương có thể một cái nói ra, có lẽ có thể giúp một tay giải quyết, cái này chính mình cũng khó mà giải quyết vấn đề, cho nên tại không có giấu diếm."),t("br"),t("br"),n._v("Chỉ là, nàng có chút kỳ quái."),t("br"),n._v(" Băng Huyền công tu luyện không biết đã bao nhiêu năm, chưa hề xuất hiện qua vấn đề, làm sao lại không hợp?"),t("br"),t("br"),n._v("Khí tràng lại là cái gì?"),t("br"),t("br"),n._v("“Khí tràng, chính là ngươi toàn thân sinh ra từ trường, cùng ngươi tu luyện công pháp, cá nhân bản tính quen thuộc, thậm chí... Cảnh vật chung quanh đều có quan hệ!”"),t("br"),t("br"),n._v("Trương Huyền cười cười: “Thể chất của ngươi, mang theo hàn âm thuộc tính, tu luyện Băng Huyền công quả thực là nhất phù hợp, không có bất cứ vấn đề gì!”"),t("br"),t("br"),n._v("“Vậy...”"),t("br"),t("br"),n._v("Bạch trưởng lão tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Ngay cả Trần Nhạc Dao cũng không rõ ràng cho lắm."),t("br"),t("br"),n._v("Mới vừa không còn nói, khí tràng không phù hợp ư? Tại sao lại nói tu luyện Băng Huyền công không có vấn đề?"),t("br"),t("br"),n._v("“Đừng có gấp, thể chất tu luyện không có vấn đề, có thể ngươi khí tràng, nhưng sớm đã phát sinh biến hóa...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Khí tràng biến hóa?”"),t("br"),t("br"),n._v("“Không sai, nếu như ta không nhìn lầm, ngươi tu vi đạt tới Thánh vực bát trọng về sau, có phải hay không bắt đầu ở viện tử của mình bên trong, gieo một gốc địa mạch hoa sen?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Đúng...”"),t("br"),t("br"),n._v("Bạch trưởng lão gật đầu."),t("br"),t("br"),n._v("“Địa mạch hoa sen, hạt sen có làm đẹp, vĩnh trú dung nhan công hiệu, không thiếu nữ tử đều ưa thích nuôi dưỡng, ngươi nuôi tới một gốc, không gì đáng trách, chỉ là... Ngươi cũng đã biết, loại này hoa sen, cũng không phải là đơn giản hấp thu địa mạch liền có thể sống sót, càng quan trọng hơn là, cần địa hỏa tẩm bổ, đặc biệt là hạt sen... Muốn trưởng thành, nhất định phải hấp thu hơi nóng, cứ việc chỉ là yếu ớt một tia, cũng đã thay đổi hoàn cảnh chung quanh, để bốn phía khí tràng, biến thành hỏa diễm thuộc tính. Đợi ở trong đó một ngày không có gì, hai ngày cũng không có gì, thời gian lâu dài, cùng ngươi Băng Huyền công, thuộc tính hàn băng thể chất, sớm chiều ở chung, liền có vấn đề lớn!”"),t("br"),t("br"),n._v("“Hỏa tính kèm theo ở chung thời gian càng dài, liền sẽ ở trong người, càng tích tụ càng nhiều, nếu như ngươi đã kết hôn, có lẽ còn có thể bài tiết, nhưng cố thủ nguyên âm, hỏa độc không ra, lại thêm lại tu luyện thuần âm công pháp, cả hai đối đầu, lúc này mới dẫn đến tóc hoa râm, chân khí ngưng trệ, cho dù lại tu luyện, cũng không cách nào đột phá...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu, than thở một tiếng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Bạch trưởng lão thân thể vụt qua."),t("br"),t("br"),n._v("Tuy là không biết đối phương nói có chính xác không, nhưng nàng trong sân quả thực trúng một gốc địa mạch hoa sen, trong cơ thể nàng cũng quả thực chân khí ngưng trệ, cẩn thận hồi tưởng trước kia trải qua từng màn, quả thực là từ bắt đầu trồng cái này gốc hoa sen về sau, tu vi mới càng ngày càng chậm..."),t("br"),t("br"),n._v("Chẳng lẽ, đối phương nói là sự thật?"),t("br"),t("br"),n._v("“Ta nên làm cái gì? Mong rằng Dương sư chỉ điểm...”"),t("br"),t("br"),n._v("Giờ này khắc này, trong lòng lại không nửa điểm nghi ngờ."),t("br"),t("br"),n._v("Trước đó, còn cảm thấy Dương sư, không có khả năng chạy đến nơi đây đến, đối phương liếc mắt liền nhìn ra nàng mấu chốt, hơn nữa nhẹ nhõm chỉ ra, trong mắt mạnh mẽ, cửu tinh danh sư cũng không bằng... Loại trừ vị kia, thực sự nghĩ không ra đến cùng có ai, có loại năng lực này."),t("br"),t("br"),n._v("“Rất đơn giản, đưa ngươi trong viện hoa sen hủy đi, sau đó tiến vào hàn băng hồ ngâm, liên tục kiên trì một tháng, bệnh dữ diệt hết, tu vi cũng sẽ có điều đột phá, xung kích Vũ Không cảnh, sẽ không còn vấn đề gì!”"),t("br"),t("br"),n._v("Trương Huyền cười nhạt một tiếng: “Đương nhiên, nếu như ngươi không kiên trì được một tháng, trực tiếp tìm người hôn phối, âm dương điều hòa, ba ngày liền có thể giải quyết!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Bạch trưởng lão hơi đỏ mặt, vội vàng lắc đầu: “Ta đi hàn băng hồ là được!”"),t("br"),t("br"),n._v("Cả đời giữ mình trong sạch, đương nhiên sẽ không vì giải quyết phiền phức, liền đánh vỡ kiên trì."),t("br"),t("br"),n._v("“Dương sư... Địa vị kính trọng, tu vi cao thâm, coi như Thánh thú bị thương, cũng sẽ không không có dược vật liền không cách nào trị liệu a? Lại nói, coi như cần dược vật, cũng không cần để cho ta tới... Không biết như vậy rối rắm, đến cùng chuyện gì?”"),t("br"),t("br"),n._v("Lại không xoắn xuýt những này, Bạch trưởng lão ngẩng đầu nhìn tới."),t("br"),t("br"),n._v("Mặc dù đối phương thủ đoạn rất hợp lý, nhưng thân là thế lực lớn nhất trưởng lão, kiến thức rộng rãi, kết hợp với Trần Nhạc Dao tại đây, vẫn như cũ nhìn ra không đúng."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1500"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1502"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);