(window.webpackJsonp=window.webpackJsonp||[]).push([[574],{927:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1493-uy-hiep"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1493-uy-hiep"}},[n._v("#")]),n._v(" Chương 1493: Uy hiếp")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1493: Uy hiếp【 canh thứ nhất】"),g("br"),g("br"),n._v("Nàng tại Băng Nguyên cung tất cả trưởng lão bên trong, địa vị không tính quá cao, dựa theo tình huống bình thường, coi như sống đến chết, cũng không có khả năng tiếp xúc đến Dương sư loại địa vị này người, đã Trần Nhạc Dao tại đây, đi theo đối phương sau lưng, lại thêm Hàn Thủy Lưu gia chủ đưa tin mang tới tin tức, còn lại đoán ra được, liền rất đơn giản."),g("br"),g("br"),n._v("“Bạch trưởng lão người sảng khoái nói chuyện sảng khoái!”"),g("br"),g("br"),n._v("Biết không ẩn giấu được, Trương Huyền cũng không nhiều lời, mỉm cười, quay đầu nhìn một cái một bên Hàn Thủy Lưu gia chủ cùng tất cả trưởng lão: “Cảm ơn Hàn gia chủ thông báo!”"),g("br"),g("br"),n._v("“Dương sư khách khí, năng lực Dương sư giúp một tay, là ta Hàn gia vận may...”"),g("br"),g("br"),n._v("Hàn gia chủ vội vàng ôm quyền."),g("br"),g("br"),n._v("Vị này chính là Danh Sư đường tổng bộ thái thượng trưởng lão, liền Bạch trưởng lão đều công nhận, tuyệt sẽ không là giả, có thể tới gia tộc bọn họ, tuyệt đối rồng đến nhà tôm."),g("br"),g("br"),n._v("“Ừm!”"),g("br"),g("br"),n._v("Trương Huyền gật đầu, nụ cười biến mất, không nói thêm lời."),g("br"),g("br"),n._v("Nhìn thấy hắn bộ biểu tình này, Hàn gia chủ lập tức hiểu được, vội vàng ôm quyền: “Thực sự thật không tiện, trong nhà của ta còn có chút sự tình, cần phải đi xử lý, trước hết được cáo từ...”"),g("br"),g("br"),n._v("Nói xong vội vã mang theo tất cả trưởng lão rời đi."),g("br"),g("br"),n._v("Rất hiển nhiên, Dương sư mới vừa nói “Cảm ơn”, mang theo đuổi người chi ý, nếu ngươi không đi, khẳng định giận thật à."),g("br"),g("br"),n._v("Gian phòng chỉ còn lại có Bạch trưởng lão, Trần Nhạc Dao."),g("br"),g("br"),n._v("“Đã Bạch trưởng lão đoán được, ta cũng liền lại không vòng do, Triệu Nhã, là đồ tôn của ta!” Xoay người trở lại chỗ ngồi xuống, Trương Huyền nhìn lại."),g("br"),g("br"),n._v("Trên mặt biểu lộ mặc dù không có bất kỳ biến hóa nào, lại cho người ta một loại to lớn cảm giác áp bách."),g("br"),g("br"),n._v("“Thiếu cung chủ... Là Dương sư đồ tôn?”"),g("br"),g("br"),n._v("Bạch trưởng lão giật nảy mình."),g("br"),g("br"),n._v("Hiển nhiên tin tức này, nàng là lần đầu tiên nghe nói."),g("br"),g("br"),n._v("“Không sai, nàng hiện tại như thế nào? Ta nghĩ nghe lời nói thật, mong rằng Bạch trưởng lão báo cho!” Trương Huyền ôm quyền."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Trên mặt lúc đỏ lúc trắng, Bạch trưởng lão không biết nên trả lời như thế nào."),g("br"),g("br"),n._v("“Ta tiếp vào đưa tin, nàng gặp phải nguy hiểm, đã hiện tại là Băng Nguyên cung thiếu cung chủ, xảy ra chuyện, hẳn là các ngươi rõ ràng nhất, ta hi vọng đạt được đáp án!”"),g("br"),g("br"),n._v("Lông mày giương lên, Trương Huyền nhìn lại, ngón tay gõ cái bàn, thanh âm dần dần trở nên rét run: “Ngươi biết thân phận của ta, nhất cử nhất động đại biểu là Danh Sư đường tổng bộ, không muốn đưa tới quá nhiều phiền phức, mới đến Hàn gia, đưa ngươi tìm ra. Nếu không, trực tiếp đi tìm các ngươi đại trưởng lão, chỉ là hỏi thăm một chút đồ tôn tình huống, tin tưởng thiên hạ còn không người dám ngỗ nghịch ta mặt mũi này!”"),g("br"),g("br"),n._v("“Ta...”"),g("br"),g("br"),n._v("Bạch trưởng lão run rụt lại."),g("br"),g("br"),n._v("Đối phương không có lấy ra thân phận lệnh bài, cũng không có gióng trống khua chiêng, rất rõ ràng là muốn lặng yên làm việc, không muốn kinh động quá nhiều tên sư."),g("br"),g("br"),n._v("Nếu không, lấy thân phận của hắn, ra lệnh một tiếng, Băng Nguyên cung có tồn tại hay không, đều cũng khó nói."),g("br"),g("br"),n._v("“Ta cho ngươi thời gian cân nhắc, chẳng qua, sự kiên nhẫn của ta có hạn, ngươi không cách nào trả lời, ta liền sẽ trực tiếp đi Băng Nguyên cung! Đến lúc đó, sẽ dẫn tới dạng gì kết quả, tin tưởng khỏi cần ta nói, hẳn là cũng sẽ minh bạch!”"),g("br"),g("br"),n._v("“Ta biết...”"),g("br"),g("br"),n._v("Thân thể trở nên cứng, Bạch trưởng lão ngập ngừng chỉ chốc lát, cắn răng: “Thiếu cung chủ quả thực là xảy ra chuyện, buổi trưa hôm nay, đại trưởng lão tự mình ra lệnh, Tứ trưởng lão, Ngũ trưởng lão, Thất trưởng lão đồng thời ra tay, đem hắn trấn áp tại【 Thanh Phạm điện】, không cho phép ra ngoài, không cho phép rời đi, càng không cho phép đưa tin... Chuyện này, là trong cung lớn nhất cơ mật, phàm là trong cung trưởng lão, không được tin tức truyền ra ngoài, nếu không... Giết chết bất luận tội!”"),g("br"),g("br"),n._v("“Thanh Phạm điện? Đây chính là giam giữ phạm nhân địa phương, thiếu cung chủ đến cùng phạm vào cái gì sai?”"),g("br"),g("br"),n._v("Trần Nhạc Dao vội vàng nhìn qua."),g("br"),g("br"),n._v("Mặc dù trước thời hạn đoán được, có thể là Băng Nguyên cung nội bộ xảy ra sự tình, nhưng chân chính nghe được, vẫn như cũ cảm thấy khó tin."),g("br"),n._v("Thanh Phạm điện là Băng Nguyên cung chuyên môn dùng để giam giữ phạm nhân chỗ, bị giam trong đó, tám chín phần mười đều là tội ác tày trời, làm ác không chịu hối cải chi đồ. Nàng rời đi tông môn trước đó, thiếu cung chủ vẫn là toàn bộ Băng Nguyên cung hi vọng cùng tương lai, thế nào thời gian nháy mắt, bị giam giữ?"),g("br"),g("br"),n._v("“Ngươi hẳn là rõ ràng thân phận của ta, tương đối xấu hổ, trong cung hạch tâm nhất sự tình, biết cũng không nhiều, thiếu cung chủ đến cùng phạm sai lầm gì, ta không rõ lắm, chỉ biết là, tam đại trưởng lão đồng thời ra tay, liên hợp trấn áp, thiếu cung chủ bây giờ bị khốn thiên tỏa khóa lại, khẽ động cũng không thể động!”"),g("br"),g("br"),n._v("Bạch trưởng lão nói."),g("br"),g("br"),n._v("“Khốn thiên tỏa?” Trương Huyền thân thể căng thẳng."),g("br"),g("br"),n._v("Loại vật này, hắn tại Thánh tử điện trong thư tịch thấy qua giới thiệu, là một loại có thể đem người vây khốn hình cụ, bị khóa lại người, kinh mạch toàn thân giam cầm, không chỉ không cách nào vận dụng lực lượng, còn muốn mỗi ngày tiếp nhận đau đớn dằn vặt."),g("br"),g("br"),n._v("Không nghĩ tới Triệu Nhã, thế mà đắp lên loại này hình cụ!"),g("br"),g("br"),n._v("Đáng ghét!"),g("br"),g("br"),n._v("“Là, chẳng qua, Dương sư yên tâm, thiếu cung chủ hẳn không có sinh mệnh lo lắng, xế chiều hôm nay, đại trưởng lão còn chuyên môn khai báo, bảo ta chuẩn bị tốt hơn đồ vật, định thời gian đưa qua, ngàn vạn không thể để cho hắn bị đói...”"),g("br"),g("br"),n._v("Cảm nhận được trước mắt trung niên nhân, trong cơ thể tản mát ra băng lãnh khí tức, hình như bất cứ lúc nào cũng sẽ đem hắn vỡ vụn, Bạch trưởng lão dọa đến cổ co rụt lại, vội vàng nói."),g("br"),g("br"),n._v("Đại trưởng lão tại sao lại vây khốn thiếu cung chủ, vì sao lại muốn ra tay, nàng đích xác không rõ ràng lắm, chẳng qua, căn cứ cử động của đối phương đến xem, hẳn là trong thời gian ngắn không có nguy hiểm tính mạng, chỉ là một loại lợi hại trừng phạt."),g("br"),g("br"),n._v("“Dẫn ta đi gặp nàng!”"),g("br"),g("br"),n._v("Trương Huyền thanh âm băng lãnh."),g("br"),g("br"),n._v("“Thấy nàng, cái này...” Bạch trưởng lão khắp khuôn mặt là rầu rĩ: “Đại trưởng lão tự mình giam giữ, ta cũng chỉ có đưa cơm thời gian, mới có thể tới gần, thời gian khác, căn bản không có cách nào đi qua...”"),g("br"),g("br"),n._v("“Ta biết, ngươi dẫn ta đi Băng Nguyên cung, chỉ cần nói cho ta biết Thanh Phạm điện cùng nàng bị giam giữ ở nơi nào liền có thể, còn lại không cần phí tâm!”"),g("br"),g("br"),n._v("Trương Huyền khoát tay áo."),g("br"),g("br"),n._v("Chỉ cần có thể tìm tới Triệu Nhã ở nơi nào, lấy năng lực của hắn, liền có cơ hội đem hắn cứu ra."),g("br"),g("br"),n._v("Đương nhiên, đến cùng chuyện gì xảy ra, hay là muốn làm mặt hỏi rõ ràng mới được."),g("br"),g("br"),n._v("Nếu như là một loại tôi luyện, hắn coi như ái đồ sốt ruột, cũng sẽ không chậm trễ."),g("br"),g("br"),n._v("Bạch trưởng lão trên mặt lúc trắng lúc xanh."),g("br"),g("br"),n._v("“Không mang theo ta đi, ta sẽ trực tiếp tiến Băng Nguyên cung muốn người, chẳng qua, ngươi hẳn là rõ ràng, quang minh chính đại đi qua, cùng như bây giờ đi qua, khác biệt có bao lớn!”"),g("br"),g("br"),n._v("Biết đối phương lo lắng, Trương Huyền nói."),g("br"),g("br"),n._v("“Ta...” Bạch trưởng lão nói không ra lời."),g("br"),g("br"),n._v("Mang đối phương đi qua, tương đương phản bội đại trưởng lão, không phải dẫn đi, đối phương là Danh Sư đường tổng bộ thái thượng trưởng lão, Một khi nổi giận... Tất nhiên sẽ cho toàn bộ Băng Nguyên cung mang đến vô tận tai hoạ."),g("br"),g("br"),n._v("Thậm chí cũng có thể như vậy hủy diệt!"),g("br"),g("br"),n._v("Coi như đối phương tôn trọng Danh Sư đường quy định, có thể thực lực thực sự quá cao, đơn đấu... Toàn bộ Băng Nguyên cung cũng không có người có thể chống lại ah!"),g("br"),g("br"),n._v("“Yên tâm, ta chỉ là đi xem một chút đồ tôn của ta, hỏi rõ ràng tình huống, nếu như sai lầm tại nàng, tuyệt sẽ không bao che, Đương nhiên... Nếu như các ngươi Băng Nguyên cung, cố ý tìm phiền toái, tận lực khó xử, ta cũng không để ý, cùng các ngươi cái kia cái gọi là đại trưởng lão nói chuyện, lần nữa định một chút quy củ!”"),g("br"),g("br"),n._v("Vịn trước mắt góc bàn, Trương Huyền híp mắt lại, giọng nói mặc dù bình thản, lại khó mà che giấu tản ra khí phách."),g("br"),g("br"),n._v("Dường như chỉ cần đối phương thật oan ức Triệu Nhã, nhất định cho Băng Nguyên cung một cái cả đời khó quên giáo huấn."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Nghe nói như thế, biết đối phương tâm ý đã quyết, Bạch trưởng lão cắn răng: “Tốt, ta mang Dương sư đi qua!”."),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1501"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1503"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);