(window.webpackJsonp=window.webpackJsonp||[]).push([[58],{413:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1029-đang-so-dung-nham-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1029-đang-so-dung-nham-thuong"}},[n._v("#")]),n._v(" Chương 1029: Đáng sợ dung nham (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1029: Đáng sợ dung nham (thượng)"),t("br"),t("br"),n._v("Ngẩng đầu nhìn lại, chỉ thấy Lạc Nhược Hi gương mặt xinh đẹp đỏ lên, tức giận đến sắp nổ tung."),t("br"),t("br"),n._v("Người khác cũng đang lo lắng, như thế nào tìm kiếm lối vào, cái tên này ngược lại tốt, gắt gao tiếp cận bản thân, trên dưới quan sát, vừa nhìn liền biết chưa nghĩ ra sự tình."),t("br"),t("br"),n._v("“Khụ khụ!”"),t("br"),t("br"),n._v("Không nghĩ tới đang tại suy tư trọng yếu như vậy sự tình, bị đối phương phát hiện, Trương Huyền bị nước bọt sặc một cái, nghiêm sắc mặt, lông mày nâng lên: “Ta đang suy nghĩ dung nham nguy hiểm như thế, khẳng định không phải để cho người ta trực tiếp chui vào, mà là một loại khảo nghiệm!”"),t("br"),t("br"),n._v("“Khảo nghiệm?” Thấy hắn nghĩ tới như vậy ngay thẳng, mà bản thân nhưng hiểu lầm, Lạc Nhược Hi tràn đầy thật không tiện."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Trương Huyền đứng tại trên không, con mắt nhìn về phía trước, bàn tay vung vẩy, rất có chỉ điểm giang sơn chi ý: “Lưu lại di tích người, nếu như không muốn để cho hậu nhân đi vào, tuyệt sẽ không để trận pháp xuất hiện sơ hở, cách một đoạn thời gian, để cho người ta có thể đi vào... Đã làm như vậy, nói rõ di tích là lưu cho hậu nhân, mà cái này dung nham có thể là cái thứ nhất khảo nghiệm, chỉ có giải quyết cái này, mới có thể càng đi càng sâu!”"),t("br"),t("br"),n._v("“Cái này...” Sửng sốt một chút, Lạc Nhược Hi nhẹ gật đầu."),t("br"),t("br"),n._v("Đối phương nói không sai, cái này di tích nếu như có thể xông ngang xông thẳng liền đi vào, nhiều năm như vậy, khẳng định sớm quang lâm không biết bao nhiêu người, không tồn tại nữa."),t("br"),t("br"),n._v("“Biết những này để làm gì? Ngươi có thể đoán được đối phương lưu lại mảnh này dung nham mục đích? Hoặc là... Có thể tìm tới di tích chân chính cửa vào ở đâu?”"),t("br"),t("br"),n._v("Lạc Nhược Hi nhìn qua."),t("br"),t("br"),n._v("“Ta tại cân nhắc...”"),t("br"),t("br"),n._v("Xoay đầu lại, Trương Huyền trong lòng thở phào nhẹ nhõm."),t("br"),t("br"),n._v("May mắn hắn phản ứng nhanh, nếu không, nếu là cho đối phương biết mình ý nghĩ, khẳng định bóp chết hắn tâm đều có."),t("br"),t("br"),n._v("“Quên đi, trước không nghĩ những thứ này...”"),t("br"),t("br"),n._v("Lắc đầu, đem trong đầu lung ta lung tung ý nghĩ dứt bỏ, Trương Huyền cúi đầu nhìn sang."),t("br"),t("br"),n._v("Phía dưới dung nham, không ngừng cuồn cuộn, giống như là đun lên nước sôi, còn không có tiến vào bên trong, liền cho người ta một loại cực nóng khó nhịn, bất cứ lúc nào cũng sẽ đốt trụi cảm giác."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn mở ra, Trương Huyền nhìn kỹ lại."),t("br"),t("br"),n._v("Chỉ thấy toàn bộ miệng núi lửa, bị hơi nóng bao phủ, giống như là tràn ngập hơi nước phòng tắm hơi, để cho người ta thấy không rõ lắm."),t("br"),t("br"),n._v("Quét một vòng, đồng thời không có phát hiện trận pháp tung tích, thậm chí liền linh khí tung tích, đều thấy không rõ lắm."),t("br"),t("br"),n._v("“Có lẽ có không gian gấp...”"),t("br"),t("br"),n._v("Nhíu nhíu mày, đang định đem câu thông Thiên Nghĩ Phong Mẫu, để hắn điều tra một phen, liền nghe đến một giọng nói vang lên."),t("br"),t("br"),n._v("“Những này hình như không phải bình thường dung nham...”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy Ngô sư cùng Hàn hội trưởng đám người đứng chung một chỗ, người sau một mặt kinh ngạc."),t("br"),t("br"),n._v("Mọi người nghi ngờ."),t("br"),t("br"),n._v("“Các ngươi nhìn!”"),t("br"),t("br"),n._v("Hàn hội trưởng cổ tay khẽ đảo, một chuôi linh phẩm trung kỳ vũ khí xuất hiện tại lòng bàn tay, chân khí truyền bá, trực tiếp hướng phía dưới bắn nhanh mà đi."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Vũ khí cùng dung nham dung hợp, lập tức phát ra Ầm thanh âm, không cần một lát, liền bị hòa tan thành chất lỏng, biến mất không thấy gì nữa."),t("br"),t("br"),n._v("“Linh phẩm trung kỳ... Đều có thể hòa tan?”"),t("br"),t("br"),n._v("Mọi người tất cả đều giật nảy mình."),t("br"),t("br"),n._v("Trước đó chỉ cảm thấy phía dưới nhiệt độ rất cao, nhưng bằng cho bọn hắn mượn cường giả Thánh vực sức chiến đấu, hẳn là có thể chống lại rất lâu, sẽ không nhận bị thương tổn, nhưng thấy cảnh này, lập tức hiểu, phía dưới dung nham so với bọn hắn tưởng tượng càng thêm đáng sợ!"),t("br"),t("br"),n._v("“Là... Địa tâm dung nham!”"),t("br"),t("br"),n._v("Vân Hư học viện viện trưởng Ô Thiên Khung chần chờ một chút, nói."),t("br"),t("br"),n._v("“Địa tâm dung nham?” Mọi người không hiểu, nhìn lại."),t("br"),t("br"),n._v("“Ta xem qua liên quan thư tịch, nghe nói dung nham, càng hướng xuống nhiệt độ càng cao, lợi hại nhất là địa tâm dung nham, một giọt liền có thể hòa tan Linh khí, thậm chí Thánh khí! Trước mắt cái này, khả năng còn không có nhiệt độ cao như thế, nhưng đã vượt qua bình thường dung nham, cho nên... Vô cùng có khả năng, phía dưới nối liền không phải di tích, mà là địa tâm!”"),t("br"),t("br"),n._v("Ô Thiên Khung phân tích nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người cau mày."),t("br"),t("br"),n._v("“Bất kể là phải hay không, nhìn một chút liền biết!” Ngô sư lông mày giương lên: “Như vậy, các ngươi trước tiên ở nơi này chờ lấy, ta đi xuống xem một chút...”"),t("br"),t("br"),n._v("“Cái này dung nham nóng như vậy, phía dưới khẳng định nhiệt độ cao hơn, cho dù thực lực ngươi mạnh mẽ, muốn đi xuống cũng khó a?”"),t("br"),t("br"),n._v("Hàn hội trưởng nhịn không được nói."),t("br"),t("br"),n._v("“Ta vừa rồi phân tích một chút, lấy ta thực lực bây giờ, ước chừng có thể ẩn vào đi chừng một trăm mét, một khi gặp được nguy hiểm, sẽ trực tiếp đi ra, yên tâm đi...”"),t("br"),t("br"),n._v("  Ngô sư gật đầu, ánh mắt ngưng trọng: “Nếu như không đi vào, chỉ ở nơi này chờ lấy, ta sợ thời gian lâu dài đồng dạng không kiên trì nổi!”"),t("br"),t("br"),n._v("Mặc dù bọn họ phi hành trên không trung, khoảng cách phía dưới dung nham còn có một số khoảng cách, nhưng phía trên có phong ấn ngăn trở, trong này nhiệt độ đã đạt đến kinh người tình trạng, trong thời gian ngắn, mọi người chỗ dựa chân khí, còn có thể chống lại, nhưng thời gian dài, chân khí hao hết, sợ rằng sẽ đồng dạng bởi vì không chịu nổi, tươi sống bị thiêu chết."),t("br"),t("br"),n._v("“Như thế, ngươi phải cẩn thận...”"),t("br"),t("br"),n._v("Nghe được hắn, Hàn hội trưởng biết nói là sự thật, nhịn không được nhẹ gật đầu, khai báo một câu."),t("br"),t("br"),n._v("“Ừm, yên tâm đi! Một khi gặp được nguy hiểm, khẳng định sẽ trực tiếp đi ra!”"),t("br"),t("br"),n._v("Cười cười, Ngô sư không nói thêm nữa, vẻ mặt trở nên ngưng trọng dị thường, chân khí trong cơ thể đột nhiên bay lên, cả người tựa như một đầu bơi lội tinh không cự long, thân thể xoay một cái, trực tiếp hướng phía dưới phóng đi."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Chân khí đem phía dưới dung nham gạt ra, cả người trực tiếp chui vào."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn động tác nhanh như vậy, Trương Huyền chần chờ một chút, vẫn là đem ngăn trở, nuốt xuống."),t("br"),t("br"),n._v("Nói thật, đến cùng chuyện gì xảy ra, hắn hiện tại cũng không nghĩ thông suốt, có lẽ Ngô sư đi xuống, liền có thể phát hiện đầu mối mới."),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Ngô sư chui vào chỗ không ngừng bốc lên bọt khí, dung nham lần nữa tụ lại, giống như căn bản không có người đi xuống đồng dạng."),t("br"),t("br"),n._v("“Hắn... Không có sao chứ?”"),t("br"),t("br"),n._v("Nhìn thấy phía dưới tình cảnh, cảm nhận được trên không cực nóng, một cái Chiến sư nhịn không được nói."),t("br"),t("br"),n._v("“Ngô sư thực lực đã đạt đến Thánh vực tứ trọng, lại thêm thân là danh sư, thủ đoạn bảo mệnh rất nhiều, vấn đề cũng không lớn!” Dừng lại một chút, Hàn hội trưởng nói."),t("br"),t("br"),n._v("Mặc dù nói khẳng định, trong mắt nhưng kìm lòng không được lộ ra vẻ lo lắng."),t("br"),t("br"),n._v("Hiển nhiên, liền xem như hắn, cũng không có niềm tin tuyệt đối."),t("br"),t("br"),n._v("“Yên tâm đi, Ngô sư thực lực mặc dù tại Thanh Nguyên đế quốc Danh Sư đường, không phải mạnh nhất, nhưng chạy trốn thủ đoạn tuyệt đối là cao minh nhất, nguyên nhân chính là như vậy, lần này di tích, mới phái hắn tới!”"),t("br"),t("br"),n._v("Một bên Mộc sư cười cười."),t("br"),t("br"),n._v("“Chạy trốn thủ đoạn?”"),t("br"),t("br"),n._v("Mọi người tất cả đều sững sờ."),t("br"),t("br"),n._v("“Không sai, Ngô sư có một bộ độc môn pháp quyết, kêu , một khi thi triển, tốc độ đuổi sát Thánh vực ngũ trọng Nguyên Thần cảnh cường giả, mặc dù một khi thi triển, đối chân khí tiêu hao cực lớn, nhưng là bảo vệ tính mạng vô thượng tuyệt chiêu!”"),t("br"),t("br"),n._v("Mộc sư nhẹ gật đầu: “Phía dưới dung nham, cứ việc cực nóng, nhờ vào thực lực của hắn, cùng rất nhiều thủ đoạn, bảo vệ tính mạng tất nhiên không là vấn đề! Lại nói, đã phong ấn bên trên lưu lại sinh môn, liền không khả năng vừa tiến đến liền biến thành tử huyệt!”"),t("br"),t("br"),n._v("“Như thế...” Mọi người ánh mắt sáng lên, đồng thời thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Cũng đúng, đường đường thất tinh đỉnh phong cấp bậc danh sư, nếu như đi vào cái dung nham liền bị thiêu chết, cái này di tích, cũng thực sự quá khó khăn!"),t("br"),t("br"),n._v("Thật muốn lời như vậy, vị kia Vưu Hư, là như thế nào còn sống đi ra?"),t("br"),t("br"),n._v("Lưu lại di tích tiền bối, thiết kế những này thì có ích lợi gì?"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Cùng lúc trước hắn ý nghĩ thật ra thì không kém nhiều."),t("br"),t("br"),n._v("Đã cái này di tích, là để cho người ta tiến vào, cửa ải có thể sẽ khó một chút, nhưng tuyệt không có khả năng đem người hoàn toàn cự tuyệt ở ngoài cửa."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Chờ bảy, tám phút, mọi người ở đây đều có chút lo lắng thời điểm, phía dưới dung nham, đột nhiên một tiếng nổ vang, một bóng người, trực tiếp chạy đi lên."),t("br"),t("br"),n._v("“Ngô sư...”"),t("br"),t("br"),n._v("Mọi người vội vàng nhìn lại, chỉ nhìn một cái, tất cả đều lông mày nhảy một cái."),t("br"),t("br"),n._v("Lúc này Ngô sư, cùng trước đó bộ dáng hoàn toàn khác biệt, toàn thân quần áo, đều bị đốt xấp xỉ, toàn thân trần trụi, nếu không phải dùng chân khí hình thành cự long chặn lại, đoán chừng mất mặt đều sẽ mất chết."),t("br"),t("br"),n._v("Đương nhiên, quần áo bị thiêu hủy, không tính là gì, chủ yếu nhất là, miệng lớn thở hổn hển, cả người vẻ mặt trắng bệch, hình như ném nửa cái mạng đồng dạng."),t("br"),t("br"),n._v("Thậm chí trên người có chút trần trụi chỗ, lưu lại khối lớn vết bỏng, một mảnh đen kịt, mười phần kinh khủng."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Hàn hội trưởng vội vàng đi tới trước mặt."),t("br"),t("br"),n._v("Vội vàng nuốt một viên đan dược, khôi phục chân khí, Ngô sư lấy một bộ y phục thay đổi, lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("“Dung nham bên trong so ta tưởng tượng nhiệt độ còn cao...”"),t("br"),t("br"),n._v("Cười khổ một tiếng, Ngô sư giải thích: “Ta vừa rồi sau khi đi vào, liền dưới đường đi đi, vốn cho rằng nhờ vào ta thể lực cùng chân khí, đi vào hơn trăm mét, không thành vấn đề, ai biết... Ba mươi mét thời điểm, liền cảm thấy nhiệt độ càng ngày càng cao, có chút gánh không được, tiêu hao chân khí tốc độ, cũng so trước đó, tăng lên không chỉ một lần!”"),t("br"),t("br"),n._v("“Miễn cưỡng đi vào bốn mươi mét, liền phát hiện, chân khí trong cơ thể đã tiêu hao hơn phân nửa, biết không dám tiếp tục đi vào, lúc này mới một đường bay lên... Kết quả vẫn là suýt chút nữa tiêu hao sạch chân khí, chết ở bên trong!”"),t("br"),t("br"),n._v("“Ngươi chỉ có tiến vào bốn mươi mét?”"),t("br"),t("br"),n._v("Ô Thiên Khung tràn đầy khiếp sợ."),t("br"),t("br"),n._v("Ngô sư thực lực, vì mọi người đứng đầu, đều chỉ đi vào như thế nông, dung nham đáng sợ có thể tưởng tượng được."),t("br"),t("br"),n._v("“Nhưng có phát hiện cái gì?”"),t("br"),t("br"),n._v("Nhịn không được tiếp tục hỏi."),t("br"),t("br"),n._v("Ngô sư lắc đầu: “Phía dưới đều là dung nham, cái gì đều không nhìn thấy, hơn nữa áp lực cực lớn, cho dù thần trí của ta, cũng chỉ có thể bao phủ hơn mười mét phạm vi, nhiều liền lực có không bằng... Bốn mươi mét phía dưới, nhiệt độ cao hơn, hơn nữa căn cứ ta quan sát, khoảng cách phần cuối, còn không biết bao sâu...”"),t("br"),t("br"),n._v("“Ngươi thần thức... Chỉ có thể lan tràn hơn mười mét?”"),t("br"),t("br"),n._v("“Không biết bao sâu?”"),t("br"),t("br"),n._v("Mọi người tất cả đều vẻ mặt thay đổi."),t("br"),t("br"),n._v("Ngô sư cấp bậc mặc dù không phải trong đám người cao nhất, nhưng sức chiến đấu tuyệt đối là mạnh nhất, Hàn hội trưởng khẳng định cũng không là đối thủ."),t("br"),t("br"),n._v("Mạnh như thế người, liền hắn đều chỉ có thể quan sát mười mét phạm vi... Đường kính hơn mười dặm miệng núi lửa, như thế nào mới có thể dò xét xong?"),t("br"),t("br"),n._v("Cho dù phía dưới thật sự có cửa vào di tích, cũng vào không được a?"),t("br"),t("br"),n._v("“Đúng vậy a, ta thần thức ở phía trên thời điểm, lan tràn phạm vi còn có vài trăm mét, hướng phía dưới càng sâu, trói buộc càng chặt, bốn mươi mét là cực hạn của ta, xuống chút nữa liền không kiên trì được...”"),t("br"),t("br"),n._v("Ngô sư lắc đầu."),t("br"),t("br"),n._v("“Ngươi chỉ có thể kiên trì bốn mươi mét, hơn nữa phía dưới còn có không biết bao sâu... Chúng ta làm sao bây giờ?”"),t("br"),t("br"),n._v("Mọi người hai mặt nhìn nhau."),t("br"),t("br"),n._v("Ngô sư chiến đấu mạnh như vậy, đều chỉ có thể đi vào bốn mươi mét, mọi người khẳng định không cách nào đi vào... Chẳng lẽ, phong ấn rốt cục mở ra, mọi người lại bị vây ở nơi này, cái gì đều không làm được?"),t("br"),t("br"),n._v("“Không được... Phong ấn đóng lại...”"),t("br"),t("br"),n._v("Ngay tại mọi người bị tin tức khiếp sợ thời điểm, đột nhiên trong đám người một thanh âm vang lên, mọi người đồng loạt về phía sau nhìn lại, chỉ thấy vừa rồi thông qua màng ánh sáng, đã biến trở về trước đó trắng xoá màu sắc, hắn Bifrost cũng biến mất không thấy gì nữa, tựa như cửa vào hoàn toàn đóng lại."),t("br"),t("br"),n._v("(Sắp hết năm, mọi người biết vì sao năm nay Valentine muốn tại hai mươi chín không? Bởi vì, ngươi nếu là xử lý không tốt Valentine, năm cũng đừng hòng qua! Ha ha, cầu mọi người chú ý công chúng số, cho quyển sách ra nữ tử, lấy cái mỹ nhân bảng, mọi người có thể nhìn một chút, Wechat lục soát “Hoành Tảo Thiên Nhai”, tăng thêm chú ý là được!)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1037"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1039"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);