(window.webpackJsonp=window.webpackJsonp||[]).push([[586],{941:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1504-set-đanh-bang-nguyen-cung"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1504-set-đanh-bang-nguyen-cung"}},[n._v("#")]),n._v(" Chương 1504: Sét đánh Băng Nguyên cung")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1504: Sét đánh Băng Nguyên cung"),g("br"),g("br"),n._v("“Ngươi... Thật to gan!”"),g("br"),g("br"),n._v("Không nghĩ tới trước mắt vị này, thế mà xé nát danh sư trường bào, nói thẳng ngày hôm nay không làm danh sư, Khúc trưởng lão suýt chút nữa nổi khùng!"),g("br"),g("br"),n._v("Danh sư là thân phận tượng trưng, há lại là ngươi muốn làm liền làm, không muốn làm liền không làm?"),g("br"),g("br"),n._v("Nếu như người người đều như thế trò đùa, Danh Sư đường tôn nghiêm ở đâu?"),g("br"),g("br"),n._v("Thiên hạ đệ nhất chức nghiệp, còn có gì quy tắc đáng nói?"),g("br"),g("br"),n._v("“Ta gan lớn? Còn không có các ngươi gan lớn! Coi trọng nhất quy tắc Danh Sư đường tổng bộ, vậy mà vì một loại nào đó lợi ích, cho phép tu luyện giả áp dụng thải bổ chi thuật kích hoạt thể chất! Hoàn toàn không để ý tu luyện giả cảm thụ, cùng giết hại sinh mệnh; Đại lục đỉnh phong nhất Băng Nguyên cung đại trưởng lão, càng là cho mình vãn bối, hạ khốn thiên tỏa, chỉ vì nàng không nghe lời... Ta hảo hảo học sinh, giao cho các ngươi, là ta Trương Huyền có mắt không tròng!”"),g("br"),g("br"),n._v("Ôm nữ hài thân ảnh, chậm rãi bay lên, thanh âm càng ngày càng vang dội: “Đã như vậy, như vậy dơ bẩn Băng Nguyên cung, cũng không có cần thiết lưu lại!”"),g("br"),g("br"),n._v("“Băng Nguyên cung có hay không cần phải lưu lại, còn không phải ngươi một cái Nhập Hư cảnh tiểu tử, định đoạt!”"),g("br"),g("br"),n._v("Nghe cái tên này càng nói càng thái quá, U Nhược Tâm cười lạnh một tiếng, đang muốn ra tay, đột nhiên biến sắc: “Đây là có chuyện gì?”"),g("br"),g("br"),n._v("Chỉ thấy nguyên bản bầu trời đầy sao, sáng sủa không mây bầu trời, chẳng biết lúc nào mây đen vây quanh, vô số lôi điện, chiếm cứ trong đó, tựa như lúc nào cũng sẽ nổ tung mà ra."),g("br"),g("br"),n._v("“Đây là... Động Hư kiếp? Cái tên này, hiện tại muốn đánh vào Động Hư cảnh?”"),g("br"),g("br"),n._v("Khúc trưởng lão sững sờ."),g("br"),g("br"),n._v("Trên không gia hỏa, luôn miệng nói muốn báo thù, muốn Băng Nguyên cung hủy diệt, làm sao còn không có ra tay, trước tiên đem Động Hư kiếp đưa tới?"),g("br"),g("br"),n._v("Độ kiếp, ốc còn không mang nổi mình ốc, sống hay chết, cũng chưa biết chừng, đâu còn có công phu đi đối phó người ta?"),g("br"),g("br"),n._v("“Lâm trận mới mài gươm, đã chậm, hiện tại mới xung kích Động Hư cảnh, có ích lợi gì? Đường đường Băng Nguyên cung, có thể trở thành đại lục đỉnh phong nhất thế lực, cũng không phải tùy tiện nói một chút!”"),g("br"),g("br"),n._v("Gặp cái tên này, không biết hối cải, giờ phút này còn muốn xung kích Động Hư cảnh, U Nhược Tâm cười lạnh, bàn tay bỗng nhiên giương lên, thanh âm vang tận mây xanh, đem lôi đình tựa hồ cũng áp chế xuống."),g("br"),g("br"),n._v("“Tất cả đệ tử nghe lệnh, có người sỉ nhục cung môn, cần các ngươi vì Băng Nguyên cung xuất lực!”"),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Rầm!"),g("br"),g("br"),n._v("Thanh âm kết thúc, tất cả Băng Nguyên cung đệ tử, cùng trưởng lão đồng thời bay lên, lơ lửng giữa không trung lít nha lít nhít, tựa như bố thành một cái đại trận."),g("br"),g("br"),n._v("Băng Nguyên cung, có thể trở thành toàn bộ đại lục nhất đẳng thế lực, gần với Trương gia, Lạc gia các gia tộc siêu lớn, cường giả nhiều, có thể xưng kinh khủng, chỉ riêng đạt tới Thánh vực bát trọng siêu cấp cường giả, cũng không dưới mấy ngàn!"),g("br"),g("br"),n._v("“Mới vừa xung kích Động Hư, liền ăn nói ngông cuồng, nhìn tới ngươi căn bản không biết, Băng Nguyên cung, đến cùng có gì loại sức mạnh...”"),g("br"),g("br"),n._v("Nhìn lấy tất cả đệ tử, trưởng lão lơ lửng, U Nhược Tâm cười lạnh."),g("br"),g("br"),n._v("Liền Động Hư cảnh đều không có đạt tới, coi như may mắn có chút năng lực, vậy thì sao?"),g("br"),g("br"),n._v("Tại lực lượng tuyệt đối bên dưới, cái gì cũng không bằng."),g("br"),g("br"),n._v("“Không đúng, đại trưởng lão, cái kia Động Hư kiếp có chút không đúng...”"),g("br"),g("br"),n._v("Ngay tại U Nhược Tâm ý định hạ mệnh lệnh, đem vị này không biết trời cao đất rộng gia hỏa chế ngự thời điểm, Khúc trưởng lão tràn đầy khẩn trương thanh âm, vang lên."),g("br"),g("br"),n._v("“Ừm?”"),g("br"),g("br"),n._v("U Nhược Tâm mang theo nghi ngờ."),g("br"),g("br"),n._v("Có thể để cho đường đường cửu tinh danh sư, ngạc nhiên biến sắc, nhất định là xảy ra đại sự gì."),g("br"),g("br"),n._v("Vội vàng ngẩng đầu nhìn về phía bầu trời Động Hư kiếp, chỉ nhìn một cái, con ngươi đột nhiên co rút lại."),g("br"),g("br"),n._v("Chỉ thấy bầu trời mây đen, không gãy dày đặc, càng ngày càng khổng lồ, càng ngày càng béo phệ, trong nháy mắt, đã bao phủ mấy vạn mẫu lớn nhỏ, xem ra, vẫn như cũ không ngừng hội tụ, tựa hồ muốn toàn bộ Băng Nguyên cung đều bao phủ ở bên trong."),g("br"),g("br"),n._v("“Mấy vạn mẫu lôi kiếp? Liền xem như Vũ Không kiếp cũng không có cường đại như vậy...”"),g("br"),g("br"),n._v("Thân thể cứng đờ, U Nhược Tâm thân thể phát run."),g("br"),g("br"),n._v("Vũ Không kiếp, mấy ngàn mẫu là cùng, đối phương một cái Động Hư kiếp, vậy mà hội tụ mấy vạn mẫu lôi vân, là nó gấp mười lần, đến cùng chuyện gì xảy ra?"),g("br"),g("br"),n._v("Dẫn tới như thế lớn lôi kiếp, chính ngươi cũng không cách nào vượt qua đi!"),g("br"),g("br"),n._v("“Ngàn vạn lôi đình, nghe ta mệnh lệnh, diệt cho ta!”"),g("br"),n._v("Không để ý tới tất cả mọi người khiếp sợ, ôm Triệu Nhã Trương Huyền, ánh mắt run lên, hét lớn một tiếng."),g("br"),g("br"),n._v("Lôi đình phát ra nổ vang, giống như là nghe rõ mệnh lệnh của hắn, vô số lôi điện, trong nháy mắt từ mây đen bên trong hạ xuống tới, thẳng tắp rơi vào bốn phía kiến trúc bên trên."),g("br"),g("br"),n._v("Ầm ầm! Ầm ầm!"),g("br"),g("br"),n._v("Đầy đất khói bụi, đâu đâu cũng có sụp đổ thanh âm, vô số kiến trúc, không chịu nổi công kích, vỡ nát tan tành, ngã trên mặt đất."),g("br"),g("br"),n._v("“Bảo vệ Băng Nguyên cung...”"),g("br"),g("br"),n._v("Không nghĩ tới đối phương dẫn tới Động Hư kiếp, vậy mà lại nghe theo mệnh lệnh của hắn, U Nhược Tâm dọa đến trắng bệch cả mặt."),g("br"),g("br"),n._v("Sưu sưu sưu sưu!"),g("br"),g("br"),n._v("Trên không tất cả trưởng lão, đệ tử thẳng tắp hướng tất cả kiến trúc phóng đi, định dùng thực lực của mình, chặn lại lôi đình, bảo vệ Băng Nguyên cung vạn năm truyền thừa cùng kiến trúc."),g("br"),g("br"),n._v("Tạch tạch! Tạch tạch!"),g("br"),g("br"),n._v("Lôi đình đại biểu Thiên Đình lực lượng, ở đâu là bọn họ có thể chống lại, từng đạo thô to lôi điện không ngừng hạ xuống, trên không phi hành tất cả đệ tử, trưởng lão bị chém trúng, từng cái còn không có kịp phản ứng, liền đầu hướng phía dưới bại đi xuống."),g("br"),g("br"),n._v("Mặc dù không có làm bị thương tính mạng, nhưng cũng toàn thân tê liệt, trong thời gian ngắn lại không cách nào động đậy."),g("br"),g("br"),n._v("“Không...”"),g("br"),g("br"),n._v("Thân thể cứng ngắc, U Nhược Tâm nhịn không được lui về phía sau, khuôn mặt trắng bệch."),g("br"),g("br"),n._v("Một bên Khúc trưởng lão cũng con ngươi co rút lại, giống như là gặp quỷ đồng dạng."),g("br"),g("br"),n._v("Lôi đình nghe theo nhân loại mệnh lệnh, đã để bọn họ khiếp sợ, giờ phút này vậy mà nhận dặn dò, đi tiến hành công kích... Đây rốt cuộc làm sao làm được?"),g("br"),g("br"),n._v("Khó trách đối phương dám nói, muốn tiêu diệt Băng Nguyên cung, lôi đình hạ xuống, không ai ngăn nổi ah!"),g("br"),g("br"),n._v("“Ta liều mạng với ngươi...”"),g("br"),g("br"),n._v("Nhìn thấy toàn bộ Băng Nguyên cung kiến trúc bị triệt để hủy hoại, trận pháp toàn bộ rạn nứt, tại không có còn lại nửa điểm, U Nhược Tâm mặt mũi phát điên, gào lên một tiếng, thẳng tắp hướng Trương Huyền vọt tới."),g("br"),g("br"),n._v("Đây là nàng căn cơ, nàng tông môn, bị hủy diệt, triệt để muốn điên rồi!"),g("br"),g("br"),n._v("“Cút xuống cho ta!”"),g("br"),g("br"),n._v("Trương Huyền bàn tay vung lên."),g("br"),g("br"),n._v("Ầm ầm!"),g("br"),g("br"),n._v("Một đạo thô to lôi điện bổ tới!"),g("br"),g("br"),n._v("U Nhược Tâm còn không có kịp phản ứng, liền bị đánh trúng, giống như những người khác, đầy người khét lẹt, từ không trung rớt xuống."),g("br"),g("br"),n._v("Ầm! Ầm! Ầm!"),g("br"),g("br"),n._v("Lại liên tiếp mấy trăm đạo lôi đình hạ xuống tới, rơi vào U Nhược Tâm sống lưng, trong nháy mắt, vị này quát tháo phong vân đại trưởng lão, bút đĩnh đĩnh nằm trên mặt đất, không ngừng run rẩy, mỗi đánh một chút, phun ra một ngụm máu tươi, mí mắt xoay loạn, không biết sinh tử."),g("br"),g("br"),n._v("Lúc trước vào hư kiếp liền để Trương gia đại trưởng lão Trương Vô Ngân đều khó mà ngăn cản, Động Hư kiếp mạnh hơn, U Nhược Tâm mặc dù là đương đại cường giả đỉnh phong, vẫn như cũ không chịu nổi."),g("br"),g("br"),n._v("“Thân là cửu tinh danh sư, chỉ vì bản thân thành kiến, liền thống hạ sát thủ, cũng nên nhận trừng phạt...”"),g("br"),g("br"),n._v("Bổ hết U Nhược Tâm, Trương Huyền lần nữa nhìn về phía một bên Khúc trưởng lão bàn tay một chỉ."),g("br"),g("br"),n._v("Tạch tạch! Tạch tạch!"),g("br"),g("br"),n._v("Sau một khắc, vô số lôi điện hạ xuống, Khúc trưởng lão bị dìm ngập trong đó."),g("br"),g("br"),n._v("“Triệu Nhã, yên tâm đi, vi sư nhất định sẽ làm cho ngươi khôi phục như lúc ban đầu, lần nữa nắm giữ đỉnh phong nhất thực lực, cũng sẽ triệt để kích hoạt ngươi thể chất!”"),g("br"),g("br"),n._v("Gặp toàn bộ Băng Nguyên cung, lại không có một người có thể bay lên, Trương Huyền cũng biết lôi đình lực lượng tiêu hao không sai biệt lắm, cúi đầu nhìn một cái trong ngực nữ hài, ánh mắt lộ ra vẻ đau lòng, thân thể nhoáng một cái, thẳng tắp hướng nơi xa bay đi."),g("br"),g("br"),n._v("Thời gian nháy mắt, liền biến mất tại đêm tối, không thấy tung tích."),g("br"),g("br"),n._v("P/S: Sr hnay bận: V"),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1512"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1514"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);