(window.webpackJsonp=window.webpackJsonp||[]).push([[59],{415:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1030-đang-so-dung-nham-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1030-đang-so-dung-nham-ha"}},[n._v("#")]),n._v(" Chương 1030: Đáng sợ dung nham (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1030: Đáng sợ dung nham (hạ)"),t("br"),t("br"),n._v("Một vị danh sư nhịn không được tiến lên, tính toán cùng trước đó đồng dạng trực tiếp xuyên qua, lại phát hiện như là đụng tại trên miếng sắt, một cỗ mênh mông lực lượng đem hắn gạt ra."),t("br"),t("br"),n._v("“Cái này...” Con ngươi co rụt lại, vị danh sư này trên mặt lộ ra khó tin vẻ: “Như vậy, chúng ta còn thế nào rời khỏi?”"),t("br"),t("br"),n._v("Không chỉ hắn bộ dáng này, cái khác rất nhiều danh sư cũng tới trước thử một chút, nguyên một đám vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Trước đó hồng sắc quang màng, xuyên qua như là đụng phải mặt nước, nhẹ nhõm như ý, không có chút nào ngăn trở, mà bây giờ hơi nước trắng mịt mờ cái này, giống như là tường thành, lấy mọi người thực lực, căn bản là không cách nào đột phá."),t("br"),t("br"),n._v("Nói cách khác, cửa vào đóng lại về sau, chẳng khác nào đi vào một cái tử thành! Tìm không thấy tiến lên phương hướng lời nói, cũng chỉ có thể vây chết tại đây, chạy trốn đều không có khả năng."),t("br"),t("br"),n._v("“Khó trách mệnh danh cửu tử nhất sinh, quả nhiên đáng sợ...”"),t("br"),t("br"),n._v("Một vị danh sư mặt mũi cười khổ lẩm bẩm."),t("br"),t("br"),n._v("Trước khi đến, Ngô sư đám người vẫn khai báo, nơi này cửu tử nhất sinh, mười phần nguy hiểm, vừa mới bắt đầu còn không để ý lắm, nhìn thấy giờ mới hiểu được quả nhiên."),t("br"),t("br"),n._v("Bọn họ cái này còn không có tính toán đi vào di tích, chỉ là tại lối vào, liền bị chơi đùa chân tay luống cuống, tìm không rõ phương hướng, thật muốn tiến vào bên trong, chẳng phải càng thêm đáng sợ?"),t("br"),t("br"),n._v("“Trước đó ta cũng đã nói, phong ấn sau 15 phút đóng lại, một khi đóng lại... Không cách nào mở ra, trừ phi có thể tìm tới biện pháp giải quyết!”"),t("br"),t("br"),n._v("Hàn hội trưởng lắc đầu nói: “Được rồi, xoắn xuýt cái này không có quá nhiều ý nghĩa, chúng ta đã đi vào, cũng đã ôm quyết tâm quyết tử, kế trước mắt, vẫn là tìm kiếm di tích lối vào đi!”"),t("br"),t("br"),n._v("“Ừm!” Mọi người gật đầu."),t("br"),t("br"),n._v("Trước khi đến liền hiểu nơi này nguy hiểm, phần lớn người đều giao phó xong hậu sự, đã không cách nào lui về phía sau, còn không bằng trực tiếp hướng về phía trước."),t("br"),t("br"),n._v("Trương Huyền cũng hiểu những này, lần nữa hướng phía dưới dung nham nhìn sang, đồng thời trong lòng lặng lẽ câu thông Thiên Nghĩ Phong Mẫu."),t("br"),t("br"),n._v("“Chủ nhân, nơi này đồng thời không có không gian gấp! Cho dù có, khả năng cũng tại dung nham phía dưới, ta quan sát không đến...”"),t("br"),t("br"),n._v("Thời gian không dài, Thiên Nghĩ Phong Mẫu thanh âm truyền tới."),t("br"),t("br"),n._v("“Không có?” Trương Huyền sắc mặt tái xanh."),t("br"),t("br"),n._v("Chẳng lẽ cái gọi là di tích thông đạo, thật tại dung nham phía dưới?"),t("br"),t("br"),n._v("Liền Ngô sư đều không thể đi xuống, huống chi bọn họ!"),t("br"),t("br"),n._v("Còn không có đi vào di tích, sẽ không thiết kế đến gian nan như vậy đi!"),t("br"),t("br"),n._v("Thật sự là như thế, lúc trước lão viện trưởng cùng ưu tú người là như thế nào đi vào?"),t("br"),t("br"),n._v("“Đáng tiếc, Độc điện bên trong bản đồ không có tới tay, bằng không thì cũng không đến mức bị động như thế...”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền mặt mũi bất đắc dĩ."),t("br"),t("br"),n._v("Nếu có thể tìm tới cái kia bản đồ, hẳn là có thể nhẹ nhõm tìm tới đường ra, không đến mức giống như bây giờ, hai mắt đen thui, cái gì đều không rõ ràng."),t("br"),t("br"),n._v("“Không thèm nghĩ nữa, thử xem thư viện, có thể hay không phát hiện cái gì.”"),t("br"),t("br"),n._v("Đem những này tâm tình tiêu cực dứt bỏ, Trương Huyền thân thể nhoáng một cái, trực tiếp hướng dung nham phương hướng bay đi."),t("br"),t("br"),n._v("Dung nham là tử vật, không thể thi triển võ kỹ, cũng không thể vận chuyển, chỉ có thể lấy tay sờ, mới có thể để cho thư viện kích hoạt."),t("br"),t("br"),n._v("“Trương sư, ngươi muốn làm gì?” Thấy hắn bay xuống đi, Mộc sư nhịn không được mở miệng hỏi."),t("br"),t("br"),n._v("“Ta nghiên cứu một chút, nhìn kỹ một chút...” Biết thư viện sự tình, không thể nói tỉ mỉ, Trương Huyền khoát tay áo."),t("br"),t("br"),t("span",[t("br"),n._v(" //truy"),t("br"),n._v("encｕatui.net/ ")]),n._v(" Trong nháy mắt, đi tới dung nham bầu trời, quả nhiên cảm thấy sóng nhiệt tập kích người, xuyên thấu qua làn da sâu tận xương tủy, tựa như lúc nào cũng sẽ đem người đốt trụi."),t("br"),t("br"),n._v("“Nóng quá!” Con ngươi co rụt lại, Trương Huyền hoảng sợ."),t("br"),t("br"),n._v("Khó trách Ngô sư chỉ có tiến vào 40 mét, liền lại không cách nào xâm nhập, nhiệt độ của nơi này so với hắn tưởng tượng còn muốn đáng sợ, liền xem như hắn toàn lực thi triển, chỉ sợ 30 mét đều là một cái đại quan."),t("br"),t("br"),n._v("Vận chuyển thoáng cái Thiên Đạo chân khí, đem bốn phía nhiệt lượng xếp tới bên ngoài cơ thể, Trương Huyền lúc này mới duỗi ra ngón tay, hướng dung nham sờ lên."),t("br"),t("br"),n._v("“Thiếu hụt!” Trong lòng hô khẽ."),t("br"),t("br"),n._v("Vù vù! Một quyển sách xuất hiện."),t("br"),t("br"),n._v("“Núi lửa dung nham, địa hỏa dung luyện nham thạch mà thành, nhiệt độ cực cao, ẩn chứa xanh đen đá, đá hoa cương, thanh kim nham các loại nhiều loại nham thạch, ngưng kết sau có thể xây dựng phòng ốc...”"),t("br"),t("br"),n._v("Thấy rõ ràng nội dung phía trên, Trương Huyền một cái lảo đảo, suýt chút nữa ngã sấp xuống."),t("br"),t("br"),n._v("Xây phòng ốc, xây em gái ngươi ah!"),t("br"),t("br"),n._v("Ta muốn biết không phải cái này được không?"),t("br"),t("br"),n._v("Trong lòng tràn đầy buồn bực, nhưng cũng biết, xuất hiện cái này, cũng là trong dự liệu sự tình, Thiên Đạo thư viện chỉ có thể phân tích hắn chạm đến đồ vật, hơn nữa chỉ là một bộ phận, cũng không thể mở rộng phạm vi."),t("br"),t("br"),n._v("Thật giống như trước đó chạm đến giá sách, muốn đem trong đó thư tịch thu nhập trong óc, thí nghiệm qua nhiều lần, đều biết căn bản là không thể nào."),t("br"),t("br"),n._v("Chạm đến phía dưới dung nham, xuất hiện là dung nham đặc tính, mà không phải xuất hiện vị trí phạm vi có không thiếu hụt."),t("br"),t("br"),n._v("Nói cách khác, mọi việc đều thuận lợi thư viện tại lúc này cũng là vô dụng..."),t("br"),t("br"),n._v("Nên làm cái gì?"),t("br"),t("br"),n._v("Xoa xoa mi tâm, Trương Huyền rơi vào đường cùng, đành phải lần nữa bay đi lên."),t("br"),t("br"),n._v("“Thực sự không được, chúng ta mỗi người phân một chút khu vực, lần lượt hướng phía dưới thăm dò... Đã phong ấn có thể mở ra, đã nói lên di tích sẽ không quá sâu! Chỉ cần mọi người đồng tâm hiệp lực, khẳng định có thể phát hiện dấu vết để lại.”"),t("br"),t("br"),n._v("Thấy không gì làm không được Trương viện trưởng cũng không công mà lui, Hàn hội trưởng lộ ra vẻ mất mát, nói."),t("br"),t("br"),n._v("“Lần lượt thăm dò?”"),t("br"),n._v(" Mọi người vẻ mặt nghiêm túc, nhẹ gật đầu."),t("br"),t("br"),n._v("Hiện tại cũng chỉ có cái này một cái biện pháp."),t("br"),t("br"),n._v("Dung nham phần ngoài không có trận pháp, không có bất kỳ cái gì đặc thù, nói rõ di tích vô cùng có khả năng giấu ở trong đó, hiện tại lui không thể lui, kế trước mắt, chỉ có một cái phương pháp, cái kia chính là phạm vi lớn dò xét, từng tấc từng tấc tìm tòi."),t("br"),t("br"),n._v("“Nếu như... Cái gọi là di tích, núp ở bốn mươi mét trở xuống, làm sao bây giờ?”"),t("br"),t("br"),n._v("La Thanh học viện viện trưởng Thẩm Bình Triều cau mày."),t("br"),t("br"),n._v("Hắn vừa nói như vậy, những người khác cũng đồng thời gật đầu."),t("br"),t("br"),n._v("Liền Ngô sư, đều chỉ có thể đi vào bốn mươi mét, bọn họ khẳng định càng không cách nào lẻn vào, phạm vi lớn tìm là không sai, có thể... Di tích giấu ở bốn mươi mét trở xuống, cho dù tất cả mọi người tự tìm cái chết tại đây, cũng vô dụng ah."),t("br"),t("br"),n._v("“Lần lượt lục soát, quả thực thực tế biện pháp tốt, nhưng cũng không tiếp thu được...”"),t("br"),t("br"),n._v("Một bên Lạc Nhược Hi mở miệng."),t("br"),t("br"),n._v("“Ồ? Lạc sư có gì cao kiến?” Hàn hội trưởng nhìn qua."),t("br"),t("br"),n._v("“Cùng bọn hắn nói đồng dạng, di tích nếu là núp ở bốn mươi mét trở xuống, bằng vào chúng ta những người này tìm tới là không thể nào, lại nói, phạm vi lớn như thế dung nham, chỉ chúng ta ba mươi người, muốn thảm thức lục soát một lần, ngươi tính toán một chút, cần bao lâu thời gian?” Lạc Nhược Hi đôi mi thanh tú nhẹ chau lại."),t("br"),t("br"),n._v("“Cái này...” Hàn hội trưởng nói không ra lời."),t("br"),t("br"),n._v("Mặc dù ở đây đại bộ phận đều nắm giữ thần thức, có thể hướng chỗ sâu dò xét, có thể dung nham càng hướng phía trong, lực cản càng lớn, dò xét phạm vi cũng liền càng nhỏ, lấy mọi người thực lực, cùng chân khí tốc độ khôi phục, muốn đem cái này phương viên hơn mười dặm miệng núi lửa toàn bộ tra xong... Chỉ sợ không có mấy tháng, là không thể nào làm được!"),t("br"),t("br"),n._v("Thời gian dài như vậy, không cần chiến đấu, mọi người khẳng định sẽ thoát lực, tươi sống chết ở chỗ này."),t("br"),t("br"),n._v("“Vậy phải làm thế nào cho phải?” Một cái danh sư hỏi."),t("br"),t("br"),n._v("“Ta cũng không biết, chẳng qua là cảm thấy, đã đối phương lưu lại sinh môn, tất nhiên có đường có thể đi, chỉ là chúng ta không nghĩ tới mà thôi, ngồi mài đao cũng không làm mất kỹ thuật đốn củi, cùng thứ tư chỗ tìm kiếm đụng đại vận, còn không bằng suy nghĩ ra xác định mục tiêu lại động thủ, xác suất thành công có lẽ sẽ lớn hơn một chút...”"),t("br"),t("br"),n._v("Lạc Nhược Hi nói."),t("br"),t("br"),n._v("Mọi người đồng thời gật đầu."),t("br"),t("br"),n._v("Nàng nói không sai, rộng rãi tung lưới đụng đại vận, đối với bọn hắn hiện tại tới nói, quả thực là không thể làm."),t("br"),t("br"),n._v("Hoàn cảnh nơi này thực sự quá kém, kiên trì một canh giờ, hai canh giờ, thậm chí mười canh giờ tất cả mọi người có thể làm được, một ngày... Chỉ sợ cũng sẽ thân trúng hỏa độc, hai ngày thời gian, đoán chừng sẽ bị thiêu đốt thoát lực, biến thành người khô."),t("br"),t("br"),n._v("“Không sai, muốn tiếp tục tìm kiếm mới được...”"),t("br"),t("br"),n._v("Trương Huyền cũng nhẹ gật đầu, lần nữa nhìn xuống dưới."),t("br"),t("br"),n._v("Toàn bộ miệng núi lửa, trình viên hình, có điều, đồng thời không có như vậy quy tắc, thỉnh thoảng có sức mạnh phát ra, cùng phía trên phong ấn tiếp xúc, phát ra thanh âm điếc tai nhức óc."),t("br"),t("br"),n._v("Đổi lại trước đó, những lực lượng này phóng thích không đi ra, khẳng định trong này nổ tung, mà giờ khắc này bị hắn ở bên ngoài ghim cái khổng, phun ra ngoài, cũng không có gì nguy hiểm."),t("br"),t("br"),n._v("“Có thể hay không... Cùng những này dâng trào lực lượng liên quan đến?”"),t("br"),t("br"),n._v("Trong lòng đột nhiên động một cái."),t("br"),t("br"),n._v("Trước đó lão viện trưởng, Vưu Hư đám người thuận lợi đi vào, khẳng định không tiêu tốn như thế đại công này, theo người sau giới thiệu, vừa tiến vào cách ngăn liền gặp lực lượng gió lốc, đem hắn tịch quyển đến không biết tên chỗ, gặp Dị Linh tộc nhân..."),t("br"),t("br"),n._v("Nói cách khác, bọn họ tới thời điểm, cách ngăn bên trong tràn ngập lực lượng, làm cho không người nào có thể ngăn cản..."),t("br"),t("br"),n._v("Những này gió lốc, có thể hay không chính là thả ra ngoài những lực lượng kia?"),t("br"),t("br"),n._v("Nếu như là lời nói, không có những vật này, trong này mặc dù trở nên an toàn rất nhiều, không có nguy hiểm, có thể nghĩ muốn tìm tới di tích lối vào, cũng làm không được."),t("br"),t("br"),n._v("“Lực lượng dâng trào... Chẳng lẽ, lực lượng bộc phát chỗ, chính là cái gọi là di tích chôn giấu chỗ?”"),t("br"),t("br"),n._v("Nghĩ tới những thứ này, trong đầu linh quang lóe lên."),t("br"),t("br"),n._v("Nếu như Vưu Hư nói đều là thật lời nói, lực lượng bộc phát chỗ, tất nhiên cùng di tích có cực lớn liên hệ."),t("br"),t("br"),n._v("Nghĩ đến cái này, Trương Huyền nhịn không được nhìn xuống dưới."),t("br"),t("br"),n._v("Vừa khéo lúc này, một cỗ lực lượng lần nữa từ phía dưới dung nham bên trong bừng lên, cuồn cuộn lấy trực tiếp hướng lên, cùng trước đó hắn trận kỳ cắm chỗ tiếp xúc, lập tức biến mất không thấy hình bóng."),t("br"),t("br"),n._v("Hắn tìm kiếm tiết điểm này, là toàn bộ phong ấn sơ hở vị trí, một khi cắm vào trận kỳ, trong đó lực lượng lại không cách nào tích góp, nguyên nhân chính là như vậy, tới lâu như vậy, thế mà đem chuyện này đều quên."),t("br"),t("br"),n._v("“Thử một chút!”"),t("br"),t("br"),n._v("Trương Huyền âm thầm nhẹ gật đầu."),t("br"),t("br"),n._v("Mặc kệ vị trí này phải hay không phải, chỉ có tự mình thí nghiệm mới có thể biết, mà loại trừ nơi này, địa phương khác tạm thời cũng thực sự nghĩ không ra, có cái gì đặc thù."),t("br"),t("br"),n._v("“Dung nham không cách nào thăm dò, Thiên Đạo thư viện cũng không có bất cứ tác dụng gì, biện pháp duy nhất chính là tiến vào bên trong dò xét... Ngô sư lực lượng hao hết, đến bây giờ đều không có khôi phục, chỉ có thể là ta tự mình tới!”"),t("br"),t("br"),n._v("Trương Huyền ngón tay hướng về phía trước một chút, một cái cực lớn lô đỉnh xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Kim Nguyên đỉnh!"),t("br"),t("br"),n._v("Cái tên này đã tấn cấp đến trung phẩm Thánh khí, thực lực có thể so với Thánh vực tứ trọng đỉnh phong, đơn đả độc đấu lời nói, khả năng không phải Ngô sư đối thủ, nhưng mà muốn nói nhịn nhiệt độ cao chống cự dung nham, người sau tuyệt đối kém xa tít tắp."),t("br"),t("br"),n._v("“Kim Nguyên đỉnh... Tấn cấp thành trung phẩm Thánh khí?”"),t("br"),t("br"),n._v("Thấy hắn đột nhiên xuất hiện động tác, tất cả mọi người nhìn lại, chỉ nhìn một cái, lập tức sững sờ."),t("br"),t("br"),n._v("Đối với cái này Hồng Viễn danh sư học viện trấn viện chi bảo, tất cả mọi người là có nghe thấy, chỉ có hạ phẩm Thánh khí cấp bậc, lúc nào biến thành trung phẩm?"),t("br"),t("br"),n._v("Hạ phẩm cùng trung phẩm mặc dù chỉ là một cái tiểu cấp bậc, nhưng lại có trên bản chất chênh lệch, tựa như thiên địa."),t("br"),t("br"),n._v("“Đúng chủ nhân tối hôm qua tạm thời thu cái luyện khí học đồ, tùy tiện cho bản đỉnh luyện chế thoáng cái, sau đó... Liền đạt đến cấp bậc này, làm sao hâm mộ đi!”"),t("br"),t("br"),n._v("Xem ra mọi người ánh mắt kinh ngạc, Kim Nguyên đỉnh cười to một tiếng, không nói ra được đắc ý: “Bất quá, hâm mộ cũng không dùng, bản đỉnh thiên tư trác tuyệt, loại này tùy tiện đột phá chuyện, các ngươi không học được...”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1038"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1040"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);