(window.webpackJsonp=window.webpackJsonp||[]).push([[593],{949:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-143-lay-tien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-143-lay-tien"}},[n._v("#")]),n._v(" Chương 143: Lấy tiền")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Nghe nói không? Trước Lăng đại nhân tìm lão gia nhà chúng ta xem bệnh, vừa ra tay liền lấy ra một trăm vạn kim tệ, thực sự là hào khí!” "),t("br"),t("br"),n._v(" Nhưng vào lúc này, trong viện vang lên cố ý đè thấp nói thầm âm thanh."),t("br"),t("br"),n._v(" Xem ra hẳn là những cái kia gác cổng hộ vệ, xem lão gia rời đi, thấp giọng thảo luận."),t("br"),t("br"),n._v(" “Một trăm vạn? Thật hay giả?” Một tên hộ vệ rõ ràng không tin."),t("br"),t("br"),n._v(" “Đương nhiên là thật sự, ta tận mắt nhìn thấy, chính xác trăm phần trăm! Một trăm vạn kim tệ, đối với chúng ta tới nói, là con số trên trời, nhưng đối với lão gia như vậy danh sư tới nói, không đáng kể chút nào. Ta nghe Tôn ca nói, lão gia thu xuống, chỉ là vì để cho đối phương an lòng, không muốn để cho người khác nợ hắn ân tình.” Cái thứ nhất hộ vệ cười nói."),t("br"),t("br"),n._v(" “Ngươi vừa nói như vậy, ta ngược lại thật ra nghĩ tới, Lăng đại nhân lúc trước ở đây quỳ một đêm, lão gia nguyên vốn không muốn để ý tới, là hắn cho Tôn ca nhét không ít tiền, Tôn ca tâm hiền lành, hỗ trợ nói chuyện, mới đi vào...” Thứ hai hộ vệ cảm khái."),t("br"),t("br"),n._v(" Hai người âm thanh tuy rằng thấp, nhưng cùng đường phố chỉ cách một đạo đại môn, Đỗ Viễn thực lực thấp, khả năng nghe không rõ ràng, Đỗ Mạc Hiên lại nghe rõ ràng."),t("br"),t("br"),n._v(" “Viễn nhi, ngươi đừng quỳ, bây giờ trở lại gia tộc, đem ta những năm này tích trữ mang tới...” "),t("br"),t("br"),n._v(" Đỗ Mạc Hiên ánh mắt sáng lên."),t("br"),t("br"),n._v(" Hắn kinh nghiệm giang hồ phong phú, nhưng đều tại luận võ, chiến đấu, tri thức người tranh luận đồ vật, tại hối lộ, đưa tiền phương diện, liền không sánh được thương nhân rồi, trước căn bản không nghĩ tới muốn nắm tiền loại hình, nghe được đối thoại, nhất thời tự nhiên hiểu ra."),t("br"),t("br"),n._v(" Đúng vậy, danh sư tự tin thân phận, không cần tiền, quản gia cần a!"),t("br"),t("br"),n._v(" Phủ đệ, người hầu, ăn uống, bên nào không dùng tiền?"),t("br"),t("br"),n._v(" Tìm người ta thầy thuốc đều cần trả tiền đây, bản thân tay không mà đến, đối phương có thể gặp mới thật liền quái."),t("br"),t("br"),n._v(" “Tốt!” "),t("br"),t("br"),n._v(" Đỗ Viễn không rõ vì sao, bất quá nghe được phụ thân bàn giao, lập tức đứng dậy."),t("br"),t("br"),n._v(" “Ta nghe nói Tôn ca bởi vậy còn chịu lão gia mắng cho một trận, thật giống Lăng đại nhân trước cũng phải tội lão gia, đối với hắn nghi vấn, danh sư tôn nghiêm không thể nhục, 100 vạn kim tệ liền cầu xin, sau đó uy nghiêm ở đâu? Lão gia còn nói liền lần này, lần sau không thể chiếu theo lệ này nữa. Nói thật, 100 vạn tại trong mắt chúng ta là trời, đối với lão gia tới nói, liền cái cầu xin tư cách đều không!” "),t("br"),t("br"),n._v(" Cái thứ nhất hộ vệ nói tiếp."),t("br"),t("br"),n._v(" “Đúng đấy! Chuyện này, ta vừa nãy đi ngang qua tiền viện thời điểm, nghe người ta nghị luận một lần, còn tưởng rằng là giả...” Thứ hai nói tiếp."),t("br"),t("br"),n._v(" Bên ngoài."),t("br"),t("br"),n._v(" “Trước không vội!” Đỗ Viễn còn không rời đi, liền nghe đến cha âm thanh bên tai một bên tiếp tục vang lên: “Ngươi trở lại đem ta trước đó vài ngày mua quý giá đan dược, tất cả bán đi, còn có tích góp man thú da cũng bán đi, đem tiền một khối lấy tới...” "),t("br"),t("br"),n._v(" “Cha...” Đỗ Viễn sững sờ."),t("br"),t("br"),n._v(" Cha đây là sao?"),t("br"),t("br"),n._v(" Lại nắm tiền, lại bán đồ vật?"),t("br"),t("br"),n._v(" “Phí lời, đương nhiên là thật sự, ta chuyên môn hỏi Tôn ca, hắn nói lúc trước Lăng đại nhân cho một trăm vạn, lão gia không hề liếc mắt nhìn, sở dĩ xuất thủ cứu trị, chủ yếu là bởi vì Lăng đại nhân thê tử sắp chết rồi, cứu người một mạng còn hơn xây bảy cấp phù đồ. Nếu như đến đây cầu hắn người, nhảy nhót tưng bừng, thân thể hoàn hảo, thậm chí còn có thể đánh ra võ kỹ, chắc chắn sẽ không để ý tới...” "),t("br"),t("br"),n._v(" Cái thứ nhất hộ vệ âm thanh tiếp tục truyền đến."),t("br"),t("br"),n._v(" “A?” Đỗ Mạc Hiên thân thể loáng một cái, cắn răng một cái, tiếp tục dặn dò Đỗ Viễn: “Đi đem ta thanh kiếm kia cũng bán, còn có... Tam trường lão không phải vẫn rất muốn lấy được ta khống chế mấy thứ sản nghiệp sao? Ngươi đi nói với hắn một lần, chỉ cần trả tiền, những cái này sản nghiệp tất cả đều là của hắn!” "),t("br"),t("br"),n._v(" “Cha...” "),t("br"),t("br"),n._v(" Này đã là cha ở gia tộc quan trọng nhất kiếp mã, một khi bán sạch, sau này làm sao bây giờ?"),t("br"),t("br"),n._v(" Đỗ Viễn bối rối."),t("br"),t("br"),n._v(" “Còn không mau đi!” Đỗ Mạc Hiên lần thứ hai bàn giao."),t("br"),n._v(" Gặp phụ thân khuôn mặt nghiêm khắc, Đỗ Viễn không dám phản bác, xoay người rời đi."),t("br"),t("br"),n._v(" “Võ kỹ đều có thể đánh, nói rõ thân thể không thành vấn đề, người như thế còn dám nghi vấn danh sư, thực sự là đầu óc có vấn đề!” Thứ hai hộ vệ bĩu môi: “Lão gia tuy không quan tâm tiền, nhưng liền một điểm tiền đều không nắm, vắt chày ra nước, liền muốn để lão gia hỗ trợ chỉ điểm, tiêu hao tâm thần, này không phải nằm mơ sao?” Thứ hai hộ vệ tiếp tục nói."),t("br"),t("br"),n._v(" Đỗ Mạc Hiên cắn răng một cái: “Còn có, đem ngươi tích trữ còn có mẹ ngươi tích trữ cũng tất cả đều lấy tới cho ta...” "),t("br"),t("br"),n._v(" “...” "),t("br"),t("br"),n._v(" Đỗ Viễn một cái lảo đảo, suýt chút nữa khóc."),t("br"),t("br"),n._v(" Nhìn dáng dấp, cha đã thần trí không rõ, triệt để điên rồi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Hai tên hộ vệ tán gẫu, chính là Trương Huyền vừa nãy bàn giao sự tình."),t("br"),t("br"),n._v(" Không cho đối phương điểm nhắc nhở, bọn hắn đưa tiền, phải đợi tới khi nào?"),t("br"),t("br"),n._v(" Ngược lại là hộ vệ nói, không có quan hệ gì với chính mình, cũng không dám đem muốn tiền tội danh, an đến một vị danh sư trên người."),t("br"),t("br"),n._v(" “Không hổ là lão gia, đem người bán, đều phải cho hắn kiếm tiền...” Nghe đến lão gia dặn dò chuyện này, cháu siêu cũng tràn đầy không nói gì."),t("br"),t("br"),n._v(" Vốn là, lão gia giúp đối phương chỉ điểm, xuất phát từ cảm kích, đối phương khẳng định cũng sẽ cho chỗ tốt đương thù lao, nhưng số tiền kia là nhiều là ít liền không nhất định, lúc này, hai tên hộ vệ trò chuyện vừa ra tới, cái nào còn dám keo kiệt?"),t("br"),t("br"),n._v(" Không tổn thương uy danh, còn có thể kiếm lời càng nhiều tiền, lão gia thực sự là hố người chết không đền mạng... Quá mất."),t("br"),t("br"),n._v(" Vốn là hắn và những người khác ý nghĩ như thế, cũng tưởng là lão gia đối với tiền tài xem thường, bây giờ nhìn lại... Danh sư cũng là người, cũng phải tiêu dùng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Rời đi phủ đệ, tìm cái yên lặng không người đường phố, Trương Huyền đem trên mặt ngụy trang rửa sạch."),t("br"),t("br"),n._v(" Mang những thứ đồ này xếp vào hơn một ngày, cũng đủ mệt."),t("br"),t("br"),n._v(" “Qua một ngày, công hội chắc là đem Ôn Mạch đan, Dưỡng Thể dịch chuẩn bị kỹ càng, mua trước đến cho Vương Dĩnh, Lưu Dương mấy người sử dụng!” "),t("br"),t("br"),n._v(" Ở đây ngụy trang danh sư kiếm tiền mục đích chính là vì cái này, Phá Âm đan cùng Cự Tê thú huyết dịch tạm thời đến không được, hai thứ này chắc là chuẩn bị đứng dậy rất đơn giản."),t("br"),t("br"),n._v(" Đi tới công hội, hai thứ này quả nhưng đã chuẩn bị thỏa đáng, còn lại khác biệt, Âu Dương hội trưởng đã xin, đang từ cái khác công hội điều chỉnh lấy."),t("br"),t("br"),n._v(" Luyện đan sư công hội trải rộng các đại vương quốc, có chút đan dược, bảo vật, Thiên Huyền Vương quốc không, không có nghĩa là những vương quốc khác không, chỉ cần hướng tổng bộ xin, sẽ theo gần đây nắm giữ Vương quốc hướng bên này vận tải, như vậy tốc độ còn có thể nhanh hơn một ít, bằng không, theo tổng bộ tới đây, không biết bao xa khoảng cách, ngăn ngắn mười ngày, làm sao có khả năng đưa tới!"),t("br"),t("br"),n._v(" Trước thanh toán một trăm vạn kim tệ xem như tiền thế chấp, thuận lợi đem hai thứ đồ này lấy đi ra."),t("br"),t("br"),n._v(" “Đúng rồi, Âu Dương hội trưởng, ngươi nơi này có Sinh Tức đan không? Cũng cho ta đến một ít!” "),t("br"),t("br"),n._v(" Đem hai loại đồ vật gian phòng trữ vật giới chỉ, Trương Huyền hỏi tiếp."),t("br"),t("br"),n._v(" Sinh Tức đan, đối với võ giả nhất trọng Tụ Tức cảnh có cực giúp đỡ lớn, Vương Dĩnh mấy người dùng, lẽ ra có thể để tốc độ tu luyện tăng nhanh không ít."),t("br"),t("br"),n._v(" “Vật này ta bình thường cũng luyện chế không ít còn có thừa ra, có thể trực tiếp đưa cho ngươi!” Âu Dương hội trưởng cười cợt, lấy ra hai cái bình ngọc đưa tới."),t("br"),t("br"),n._v(" Tiếp nhận bình ngọc, xem bên trong có tới mấy chục viên, Trương Huyền biết đối phương là đang hướng về mình lấy lòng, từ chối không tốt lắm, gật đầu thu xuống."),t("br"),t("br"),n._v(" Sinh Tức đan, tuy rằng phẩm chất không cao, nhưng cũng là thật nhất phẩm đan dược, mỗi một viên đều phải kể tới vạn kim tệ, một lần cho mấy chục viên, vị này Âu Dương hội trưởng, thật là hào phóng."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-151"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-153"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);