(window.webpackJsonp=window.webpackJsonp||[]).push([[594],{950:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1511-đien-cuong-danh-su-đuong-tong-bo-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1511-đien-cuong-danh-su-đuong-tong-bo-thuong"}},[n._v("#")]),n._v(" Chương 1511: Điên cuồng Danh Sư đường tổng bộ (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1511: Điên cuồng Danh Sư đường tổng bộ (thượng)"),t("br"),t("br"),n._v("Danh Sư đường tổng bộ, nghị sự đại điện."),t("br"),t("br"),n._v("Thay mặt đường chủ Nhậm Thanh Viễn ngồi ngay ngắn trong đó, nhìn trước mắt truyền tới tin tức, nhìn quanh một vòng."),t("br"),t("br"),n._v("“Các nơi đều sẽ lệnh truy nã ban bố?”"),t("br"),t("br"),n._v("“Bẩm báo Nhậm đường chủ, đã toàn bộ ban bố, không có gì bất ngờ xảy ra, trong một ngày, tin tức sẽ truyền khắp toàn bộ đại lục, chỉ cần vị này Trương Huyền xuất hiện, tổng bộ liền có thể nhận được đưa tin, lập tức phái người tiến hành bắt!”"),t("br"),t("br"),n._v("Một vị cửu tinh danh sư, tiến về phía trước một bước, ôm quyền nói."),t("br"),t("br"),n._v("Chính là vị kia Khúc trưởng lão."),t("br"),t("br"),n._v("Hắn đã từ Băng Nguyên cung trở lại tổng bộ, hơn nữa đem tất cả tin tức, báo lên tới trước mắt vị này, nếu không, cũng không có khả năng mệnh lệnh truyền nhanh như vậy."),t("br"),t("br"),n._v("“Đường chủ, danh sư lệnh truy nã, dính dáng cực lớn, từ khi Danh Sư đường xây dựng đến nay, đến bây giờ hết thảy mới dùng chín lần, vị này Trương Huyền cứ việc có chút tùy hứng, nhưng dù sao thân là danh sư, như vậy truy nã, sẽ có hay không có tổn hại tổng bộ uy danh, chuyện bé xé ra to?”"),t("br"),t("br"),n._v("Một cái lão giả cau mày."),t("br"),t("br"),n._v("Danh Sư đường tổng bộ một vị khác trưởng lão."),t("br"),t("br"),n._v("“Như thế nào chuyện bé xé ra to?”"),t("br"),t("br"),n._v("Khúc trưởng lão hất lên ống tay áo: “Vị này Trương Huyền, thân là bát tinh danh sư, bỏ qua trên dưới, giả mạo thái thượng trưởng lão không nói, càng hủy hoại Trương gia, phá hư Thánh tử điện, đem Băng Nguyên cung vạn năm cơ nghiệp hủy hoại chỉ trong chốc lát... Mấu chốt nhất là, không biết hối cải, biết rõ ta là Danh Sư đường người của tổng bộ, còn lớn hơn lớn ra tay! Loại người này nhất định phải nghiêm trị, nếu không, tổng bộ uy nghiêm ở đâu? Danh Sư đường còn có mặt mũi nào, giáo thư dục nhân, truyền thụ thiên hạ?”"),t("br"),t("br"),n._v("Đối phương chân khí hóa độc, lôi đình diệt thế, ngụy trang Dương sư, khó mà nhận ra, quan trọng hơn phải là, nhãn lực thiên hạ vô song... Thật tình làm ác, Danh Sư đường đều khó mà khống chế!"),t("br"),t("br"),n._v("Nhất định phải nhanh xử lý, nếu không mặc cho hắn đi loạn, còn không biết bao nhiêu thế lực lớn, gặp được kiếp nạn."),t("br"),t("br"),n._v("Cứ việc người này, có thể trị liệu thương thế của hắn, nhưng đại nghĩa phía trước, việc nghĩa chẳng từ."),t("br"),t("br"),n._v("“Ngươi nói ta cũng biết, nhưng ta chuyên môn nhìn qua kinh nghiệm của hắn, từ Thiên Huyền vương quốc cùng nhau đi tới, đến bây giờ chẳng qua thời gian hơn một năm, hai lần xông Danh Sư đường thành công, một lần xông danh sư tháp thông qua, thiên tư có một không hai không nói, càng là dạy dỗ vương quốc, để hắn thành công tấn cấp, Thanh Nguyên đế quốc, diệt đi Thanh Điền Hoàng, công lao vô số... Nếu như chỉ vì tại Trương gia, Thánh tử điện, Băng Nguyên cung làm ầm ĩ một chút, liền trực tiếp bắt, hơn nữa còn là lợi hại nhất danh sư lệnh truy nã, có thể hay không... Quá nghiêm trọng chút!”"),t("br"),t("br"),n._v("Vừa rồi vị trưởng lão kia vội nói."),t("br"),t("br"),n._v("Trương Huyền trải qua, tại Danh Sư đường đều có hồ sơ, tổng bộ muốn thẩm tra, cũng không khó khăn."),t("br"),t("br"),n._v("“Ngô trưởng lão nói rất đúng, Danh Sư đường chú trọng nhân tài, phàm là đại tài, nhất định chí tình chí nghĩa, vị này Trương Huyền, mặc dù không tuân quy củ, gây ra không ít chuyện, nhưng cũng vì nhân tộc đã làm nhiều lần cống hiến, Thanh Nguyên đế quốc càng là thụ đồ vô số... Trực tiếp truy nã, ta cũng cảm thấy không thích hợp!”"),t("br"),t("br"),n._v("Lại một vị trưởng lão nói: “Huống chi, hắn là Chiến Sư đường chiến tử Trịnh Dương thụ nghiệp ân sư, ta sợ làm như vậy, sẽ gây ra càng lớn mâu thuẫn!”"),t("br"),t("br"),n._v("“Trịnh Dương chiến tử thụ nghiệp ân sư lại như thế nào? Chiến Sư đường cho hắn vô số tài nguyên, Danh Sư đường tổng bộ, mượn  cho hắn dùng, chẳng lẽ hắn cũng bởi vì một cái hồi nhỏ thụ nghiệp lão sư, phản loạn hay sao?”"),t("br"),t("br"),n._v("Khúc trưởng lão cười lạnh."),t("br"),t("br"),n._v("“Ngươi biết ta nói không phải phản loạn, mà là gây ra mâu thuẫn, chiến tử là Danh Sư đường chiến lực mạnh nhất, thật muốn bởi vì việc này, dẫn đến nội bộ lục đục sẽ không tốt...”"),t("br"),t("br"),n._v("Vừa rồi vị trưởng lão kia vội nói."),t("br"),t("br"),n._v("“Nội bộ lục đục? Hừ, tất cả đều là Danh Sư đường ban tặng, như vậy đều phải lục đục, muốn hắn làm gì dùng?” Khúc trưởng lão nói: “Loại này oai phong tà khí không thể mặc cho sinh sôi, nếu không, đường đem không đường!”"),t("br"),t("br"),n._v("“Ngươi đây là oai lý tà thuyết! Sư đạo truyền thừa, thụ nghiệp ân sư chịu khổ, nếu như vị này Trịnh Dương chiến tử, làm thinh, để hắn quản lý Chiến Sư đường, ai dám tin tưởng?”"),t("br"),t("br"),n._v("Ngô trưởng lão mở miệng."),t("br"),t("br"),n._v("“Khó tin, đổi đi là được, Ngô trưởng lão ngươi hẳn phải biết, hiện nay đại lục ở bên trên, mạnh nhất vẫn là Danh Sư đường, uy nghiêm không còn gì để mất...”"),t("br"),n._v("Khúc trưởng lão quát tháo."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Hai bên còn muốn cãi lộn, Nhậm Thanh Viễn quyền điện chủ lắc đầu đánh gãy hai người đối thoại."),t("br"),t("br"),n._v("“Các ngươi nói ta đều cân nhắc qua, có này thiên tài, quả thực là Danh Sư đường chuyện may mắn, cực kỳ... Trương gia, Băng Nguyên cung, Thánh tử điện liên tục báo lên, yêu cầu nghiêm trị, vị này Trương Huyền, đã chạm đến Danh Sư đường ranh giới cuối cùng! Nếu như không xử lý, ta sợ nhân tâm hoang mang, càng nhiều người sẽ dựa dẫm tài năng, làm ra khác người sự tình!”"),t("br"),t("br"),n._v("“Hiện tại tình cảnh, các ngươi cũng biết, đến vài vạn năm tới lớn nhất nguy cơ, nhân tộc nhất định phải ổn định, hơn nữa toàn bộ một lòng, mới có thể mọi người đồng tâm hiệp lực, chống lại kiếp nạn, nếu không, ta sợ rất khó vượt qua cửa ải khó khăn này...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe hắn nói như vậy, đám người an tĩnh lại."),t("br"),t("br"),n._v("Vừa rồi Ngô trưởng lão hai người, cũng lộ ra vẻ lo lắng, một lát sau, cũng nhịn không được nữa: “Đường chủ, tin tức kia... Đến cùng phải hay không thật?”"),t("br"),t("br"),n._v("“Chính xác trăm phần trăm!”"),t("br"),t("br"),n._v("Đảm nhiệm sư gật đầu."),t("br"),t("br"),n._v("“Dị Linh chi Hoàng, khống chế Dị Linh nhất tộc, sẽ không tùy tiện nhẹ liên quan hắn hiểm, lặng yên không một tiếng động đi tới chúng ta nhân tộc đại lục, đến cùng ý muốn như thế nào? Chẳng lẽ, liền không sợ bị chúng ta liên hợp vây giết?”"),t("br"),t("br"),n._v("Nhớ tới tin tức kia, Ngô trưởng lão đến bây giờ cũng không dám tin tưởng."),t("br"),t("br"),n._v("“Không riêng gì hắn, cái khác cũng có người tới, ý muốn như thế nào, điểm ấy còn dùng đoán ư?” Đảm nhiệm sư lắc đầu."),t("br"),t("br"),n._v("“Ta biết chỗ kia đã xuất hiện biến cố, đều hai lần gợn sóng rung chuyển, nhưng cụ thể có thể hay không xuất hiện, có thể hay không xuất hiện, dù ai cũng không cách nào xác nhận, bọn họ vậy mà đều nhận được tin tức?”"),t("br"),t("br"),n._v("Ngô trưởng lão nghi ngờ."),t("br"),t("br"),n._v("“Danh sư đại lục, sẽ là thời buổi rối loạn, hiện tại nhất định muốn giảm bớt bên trong hao tổn cùng bằng mặt không bằng lòng!”"),t("br"),t("br"),n._v("Đảm nhiệm sư than thở một tiếng: “Trương gia cùng Lạc gia kết hợp, liên lụy đến cái gì, không cần ta nói nhiều, các ngươi cũng biết, Thánh tử điện càng là ẩn chứa tiên thánh ý niệm, có thể chỉ dẫn phương hướng. Quãng thời gian trước, ta câu thông qua Khôi Thánh ý niệm, hắn nói cho ta biết, đã tìm ra mới điện chủ, thiên phú có một không hai, nhất định có thể đuổi tại sự tình đến trước đó, phù hợp yêu cầu. Chuyện này ta chuyên môn hỏi thăm qua Chiêm sư, hắn cũng nói cho ta biết, vị này mới điện chủ, hẳn là Dương sư thân truyền, Phùng Tử Dật!”"),t("br"),t("br"),n._v("“Hai cái này, cũng không thể loạn, còn Băng Nguyên cung, càng không cần phải nói, Tiên Thiên thể chất, mới có thể câu thông Thượng Thương, ba cái địa phương đồng thời xảy ra chuyện, không thích đáng xử lý, làm sao bình dân tâm? Ta chỉ là cái quyền điện chủ, vốn danh vọng liền không đủ, thật muốn xuất hiện náo động, đừng nói ta, liền xem như Dương sư, đều áp chế không nổi, trừ phi...”"),t("br"),t("br"),n._v("Đảm nhiệm sư nói đến đây, ánh mắt xa xăm, ánh mắt bên trong lộ ra tôn sùng cùng điên cuồng: “Trong truyền thuyết thiên nhận danh sư, chân chính điện chủ xuất hiện!”"),t("br"),t("br"),n._v("“Thiên nhận danh sư? Muốn lấy được ông trời đồng ý, nào có dễ dàng như vậy! Vài vạn năm đến, cũng chỉ có Khổng sư một người làm được thôi!”"),t("br"),t("br"),n._v("“Đúng vậy a, đây là không thể nào...”"),t("br"),t("br"),n._v("Tất cả trưởng lão đồng thời lắc đầu."),t("br"),t("br"),n._v("“Nếu quả thật sẽ có thiên nhận danh sư xuất hiện, ta tuyệt đối sẽ lập tức cho hắn dập đầu, bái hắn làm thầy!” Khúc trưởng lão cũng nhẹ gật đầu, khẽ nói."),t("br"),t("br"),n._v("“Bái hắn làm thầy? Thật có loại người này, ngươi chỉ sợ còn chưa đủ tư cách làm đệ tử a?”"),t("br"),t("br"),n._v("Ngô trưởng lão liếc bĩu môi."),t("br"),t("br"),n._v("“Ta là không có tư cách, dù là không làm được đệ tử, làm hạ nhân, ta cũng vui vẻ!” Khúc trưởng lão nâng lên cái cổ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1519"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1521"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);