(window.webpackJsonp=window.webpackJsonp||[]).push([[595],{948:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1512-đien-cuong-danh-su-đuong-tong-bo-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1512-đien-cuong-danh-su-đuong-tong-bo-ha"}},[n._v("#")]),n._v(" Chương 1512: Điên cuồng Danh Sư đường tổng bộ (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1512: Điên cuồng Danh Sư đường tổng bộ (hạ)"),t("br"),t("br"),n._v("Thiên nhận danh sư, đây chính là cùng Khổng sư đồng dạng siêu tuyệt nhân vật, một khi xuất hiện, nhất định thay đổi lịch sử, khống chế Danh Sư đường, ra lệnh một tiếng, chỉ cần là danh sư, không dám không theo, ai không muốn bái làm sư?"),t("br"),t("br"),n._v("Nhận hắn vi tôn?"),t("br"),t("br"),n._v("Cửu tinh danh sư trong mắt người ngoài, được cho kính trọng, nhưng ở loại người này trong mắt, cái gì cũng không tính!"),t("br"),t("br"),n._v("Năm đó Khổng sư, môn hạ đệ tử ba ngàn, cái nào không đạt đến cửu tinh cấp bậc?"),t("br"),t("br"),n._v("Liền dự thính đều là cửu tinh... Khúc trưởng lão dạng này, muốn trở thành đệ tử, đoán chừng người ta đều không thu!"),t("br"),t("br"),n._v("“Thiên nhận danh sư chỉ là truyền thuyết!”"),t("br"),t("br"),n._v("Nghe được Khúc trưởng lão cảm xúc, Nhậm sư cũng không phản bác, đồng dạng than thở: “Nếu như bây giờ nhân tộc thật có thể xuất hiện một vị, nhất định có thể hiệu lệnh quần hùng, Dị Linh tộc nhân coi như trở lại, cũng tuyệt đối không dám đến tìm phiền phức, mà ta, cũng cúi đầu nghe lệnh, như Thiên Lôi sai đâu đánh đó...”"),t("br"),t("br"),n._v("Nghe được quyền điện chủ đều như vậy cảm xúc, đám người đồng thời gật đầu."),t("br"),t("br"),n._v("Thượng Cổ thời đại, thuộc về tối tăm nhất thời đại, lúc đó Dị Linh tộc nhân cường đại đến cực hạn, thành lập thế lực, so với thời khắc này Danh Sư đường, đều chỉ mạnh không yếu, Khổng sư vẫn như cũ có thể nhờ vào sức một người, lật tung hắc ám, đủ thấy mạnh mẽ."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Dị Linh tộc nhân, đối Khổng sư sợ hãi tới cực điểm, vừa nhắc tới tên của hắn, đều thấp thỏm lo âu, không dám vượt qua nửa bước."),t("br"),t("br"),n._v("Thật có đồng dạng một vị kinh tài tuyệt diễm nhân vật, đột nhiên xuất hiện, nhất định có thể khiến người ta tộc tiến thêm một bước, đạt tới chỗ không dám tưởng tượng chí cao điểm."),t("br"),t("br"),n._v("“Được rồi, đây đều là mơ mộng hão huyền, không nói cũng được! Ta có thể trở thành quyền điện chủ, chỉ là thực lực hơi mạnh, trên thực tế đức uy không đủ, tạm thời, Danh Sư đường là có thể đè ép được Trương gia, Băng Nguyên cung các thế lực lớn, nhưng thật muốn đối với chuyện này xử lý bất công, nhất định thất đức... Đến lúc đó, cho dù bọn họ không dám trắng trợn phản đối, cần phải sau lưng đối mệnh lệnh qua loa cho xong, cũng sẽ mang đến không thể xóa nhòa tai nạn!”"),t("br"),t("br"),n._v("Nhậm sư nhìn quanh một vòng: “Khổng sư từng nói, ba tỉnh thân ta, điểm ấy ta một mực ghi nhớ, không dám có chút quên được! Bởi vậy, Trương Huyền công lao tuy có, nhưng cùng Trương gia, Thánh tử điện, Băng Nguyên cung so, vẫn là kém quá nhiều, cân nhắc trái phải, chỉ có thể như vậy, không cần lại cãi cọ. Như vậy đi, cũng cho hắn một cái cơ hội, một khi phát hiện, trước không vội đả thương người, chỉ cần tam đại thế lực, nguyện ý cùng am hiểu, không truy cứu cũng không có gì. Không nguyện ý, vậy cũng chỉ có thể không khách khí...”"),t("br"),t("br"),n._v("“Điện chủ nói tới rất đúng...”"),t("br"),t("br"),n._v("Đám người đồng thời gật đầu."),t("br"),t("br"),n._v("Trước đó Ngô trưởng lão đám người mặt mũi xấu hổ."),t("br"),t("br"),n._v("Bọn họ thân là danh sư, chỉ vì danh sư cân nhắc, không nghĩ tới đại cục... Vẫn là quyền điện chủ suy nghĩ chu đáo chặt chẽ, mới không có ủ thành đại họa, nếu không, nhờ vào Danh Sư đường lực lượng ai không phục coi như có thể diệt đi ai, làm việc bất công lời nói, nhất định lớn mất dân tâm, đến lúc đó, Danh Sư đường tổng bộ, đều sẽ nhận cực lớn hỏi khó."),t("br"),t("br"),n._v("“Nhậm sư như vậy khoan dung độ lượng, vị kia Trương Huyền xem như may mắn, chỉ bất quá, Trương Vô Ngân đưa tin tới, khí thế hùng hổ, Chiêm sư cũng lên cơn giận dữ, Băng Nguyên cung U Nhược Tâm, càng là sắp tức điên... Muốn hoà giải, gần như không có khả năng!”"),t("br"),t("br"),n._v("Khúc trưởng lão lắc đầu: “Vị kia Trương Huyền, một khi bị phát hiện, coi như chúng ta không ra tay, tam đại thế lực cũng sẽ đem hắn nuốt sống...”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Tiếng nói của hắn còn không có kết thúc, một cỗ kỳ dị lực lượng truyền lại mà đến, toàn bộ đại điện một hồi lắc lư, tựa như lúc nào cũng sẽ sụp đổ."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Nhậm sư bỗng nhiên đứng lên, Ngô sư đám người, cũng vội vàng xoay người, hướng năng lượng ba động truyền đến phương hướng nhìn lại."),t("br"),t("br"),n._v("“Chẳng lẽ... Vị kia Trương Huyền, giết tới Danh Sư đường tổng bộ?”"),t("br"),t("br"),n._v("Khúc trưởng lão da mặt co lại."),t("br"),t("br"),n._v("Sẽ không như thế gan lớn a?"),t("br"),t("br"),n._v("Bất quá, nếu không phải hắn, Danh Sư đường tổng bộ nắm giữ đại lực lượng bao phủ, vài vạn năm bất động, làm sao có thể xuất hiện lắc lư?"),t("br"),t("br"),n._v("“Là Phong Sư điện, đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Nhậm sư đi đầu phát giác phương vị, kịp phản ứng, thân thể nhoáng một cái, vọt ra ngoài, cái khác tất cả trưởng lão, theo sát phía sau."),t("br"),t("br"),n._v("Tất cả mọi người là cửu tinh danh sư, thực lực cực mạnh, thời gian nháy mắt, liền đến đến Phong Sư điện trước mặt, đẩy cửa đi vào."),t("br"),t("br"),n._v("Hoa lạp lạp lạp!"),t("br"),t("br"),n._v("Mới vừa vào đi, liền thấy chứa đựng vô số tiền bối bài vị giống như là nhận lấy cực lớn lực lượng công kích, không ngừng lắc lư."),t("br"),t("br"),n._v("Làm cho cả Danh Sư đường đều xuất hiện lắc lư lực lượng, chính là như vậy sinh ra."),t("br"),t("br"),n._v("“Tiên thánh sợ hãi, đây là thế nào?”"),t("br"),n._v(" Khúc trưởng lão bờ môi run rẩy, dọa đến thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Ngô sư đám người, cũng vội vàng ngắm nhìn bốn phía, muốn nhìn một chút, có phải hay không có địch nhân tập kích."),t("br"),t("br"),n._v("“Không phải địch tấn công, mà là...”"),t("br"),t("br"),n._v("Con mắt trợn tròn, Nhậm sư kích động thân thể không ngừng run rẩy."),t("br"),t("br"),n._v("“Không phải địch tấn công, có thể để cho vô số tiền bối sợ hãi, chẳng lẽ...”"),t("br"),t("br"),n._v("Nghe được quyền điện chủ lời nói, Ngô sư đi đầu kịp phản ứng, cũng thân thể cứng đờ, khó có thể tin."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Lời còn chưa dứt, đại điện bài vị, cũng lại không chịu nổi, đồng thời té ngã trên đất, giống như là thấy được một loại nào đó nhân vật lợi hại đồng dạng, từng cái thành tâm quỳ gối, không dám có bất kỳ động tác."),t("br"),t("br"),n._v("“Đây là chúng thánh cung nghênh...”"),t("br"),t("br"),n._v("Khúc trưởng lão giờ phút này cũng kịp phản ứng, lông mày nhịn không được nhảy loạn, khóe miệng kìm lòng không được run rẩy."),t("br"),t("br"),n._v("Có thể được tế bái tại Danh Sư đường tổng bộ Phong Sư điện, không có chỗ nào mà không phải là công lao hiển hách, thực lực siêu cường hạng người, nói thật, liền xem như hắn, loại thực lực này cửu tinh danh sư, chết đi cũng không có tư cách ở đây lập bài vị."),t("br"),t("br"),n._v("Như vậy đủ thấy khuôn mẫu cao."),t("br"),t("br"),n._v("Có thể để cho lợi hại như thế người, đồng thời cung kính nghênh đón, nguyên nhân chỉ có một cái..."),t("br"),t("br"),n._v("Tạch tạch!"),t("br"),t("br"),n._v("Đám người run rẩy, còn không có kết thúc, chính giữa tượng Khổng sư, đột nhiên lơ lửng dựng lên, cung kính ôm quyền, một mặt vui vẻ, chợt một vết nứt từ dưới đáy chậm rãi sinh ra, càng khuếch tán càng lớn."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Thời gian nháy mắt, đổ sụp xuống."),t("br"),t("br"),n._v("“Chúng sinh cung nghênh, Khổng sư hành lễ... Đây là thiên nhận danh sư!”"),t("br"),t("br"),n._v("Tất cả trưởng lão lúc này tất cả đều kịp phản ứng, từng cái cũng không biết là kích động, vẫn là khiếp sợ, tất cả đều khuôn mặt trắng bệch."),t("br"),t("br"),n._v("Bọn họ đều là đạt tới cửu tinh siêu cấp cường giả, biết thiên nhận danh sư xuất hiện dị tượng cùng ý nghĩa, coi như không cần giải thích, cũng đều biết, trước mắt tất cả, đại biểu cái gì."),t("br"),t("br"),n._v("Mới vừa nói xong chưa thiên nhận danh sư, thời gian nháy mắt liền xuất hiện, nhân sinh thay đổi rất nhanh, cùng lắm cũng chỉ như thế này thôi."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Tượng Khổng sư sụp đổ, Phong Sư điện cũng lại không chịu nổi, rền vang sụp xuống, tất cả trưởng lão từng cái đứng tại chỗ, tùy ý sụp xuống đồ vật, đập ở trên đầu, cũng không ngăn cản."),t("br"),t("br"),n._v("Thực lực bọn hắn Thông Huyền, kiến trúc đổ sụp xuống đá, đập ở trên đầu, cùng bông vải rơi vào trên người không có cái gì khác nhau, không có quá đáng lo."),t("br"),t("br"),n._v("Con mắt sáng lên, Nhậm sư hưng phấn mà nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Nhân tộc được cứu rồi, Danh Sư đường, sẽ lần nữa đứng sừng sững đại địa chi đỉnh...”"),t("br"),t("br"),n._v("Cười to một tiếng, Nhậm sư kích động nước mắt tung hoành."),t("br"),t("br"),n._v("Thân là quyền điện chủ, đức uy không đủ, nhưng thống lĩnh thiên hạ, những năm này áp lực có thể tưởng tượng được, hiện tại thiên nhận danh sư xuất hiện, gánh nặng rốt cục có thể buông xuống!"),t("br"),t("br"),n._v("“Truyền mệnh lệnh của ta, nhanh đi tìm kiếm lực lượng nguồn gốc, tìm tới vị này thiên nhận danh sư, lập tức tiếp trở về, kế nhiệm điện chủ chi vị...”"),t("br"),t("br"),n._v("Bỗng nhiên xoay người, Nhậm sư hét lớn."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Tất cả cửu tinh danh sư, đồng thời ôm quyền, tiếp nhận mệnh lệnh."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1520"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1522"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);