(window.webpackJsonp=window.webpackJsonp||[]).push([[601],{955:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1518-duong-huyen-gap-truong-huyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1518-duong-huyen-gap-truong-huyen"}},[n._v("#")]),n._v(" Chương 1518: Dương Huyền gặp Trương Huyền")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1518: Dương Huyền gặp Trương Huyền"),t("br"),t("br"),n._v("“Thay thế kinh mạch?”"),t("br"),t("br"),n._v("Hội trưởng dừng lại một chút: “Kinh mạch có thể dung nạp chân khí lưu thông, kèm theo tu vi tăng thêm, còn có thể vì đó phát triển, để lực lượng trôi chảy... Công hội đồ vật, cứ việc không ít, nhưng cũng làm không được điểm ấy!”"),t("br"),t("br"),n._v("Kinh mạch là một loại có thể sinh trưởng đồ vật, theo sát tu luyện giả tu vi, mà phát sinh biến hóa, thực lực mạnh mẽ, kinh mạch liền rộng lớn, thực lực yếu, liền chật hẹp... Là thân thể không thể chia cắt một bộ phận, luyện khí sư công hội luyện chế vũ khí, cứ việc ảo diệu, nhưng cũng không có vật thay thế."),t("br"),t("br"),n._v("“Làm không được?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhăn lại: “Một chút lợi hại vũ khí, ta nhớ được cũng có thể dài ngắn co duỗi, có thể lớn có thể nhỏ, nếu như đem dùng tại kinh mạch, không biết có được không?”"),t("br"),t("br"),n._v("Lợi hại vũ khí, cũng có thể lớn nhỏ tùy ý biến hóa, liền giống như trong tay hắn Thánh tử điện điện chủ lệnh, bình thường chỉ lớn cỡ lòng bàn tay, đồng dạng có thể biến thành một mặt to lớn lá chắn."),t("br"),t("br"),n._v("“Vũ khí là có thể tùy tâm biến hóa, lại không cách nào ẩn chứa chân khí, hơn nữa, kinh mạch biến hóa, liên lụy đến không gian, cũng không phải là vách ngoài biến lớn, một khi đi vào trong cơ thể, đột nhiên biến hóa, ta sợ tu luyện không có xảy ra vấn đề, ngược lại lại bởi vậy bị thương...”"),t("br"),t("br"),n._v("Hội trưởng cười khổ."),t("br"),t("br"),n._v("“Ai...”"),t("br"),t("br"),n._v("Trương Huyền sắc mặt tái xanh."),t("br"),t("br"),n._v("Những này hắn cứ việc cũng biết, nhưng có chút chưa từ bỏ ý định, hy vọng có thể từ trong miệng người khác biết được không giống tin tức, hiện tại xem ra, muốn dùng luyện chế binh khí phương pháp, giúp một tay luyện chế kinh mạch, gần như là không có khả năng hoàn thành."),t("br"),t("br"),n._v("“Thật ra thì cũng không cần chán nản, luyện chế đồ vật quả thực không thể giải quyết, nhưng mà kinh mạch thay thế, ta đã nghe qua truyền thuyết, có người có thể hoàn thành!”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, hội trưởng nhớ tới cái gì, nói."),t("br"),t("br"),n._v("“Ai?”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Đã có người có thể làm được, hắn nhất định cũng có thể! Coi như làm không được, cũng sẽ tìm tới người này, giúp Triệu Nhã tiếp theo nối liền kinh mạch, để hắn lần nữa có thể tu luyện."),t("br"),t("br"),n._v("“Ta nghe được cũng chỉ là truyền thuyết, cụ thể có phải thật vậy hay không, không có kiểm chứng qua! Thời gian không dài, cũng liền hai mươi năm trước đi, Danh Sư đường tổng bộ một vị cường giả, từng bốn phía tìm kiếm thay thế kinh mạch chi pháp, nghe nói cuối cùng còn thành công!”"),t("br"),t("br"),n._v("Suy tư một chút, hội trưởng nói."),t("br"),t("br"),n._v("“Danh Sư đường tổng bộ? Ai?”"),t("br"),t("br"),n._v("Trương Huyền vội vàng nhìn qua."),t("br"),t("br"),n._v("Dính dáng Triệu Nhã có thể khôi phục hay không, ngàn vạn không qua loa được."),t("br"),t("br"),n._v("“Thái thượng trưởng lão, Dương Huyền!”"),t("br"),t("br"),n._v("Hội trưởng gật đầu."),t("br"),t("br"),n._v("“Dương Huyền?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Bản thân giả mạo vị này thái thượng trưởng lão, tự xưng học sinh của hắn, yếm vòng vòng, không nghĩ tới, hắn từng làm qua những thứ này... Chẳng lẽ, muốn bản thân chủ động đi tìm?"),t("br"),t("br"),n._v("“Đúng vậy a, Dương sư thiên phú tuyệt đỉnh, thực lực mạnh không nói, trọng yếu nhất chính là, tại từng cái chức nghiệp, đều có rất sâu trình độ, bởi vì thay thế kinh mạch sự tình, hắn từng đi tìm luyện khí sư công hội, cùng tổng bộ hội trưởng, nghiên cứu qua thật nhiều năm, nghe nói thành công... Đương nhiên, ta cũng chỉ là nghe nói, cụ thể tình huống gì, khả năng còn cần tìm tới người trong cuộc hỏi thăm mới có thể rõ ràng!”"),t("br"),t("br"),n._v("Hội trưởng nói."),t("br"),t("br"),n._v("“Nha...”"),t("br"),t("br"),n._v("Trương Huyền xoa xoa mi tâm: “Nhìn tới muốn đi tìm vị kia Dương sư!”"),t("br"),t("br"),n._v("Vị này Dương sư, chắc còn ở Trương gia, chỉ sợ còn cần Trương gia một nhóm."),t("br"),t("br"),n._v("Chỉ bất quá, bản thân giả mạo đối phương học sinh, lại giả mạo đối phương, huyên náo thiên hạ xôn xao, lại đi tìm người ta, có thể hay không bị trực tiếp ra tay trấn áp?"),t("br"),t("br"),n._v("Đối mặt đại trưởng lão Trương Vô Ngân, hắn có lẽ còn có thể chạy trốn, vị này Dương sư... Thật muốn ra tay, đủ loại thủ đoạn toàn bộ thi triển, chỉ sợ đều vô dụng."),t("br"),t("br"),n._v("“Dương sư, Dương Huyền?”"),t("br"),t("br"),n._v("Một bên Triệu Nhã, nghe một hồi chóng mặt."),t("br"),t("br"),n._v("Tận mắt thấy Dương sư biến thành lão sư, nàng đã biết, vị tổ sư này là giả, là lão sư ngụy trang, hiện tại lại toát ra một vị... Đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Dương sư Thần Long gặp đầu gặp đuôi, Danh Sư đường tổng bộ, đều rất ít trở về, chỉ sợ rất khó tìm đến, nếu như ngươi học sinh nguy cấp lời nói, Y Sư công hội Hoàng y sư, là ta hảo hữu, ngược lại là có thể để cho hắn giúp một tay chẩn bệnh một chút...”"),t("br"),t("br"),n._v("Gặp thanh niên trước mắt suy nghĩ, hội trưởng còn tưởng rằng lo lắng học sinh bệnh tình, nhịn không được nói."),t("br"),t("br"),n._v("“Đa tạ hội trưởng hậu ý, tạm thời không cần...”"),t("br"),n._v("Trương Huyền ôm quyền, đang muốn tiếp tục nói chuyện, chỉ thấy một cái luyện khí sư vội vã đi đến: “Hội trưởng, bên ngoài một vị danh sư, nói muốn gặp ngươi?”"),t("br"),t("br"),n._v("“Danh sư? Mấy tinh? Ai?”"),t("br"),t("br"),n._v("Hội trưởng quay đầu."),t("br"),t("br"),n._v("“Hắn không mang đẳng cấp huy chương, không biết đẳng cấp, bất quá... Hình như họ Dương!” Luyện khí sư chần chờ một chút, nói."),t("br"),t("br"),n._v("“Họ Dương?”"),t("br"),t("br"),n._v("Sửng sốt một chút, hội trưởng suy tư hồi lâu cũng không nghĩ ra liên minh đế quốc, vị nào lợi hại danh sư họ Dương, có điều, vẫn gật đầu: “Mời hắn vào!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Vị luyện khí sư này xoay người đi ra ngoài."),t("br"),t("br"),n._v("“Nếu ngươi có khách muốn gặp, ta trước hết cáo từ...”"),t("br"),t("br"),n._v("Biết Dương sư khả năng có thể tiếp theo tiếp kinh mạch, Trương Huyền biết ở lại chỗ này cũng không có ý nghĩa gì, mang theo Triệu Nhã, ôm quyền cáo từ, vừa mới xoay người, liền nghe đến sau lưng tiếng bước chân vang lên, ngay sau đó vừa rồi vị luyện khí sư kia mang theo một cái lão giả đi đến."),t("br"),t("br"),n._v("“Vị tiểu hữu này, làm sao mới đi đến, liền muốn rời khỏi?”"),t("br"),t("br"),n._v("Lão giả nhìn qua mỉm cười."),t("br"),t("br"),n._v("“Ta?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Vị này đến tìm hội trưởng, cùng mình nói lời này làm gì?"),t("br"),t("br"),n._v("“Đúng vậy a! Cùng hắn tới thăm hỏi hội trưởng, không bằng nói là vì ngươi mà tới!”"),t("br"),t("br"),n._v("Lão giả vuốt râu, cười nhạt một tiếng."),t("br"),t("br"),n._v("“Vì ta?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày, một mặt kỳ lạ."),t("br"),t("br"),n._v("Trước mắt vị lão giả này, thoạt nhìn hết sức bình thường, tuy là mặc danh sư trường bào, nhưng không có đẳng cấp huy chương, để cho người ta không biết cấp bậc, một mặt mỉm cười, mười phần ôn hoà."),t("br"),t("br"),n._v("Nhưng hắn có thể xác nhận, trước kia tuyệt đối chưa bao giờ thấy qua."),t("br"),t("br"),n._v("“Chúng ta quen biết?”"),t("br"),t("br"),n._v("Cũng lại kìm nén không được, nhìn lại."),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Lão giả cũng không trả lời, nhẹ nhàng cười một tiếng, ngẩng đầu nhìn về phía trước mắt luyện khí sư công hội hội trưởng cùng Trần Mặc luyện khí sư: “Hai vị đại sư, ta có một số việc muốn cùng vị tiểu huynh đệ này đơn độc trò chuyện một chút, không biết có thể tạo thuận lợi!”"),t("br"),t("br"),n._v("Nói xong ngón tay búng một cái, một cái huy chương hiện lên ở hai người trước mặt, phía trên chín khỏa sao lấp lánh chói mắt."),t("br"),t("br"),n._v("“Cửu tinh luyện khí sư?”"),t("br"),t("br"),n._v("Nhìn thấy huy chương, hội trưởng cùng Trần Mặc, đồng thời giật nảy mình, vội vàng ôm quyền: “Vâng!”"),t("br"),t("br"),n._v("Vốn cho rằng đối phương là danh sư, có thể sẽ lấy ra danh sư huy chương, không nghĩ tới, còn là một vị cửu tinh luyện khí sư, chỉ bằng vào điểm ấy, liền để bọn họ không cách nào cự tuyệt."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Hai người lui xuống, rất nhanh gian phòng chỉ còn lại Trương Huyền, Triệu Nhã cùng vị này kỳ quái lão giả."),t("br"),t("br"),n._v("“Ngô Chương Hiên, ta Trương Huyền, ngươi lấy cái tên giả này, một điểm thành ý đều không có...”"),t("br"),t("br"),n._v("Gặp gian phòng không ai, lão giả nhẹ nhàng cười một tiếng, nhìn lại: “Có phải hay không ah, Trương sư? Lại hoặc là, ta cái kia xưng hô ngươi là... Trương điện chủ?”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương một cái liền nói ra lai lịch của hắn, Trương Huyền giật nảy mình, con ngươi co rụt lại, nghĩ đến cái gì, da đầu nổ tung, tinh thần kết nối phân thân, tùy thời đều làm xong chạy trốn chuẩn bị: “Ngươi là danh sư, họ Dương... Chẳng lẽ là...”"),t("br"),t("br"),n._v("“Ha ha, tại hạ...”"),t("br"),t("br"),n._v("Lần nữa vuốt râu, lão giả nhìn qua, một mặt vui vẻ: “Chính là ngươi giả mạo cái kia... Dương Huyền!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1526"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1528"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);