(window.webpackJsonp=window.webpackJsonp||[]).push([[604],{959:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-144-thuong-phap-cao-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-144-thuong-phap-cao-thu"}},[n._v("#")]),n._v(" Chương 144: Thương pháp cao thủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Đổi làm cái khác luyện đan sư, đều là nhất tinh, coi như là bằng hữu, cũng không thể vừa ra tay như thế hào khí, nhưng trước mắt Trương Huyền, thực sự quá lợi hại, khẩu chiến mọi người, bất luận luyện đan, thuốc... Khắp mọi mặt đều cơ hồ hoàn mỹ, không chê vào đâu được, làm cho tất cả mọi người đều khàn giọng lại không nói gì."),t("br"),t("br"),n._v(" Chỉ phần này năng lực, chỉ cần cấp cho đầy đủ thời gian, nhất định sẽ Nhất Phi Trùng Thiên, hiện tại giao hảo, xem như là tiền kỳ đầu tư."),t("br"),t("br"),n._v(" Đem Sinh Tức đan thu cẩn thận, Trương Huyền đột nhiên nhớ tới một chuyện, hỏi: “Âu Dương hội trưởng, ngươi biết chúng ta Thiên Huyền Vương thành, nơi nào có thương pháp bí tịch bán ra?” "),t("br"),t("br"),n._v(" Vương Dĩnh có Dưỡng Thể dịch, Lưu Dương có Ôn Mạch đan, Phá Âm đan cùng Cự Tê thú huyết dịch tuy rằng tạm thời không đến, nhưng Triệu Nhã, Viên Đào một khi dùng, kích hoạt thể chất, tu vi tất nhiên mức độ lớn bay vọt."),t("br"),t("br"),n._v(" Hiện tại năm học sinh, bốn vị đều có phương pháp giải quyết, chỉ còn dư lại Trịnh Dương một cái."),t("br"),t("br"),n._v(" Hắn am hiểu thương pháp, Lục Tầm lại tìm vị thương pháp còn mạnh hơn hắn người cùng hắn làm đối thủ, không tìm chút lợi hại chiêu số, để hắn học tập, sợ rằng sẽ sẽ là yếu kém nhất một khâu."),t("br"),t("br"),n._v(" Học viện giáo sư tàng thư khố, hắn tất cả nhìn, võ kỹ có một ít, nhưng không có liên quan với thương pháp, chính vì như thế, Vương Siêu lão sư mới như thế nổi tiếng, để vô số am hiểu thương pháp học viên nổi danh mà tới."),t("br"),t("br"),n._v(" Vương Siêu có thể đem vị kia Mạc Hiểu “Tiễn đưa” cho Lục Tầm, nói rõ hai người quan hệ không tầm thường, hướng hắn hỏi dò thương pháp, khẳng định không được, mà bản thân không có chút nào biết, chỉ có thể nhìn xem Vương thành có hay không bán ra loại bí tịch này địa phương."),t("br"),t("br"),n._v(" “Thương pháp bí tịch? Ngươi muốn học tập thương pháp?” Âu Dương hội trưởng nghi hoặc nhìn sang."),t("br"),t("br"),n._v(" Luyện đan sư bản thân ngay tại chỗ vị tôn sùng, không học tập luyện đan, học thương pháp làm gì?"),t("br"),t("br"),n._v(" “Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v(" “Thương pháp so kiếm pháp ít lưu ý, học ít, chân chính có thể đạt đến mọi người trình độ, không có mấy vị, ngươi hỏi lời của người khác, khả năng không có cách nào nói cho ngươi, ta ngược lại thật ra vừa vặn nhận thức một cái, gia truyền thương pháp, ảo diệu vô song, toàn bộ Vương thành đều là nổi tiếng!” "),t("br"),t("br"),n._v(" Âu Dương Thành vuốt râu cười nói."),t("br"),t("br"),n._v(" “Ồ? Có thể hay không mang ta đi học tập?” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v(" “Người này ít giao du với bên ngoài, dựa theo đạo lý, người ngoài bình thường không thấy, cũng không dễ dàng truyền thụ thương pháp, nhưng ta cùng hắn là bạn tri kỉ bạn tốt, có lẽ sẽ cho cái mặt mũi!” "),t("br"),t("br"),n._v(" Âu Dương Thành nói."),t("br"),t("br"),n._v(" “Vậy làm phiền Âu Dương hội trưởng.” Biết đối phương vẫn như cũ tại lấy lòng, lúc này gật đầu, Trương Huyền trong lòng âm thầm quyết định khi nào nhìn hắn luyện đan, cũng cố gắng chỉ điểm một chút."),t("br"),t("br"),n._v(" “Đi thôi, ta dẫn ngươi đi gặp gỡ vị lão hữu này.” "),t("br"),t("br"),n._v(" Thấy đối phương rõ ràng ý tứ, Âu Dương Thành thoả mãn gật đầu."),t("br"),t("br"),n._v(" Đi ra luyện đan sư công hội, hai người đi rồi đại khái nửa canh giờ, đi tới trước một tòa phủ đệ."),t("br"),t("br"),n._v(" Bởi vì Âu Dương hội trưởng cùng với là bạn tốt duyên cớ, gác cổng hộ vệ vẫn chưa ngăn cản, một đường đi vào."),t("br"),t("br"),n._v(" “Âu Dương hội trưởng chờ, lão gia nhà ta đang luyện thương, một hồi liền ra tới!” "),t("br"),t("br"),n._v(" Quản gia đem hai người mang tới một cái tiểu đình, nói."),t("br"),t("br"),n._v(" “Ân, để hắn luyện xong tới tìm ta là được!” Âu Dương hội trưởng cũng biết đối phương thói quen, không để ý lắm, tùy ý khoát tay áo một cái, bắt chuyện Trương Huyền: “Trương đan sư, ngồi!” "),t("br"),t("br"),n._v(" “Tốt!” Trương Huyền ngồi xuống ngắm nhìn bốn phía."),t("br"),t("br"),n._v(" Viện rộng rãi, trang trí tương đối nhạt nhã, làm cho người ta một loại yên tĩnh cảm giác, toàn bộ viện vẫn chưa thấy cái gì người ở cùng người hầu, xem ra nơi đây chủ nhân không phải cái xa mỹ người."),t("br"),t("br"),n._v(" “Ta vị lão hữu này gọi Vương Sùng, hơn hai mươi năm trước liền nhận thức, một đời chìm đắm tại thương pháp thế giới bên trong, nói riêng về thương pháp, toàn bộ Thiên Huyền Vương thành, tuyệt đối phải kể đến, Thẩm Truy bệ hạ đều từng tự mình tán dương hắn thương pháp vô song.” "),t("br"),t("br"),n._v(" Nâng chung trà lên, Âu Dương hội trưởng giới thiệu."),t("br"),t("br"),n._v(" “Lợi hại!” Đối với mỗi cái ngành nghề đi tới đỉnh phong nhân vật, Trương Huyền luôn luôn đều là khâm phục."),t("br"),t("br"),n._v(" Bất luận cái nào ngành nghề, có thể tại mấy chục triệu người bên trong bộc lộ tài năng, đủ để chứng minh nghị lực cùng thiên phú."),t("br"),t("br"),n._v(" “Âu Dương lão đầu, có phải là lại ở sau lưng nói ta nói xấu?” "),t("br"),t("br"),n._v(" Chính đang tán gẫu, một cái trong sáng âm thanh tiếng vang lên, lập tức một ông già nhanh chân đi đến."),t("br"),n._v("  Xem ra hơn năm mươi tuổi,"),t("br"),t("br"),n._v(" Thân thể lại giống Long hổ bình thường tinh mãnh liệt, tựa hồ có dùng mãi không hết khí lực, ăn mặc quần áo luyện công, cầm trong tay một thanh trường thương, cùng quản gia nói như thế, hẳn là đang luyện thương, liên y phục đều không đổi, liền đi tới."),t("br"),t("br"),n._v(" “Nói nói xấu ngươi? Ta mới không nhàm chán như vậy!” Âu Dương Thành cười đứng dậy."),t("br"),t("br"),n._v(" Nghe hai người khẩu khí, liền biết quan hệ rất quen thuộc."),t("br"),t("br"),n._v(" “Này còn tạm được, làm sao ngày hôm nay rảnh rỗi đến xem ta?” Vương Sùng vài bước đi tới đình nghỉ mát, lúc này mới nhìn thấy Trương Huyền, nghi ngờ hỏi: “Đây là của ngươi vãn bối?” "),t("br"),t("br"),n._v(" “Khặc khặc!” Âu Dương Thành tràn đầy lúng túng, một hớp nước trà suýt chút nữa phun ra ngoài."),t("br"),t("br"),n._v(" Vãn bối cái lông a!"),t("br"),t("br"),n._v(" Ta còn muốn lấy lòng đối phương, để hắn chỉ điểm một chút, ngươi ngược lại tốt, một câu vãn bối, suýt chút nữa để ta nỗ lực làm không..."),t("br"),t("br"),n._v(" Lén lút nhìn Trương Huyền một chút, thấy hắn vẫn chưa bởi vậy tức giận loại hình, lúc này mới thở phào nhẹ nhõm, chỉ lo vị lão hữu này lại nói linh tinh gì vậy, vội vàng giới thiệu: “Ta đến giới thiệu cho ngươi một hồi, vị này chính là công hội mới lên cấp nhất tinh luyện đan sư Trương Huyền!” "),t("br"),t("br"),n._v(" “Nhất tinh luyện đan sư?” "),t("br"),t("br"),n._v(" Vương Sùng sững sờ, lập tức ngơ ngác."),t("br"),t("br"),n._v(" Luyện đan sư sát hạch có bao nhiêu khó, hắn cùng Âu Dương Thành là lão hữu, biết đến rất rõ ràng, vốn là nhìn thấy đối phương trẻ tuổi như vậy, còn tưởng rằng là vãn bối cái gì, không nghĩ tới đã trở thành chính thức luyện đan sư."),t("br"),t("br"),n._v(" Quả thực khó mà tin nổi. "),t("br"),t("br"),n._v(" Liền ngay cả một bên quản gia cũng tràn đầy kinh ngạc."),t("br"),t("br"),n._v(" “Không những như vậy, Trương đan sư, là thông qua Luận đan sát hạch thành công, một người nói mười vị luyện đan sư á khẩu không trả lời được, bàn về thuật luyện đan, e sợ toàn bộ Vương thành cũng không có người có thể ra hắn phía trên!” "),t("br"),t("br"),n._v(" Âu Dương Thành tiếp tục nói."),t("br"),t("br"),n._v(" “Luận đan?” Vương Sùng líu lưỡi."),t("br"),t("br"),n._v(" Hắn tuy không phải luyện đan sư, đối với loại này sát hạch phương pháp, lại nghe Âu Dương hội trưởng đã nói, một cái không đủ hai mươi gia hỏa, không chỉ trở thành luyện đan sư, vẫn là Luận đan thành công, không phải tận mắt nhìn thấy, đều có chút khó có thể tin tưởng được."),t("br"),t("br"),n._v(" “Âu Dương hội trưởng khách khí, ta chỉ là may mắn học chút tri thức, thật muốn ta luyện đan, ta khả năng đều luyện chế không ra...” "),t("br"),t("br"),n._v(" Trương Huyền vội nói."),t("br"),t("br"),n._v(" Hắn thực sự nói thật, có thể Luận đan thành công, là bởi vì Thiên Đạo thư viện, để hắn luyện đan mà nói, khả năng liền đơn giản nhất viên thuốc đều không thể làm ra đến."),t("br"),t("br"),n._v(" Mấy người lại hàn huyên một hồi, Trương Huyền cũng biết Âu Dương Thành cố ý ca ngợi bản thân, là cho vị này Vương Sùng lưu cái ấn tượng tốt, mới tốt mở miệng."),t("br"),t("br"),n._v(" Quả nhiên, hàn huyên vài câu, Âu Dương Thành cười nói: “Lần này lại đây, e sợ còn có việc muốn phiền phức lão hữu!” "),t("br"),t("br"),n._v(" “Ồ?” Vương Sùng nhìn sang."),t("br"),t("br"),n._v(" “Là như vậy, Trương đan sư cũng vô cùng yêu thích thương pháp, không biết Vương huynh có thời gian hay không, các ngươi cũng tốt lẫn nhau thảo luận một, hai!” "),t("br"),t("br"),n._v(" Âu Dương Thành nói."),t("br"),t("br"),n._v(" “Ồ? Trương đan sư cũng là thương pháp cao thủ? Cái kia quá tốt rồi...” "),t("br"),t("br"),n._v(" Nghe nói như thế, Vương Sùng ánh mắt sáng lên, tràn đầy hưng phấn nhìn sang."),t("br"),t("br"),n._v(" Hắn ưa thích thương thành si, thích nhất cùng thương pháp cao thủ đọ sức, vị này Trương Huyền tuổi còn trẻ liền trở thành chính thức luyện đan sư, e sợ thương pháp thiên phú cũng không kém, có thể cùng thảo luận, có thể nào không hưng phấn?"),t("br"),t("br"),n._v(" “Ta... Khặc khặc, ta chưa bao giờ học được thương pháp...” "),t("br"),t("br"),n._v(" Nhìn đối phương dự định muốn cùng mình tỷ thí dáng vẻ, Trương Huyền đầy mặt lúng túng."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-152"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-154"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);