(window.webpackJsonp=window.webpackJsonp||[]).push([[605],{960:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1521-lam-hoc-tro-ta-a"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1521-lam-hoc-tro-ta-a"}},[n._v("#")]),n._v(" Chương 1521: Làm học trò ta a")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1521: Làm học trò ta a (bên dưới)"),t("br"),t("br"),n._v("“Học, học xong?” Dương sư vội vàng nhìn tới, sợ mình nghe lầm."),t("br"),t("br"),n._v("Ngươi nhận lấy ngọc bài, cái gì đều không có làm, chân khí cũng không có truyền vào đi vào, nói “Học xong” Có ý tứ gì?"),t("br"),t("br"),n._v("Lại nói, chiêu này Đoạn Dương chỉ, thâm ảo khó hiểu, coi như hắn lần đầu tiên tiếp xúc, toàn bộ xem xong, hơn nữa lý giải, cũng hao tốn tiếp cận nửa canh giờ, mới vừa lấy đến trong tay, nói còn chưa dứt lời, liền như thế nói, thật hay giả?"),t("br"),t("br"),n._v("“Nếu là vũ kỹ của ngươi, có thể đè thấp tu vi, thi triển chiêu số giống vậy, so với ta thử một chút, liền biết!”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Thấy thanh niên trước mắt, như vậy chắc chắn, dường như mảy may đều không e ngại thân phận của hắn cùng thực lực, Dương sư nhẹ gật đầu."),t("br"),t("br"),n._v("Đối phương nói không sai, kiểm nghiệm một người đối võ kỹ nắm giữ, phương pháp tốt nhất, liền là đối chiến!"),t("br"),t("br"),n._v("Có thể trong chiến đấu thi triển đi ra, hơn nữa vận dụng lên, không có không lưu loát, mới nói rõ chân chính nắm giữ."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Thấy hắn tràn đầy tự tin, Dương sư hít sâu một hơi, sau một khắc, toàn thân tu vi lập tức rơi xuống, trở nên cùng Trương Huyền giống nhau như đúc, Động Hư cảnh sơ kỳ!"),t("br"),t("br"),n._v("“Ta bắt đầu...”"),t("br"),t("br"),n._v("Ngón trỏ ngón giữa dựng thẳng lên, đầu ngón tay một đạo lực lượng xoay chầm chậm, thời gian nháy mắt, liền tạo thành một cái to lớn gió lốc."),t("br"),t("br"),n._v("Phong bạo bên trong, lực lượng không ngừng gào thét, giống như là muốn đem không gian xé rách."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền lên tiếng, cũng không có bất kỳ động tác gì, tiện tay một chỉ điểm tới."),t("br"),t("br"),n._v("Đầu ngón tay hắn đã không có gió lốc, cũng không có chân khí lực lượng, thậm chí động tác chậm rãi giống như là lão Ngưu cày ruộng."),t("br"),t("br"),n._v("Vốn là Dương sư muốn mở miệng nói, thi triển bất thường, nhưng vừa nhìn thấy đầu ngón tay tới, con ngươi kìm lòng không được thu hẹp, không để ý tới nói chuyện, trên ngón tay gió lốc, tới đón."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Đầu ngón tay đối đầu cùng một chỗ, thời gian nháy mắt gió lốc liền tiêu tán không thấy hình bóng, sau một khắc, Dương sư liền cảm thấy giống như là bị một cỗ cự lực đánh trúng, dưới chân kìm lòng không được lui về sau bảy, tám bước, trong cơ thể lực lượng từng tầng từng tầng mở ra, thẳng đến khôi phục Động Hư cảnh đỉnh phong, lúc này mới ngừng lại."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Đè nén trong cơ thể khí huyết sôi trào, Dương sư mặt mũi kinh hãi."),t("br"),t("br"),n._v("Đối phương vừa rồi thi triển cái kia chỉ, vừa mới bắt đầu nhìn cùng Đoạn Dương chỉ không quá tương tự, nhưng vừa ra tay liền hiểu được, hoàn toàn do nó biến hóa mà thành, mấu chốt nhất là, mặc dù sửa lại công kích phương pháp, cùng vận hành chân khí đường tắt, uy lực không những không có yếu bớt, lại chợt tăng tiếp cận gấp đôi!"),t("br"),t("br"),n._v("Đặc biệt là một chỉ điểm ra, bao phủ đối thủ toàn thân, thậm chí chân khí trong cơ thể đều bị chặt đứt!"),t("br"),t("br"),n._v("Loại lực lượng này, dựa theo trước kia phương pháp tu luyện, coi như luyện đến đỉnh phong, đều làm không được."),t("br"),t("br"),n._v("“Không có bị thương chứ...”"),t("br"),t("br"),n._v("Trương Huyền có chút xấu hổ."),t("br"),t("br"),n._v("“Không có!”"),t("br"),t("br"),n._v("Dương sư lắc đầu, cũng lại kìm nén không được: “Ngươi đem bộ này chỉ pháp sửa chữa... Hơn nữa còn luyện thành?”"),t("br"),t("br"),n._v("Thông qua đối chiến, hắn đã rõ ràng, đối phương không chỉ luyện thành, còn thuận tiện sửa đổi một chút, đây rốt cuộc... Làm sao làm được?"),t("br"),t("br"),n._v("Ngắn ngủi mấy phút, không chỉ nhìn xong học được, càng là sửa chữa... Hắn tận mắt nhìn thấy, đều cảm thấy tại như là nằm mơ!"),t("br"),t("br"),n._v("Trên đời này, lại có lợi hại như thế cường giả?"),t("br"),t("br"),n._v("“Võ kỹ mục đích là vì tốt hơn thi triển chân khí, để tu luyện giả phát huy ra lực lượng cường đại nhất, mà không giết người! Đoạn Dương chỉ, một chỉ đoạn dương, quá mức bá đạo, làm đất trời oán giận, cho nên, ta liền thuận tiện sửa lại một chút, lấy cắt đứt đối thủ chân khí vận chuyển lực lượng làm mục đích, cũng không giết người, lại có thể để đối thủ, tự biết không địch lại...”"),t("br"),t("br"),n._v("Trương Huyền giải thích."),t("br"),t("br"),n._v("Cái này Đoạn Dương chỉ, quả thực rất lợi hại, là giết địch tuyệt chiêu, nhưng đối chân khí, tinh khí thần tiêu hao thực sự quá lớn, một chiêu đánh không trúng đối phương, làm không cẩn thận còn có thể bị người có thể thừa dịp."),t("br"),t("br"),n._v("Dung hợp rất nhiều chỉ pháp, chỗ thiếu hụt này liền được bổ sung hoàn chỉnh."),t("br"),t("br"),n._v("“Cắt đứt đối thủ chân khí, quả thực không có lớn như vậy sát lục, nhưng mà... Nếu như đối mặt Dị Linh tộc nhân, tha đối phương tính mạng, đối phương lại đưa ngươi đưa vào chỗ chết... Thường thường một ý nghĩ sai lầm, liền có thể mang đến vô tận tai hoạ!”"),t("br"),n._v("Dương sư nhìn qua."),t("br"),t("br"),n._v("Sát chiêu, tử chiêu, mặc dù làm đất trời oán giận, nhưng mà cũng phải nhìn đối với người nào."),t("br"),t("br"),n._v("Đối chiến danh sư tự nhiên không thể sử dụng, nhưng cùng Dị Linh tộc nhân đại chiến thời điểm, hơi nương tay, một khi cho đối thủ lưu lại cơ hội, bản thân liền vô cùng có khả năng rơi vào bị động, thậm chí chết oan chết uổng."),t("br"),t("br"),n._v("Loại chuyện này, chỗ nào cũng có."),t("br"),t("br"),n._v("Năm đó, hắn cùng lão sư lần đầu tiên đi vào Địa quật, cũng bởi vì nhất niệm chi nhân, không có thống hạ sát thủ, cuối cùng lão sư nhận lấy công kích, suýt chút nữa vẫn lạc."),t("br"),t("br"),n._v("Sau chuyện này hối hận không biết bao lâu, từ đây cũng coi là mọc ra giáo huấn, cùng bằng hữu đối chiến, có thể hạ thủ lưu tình, nhưng cùng kẻ địch, liền muốn không từ thủ đoạn, nếu không, di hoạ vô tận."),t("br"),t("br"),n._v("“Đúng vậy a, đối chiến Dị Linh tộc nhân, quả thực không năng thủ mềm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Vậy ngươi vừa rồi chiêu số, sát phạt chi lực không đủ...”"),t("br"),t("br"),n._v("Dương sư kỳ lạ."),t("br"),t("br"),n._v("Biết rõ có loại tình huống này, vì sao còn đem chiêu này đổi thành bộ dáng như thế?"),t("br"),t("br"),n._v("“A, mới vừa rồi cùng ngươi đối chiến, ta chỉ dùng ba thành lực, cho nên thoạt nhìn thu phóng tự nhiên, thi triển toàn lực lời nói, uy lực cũng là không sai!”"),t("br"),t("br"),n._v("Rõ ràng đối phương ý tứ, Trương Huyền cười cười."),t("br"),t("br"),n._v("“Ba thành?”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, Dương sư chỉ cảm thấy đầu óc sắp nổ tung: “Ngươi nói... Mới vừa dùng ba thành lực?”"),t("br"),t("br"),n._v("Ba thành lực, liền đem cùng cấp bậc hắn đánh liên tục lui về sau bảy, tám bước, tu vi tăng lên tới Động Hư cảnh đỉnh phong mới kết thúc..."),t("br"),t("br"),n._v("Xác định không có nói đùa?"),t("br"),t("br"),n._v("“Đúng vậy a, đây mới là dùng ra toàn lực...”"),t("br"),t("br"),n._v("Gật gật đầu, Trương Huyền bàn tay nhẹ nhàng một phen, ngón tay lần nữa điểm tới."),t("br"),t("br"),n._v("Lần này, động tác so vừa rồi chậm hơn, nhưng trong nháy mắt Dương sư cũng cảm giác toàn bộ thiên địa đều giống như đổ sụp đồng dạng, Động Hư cảnh đỉnh phong lực lượng, đều cảm nhận được một loại tuyệt vọng dâng lên, vô luận như thế nào đều né tránh không được."),t("br"),t("br"),n._v("Tạch tạch! Tạch tạch!"),t("br"),t("br"),n._v("Không dám dừng lại nghỉ, vội vàng giải tỏa tu vi, thời gian nháy mắt trở lại Thánh vực cửu trọng Vũ Không cảnh sơ kỳ, lúc này mới cảm thấy áp lực giảm bớt không ít."),t("br"),t("br"),n._v("Ngón tay một phen, tới đón."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Lực lượng của đối phương như thủy triều vọt tới, giống như là muốn đem hắn kinh mạch trong cơ thể chấn vỡ, lại giống là phá hủy tinh thần của hắn, để hắn không còn chút nào nữa sức phản kháng."),t("br"),t("br"),n._v("Lần nữa rời khỏi vài chục bước, trong cơ thể khí huyết sôi trào, miệng lớn thở hổn hển, vẻ mặt như là giấy trắng, tràn đầy khó tin."),t("br"),t("br"),n._v("Hắn là làm thế người mạnh nhất một trong, coi như đè thấp tu vi, cùng cấp bậc có thể vượt qua, cũng có thể đếm được trên đầu ngón tay, đối phương chỉ học tập hắn chiêu số, vài phút, cao nhất cái đại cấp bậc đều không thể ngăn cản..."),t("br"),t("br"),n._v("Quá khủng bố đi!"),t("br"),t("br"),n._v("Chẳng lẽ, trên thế giới này, thật có vừa học liền biết, một hồi liền tinh, thậm chí còn có thể tự mình sửa lại siêu tuyệt nhân vật?"),t("br"),t("br"),n._v("“Uy lực không tệ a!”"),t("br"),t("br"),n._v("Thấy hắn ngừng lại, Trương Huyền cười cười."),t("br"),t("br"),n._v("“Là...”"),t("br"),t("br"),n._v("Mạnh ổn định trong lòng rung động, Dương sư thở ra một hơi, hai mắt sáng lên nhìn lại: “Đã ngươi không có lão sư... Có bằng lòng hay không chân chính bái ta làm thầy?”"),t("br"),t("br"),n._v("Đối phương giả mạo học sinh của hắn, cũng liền nói rõ, muốn bái nhập môn hạ của hắn, trước đó xem thường, tận mắt nhìn đến loại thiên phú này, lòng tràn đầy hừng hực, cũng lại kìm nén không được."),t("br"),t("br"),n._v("Thật muốn nhận lấy loại học sinh này, về sau danh tiếng của hắn, cũng sẽ càng thêm huy hoàng, đạt tới cao độ trước đó chưa từng có.."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1529"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1531"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);