(window.webpackJsonp=window.webpackJsonp||[]).push([[606],{961:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1522-nguoi-la-troi-nhan-danh-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1522-nguoi-la-troi-nhan-danh-su"}},[n._v("#")]),n._v(" Chương 1522: Ngươi là trời nhận danh sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1522: Ngươi là trời nhận danh sư?"),t("br"),t("br"),n._v("“Bái ngươi làm thầy?”"),t("br"),t("br"),n._v("“Không sai, chỉ cần bằng lòng, danh sư đuổi bắt lệnh, Trương gia, Băng Nguyên cung vấn đề... Ta đều có thể giúp ngươi giải quyết!” Dương sư cười gật đầu."),t("br"),t("br"),n._v("Bằng vào mặt mũi của hắn, Trương gia, Băng Nguyên cung, coi như rất mất thể diện, không có chân chính hao tổn tinh anh, đối phương lại là Thánh tử điện điện chủ, Danh Sư đường tổng bộ, tự mình đi nói, hẳn là cũng sẽ không thái quá nghiêm khắc, nhiều nhất tự mình dẫn hắn, nhiều lập chút công lao, tình thế nguy cấp trước mắt, thoạt nhìn phức tạp, trên thực tế hẳn là có thể nhẹ nhõm hóa giải."),t("br"),t("br"),n._v("Coi như không phải những yếu tố này, đơn hắn Danh Sư đường thái thượng trưởng lão thân phận, mở miệng thu đệ tử, cướp tới, không biết bao nhiêu, hiện tại cũng tự mình mở miệng, chắc hẳn sẽ không cự tuyệt."),t("br"),t("br"),n._v("Đang tại hắn cảm thấy cái này đệ tử, nhất định lòng tràn đầy vui sướng, nhanh bái sư thời điểm, chỉ thấy thanh niên lắc đầu: “Cảm ơn Dương sư ưu ái... Ta tạm thời còn không có bái sư ý định!”"),t("br"),t("br"),n._v("Năm lần trời nhận danh sư, trên người phong thái và khí chất, đã hình thành, dù là hiện tại Khổng sư tự mình thu đồ, cũng chưa chắc đồng ý, huống chi trước mắt vị này."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Dương sư sửng sốt một chút, tràn đầy khó tin: “Ngươi trụ cột kiên cố, đối võ kỹ cùng tu luyện đều có đặc biệt lý giải, dựa theo tình huống bình thường, có cần hay không lão sư, đều có bản thân phương thức tu luyện, rất khó sửa đổi, nhưng Danh Sư đường, chú ý truyền thừa, có một cái lão sư tốt, bất kể địa vị vẫn là về sau làm việc, đều sẽ có rất lớn bổ ích. Hơn nữa, danh sư bước vào cửu tinh, đã không có tiền nhân đường, cần tự mình tìm tòi, không có lão sư trợ giúp chỉ điểm, rất dễ dàng đi nhầm vào lạc lối, xuất hiện cực lớn phiền phức!”"),t("br"),t("br"),n._v("“Không có tiền nhân đường?” Trương Huyền nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Đối với cửu tinh danh sư sự tình, tu vi không có đạt tới, cấp bậc không có đạt tới, hầu như hỏi thăm không ra bất kỳ tin tức, đến cùng như thế nào đạt tới, tu vi lại thế nào đột phá, có thể nói không hiểu ra sao."),t("br"),t("br"),n._v("“Đúng vậy a, cửu tinh danh sư, đại lục đỉnh phong nhất, cũng không phải là tu vi, tâm cảnh đạt tới liền đủ tư cách, càng quan trọng hơn là muốn nắm giữ đặc biệt giáo dục lý niệm, có thể đang dạy học trồng người phương diện, nắm giữ cực cao thành tích, tựa như Khổng sư, lưu lại hữu giáo vô loại, tùy theo tài năng tới đâu mà dạy, học mà không ngại, dạy không biết mệt, toàn diện giáo dục cùng học nghĩ đều xem trọng mấy người lý niệm, đối hậu thế, đều có rất lớn nhập môn...”"),t("br"),t("br"),n._v("“Không chỉ có như vậy, tu vi phương diện, trở thành chân chính cửu tinh danh sư, cần đột phá Thánh vực cửu trọng, thực lực đạt tới loại cảnh giới này, một bước so một bước khó, liền xem như ta, đều cần cẩn thận từng li từng tí, như giẫm trên băng mỏng! Chỉ dựa vào một mình tìm tòi, muốn càng đi càng xa, thực sự quá khó!”"),t("br"),t("br"),n._v("Dương sư lắc đầu."),t("br"),t("br"),n._v("“Tu vi nghịch thiên mà đi, đương nhiên sẽ không cho phép dễ dàng, chẳng qua...”"),t("br"),t("br"),n._v("Biết hảo ý của đối phương, Trương Huyền không có cách nào giải thích, đành phải tiến về phía trước một bước, đi tới đối phương trước mặt: “Dương sư, mời xem cái này cái!”"),t("br"),t("br"),n._v("Nói xong ngón tay một chút, một đạo Thiên đạo chân khí, theo đầu ngón tay chậm rãi dòng nước chảy mà ra, đồng thời trong cơ thể, một cỗ đặc thù khí tức chậm rãi phóng thích, hướng lão giả trước mắt uy hiếp mà tới."),t("br"),t("br"),n._v("Dương sư vốn là tràn đầy nghi ngờ, cảm nhận được cỗ khí tức này, con ngươi co rụt lại, vội vàng lui về sau hai bước, tròng mắt sắp trừng ra hốc mắt, ngón tay không ngừng run rẩy. Trong lòng lật lên thao thiên cự lãng: “Ngươi, ngươi là...”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu, không nói thêm gì nữa."),t("br"),t("br"),n._v("“Cái này... Khó trách!”"),t("br"),t("br"),n._v("Thở ra một hơi, Dương sư cung kính thi lễ một cái, lúc này mới khôi phục lại, vẫn như cũ cảm thấy khó tin."),t("br"),t("br"),n._v("Khó trách hắn Một mực mời, đối phương cũng không nguyện ý, nằm mơ đều không có nghĩ đến, lại là trời nhận danh sư!"),t("br"),t("br"),n._v("Thiên địa công nhận, cũng chính là thiên địa chi đồ, địa vị có thể so với Khổng sư, đừng nói là hắn, coi như Khổng sư phục sinh, muốn thu làm đệ tử, cũng chưa chắc sẽ bằng lòng!"),t("br"),t("br"),n._v("“Ta cái thân phận này quá mức kinh thế hãi tục, một khi tin tức truyền ra, nhất định dẫn tới Dị Linh tộc nhân vây giết, cho nên, chỉ có thể giả mạo học sinh của ngươi, che giấu sự thật... Chưa đồng ý, mong rằng thông cảm!”"),t("br"),t("br"),n._v("Thấy hắn rõ ràng, Trương Huyền ôm quyền."),t("br"),t("br"),n._v("Lúc trước giả mạo đối phương thời điểm, cũng không biết có hắn tồn tại, chẳng qua, đã từ nơi sâu xa tự có thiên ý, hơi sửa chữa một chút lời giải thích cũng không sao."),t("br"),n._v(" Như vậy trải qua, coi như ngụy trang đối phương, cũng có căn cứ."),t("br"),t("br"),n._v("“Cái này, đây là vinh hạnh của ta!”"),t("br"),t("br"),n._v("Liền vội vàng gật đầu, Dương sư vẻ mặt vẫn như cũ không có khôi phục."),t("br"),t("br"),n._v("Có thể để cho trời nhận danh sư, giả mạo học sinh của hắn, có thể nói đã tu luyện mấy đời phúc phận, đổi lại trước đó, nghĩ cũng không dám suy nghĩ!"),t("br"),t("br"),n._v("“Cũng đúng, loại trừ trời nhận danh sư, ai có loại năng lực này?”"),t("br"),t("br"),n._v("Tiêu hóa một hồi thật lâu, lúc này mới tỉnh táo lại."),t("br"),t("br"),n._v("Khó trách trước mắt vị này, có thể làm ra nhiều như vậy nghịch thiên việc làm, thậm chí liền lôi kiếp đều cúi đầu nghe lệnh, cả đời ngang dọc, thiên tài gặp qua không biết bao nhiêu, đừng nói so ra mà vượt, liền một phần mười có thể so sánh với đều phượng mao lân giác."),t("br"),t("br"),n._v("Không nói cái khác, vị kia thân truyền Phùng Tử Dật, ở trong mắt những người khác, thiên tư tuyệt đỉnh, nhưng cùng trước mắt vị này so, thật cái gì cũng không tính, căn bản không tại một cái cấp bậc."),t("br"),t("br"),n._v("“Dương sư, trời nhận danh sư dính dáng quá lớn, mong rằng có thể giữ miệng giữ mồm!”"),t("br"),t("br"),n._v("Thấy hắn hiểu được, Trương Huyền ôm quyền."),t("br"),t("br"),n._v("“Yên tâm, chuyện này, ta nhất định sẽ không tiết lộ!” Dương sư nhẹ gật đầu."),t("br"),t("br"),n._v("Trời nhận danh sư, dính dáng thực sự quá lớn, một khi truyền ra, nhất định gây nên oanh động to lớn, thậm chí toàn bộ Danh Sư đường, đều sẽ vì đó bùng nổ!"),t("br"),t("br"),n._v("Dị Linh tộc nhân biết tin tức, càng sẽ dốc toàn bộ lực lượng, toàn bộ nhân tộc, đều có thể trước thời hạn đi vào nguy cơ, khó mà khống chế."),t("br"),t("br"),n._v("“Cảm ơn!”"),t("br"),t("br"),n._v("Biết lấy đối phương thân phận, chỉ cần bằng lòng, chắc chắn sẽ không truyền ra ngoài, Trương Huyền lần nữa ôm quyền."),t("br"),t("br"),n._v("“Không cần khách khí, đây cũng là danh sư chức trách...”"),t("br"),t("br"),n._v("Dương sư nghiêm túc nhìn qua: “Ngươi là trời nhận danh sư, ta tự nhiên không có tư cách thu ngươi làm học sinh, chẳng qua... Không tiết lộ trời nhận danh sư, lại không có truyền thừa lời nói, Danh Sư đường bên kia giải thích, sẽ khá phiền phức!”"),t("br"),t("br"),n._v("“Phiền phức?” Trương Huyền cau mày."),t("br"),t("br"),n._v("Trước mắt vị này chính là Danh Sư đường đệ nhất thái thượng trưởng lão, chuyện gì còn không phải chuyện một câu nói, phiền phức cái gì?"),t("br"),t("br"),n._v("“Cùng Danh Sư đường tiếp xúc nhiều hơn, chắc hẳn ngươi cũng nhìn ra rồi, hiện tại Danh Sư đường, đã không phải là trước kia Danh Sư đường!”"),t("br"),t("br"),n._v("Lắc đầu, Dương sư khắp khuôn mặt là than thở."),t("br"),t("br"),n._v("Trương Huyền nháy con mắt."),t("br"),t("br"),n._v("“Danh Sư đường xây dựng dự tính ban đầu là, bảo vệ nhân tộc, vì nhân tộc mà chiến, những người khác sự tình, đều không đi quản, thậm chí không đi qua hỏi... Khổng sư về sau, vô số tiên tổ, cũng quả thực là làm như vậy! Nhưng, người đều có nhân tính, không thể nắm giữ quyền lợi quá lâu!”"),t("br"),t("br"),n._v("Cười khổ lắc đầu, Dương sư trong ánh mắt mang theo thâm thúy cùng tang thương: “Thứ này thoạt nhìn không có gì, nhưng hưởng thụ thời gian lâu dài, tự nhiên sẽ sinh ra một loại tự ngạo, tự mãn cảm xúc! Hiện tại Danh Sư đường liền là như vậy, bọn họ vẫn cảm thấy đại lục an nguy, là bọn họ ném đầu lâu, tung nhiệt huyết duy trì, bất luận người nào, bất kỳ thế lực nào cũng không thể phản bác, danh sư uy quyền không được khiêu khích, đáng tiếc, bọn họ cũng không biết, cái này cái gọi là quyền uy, là tất cả Nhân tộc cấp cho, không còn nhân tộc... Ai biết ngươi danh sư là ai?”"),t("br"),t("br"),n._v("“Ta chính là không ưa loại này tập tục, quanh năm dạo chơi bên ngoài, Danh Sư đường tổng bộ, đều rất ít đợi!”."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1530"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1532"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);