(window.webpackJsonp=window.webpackJsonp||[]).push([[607],{962:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1523-duong-huyen-bien-su-đe"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1523-duong-huyen-bien-su-đe"}},[n._v("#")]),n._v(" Chương 1523: Dương Huyền biến sư đệ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1523: Dương Huyền biến sư đệ"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Sửng sốt một chút, Trương Huyền ngay sau đó gật đầu."),t("br"),t("br"),n._v("Cùng nhau đi tới, danh sư gặp qua không ít, có, mặc kệ đẳng cấp gì, đều khiêm cung lễ phép, hòa ái dễ gần, tuân theo sư đạo truyền thừa, mảy may cũng không dám quên được; Cũng có bảo thủ, tự cho là đúng, dựa dẫm thân phận làm việc, cùng thế lực khác, hào môn không có gì khác biệt."),t("br"),t("br"),n._v("Danh Sư đường xây dựng ước nguyện ban đầu là tốt, nhưng thời gian lâu dài, khó tránh khỏi sẽ hư hỏng."),t("br"),t("br"),n._v("Quyền lợi, cao cao tại thượng, quả thực có thể hủ thực tâm tính của người ta, danh sư cũng không thể ngoại lệ."),t("br"),t("br"),n._v("“Tựa như lần này, Danh Sư đường ban bố lệnh truy nã, ta điều tra hồ sơ của ngươi, công lao trong người, lại là tuyệt đỉnh thiên tài, tình huống bình thường, Danh Sư đường hẳn là đi chủ động điều hòa... Nhưng mà, ngươi có thể hóa giải lôi kiếp, thiên tư trác tuyệt, không có sư thừa lời nói, chính là một cái to lớn bánh trái thơm ngon, bắt ngươi việc nhỏ, càng nhiều người muốn từ ngươi nơi này thu hoạch được đột phá bí pháp, cùng đối phó lôi kiếp thủ đoạn...”"),t("br"),t("br"),n._v("Dương sư lắc đầu, một mặt than thở: “Danh Sư đường tổng bộ, hiện tại đã chia làm mấy cái đoàn thể, riêng phần mình của mình mình quý, đều muốn vượt qua đối phương, đã không giống trước kia chân chính học thuật trao đổi, một phương từ ngươi nơi này nhận được phương pháp, nhất định có thể vượt qua đối phương, đem áp chế... Khổng sư lúc trước lưu lại danh sư, là muốn cho hắn lớn mạnh nhân tộc, nhưng nhân tính... Ai!”"),t("br"),t("br"),n._v("“Nhân tính... Quả thực khó mà nói!”"),t("br"),t("br"),n._v("Trương Huyền cảm xúc."),t("br"),t("br"),n._v("Danh Sư đường xây dựng sơ kỳ, tuân theo thiên hạ vì công, hữu giáo vô loại lý niệm, nhưng vài vạn năm thời gian, nhân tộc ổn định, không ít người đã bị quyền lợi che đôi mắt, quên đi ước nguyện ban đầu."),t("br"),t("br"),n._v("Chân chính đỉnh phong danh sư, phẩm chất có lúc còn không bằng ở giữa thấy qua những cái kia nhất tinh danh sư."),t("br"),t("br"),n._v("Chí ít bọn họ còn không có bị quyền lợi cùng lực lượng vẩn đục hai mắt."),t("br"),t("br"),n._v("“Đúng vậy a, Thánh vực chỉ có được một ngàn năm tuổi thọ, vài vạn năm đều không có người có thể đánh vỡ, không ít người đạt tới đỉnh phong về sau, liền mất đi động lực, loại tình huống này, tự nhiên muốn hưởng thụ tốt hơn đãi ngộ... Được rồi, không nói những thứ này!”"),t("br"),t("br"),n._v("Khắp khuôn mặt là thất lạc, Dương sư không tại vấn đề này tiếp tục vướng víu."),t("br"),t("br"),n._v("Việc này, đã đi sâu cốt tủy, không phải một mình hắn, hai người có thể giải quyết."),t("br"),t("br"),n._v("“Đây là Danh Sư đường truyền thừa vài vạn năm đến, tích tụ mà sinh ra tai hại... Bất quá, danh sư coi như lại xốc nổi, tại đối đãi Dị Linh tộc phương diện này, cũng đều là không thể đổ trách nhiệm cho người khác, không có bất luận người nào chối từ! Bởi vậy, những này tại các đời quyền điện chủ trong mắt, giới tiển chi nhanh, không quan trọng gì, chỉ cần sơ tâm không thay đổi, liền không sao, dần dà, liền thành cục diện bây giờ!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Dù là quyền lợi lại để cho người hư hỏng, chỉ cần đối đãi Dị Linh tộc vấn đề bên trên, không có phát sinh biến hóa, đã nói lên danh sư vẫn như cũ huy hoàng, cũng không chân chính nước chảy bèo trôi, quên bản thân."),t("br"),t("br"),n._v("“Khổng sư mất đi tin tức về sau, đại thánh đã là đỉnh phong, lại không người có thể đột phá, không cách nào làm cho tất cả mọi người tin phục, ngươi xuất hiện liền không đồng dạng, thiên nhận danh sư... Nhất định có thể dẫn đầu chúng ta, đi ra không giống con đường, tái hiện nhân tộc huy hoàng!”"),t("br"),t("br"),n._v("Cảm xúc xong, Dương sư tràn đầy hưng phấn nhìn qua."),t("br"),t("br"),n._v("Kỳ trước quyền điện chủ, đức uy không đủ, rất khó khống chế toàn cục, trước mắt vị này vừa xuất hiện, liền hoàn toàn khác biệt."),t("br"),t("br"),n._v("“Đại thánh?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("“Không sai, Thánh vực cửu trọng phía trên, còn có đại thánh cấp bậc, có điều, cảnh giới này, cũng không phải là cửu trọng, mà là tứ trọng! Đột phá đại thánh, là cổ thánh! Cũng chính là Khưu Ngô, Bốc Thương đám người chỗ đi đến cảnh giới, từ khi Khổng sư rời đi về sau, đã vài vạn năm, đều không có người có thể đạt đến!”"),t("br"),t("br"),n._v("Dương sư gật đầu."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền chấn động trong lòng."),t("br"),t("br"),n._v("Nguyên lai tưởng rằng cổ thánh chỉ là đúng Thượng Cổ Thánh giả tôn xưng, không nghĩ tới lại là một cái lợi hại đại cấp bậc."),t("br"),t("br"),n._v("“Khổng sư là cảnh giới gì?”"),t("br"),t("br"),n._v("Có chút nhịn không được, Trương Huyền tràn đầy tò mò nhìn qua."),t("br"),t("br"),n._v("Đã người hầu, đệ tử đều là cổ thánh cấp bậc, vị này nhân tộc đệ nhất nhân, danh sư chức nghiệp người sáng lập, đến cùng đạt đến loại cảnh giới nào? Lại có gì chủng thực lực?"),t("br"),n._v("“Khổng sư đã đạt đến quỷ thần khó lường cảnh giới... Không phải ta đợi hậu bối có thể phỏng đoán!”"),t("br"),t("br"),n._v("đọc"),t("br"),n._v("ngantruyen.com/ Hai mắt nâng lên, nhìn về phía không biết tên nơi xa, Dương sư mặt mũi mê mẩn."),t("br"),t("br"),n._v("Khổng sư, vừa ra đời chính là vô địch, không ai biết hắn rốt cục mạnh đến mức nào, lại có bao nhiêu cao thâm, cho dù có người biết được, cũng tuyệt không phải bọn họ những vãn bối này, có thể đi hỏi thăm."),t("br"),t("br"),n._v("“Nói nhiều như vậy, chắc hẳn đối với hiện tại Danh Sư đường, ngươi cũng hẳn là biết một chút, không tiết lộ thiên nhận danh sư thân phận, vậy liền cần một thân phận khác, có thể dừng chân, để cho người ta đừng nghi ngờ, nếu không, ngắn ngủi thời gian một năm, xông đường hai lần đều thành công, lại làm ra động tĩnh lớn như vậy... Ta sợ khó mà tốt!”"),t("br"),t("br"),n._v("Dương sư lần nữa nhìn qua."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền lặng im."),t("br"),t("br"),n._v("Đối phương nói không sai, danh sư cấp bậc cao, chỉ nói rõ là thực lực của hắn mạnh, tâm cảnh khắc độ cao, đúng giáo dục có nhất định cống hiến, nhưng cá nhân phẩm chất, liền nhất định sẽ cao ư?"),t("br"),t("br"),n._v("Bộ giáo dục cục trưởng, cá nhân đức tính, liền nhất định hơn được bình thường lão sư?"),t("br"),t("br"),n._v("Chưa hẳn!"),t("br"),t("br"),n._v("Kỳ ngộ tốt, thiên phú cao thôi, cùng cá nhân phẩm chất không có bất cứ quan hệ nào."),t("br"),t("br"),n._v("Thậm chí, mạnh mẽ danh sư, cao tại trong mây, thoát khỏi người bình thường, ngược lại càng thêm lạnh lùng."),t("br"),t("br"),n._v("Bởi vậy, Danh Sư đường tổng bộ, cứ việc đồng dạng quán triệt “Sư” đạo, thế nhưng cũng không tưởng tượng bên trong bình tĩnh như vậy."),t("br"),t("br"),n._v("“Ta đương nhiên là không dám thu ngươi làm đồ, bất quá... Ngược lại là có thể thay mặt tiên sư thu đồ, nói cách khác, có thể đối ngoại tuyên bố, ngươi là sư huynh của ta, như vậy trải qua, người ngoài coi như nghi ngờ, tiên sư đã qua đời, từ ta ra mặt, cũng không nói được gì! Đương nhiên, đây cũng chỉ là xưng hô, liền xem như tiên sư, cũng không có tư cách thu ngươi làm đồ...”"),t("br"),t("br"),n._v("Dương sư đem ý nghĩ của mình nói ra."),t("br"),t("br"),n._v("“Sư huynh?” Da mặt run lên, Trương Huyền gãi đầu một cái: “Cái này... Có chút không tốt lắm đâu... Sư đệ!”"),t("br"),t("br"),n._v("“Thiên nhận danh sư, địa vị kính trọng, có thể cùng Khổng sư ngồi ngang hàng, nếu như không phải sợ quá mức kinh thế hãi tục, ta đều muốn bái ngươi vi sư... Xưng hô ngươi là sư huynh, đã là đi quá giới hạn!”"),t("br"),t("br"),n._v("Dương sư vội nói."),t("br"),t("br"),n._v("Hắn đệ nhất thái thượng trưởng lão thân phận, cứ việc rất cao, đương đại so ra mà vượt không có mấy cái, nhưng... Cùng thiên nhận danh sư so, còn là kém quá nhiều."),t("br"),t("br"),n._v("Xưng hô đối phương sư huynh, giả mạo sư đệ, đã là làm trái lễ, tràn đầy sợ hãi."),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền đành phải cố hết sức nhẹ gật đầu: “Thôi được, Dương Huyền sư đệ!”"),t("br"),t("br"),n._v("“Trương sư huynh...”"),t("br"),t("br"),n._v("Dương sư thở phào nhẹ nhõm, lòng tràn đầy vui sướng."),t("br"),t("br"),n._v("Đuổi theo vốn định thu đối phương vì học sinh, kết quả lại trở thành bái sư huynh... Mặc dù như thế, đối với hắn mà nói, vẫn như cũ tràn đầy hưng phấn."),t("br"),t("br"),n._v("Thiên nhận danh sư xuất hiện... Hỗn loạn Danh Sư đường, nhất định sẽ triệt để thống nhất, nhân tộc sẽ càng ngày càng mạnh, lại ứng đối tình thế nguy hiểm, cũng đã rất thoải mái."),t("br"),t("br"),n._v("“Đúng rồi, sư đệ, ta nghe nói ngươi từng giúp người đúc lại kinh mạch, nhưng có việc này?”"),t("br"),t("br"),n._v("Xác nhận quan hệ, có đối sách, Trương Huyền không tại tiếp tục vướng víu những vấn đề này, mà là tràn đầy kỳ quái nhìn qua."),t("br"),t("br"),n._v("Vừa rồi vị hội trưởng kia nói, trước mắt vị này, đã từng thay thế qua kinh mạch, đã gặp mặt, vừa vặn cẩn thận hỏi thăm một chút."),t("br"),t("br"),n._v("“Thật có việc này!” Dương sư nhẹ gật đầu."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1531"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1533"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);