(window.webpackJsonp=window.webpackJsonp||[]).push([[61],{416:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1031-đi-vao-dung-nham"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1031-đi-vao-dung-nham"}},[n._v("#")]),n._v(" Chương 1031: Đi vào dung nham")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1031: Đi vào dung nham"),t("br"),t("br"),n._v("“Im miệng!”"),t("br"),t("br"),n._v("Trương Huyền lông mày nâng lên."),t("br"),t("br"),n._v("“Vâng!” Nghe được trách mắng, Kim Nguyên đỉnh lập tức câm đi tại không dám nói nhảm."),t("br"),t("br"),n._v("Mặc dù nó ngừng lại, tất cả mọi người vẫn là nghe cái hiểu, nguyên một đám nhịn không được nhìn lại."),t("br"),t("br"),n._v("“Tạm thời thu cái luyện khí học đồ?”"),t("br"),t("br"),n._v("“Tùy tiện luyện chế? Trương viện trưởng, đây rốt cuộc chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Kim Nguyên đỉnh bản thân là Thánh khí, muốn đối hắn tiến hành luyện chế, tấn thăng thực lực lời nói, ít nhất cũng phải nắm giữ thất tinh luyện khí sư thực lực... Tạm thời thu cái học đồ, làm sao cái tình huống?"),t("br"),t("br"),n._v("Luyện khí học đồ, hẳn là cấp bậc gì đều không có đi, loại người này... Có thể làm cho Thánh khí tấn cấp?"),t("br"),t("br"),n._v("“A, đêm qua, gặp được một cái muốn tự sát học đồ...”"),t("br"),t("br"),n._v("Thấy mọi người ánh mắt, Trương Huyền cũng không có gì có thể giấu diếm, đem trước sự tình nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Một cái liền phàm khí đều luyện chế không ra được học đồ, ngươi chỉ điểm nửa canh giờ, liền để Kim Nguyên đỉnh tấn cấp?”"),t("br"),t("br"),n._v("“Cái này... Điều đó không có khả năng a?”"),t("br"),t("br"),n._v("Nghe xong giới thiệu, tất cả mọi người cảm thấy điên rồi."),t("br"),t("br"),n._v("Chỉ điểm học đồ, rèn luyện hạ phẩm Thánh khí... Đây là đối với mình có bao lớn lòng tin, mới dám như vậy?"),t("br"),t("br"),n._v("Cái này muốn đối luyện khí, rất cao thâm lý giải, mới có thể làm đến?"),t("br"),t("br"),n._v("“Trước đó ta liền nghe nói, Trương viện trưởng thuật luyện khí cực mạnh, bây giờ mới biết... Còn đánh giá thấp...”"),t("br"),t("br"),n._v("“Đúng vậy a, loại này luyện khí năng lực, chỉ sợ đã đạt đến thất tinh, thậm chí đỉnh phong cấp bậc.”"),t("br"),t("br"),n._v("“Chỉ bằng vào một chiêu này, chính là chúng ta không cách nào so sánh...”"),t("br"),t("br"),n._v("Ba đại học viện viện trưởng, tất cả đều đồng thời cười khổ."),t("br"),t("br"),n._v("Bọn họ theo Triệu Bính Tuất trong miệng biết trước mắt vị này luyện khí rất mạnh, nhưng cũng chỉ tưởng là, đạt tới lục tinh liền cao nữa là, giờ phút này mới biết được, tuyệt đối không có đơn giản như vậy."),t("br"),t("br"),n._v("Phải biết, cho dù chân chính thất tinh đỉnh phong luyện khí sư, cũng không có khả năng chỉ điểm học đồ, dễ dàng rèn luyện hạ phẩm Thánh khí thành công."),t("br"),t("br"),n._v("“Ngươi... Cùng ta sau khi tách ra, cứu người, còn vạch trần một cái luyện khí sư ngụy sư khuôn mặt?”"),t("br"),t("br"),n._v("Cùng mọi người chú ý chút khác biệt, một bên Lạc Nhược Hi đôi mắt thanh tú trợn tròn, nhìn về phía cách đó không xa thanh niên."),t("br"),t("br"),n._v("“Ừm, ngươi rời khỏi về sau, ta đang định đuổi theo, liền phát hiện có người tự sát, cho nên... Mới làm trễ nải thời gian!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("Lắc đầu, Lạc Nhược Hi tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("Hôm qua khí nghiến răng nghiến lợi, cảm thấy cái tên này, một chút nhãn lực sức lực đều không có, nguyên lai cũng không phải là như vậy."),t("br"),t("br"),n._v("Thân là danh sư, bảo vệ “Sư đạo” tôn nghiêm, rõ ràng càng trọng yếu hơn."),t("br"),t("br"),n._v("Biết rõ bị bản thân hiểu lầm, nhưng không có mảy may giải thích, nếu không phải Kim Nguyên đỉnh quá mức đắc ý, chỉ sợ nàng đến bây giờ cũng không biết... Loại này phẩm chất, thật khiến cho người ta khâm phục."),t("br"),t("br"),n._v("“Ta suy đoán dung nham chỗ cốt lõi tại lực lượng bạo phát vị trí, cho nên tính toán đi xuống dò xét một phen...”"),t("br"),t("br"),n._v("Không tại vấn đề này tiếp tục thảo luận, Trương Huyền con mắt lần nữa nhìn sang."),t("br"),t("br"),n._v("“Lực lượng bạo phát chỗ?”"),t("br"),t("br"),n._v("Mọi người cau mày."),t("br"),t("br"),n._v("“Nơi này như vậy cuồng mãnh, cho dù có trận pháp cùng không gian gấp, cũng sẽ bị phá huỷ đi, làm sao lại xây dựng ở nơi này?”"),t("br"),t("br"),n._v("Hàn hội trưởng nhịn không được nói."),t("br"),t("br"),n._v("Nơi này hắn đã sớm quan sát, cái thứ nhất liền bị bài trừ."),t("br"),t("br"),n._v("Phía dưới nhúc nhích lực lượng, một khi nổ tung, phạm vi hơn nghìn dặm chỗ đều sẽ bị nổ nát, đủ thấy uy lực... Như vậy cuồng bạo khí tức trùng kích vào, lại ổn định không gian gấp, lại ổn định trận pháp, cũng không chịu nổi đi!"),t("br"),t("br"),n._v("“Có phải hay không, chỉ có đi xuống xem một chút mới hiểu được, lực lượng lớn, có thể tiêu diệt trận pháp, cũng có khả năng duy trì trận pháp vận chuyển! Có lợi có hại, chỉ là cấp bậc quá cao, chúng ta không thể nào hiểu được thôi...”"),t("br"),t("br"),n._v("Trương Huyền xua tay."),t("br"),t("br"),n._v("“Cái này...” Hàn hội trưởng miệng há ra, muốn phản bác, lại nói không ra lời nói tới."),t("br"),t("br"),n._v("Đối phương nói không sai, nước có thể chở thuyền cũng có thể lật thuyền, lực lượng hung mãnh có thể tiêu diệt trận pháp, nếu như có thể hảo hảo lợi dụng, cũng có thể trở thành động lực, để trận pháp phát huy ra càng thêm lực lượng đáng sợ."),t("br"),t("br"),n._v("Đương nhiên, khả năng này là cấp bậc cao hơn trận pháp sư mới có thể làm đến, hắn loại thực lực này, kém quá xa."),t("br"),t("br"),n._v("“Được rồi, ta đi vào trước nhìn một chút!”"),t("br"),t("br"),n._v("Giải thích xong, không nói thêm lời, Trương Huyền gọi Kim Nguyên đỉnh một tiếng, thân thể nhảy lên, lập tức nhảy vào đỉnh lô bên trong."),t("br"),t("br"),n._v("Cái tên này cái đầu cực lớn, đỉnh lô dung nạp lửa than chỗ, ngồi lên ba, bốn người cũng không thành vấn đề, vừa vặn có thể để cho nó mang bản thân đi vào dung nham chỗ sâu nhìn một chút."),t("br"),t("br"),n._v("Chỉ bằng vào chính nó lời nói, lấy cái tên này không đáng tin cậy hơn nữa lại đắc ý tính cách, khẳng định cũng nhìn không ra như thế về sau."),t("br"),t("br"),n._v("“Cẩn thận...”"),t("br"),t("br"),n._v("Thấy hắn đi vào đỉnh lô, bất cứ lúc nào cũng sẽ đi xuống, Lạc Nhược Hi nhịn không được nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Ta đến rồi!” Kim Nguyên đỉnh một tiếng hưng phấn la lên, trực tiếp hướng lực lượng bạo phát chỗ vọt tới."),t("br"),t("br"),n._v("Giờ phút này, lực lượng đã bạo phát xong, đại khái sẽ có mười phút đồng hồ thỉnh thoảng, vừa vặn có thể thừa dịp thời gian này tiến vào bên trong, nếu không, lực lượng một khi bạo phát, liền xem như Kim Nguyên đỉnh, cũng tất nhiên không chịu nổi."),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Cực lớn lô đỉnh vừa tiến vào dung nham, lập tức phát ra nổi lên thanh âm, ngay sau đó ngồi ở trong đó Trương Huyền, liền cảm thấy bốn phía nồng đậm nhiệt lượng không ngừng đánh tới."),t("br"),t("br"),n._v("Kim Nguyên đỉnh là Khí đỉnh, liền sắt thép khoáng thạch đều có thể hòa tan, đủ thấy bên trong nhiệt độ, giấu ở trong đó, mặc dù không tiếp xúc dung nham, có thể phong kín môi trường bên dưới, nhiệt lượng không cách nào toả ra, nhiệt độ cũng không thể so với bên ngoài thấp hơn bao nhiêu."),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền chân khí trong cơ thể vận chuyển, lập tức cảm giác áp lực nhỏ đi rất nhiều, thân thể nhoáng một cái, đứng dậy: “Kim Nguyên đỉnh, dừng lại!”"),t("br"),t("br"),n._v("“Dừng ở cái này?”"),t("br"),t("br"),n._v("Lô đỉnh sững sờ."),t("br"),t("br"),n._v("Bọn họ hiện tại mới đi vào dung nham, liền mười mét khoảng cách cũng chưa tới, vậy thì dừng lại... Làm gì?"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu: “Không cần tiến quá sâu... Ở chỗ này chờ ta!”"),t("br"),t("br"),n._v("Nói xong, tinh thần khẽ động, Vu hồn lập tức thoát thể mà ra, trực tiếp chui ra ngoài, đi vào dung nham."),t("br"),t("br"),n._v("Kim Nguyên đỉnh thực lực cho dù tăng lên không ít, nhưng hơn bốn mươi mét cũng là cực hạn, hơn nữa một chuyến vừa đi vừa về, nguy hiểm không nói, càng là phiền phức, bởi vậy, để hắn đi vào chỉ là lấy cớ, vì bảo hộ thân thể thôi!"),t("br"),t("br"),n._v("Hắn mục đích thực sự là Vu hồn!"),t("br"),t("br"),n._v("Vu hồn vô hình vô chất, mặc dù cũng sẽ nhận dung nham cực nóng ảnh hưởng, lại sẽ không quá lớn, lại thêm, ở trong đó xuyên qua, tốc độ không giảm, tất nhiên có thể đi vào càng sâu chỗ, dò xét ra càng nhiều bí ẩn."),t("br"),t("br"),n._v("“Quả nhiên cùng tưởng tượng đồng dạng, ta Vu hồn tiến vào bên trong, không những sẽ không nhận tổn thương, còn có thể hấp thu lực lượng, trở nên càng thêm lớn mạnh!”"),t("br"),t("br"),n._v("Hồn phách đi vào dung nham, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Hắn Vu hồn, là Thiên Đạo công pháp tu luyện, không sợ cực nóng cùng nắng gắt, nếu là đổi lại bình thường vu hồn sư, toàn thân âm lãnh, đừng nói tiến vào bên trong, chỉ sợ tại đây cái lô đỉnh bên trong, đều sẽ bị tươi sống nướng chết."),t("br"),t("br"),n._v("“Cái này nhiệt lượng, có thể rèn luyện vũ khí, để hắn tạp chất giảm bớt, có phải hay không cũng có thể rèn luyện hồn thể?”"),t("br"),t("br"),n._v("Trong lòng đột nhiên động một cái."),t("br"),t("br"),n._v("Vu hồn hồn thể, mặc dù vô hình vô chất, nhưng cùng nguyên thần đồng dạng, thuộc về có ý thức, thật tồn tại đồ vật, đã vũ khí có thể rèn luyện, vì cái gì thứ này không thể?"),t("br"),t("br"),n._v("“Thử một chút...”"),t("br"),t("br"),n._v("Tinh thần khẽ động, hồn thể an tĩnh lơ lửng tại dung nham bên trong, chậm chạp hấp thu chung quanh nhiệt lượng."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Nhiệt lưu lập tức lao qua, để hắn hồn phách, tại cực lớn nhiệt lượng bên dưới, trở nên cứng cáp hơn, càng thêm cường đại, ẩn chứa trong đó tạp chất, cùng trong tưởng tượng đồng dạng, không ngừng bị loại bỏ."),t("br"),t("br"),n._v("“Quả nhiên có thể thực hiện...”"),t("br"),t("br"),n._v("Mắt sáng rực lên."),t("br"),t("br"),n._v("Hắn hồn thể cho tới nay đều cảm thấy quá mức béo phệ, đi vào thân thể, đều mười phần phiền phức, muốn rèn luyện, nhưng thủy chung làm không được, cái này dung nham, nhiệt độ cực nóng vô cùng, vừa vặn phù hợp."),t("br"),t("br"),n._v("“Bất quá, trước không vội tu luyện, nơi này, mười phút đồng hồ bạo phát một lần năng lượng, hồn thể có thể sẽ không bị thương tổn, Kim Nguyên đỉnh có khả năng sẽ bị trực tiếp chấn bay ra ngoài, thật muốn như thế liền phiền toái...”"),t("br"),t("br"),n._v("Mặc dù rất muốn tu luyện, dùng sức rèn luyện hiện tại hồn phách, nhưng cũng hiểu, bây giờ không phải là rèn luyện thời điểm, nhẹ nhàng cười một tiếng, tiếp tục hướng xuống bay đi."),t("br"),t("br"),n._v("Hiện tại trọng yếu nhất chính là tìm tới di tích lối vào ở đâu, còn tu luyện, hoàn toàn có thể đợi một hồi lại nói."),t("br"),t("br"),n._v("Vu hồn dưới đường đi đi, cùng Ngô sư nói đồng dạng, càng hướng phía dưới, nhiệt độ cũng liền càng lớn, dường như có khu ở giữa đồng dạng, mười mét vì một cái giới hạn."),t("br"),t("br"),n._v("“Dựa theo phỏng đoán, Thánh vực nhất trọng có thể tại trong phạm vi mười thước hoạt động, nhị trọng, hai mươi mét là cực hạn, cứ thế mà suy ra, Ngô sư là Thánh vực tứ trọng, cho nên tối đa cũng liền đến đến bốn mươi mét, lại sau này, liền không làm được!”"),t("br"),t("br"),n._v("Vu hồn tốc độ rất nhanh, mấy hơi thở qua đi, là đến bốn mươi mét chiều sâu, cảm thụ một đoạn này khoảng cách nhiệt độ biến hóa, Trương Huyền trong lòng phỏng đoán."),t("br"),t("br"),n._v("“Bốn mươi mét quả nhiên đủ nóng, ta hiện tại liền xem như Vu hồn trạng thái, cũng có chút khó mà chống lại...”"),t("br"),t("br"),n._v("Ba mươi vị trí đầu mét, thân là Vu hồn, cảm giác ảnh hưởng không lớn, nhưng đến bốn mươi mét, lập tức cảm thấy nhiệt lượng đánh tới, liền xem như hồn phách trạng thái, đều có chút chống lại không được."),t("br"),t("br"),n._v("“Tiếp tục!”"),t("br"),t("br"),n._v("Thiên Đạo Vu hồn công pháp, ở trong người vận chuyển, cũng không ngừng nghỉ, đột phá bốn mươi ràng buộc, tiếp tục hướng xuống phóng đi."),t("br"),t("br"),n._v("Vừa qua giới hạn, lập tức cảm thấy giống như là tiến vào chảo dầu, cho dù trạng thái của hắn bây giờ, đều bị thiêu đốt có chút vặn vẹo, mặc dù không có mồ hôi, nhưng cảm thấy béo phệ hồn thể bất cứ lúc nào cũng sẽ không chịu nổi, trực tiếp sụp đổ."),t("br"),t("br"),n._v("“Không được... Đây là Xuất Khiếu cảnh cường giả mới có thể đi vào chỗ, ta mặc dù là Vu hồn, cũng đạt tới cực hạn...”"),t("br"),t("br"),n._v("Sầm mặt lại."),t("br"),t("br"),n._v("Vốn nghĩ nhờ vào hồn phách, khẳng định có thể đi vào càng sâu chỗ, sẽ không nhận ảnh hưởng, hiện tại xem ra, vẫn là quá coi thường dung nham nhiệt độ."),t("br"),t("br"),n._v("Có thể để cho Thánh vực tứ trọng danh sư, đều không chịu nổi, hắn Vu hồn hiện nay chỉ có Hóa Phàm cửu trọng thực lực, không chịu nổi cũng rất bình thường."),t("br"),t("br"),n._v("Cắn răng lại hướng hạ du một khoảng cách, vừa qua khỏi năm mươi mét phạm vi, lập tức cảm thấy hồn phách bắt đầu sụp đổ, đã không cách nào khống chế."),t("br"),t("br"),n._v("Nhiệt lượng càng lớn, vật chất hoạt tính cũng liền càng lớn, trở nên càng thêm không ổn định, thật giống như rất nhiều thể rắn vũ khí, tại cực nóng phía dưới, biến thành chất lỏng đồng dạng."),t("br"),t("br"),n._v("Cho dù Vu hồn không thuộc về vật chất, nhưng bốn phía hoạt tính quá lớn, không gian đều sẽ trở nên không ổn định, huống chi một cái hồn phách, lập tức trở nên sụp xuống."),t("br"),t("br"),n._v("Vội vàng lùi về đến năm mươi mét, lần này cảm thấy thoải mái rất nhiều, đồng thời mày nhăn lại."),t("br"),t("br"),n._v("“Đều đi tới hơn năm mươi mét, cũng không phát hiện di tích tung tích, chẳng lẽ... Suy tính sai, không ở nơi này?”"),t("br"),t("br"),n._v("Vừa rồi đi vào sáu mươi mét, vẫn như cũ cái gì cũng không phát hiện, chẳng qua là cảm thấy càng ngày càng nóng, nhiệt độ càng ngày càng cao, có lẽ cùng trước đó Ô Thiên Khung nói như vậy, sắp tiếp xúc địa tâm dung nham."),t("br"),t("br"),n._v("Nóng như vậy chỗ, trận kỳ đều sẽ hòa tan, cho dù có không gian gấp, khẳng định cũng sẽ không chịu nổi, chẳng lẽ, trước đó tính toán sai lầm, cái gọi là cửa vào di tích, cũng không tại đây cái vị trí?"),t("br"),t("br"),n._v("Đang trầm tư, đột nhiên phía dưới một tiếng vang thật lớn, tựa như cối xay khổng lồ xoay tròn."),t("br"),t("br"),n._v("“Nguy rồi? Làm sao lần này lực lượng trước thời hạn bạo phát... Xong...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, lập tức nhìn thấy phía dưới một cỗ cường đại lực lượng bỗng nhiên hướng hắn dâng trào mà tới!"),t("br"),t("br"),n._v("(Valentine hạnh phúc, không có tình nhân, tắm một cái ăn tết, thế nào, năm nay cũng không tệ lắm, chí ít... Không có tình nhân, vẫn có thể khúc mắc.)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1039"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1041"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);