(window.webpackJsonp=window.webpackJsonp||[]).push([[616],{971:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1531-đoc-su-pha-tran"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1531-đoc-su-pha-tran"}},[n._v("#")]),n._v(" Chương 1531: Độc sư phá trận")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1531: Độc sư phá trận"),t("br"),t("br"),n._v("Mười mấy vị cửu tinh danh sư sức chiến đấu thực sự quá kinh người, đồng thời ra tay, đừng nói ba vị độc sư, coi như Dương sư tới, chỉ sợ đều không chịu nổi!"),t("br"),t("br"),n._v("“Nghe ngươi an bài?”"),t("br"),t("br"),n._v("Thẩm Khuyết đám người sửng sốt một chút."),t("br"),t("br"),n._v("Đối mặt nhiều như vậy cửu tinh danh sư, bọn họ đều không có phương pháp, ngươi một cái chỉ có Thánh vực bát trọng tiểu nhân vật, có thể có biện pháp nào?"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Lên tiếng, Trương Huyền biểu hiện trên mặt không thay đổi, ngẩng đầu nhìn về phía ba người bọn họ, đồng thời theo trên thân mọi người từng cái đảo qua."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, ba người nhìn nhau, mỗi người cau mày."),t("br"),t("br"),n._v("Trên không một đám, thế nhưng là cửu tinh danh sư, mỗi một cái đều cường đại đến khó tin, nghe ngươi dặn dò... Muốn chúng ta toàn quân bị diệt à?"),t("br"),t("br"),n._v("“Nghe hắn a, điện chủ từng nói qua, muốn so tôn trọng nàng còn muốn tôn trọng vị này!”"),t("br"),t("br"),n._v("Lâm Giang Hải truyền âm."),t("br"),t("br"),n._v("Thẩm Khuyết hai người nhẹ gật đầu."),t("br"),t("br"),n._v("Điện chủ đối trước mắt vị này coi trọng, bọn họ hết sức rõ ràng, thật muốn xảy ra chuyện, tuyệt đối sẽ không có hai lời trực tiếp đem hắn tại chỗ độc chết!"),t("br"),t("br"),n._v("Tình huống bây giờ như vậy, không có những biện pháp khác, cũng chỉ có thể nghe đối phương, nhìn có hay không biện pháp tốt, vượt qua nguy cơ trước mắt."),t("br"),t("br"),n._v("“Trương sư, chúng ta làm sao bây giờ?”"),t("br"),t("br"),n._v("Dùng hết toàn lực, chặn lại một đợt công kích, Lâm Giang Hải cũng nhịn không được nữa nhìn lại."),t("br"),t("br"),n._v("“Rất đơn giản, từ bỏ Lồng ánh sáng, Thẩm Khuyết, vận chuyển Kim Dương độc công, tiến lên hai mươi bước, bấm tay bên trái đạn, tay trái ngươi móng tay, có【 phá trần tiêu】 nọc độc, trực tiếp đánh đi ra. Lâm Giang Hải, ngươi phía bên phải tiến lên hai mươi bảy bước, đem chuẩn bị xong【 hòa âm hồng】 kịch độc thả ra, không cần hại người, chỉ cần rải ra, liền sẽ có kỳ hiệu. Gì cao ốc mệnh danh thiên thủ, trên người mang theo kịch độc chủng loại rất nhiều, hẳn là có năm mươi bốn loại, đứng tại chỗ, trước hướng về phía trên không bắn ra tám loại, sau đó, bình bắn mười bảy loại, còn lại nghe ta dặn dò, rồi quyết định có phải hay không tiếp tục nữa!”"),t("br"),t("br"),n._v("Trương Huyền thanh âm vang lên bên tai mọi người."),t("br"),t("br"),n._v("“Ngươi... Ngươi biết ta có phá trần tiêu nọc độc?”"),t("br"),t("br"),n._v("Thẩm Khuyết thân thể cứng đờ."),t("br"),t("br"),n._v("Đây tuyệt đối là lần đầu tiên nhìn thấy vị này Trương sư, trước đó có thể bảo đảm, vốn không che mặt, phá trần tiêu kịch độc, là trước khi hắn tới vừa mới nghiên cứu ra tới, trên đường lệnh tên... Ngay cả điện chủ đều không rõ ràng lắm, hắn một cái nói ra, hơn nữa biết Đạo Tạng tại tay trái móng tay..."),t("br"),t("br"),n._v("Không chỉ hắn bộ dáng như thế, gì cao ốc cũng đầy mặt điên cuồng, giống như gặp quỷ: “Ngươi biết trên người của ta có năm mươi bốn loại kịch độc?”"),t("br"),t("br"),n._v("Trên người hắn kịch độc chủng loại, Lâm Giang Hải bọn người không biết được, làm che giấu sâu nhất át chủ bài, có hai loại kịch độc, giấu ở bí ẩn nhất chỗ, không đến sống chết trước mắt, không có khả năng dùng ra, dần dà, ngay cả hắn đều nhớ không rõ, đối phương một cái nói ra, không có sai lầm chút nào..."),t("br"),t("br"),n._v("Mấu chốt nhất là, để hắn bắn độc... Hướng lên bắn ra, hơn nữa lại vô cùng bí ẩn, vừa vặn có tám loại, bình lấy bắn ra, không có chút nào trắc trở, tốc độ nhanh nhất, cũng vừa tốt là mười bảy loại..."),t("br"),t("br"),n._v("Nói cách khác, đối phương đối với hắn tình huống hiện tại, biết đến rõ rõ ràng ràng!"),t("br"),t("br"),n._v("Thật chỉ là lần đầu tiên gặp mặt?"),t("br"),t("br"),n._v("“Không muốn chết liền động tác nhanh lên một chút!”"),t("br"),t("br"),n._v("Thấy mình phân phó xong, mấy người không những không có động tĩnh, ngược lại ngây người, Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("“Là!”"),t("br"),n._v(" Biết giờ phút này không phải lúc nghĩ những thứ này, ba người lại không chần chừ, đồng thời dựa theo dặn dò đồng loạt ra tay."),t("br"),t("br"),n._v("Mặc dù không biết như vậy dặn dò đến cùng có ích lợi gì, nhưng có thể nói thẳng ra bọn họ am hiểu công pháp cùng kịch độc, nói rõ nhất định có mục đích của mình."),t("br"),t("br"),n._v("Thẩm Khuyết trong cơ thể Kim Dương độc công nhanh chóng vận chuyển, từ bỏ vừa rồi Lồng ánh sáng, bỗng nhiên xông về trước ra, không nhiều không ít, vừa vặn hai mươi bước."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Vừa mới dừng lại, liền cảm thấy một cỗ lực lượng khổng lồ đánh tới, hai vị danh sư công kích đã xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Tay trái móng tay nhẹ nhàng vạch một cái, phá trần tiêu nọc độc đánh ra ngoài."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Nọc độc cùng trước mắt chân khí đụng một cái, lập tức xuất hiện hủ thực hiện tượng."),t("br"),t("br"),n._v("“Không tốt, là kịch độc...”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, hai vị danh sư, đồng thời lui về phía sau, mà liền tại lúc này, đi tới hai mươi bảy bước Lâm Giang Hải, đã đem hòa âm hồng độc phấn thả ra, hai người lần này lui về phía sau, vừa vặn bước vào phạm vi, chân khí tới hơi dính, lập tức sôi trào lên, giống như là thiêu đốt đồng dạng."),t("br"),t("br"),n._v("Phốc! Phốc!"),t("br"),t("br"),n._v("Hai vị danh sư, nhất thời phun ra máu tươi, cũng lại không để ý tới liên thủ công kích, đồng loạt khoanh chân ngồi dưới đất, điều chỉnh hô hấp."),t("br"),t("br"),n._v("Rất nhiều danh sư hình thành là trận pháp, hai vị này dừng lại, lập tức phá, gì cao ốc hơn hai mươi loại kịch độc thả ra ngoài, bốn phía tựa như tạo thành một cái khu vực chân không, đám người nhao nhao trốn đi, mỗi người lơ lửng ở trên trăm mét có hơn, trong lúc nhất thời thế mà không có người còn dám trước mắt."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nhìn thấy nguy cơ trước mắt, một chút liền được giải quyết, ba đại độc sư hai mặt nhìn nhau, mỗi người nắm đấm căng thẳng."),t("br"),t("br"),n._v("Bọn họ đối với mình thực lực, biết đến rất rõ ràng, cho dù có độc, nhưng đối phương nắm giữ thiên vân Thánh thủy, vừa rồi loại tình huống kia, tiếp tục nữa, khẳng định sớm đã bản thân bị trọng thương, lại khó trốn."),t("br"),t("br"),n._v("Mà bây giờ, nghe theo Trương sư an bài, không chỉ tránh đi Thánh thủy công kích, còn độc hai vị danh sư mất đi sức chiến đấu, càng đem một đám danh sư sợ hãi đến lui về phía sau, không dám tới... Phần này chiến tích truyền đi, tuyệt đối có thể vinh quang cửa nhà, làm cho cả Độc điện cũng vì đó mở mày mở mặt!"),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, nhìn lại, ba người tràn đầy khó tin."),t("br"),t("br"),n._v("Đối phương muốn bọn họ tiến hành việc làm, có thể thi hành, nhưng tại sao phải làm như vậy, liền không có chút nào hiểu."),t("br"),t("br"),n._v("“Vừa rồi vây công hết thảy có mười sáu vị cửu tinh danh sư, trong đó ba vị, là mười năm gần đây bên trong, mới khảo hạch hoàn thành, bất kể tâm cảnh vẫn là thực lực, đều có chút chênh lệch, còn lại có bảy người, từng cùng Dị Linh tộc nhân đối chiến nhận qua ám thương, đến bây giờ đều không có triệt để khôi phục. Còn có bốn vị, làm trận pháp trung tâm, không thể di chuyển nhanh chóng... Thẩm Khuyết tiến lên hai mươi bước, vị trí, vừa lúc là tân tấn ba vị cửu tinh danh sư cùng có ám thương bảy người trận nhãn, phóng thích nọc độc, sẽ để cho tổ hợp mà thành trận pháp, xuất hiện bối rối, lực lượng sẽ xuất hiện vấn đề, lúc này, gì cao ốc phóng thích kịch độc, ép trung tâm rời đi, trận pháp tự nhiên là lập tức tán loạn.”"),t("br"),t("br"),n._v("Biết bọn họ tò mò, Trương Huyền cũng không che giấu: “Đương nhiên, trận pháp tán loạn, lấy thực lực của bọn hắn, cùng ứng biến, chỉ cần lần nữa động thủ, các ngươi đồng dạng khó mà chống lại, mấu chốt nhất là Lâm Giang Hải phía bên phải tiến lên hai mươi bảy bước... Là bọn họ chân khí tụ hợp vị trí, hòa âm hồng kịch độc vừa vặn lại có thể cùng chân khí giao hòa, phóng thích nhiều như vậy, mặc dù những danh sư này mặt không đổi sắc, trên thực tế lại tất cả đều hoặc nhiều hoặc ít trúng độc... Mặc dù lấy thực lực của bọn hắn tới nói, ảnh hưởng không lớn, cũng đã có sợ ném chuột vỡ bình tác dụng, chí ít, trong thời gian ngắn, không dám tùy tiện công kích!”"),t("br"),t("br"),n._v("“Ừm, cứ như vậy nhiều, thời gian vội vàng, nghĩ tới còn chưa đủ chu toàn, cũng chỉ có thể làm được chút này!”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền thở dài một tiếng."),t("br"),t("br"),n._v("“Cái này đều gọi không chu toàn?”"),t("br"),t("br"),n._v("“Không đến một cái hô hấp nghĩ ra nhiều như vậy...”"),t("br"),t("br"),n._v("“Thời cơ, trận pháp, đối thủ tình huống... Tất cả đều rõ như lòng bàn tay, phân tích chút xíu không kém, đem bản thân cùng thực lực của đối thủ, phán đoán không có bất kỳ cái gì sai lầm... Cái này, cái này... Là ngươi vừa rồi liếc nhìn liếc mắt, nhìn ra được, cũng phân tích ra được?”"),t("br"),t("br"),n._v("Thẩm Khuyết đám người tất cả đều thân thể run rẩy, từng cái giống như gặp quỷ.."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1539"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1541"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);